INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '1', '1', '1', '<span class=\"verse-title\">Prologue</span>Proverbs of Solomon, son of David, king of Israel:'),
('LEB', 'Proverbs', '1', '2', '1', 'To know wisdom and instruction,  to understand sayings of understanding,'),
('LEB', 'Proverbs', '1', '3', '1', 'to gain <span class=\"idiom\">insightful instruction</span>,<span class=\"note\">“the instruction of insight”</span> righteousness and justice and equity,'),
('LEB', 'Proverbs', '1', '4', '1', 'to give shrewdness<span class=\"note\">Or “cleverness,” or “prudence”</span>to the simple,  knowledge and purpose<span class=\"note\">Or “plan”</span>to the young,'),
('LEB', 'Proverbs', '1', '5', '1', 'may the wise hear and increase learning,  and the one who understands gain direction,<span class=\"note\">Or “guidance”</span>'),
('LEB', 'Proverbs', '1', '6', '1', 'to understand a proverb and an expression,  words of wisdom and their riddles.'),
('LEB', 'Proverbs', '1', '7', '1', 'Fear of Adonai <span class=\"supplied\">is the</span> beginning of knowledge;  wisdom and instruction, fools despise.'),
('LEB', 'Proverbs', '1', '8', '3', '<span class=\"verse-title\">Lecture Against Gang Behavior</span>My child, may you keep<span class=\"note\">Or “obey,” or “hear”</span>your father’s instruction,  and do not reject your mother’s teachings,'),
('LEB', 'Proverbs', '1', '9', '3', 'for they <span class=\"supplied\">are</span> a garland of favor for your head,  and pendants for your neck.'),
('LEB', 'Proverbs', '1', '10', '3', 'My child, if sinners entice you, do not consent.'),
('LEB', 'Proverbs', '1', '11', '3', 'If they say,  “Come with us!  We shall lie in wait for blood;  we shall ambush the innocent without cause.<span class=\"note\">Or “wantonly,” or “for nothing”</span>'),
('LEB', 'Proverbs', '1', '12', '3', 'Like Sheol,<span class=\"note\">A term for the place where the dead reside, i.e., the Underworld</span>we will swallow them alive  and whole, <span class=\"idiom\">like those who descend to the pit</span>.<span class=\"note\">“like the descenders of a pit”</span>'),
('LEB', 'Proverbs', '1', '13', '3', 'We shall find all precious wealth,  we shall fill our houses <span class=\"supplied\">with</span> booty,'),
('LEB', 'Proverbs', '1', '14', '3', 'you shall throw your lot in our midst,  there will be one purse for all of us.”'),
('LEB', 'Proverbs', '1', '15', '3', 'My child, do not walk in their way.<span class=\"note\">Or “on their road”</span> Keep your foot from their paths,'),
('LEB', 'Proverbs', '1', '16', '3', 'for their feet run to evil,  and they hurry to shed blood,'),
('LEB', 'Proverbs', '1', '17', '3', 'for “in vain<span class=\"note\">Or “without cause,” or “for nothing”</span>is the net scattered,  in the sight of any <span class=\"idiom\">winged bird</span>.”<span class=\"note\">“bird of wing”</span>'),
('LEB', 'Proverbs', '1', '18', '3', 'They lie in wait for their <span class=\"supplied\">own</span> blood.  They ambush their <span class=\"supplied\">own</span> lives.'),
('LEB', 'Proverbs', '1', '19', '3', 'Thus are the ways of all who are greedy <span class=\"supplied\">for</span> gain—  it will take the life of its possessors.'),
('LEB', 'Proverbs', '1', '20', '5', '<span class=\"verse-title\">The Call of Lady Wisdom</span>Wisdom calls out in the streets,  in the squares she raises her voice.'),
('LEB', 'Proverbs', '1', '21', '5', 'On a <span class=\"idiom\">busy corner</span><span class=\"note\">“head of commotion,” or “head of stirring, bustling”</span>she cries out,  at the entrances of the gates in the city, she speaks her sayings:'),
('LEB', 'Proverbs', '1', '22', '5', '“<span class=\"idiom\">How long</span>,<span class=\"note\">“Until when”</span>O simple ones, will you love simplicity?  And <span class=\"supplied\">how long</span> will scoffers delight in their scoffing,  and fools hate knowledge?'),
('LEB', 'Proverbs', '1', '23', '5', 'May you turn to my argument!<span class=\"note\">Or “turn at my reproach”</span> Behold, I shall pour out my spirit upon you;  I will make my words known to you.'),
('LEB', 'Proverbs', '1', '24', '5', 'Because I called out and you refused me,  I stretched out my hand, yet there is none who heeds.'),
('LEB', 'Proverbs', '1', '25', '5', 'You have ignored all my counsel,  and my reproof you are not willing <span class=\"supplied\">to accept</span>.'),
('LEB', 'Proverbs', '1', '26', '5', 'I will also laugh at your calamity;<span class=\"note\">Or “downfall”</span> I will mock when panic<span class=\"note\">Or “dread, fear”</span>comes <span class=\"supplied\">upon</span> you.'),
('LEB', 'Proverbs', '1', '27', '5', 'When your panic comes like a storm,  and your calamity arrives like a whirlwind,  when distress and anguish come upon you,'),
('LEB', 'Proverbs', '1', '28', '5', 'then they will call<span class=\"note\">Or “cry out <span class=\"supplied\">to</span>”</span>me, but I will not answer;  they will seek me diligently but not find me.'),
('LEB', 'Proverbs', '1', '29', '5', 'Since they hated knowledge,  and did not choose the fear of Adonai,'),
('LEB', 'Proverbs', '1', '30', '5', 'they were not willing <span class=\"supplied\">to accept</span> my counsel,  they despised all my reproof.'),
('LEB', 'Proverbs', '1', '31', '5', 'They shall eat from the fruit of their way,  and they shall be sated from their own schemes,'),
('LEB', 'Proverbs', '1', '32', '5', 'for the waywardness of the simple ones will kill them,  and the complacency of fools will destroy them.'),
('LEB', 'Proverbs', '1', '33', '5', 'Whoever listens to me will dwell in security  and rest securely<span class=\"note\">Or “be at ease, peace”</span>from dread and disaster.”')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '2', '1', '1', '<span class=\"verse-title\">The Benefits of Wisdom</span>My child, if you will receive<span class=\"note\">Or “take, seize”</span>my sayings,  and hide my commands with you,'),
('LEB', 'Proverbs', '2', '2', '1', '<span class=\"supplied\">in order</span> to incline your ear toward wisdom,  <span class=\"supplied\">then</span> you shall apply your heart to understanding.'),
('LEB', 'Proverbs', '2', '3', '1', 'For if you cry out for understanding,<span class=\"note\">Or “call to understanding”</span> <span class=\"supplied\">if</span> you lift<span class=\"note\">Or “carry, give”</span>your voice for insight,'),
('LEB', 'Proverbs', '2', '4', '1', 'if you seek her like silver  and search her out <span class=\"idiom\">like treasure</span>,<span class=\"note\">“like the treasures”</span>'),
('LEB', 'Proverbs', '2', '5', '1', 'then you will understand the fear of Adonai,  and the knowledge of God you will find.'),
('LEB', 'Proverbs', '2', '6', '1', 'For Adonai will give wisdom;  from his mouth <span class=\"supplied\">come</span> knowledge and understanding.'),
('LEB', 'Proverbs', '2', '7', '1', 'For the upright, he stores<span class=\"note\">Or “hides, keeps”</span>sound judgment, a shield for those who walk uprightly,'),
('LEB', 'Proverbs', '2', '8', '1', '<span class=\"supplied\">in order</span> to guard paths of justice  and keep<span class=\"note\">Or “preserve, guard, watch”</span>the way of his faithful <span class=\"supplied\">ones</span>.'),
('LEB', 'Proverbs', '2', '9', '1', 'Then you will understand righteousness and justice  and uprightness—every good course—'),
('LEB', 'Proverbs', '2', '10', '1', 'for wisdom will enter your heart,  and knowledge will be pleasing to your soul.<span class=\"note\">Or “inner self”</span>'),
('LEB', 'Proverbs', '2', '11', '1', 'Discretion will watch over you;  understanding will protect you,'),
('LEB', 'Proverbs', '2', '12', '1', '<span class=\"supplied\">in order</span> to deliver you from the way of evil,<span class=\"note\">Or “evildoer”</span> from a man who speaks devious things—'),
('LEB', 'Proverbs', '2', '13', '1', 'those who forsake the paths of uprightness  to walk in ways of darkness,'),
('LEB', 'Proverbs', '2', '14', '1', 'those who are happy to do evil,  <span class=\"supplied\">for</span> they delight in <span class=\"supplied\">the</span> deviousness of evil,'),
('LEB', 'Proverbs', '2', '15', '1', '<span class=\"idiom\">who are crooked in their ways</span>,<span class=\"note\">“that <span class=\"supplied\">in</span> their ways are crooked”</span> and devious in their paths;'),
('LEB', 'Proverbs', '2', '16', '1', '<span class=\"supplied\">in order</span> to deliver you from a strange woman,  from a foreign woman <span class=\"supplied\">who</span> flatters<span class=\"note\">Or “makes smooth”</span><span class=\"supplied\">with</span> her sayings,'),
('LEB', 'Proverbs', '2', '17', '1', 'she who forsakes the partner of her youth  and has forgotten the covenant of her God,'),
('LEB', 'Proverbs', '2', '18', '1', 'for her house sinks to death,  and to the dead<span class=\"note\">Or “Rephaim”</span>are her paths.'),
('LEB', 'Proverbs', '2', '19', '1', '<span class=\"supplied\">Of</span> all who go to her, none shall return,  nor do they reach paths of life.'),
('LEB', 'Proverbs', '2', '20', '1', 'So that you will walk on the road of those who are good,  and the paths of those who are righteous you shall keep.'),
('LEB', 'Proverbs', '2', '21', '1', 'For those who are upright will dwell <span class=\"supplied\">in the</span> land,<span class=\"note\">Or “earth”</span> and those who are blameless<span class=\"note\">Or “innocent”</span>will remain in it.'),
('LEB', 'Proverbs', '2', '22', '1', 'And those who are wicked will be cut off from the land,  and those who are treacherous will be uprooted from it.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '3', '1', '1', '<span class=\"verse-title\">Wisdom and Piety</span>My child, do not forget my instruction,  and may your heart guard my commands.'),
('LEB', 'Proverbs', '3', '2', '1', 'For length of days, years of life,  and peace they<span class=\"note\">That is, “my commands” (3:1)</span>shall add to you.'),
('LEB', 'Proverbs', '3', '3', '1', 'May loyal love<span class=\"note\">Or “kindness, mercy”</span>and truth not forsake you;  bind them around your neck,  write them upon your heart.'),
('LEB', 'Proverbs', '3', '4', '1', 'And you shall find favor and good sense<span class=\"note\">Or “understanding, prudence”</span> in the eyes of God and humankind.'),
('LEB', 'Proverbs', '3', '5', '1', '<span class=\"idiom\">Trust Adonai</span><span class=\"note\">“trust toward Adonai”</span>with all your heart;  do not lean toward your own understanding.'),
('LEB', 'Proverbs', '3', '6', '1', 'In all your ways acknowledge him,  and he will straighten your paths.'),
('LEB', 'Proverbs', '3', '7', '1', 'Do not be wise in your own eyes;  fear Adonai and retreat from evil.'),
('LEB', 'Proverbs', '3', '8', '1', 'There shall be healing for your <span class=\"idiom\">flesh</span>,<span class=\"note\">“navel”</span> and refreshment for your body.'),
('LEB', 'Proverbs', '3', '9', '1', 'Honor Adonai from your substance,<span class=\"note\">Or “wealth, property”</span> and from the firstfruits of all that will come to you,'),
('LEB', 'Proverbs', '3', '10', '1', 'and your barns shall be full of plenty,  and your vats shall burst <span class=\"supplied\">with</span> new wine.'),
('LEB', 'Proverbs', '3', '11', '3', '<span class=\"verse-title\">Happy Is the One Who Finds Wisdom</span>Do not despise the discipline of Adonai, my child.  Do not be weary of his reproof'),
('LEB', 'Proverbs', '3', '12', '3', 'because whomever Adonai will love, he will rebuke,  as a father delights in his son.'),
('LEB', 'Proverbs', '3', '13', '3', 'Happy <span class=\"supplied\">is</span> <span class=\"idiom\">the one</span><span class=\"note\">“a man”</span>who finds wisdom,  and one who obtains understanding.'),
('LEB', 'Proverbs', '3', '14', '3', 'For her<span class=\"note\">Or “its”; both “wisdom” and “understanding” are feminine nouns and can be read as either “she” or “it”</span>income is better than the income of silver,  and her revenue than <span class=\"supplied\">that of</span> gold.'),
('LEB', 'Proverbs', '3', '15', '3', 'She is more precious than rubies,  and all your desires shall not compare with her.'),
('LEB', 'Proverbs', '3', '16', '3', 'Length of days <span class=\"supplied\">is</span> in her right hand;  in her left hand <span class=\"supplied\">are</span> riches and honor.'),
('LEB', 'Proverbs', '3', '17', '3', 'Her ways <span class=\"supplied\">are</span> ways of pleasantness,  and all her paths <span class=\"supplied\">are</span> peace.'),
('LEB', 'Proverbs', '3', '18', '3', 'She is a tree of life for those who seize her;  those who take hold of her are considered happy.'),
('LEB', 'Proverbs', '3', '19', '5', '<span class=\"verse-title\">Role of Wisdom in Creation and Society</span>Adonai in wisdom founded the earth;  he established the heavens in understanding.'),
('LEB', 'Proverbs', '3', '20', '5', 'With his knowledge, depths broke open,  and clouds dropped dew.'),
('LEB', 'Proverbs', '3', '21', '5', 'My child, may they<span class=\"note\">Grammatically, “they” most likely refers to clouds (3:20). In context, “they” may refer to wisdom and understanding (3:13).</span>not escape from your <span class=\"idiom\">sight</span>;<span class=\"note\">“eyes”</span> may you keep sound wisdom and prudence.'),
('LEB', 'Proverbs', '3', '22', '5', 'They shall be life for your soul<span class=\"note\">Or “inner self”</span> and adornment<span class=\"note\">Or “favor”</span>for your neck.'),
('LEB', 'Proverbs', '3', '23', '5', 'Then you will walk in the confidence of your ways,  and your foot will not stumble.'),
('LEB', 'Proverbs', '3', '24', '5', 'If you sit down, you will not panic,<span class=\"note\">Or “fear, dread”</span> and <span class=\"supplied\">if</span> you lie down, then your sleep shall be sweet.'),
('LEB', 'Proverbs', '3', '25', '5', 'Do not be afraid of sudden panic,<span class=\"note\">Or “fear, dread”</span> or the storm of wickedness that will come.'),
('LEB', 'Proverbs', '3', '26', '5', 'Adonai will be <span class=\"idiom\">your confidence</span><span class=\"note\">“in your confidence”</span> and guard your foot from capture.'),
('LEB', 'Proverbs', '3', '27', '5', 'Do not withhold good from its owner<span class=\"note\">Or “lord”</span> when it is in the power of your hand to do.'),
('LEB', 'Proverbs', '3', '28', '5', 'Do not say to your neighbor,  “Go and return and tomorrow I will give <span class=\"supplied\">it</span>,”  <span class=\"idiom\">when it is with you</span>.<span class=\"note\">“and there is with you”</span>'),
('LEB', 'Proverbs', '3', '29', '5', 'Do not plot harm against your neighbor  <span class=\"supplied\">who</span> dwells in confidence beside you.'),
('LEB', 'Proverbs', '3', '30', '5', 'Do not quarrel with anyone without cause,  when he did not do you harm.'),
('LEB', 'Proverbs', '3', '31', '5', 'Do not envy a man of violence,  and do not choose any of his ways,'),
('LEB', 'Proverbs', '3', '32', '5', 'for he who is perverse <span class=\"supplied\">is</span> an abomination of Adonai,  but those who are upright <span class=\"supplied\">are</span> his confidence.'),
('LEB', 'Proverbs', '3', '33', '5', 'The curse of Adonai <span class=\"supplied\">is</span> on the house of the wicked,  and the abode of the righteous ones he blesses.'),
('LEB', 'Proverbs', '3', '34', '5', 'With those who scorn, he is scornful,  but to those who are humble, he gives favor.'),
('LEB', 'Proverbs', '3', '35', '5', 'They will inherit the honor of the wise,  but stubborn fools, disgrace.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '4', '1', '1', '<span class=\"verse-title\">The Father’s Wisdom</span>Children, listen <span class=\"supplied\">to the</span> instruction of a father,  and be attentive <span class=\"supplied\">in order</span> to know insight.'),
('LEB', 'Proverbs', '4', '2', '1', 'For I have given you good instruction;  do not forsake my teaching.'),
('LEB', 'Proverbs', '4', '3', '1', 'When I was a son to my father,  tender and alone before my mother,'),
('LEB', 'Proverbs', '4', '4', '1', 'he taught me and said to me,  “May your heart hold fast <span class=\"supplied\">to</span> my words;  guard my commandments and live.'),
('LEB', 'Proverbs', '4', '5', '1', 'Get wisdom and insight;  do not forget and do not turn from the sayings of my mouth.'),
('LEB', 'Proverbs', '4', '6', '1', 'Do not forsake her—then she will guard you;  love her and she will keep you.'),
('LEB', 'Proverbs', '4', '7', '1', 'The beginning of wisdom: Get wisdom!  With all <span class=\"supplied\">that is in</span> your possession, gain insight.'),
('LEB', 'Proverbs', '4', '8', '1', 'Cherish her and she will exalt you;  she will honor you if you embrace her.'),
('LEB', 'Proverbs', '4', '9', '1', 'She will give a garland for your head;  she shall bestow a crown of glory upon you.”'),
('LEB', 'Proverbs', '4', '10', '3', '<span class=\"verse-title\">The Right Path</span>Listen, my child, take my sayings,  and <span class=\"supplied\">the</span> years of your life shall be great.'),
('LEB', 'Proverbs', '4', '11', '3', 'In the way of wisdom I have instructed you;  I have led you in the path of uprightness.'),
('LEB', 'Proverbs', '4', '12', '3', '<span class=\"idiom\">When you walk</span>,<span class=\"note\">“In your walking”</span>your step will not be hampered,  and if you run, you will not stumble.'),
('LEB', 'Proverbs', '4', '13', '3', 'Seize the instruction! Do not let go!  Guard her, for she is your life.'),
('LEB', 'Proverbs', '4', '14', '3', 'In the path of the wicked do not go;  do not walk in the way of those who do evil.'),
('LEB', 'Proverbs', '4', '15', '3', 'Avoid it; do not transgress it;  turn away from it and pass by.'),
('LEB', 'Proverbs', '4', '16', '3', 'For they will not sleep if they have not done wrong,  and they are robbed of their sleep if they do not cause stumbling.'),
('LEB', 'Proverbs', '4', '17', '3', 'For they ate the bread of wickedness,  and they drank the wine of violence.'),
('LEB', 'Proverbs', '4', '18', '3', 'But the path of the righteous ones <span class=\"supplied\">is</span> like the light of dawn,  leading and shining until the day <span class=\"idiom\">is full</span>.<span class=\"note\">“<span class=\"supplied\">is</span> correct”</span>'),
('LEB', 'Proverbs', '4', '19', '3', 'The way of the wicked ones <span class=\"supplied\">is</span> like deep darkness;  they do not know what they stumble over.'),
('LEB', 'Proverbs', '4', '20', '5', '<span class=\"verse-title\">Staying the Course</span>My child, be attentive to my words;  to my sayings incline your ear.'),
('LEB', 'Proverbs', '4', '21', '5', 'May they not escape from your <span class=\"idiom\">sight</span>;<span class=\"note\">“eyes”</span> keep them in <span class=\"supplied\">the</span> midst of your heart.'),
('LEB', 'Proverbs', '4', '22', '5', 'For they are life to those who find them  and healing <span class=\"idiom\">to the entire body</span>.<span class=\"note\">“for all his flesh”</span>'),
('LEB', 'Proverbs', '4', '23', '5', 'With all vigilance, keep your heart,  for from it <span class=\"supplied\">comes</span> the source<span class=\"note\">Or “extremity”</span>of life.'),
('LEB', 'Proverbs', '4', '24', '5', 'Remove from yourself <span class=\"idiom\">deceitful speech</span>,<span class=\"note\">“crookedness of mouth”</span> and abolish <span class=\"idiom\">devious talk</span><span class=\"note\">“deviousness of lips”</span>from yourself.'),
('LEB', 'Proverbs', '4', '25', '5', 'May your eyes look forward<span class=\"note\">Or “opposite”</span> and your gaze be straight before you.'),
('LEB', 'Proverbs', '4', '26', '5', 'May the path of your foot be balanced  and all your ways be sure.'),
('LEB', 'Proverbs', '4', '27', '5', 'Do not swerve right or left;  remove your foot from evil.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '5', '1', '0', 'My child, be attentive to my wisdom,  and to my understanding incline your ear;'),
('LEB', 'Proverbs', '5', '2', '0', '<span class=\"supplied\">in order</span> to keep prudence,  and knowledge will guard your lips.'),
('LEB', 'Proverbs', '5', '3', '0', 'For the lips of the strange woman will drip honey,  and smoother than oil is her mouth.<span class=\"note\">Or “palate”</span>'),
('LEB', 'Proverbs', '5', '4', '0', 'But her end is bitter as the wormwood,  sharp as a two-edged sword.'),
('LEB', 'Proverbs', '5', '5', '0', 'Her feet go down <span class=\"supplied\">to</span> death;  her steps take hold <span class=\"supplied\">of</span> Sheol.<span class=\"note\">A term for the place where the dead reside, i.e., the Underworld</span>'),
('LEB', 'Proverbs', '5', '6', '0', 'She does not observe<span class=\"note\">Or “examine, weigh”</span>the path of life;  her ways wander, <span class=\"supplied\">and</span> she does not know <span class=\"supplied\">it</span>.'),
('LEB', 'Proverbs', '5', '7', '2', '<span class=\"verse-title\">Do Not Commit Adultery Against Wisdom</span>Now, O children, listen to me;  do not depart from the sayings of my mouth.'),
('LEB', 'Proverbs', '5', '8', '2', 'Keep your paths far from her,  and do not go near to the door of her house,'),
('LEB', 'Proverbs', '5', '9', '2', 'lest you give your honor to the others,  and your years to the merciless,'),
('LEB', 'Proverbs', '5', '10', '2', 'lest strangers take <span class=\"supplied\">their</span> fill of your strength,  and your labors <span class=\"supplied\">go</span> to the house of a foreigner,'),
('LEB', 'Proverbs', '5', '11', '2', 'and you groan at your end,  when your flesh and body are consumed,'),
('LEB', 'Proverbs', '5', '12', '2', 'and say “How I hated discipline,  and I despised reproof!”'),
('LEB', 'Proverbs', '5', '13', '2', 'and “I did not listen to the voice of my teachers,  and I did not incline my ear to my instructors!'),
('LEB', 'Proverbs', '5', '14', '2', 'I was almost at utter<span class=\"note\">Or “all, every, whole”</span>ruin<span class=\"note\">Or “evil”</span> in the midst of the assembly and congregation.”'),
('LEB', 'Proverbs', '5', '15', '2', 'Drink water from your <span class=\"supplied\">own</span> cistern  and flowing waters from inside your own well.'),
('LEB', 'Proverbs', '5', '16', '2', 'Shall your springs be scattered outward?  In the streets, <span class=\"supplied\">shall there be</span> streams of water?'),
('LEB', 'Proverbs', '5', '17', '2', 'May they be yours alone,  and not for strangers <span class=\"supplied\">who are</span> with you.'),
('LEB', 'Proverbs', '5', '18', '2', 'May your fountain be blessed,  and rejoice in the wife<span class=\"note\">Or “woman”</span>of your youth.'),
('LEB', 'Proverbs', '5', '19', '2', '<span class=\"supplied\">She is</span> a deer of love and a doe of grace;  may her breasts satisfy you <span class=\"idiom\">always</span>;<span class=\"note\">“at all time”</span> by her love may you be intoxicated continually.'),
('LEB', 'Proverbs', '5', '20', '2', 'Why should you be intoxicated, my child, by a strange woman,  and embrace the bosom of a foreigner?'),
('LEB', 'Proverbs', '5', '21', '2', 'For before the eyes of Adonai <span class=\"supplied\">are</span> <span class=\"idiom\">human ways</span>,<span class=\"note\">“the ways of man/humankind”</span> and all his paths he examines.'),
('LEB', 'Proverbs', '5', '22', '2', 'His iniquities shall ensnare him, the evildoer,  and in the vanity of his sin he shall be caught.'),
('LEB', 'Proverbs', '5', '23', '2', 'He shall die <span class=\"idiom\">for lack of</span><span class=\"note\">“with there is no”</span>discipline,  and in the greatness of his folly he shall be lost.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '6', '1', '1', '<span class=\"verse-title\">Against Pledges</span>My child, if you have pledged to your neighbor,  <span class=\"supplied\">if</span> you have bound <span class=\"idiom\">yourself</span><span class=\"note\">“palms of your hands”</span>to the stranger,'),
('LEB', 'Proverbs', '6', '2', '1', '<span class=\"supplied\">if</span> you are snared by the sayings of your mouth,  <span class=\"supplied\">if</span> you are caught by the sayings of your mouth,'),
('LEB', 'Proverbs', '6', '3', '1', 'do this, then, my child, and save yourself,  for you have come into <span class=\"idiom\">the palm of your neighbor’s hand</span>:<span class=\"note\">“the palm of the hand of your neighbor”</span> Go, humble yourself, plead with your neighbor.'),
('LEB', 'Proverbs', '6', '4', '1', 'Do not give sleep to your eyes,  or slumber to your eyelids.'),
('LEB', 'Proverbs', '6', '5', '1', 'Save yourself like a gazelle from a hand,  or like a bird from the hand of a fowler.'),
('LEB', 'Proverbs', '6', '6', '3', '<span class=\"verse-title\">Against Sloth</span>Go to the ant, lazy!  Consider its ways and be wise.'),
('LEB', 'Proverbs', '6', '7', '3', 'It has no chief,  officer, or ruler.'),
('LEB', 'Proverbs', '6', '8', '3', 'In the summer, it prepares its food;  in the harvest, it gathers its sustenance.'),
('LEB', 'Proverbs', '6', '9', '3', 'How long will you lie down, lazy?  When will you rise up from your sleep?'),
('LEB', 'Proverbs', '6', '10', '3', 'A little sleep, a little slumber,  a little folding of the hands for rest—'),
('LEB', 'Proverbs', '6', '11', '3', 'like a <span class=\"idiom\">robber</span><span class=\"note\">“one who walks,” that is, a vagabond</span>shall your poverty come,  and what you lack like an armed man.'),
('LEB', 'Proverbs', '6', '12', '5', '<span class=\"verse-title\">Against Worthlessness</span>A worthless man, an evil man,  goes around with <span class=\"idiom\">deceitful speech</span>.<span class=\"note\">“crookedness of mouth”</span>'),
('LEB', 'Proverbs', '6', '13', '5', 'Winking in his eye, shuffling in his foot,  pointing in his fingers,'),
('LEB', 'Proverbs', '6', '14', '5', 'perversion in his heart, he devises evil;  at all times he will send out discord.<span class=\"note\">Or “quarrels”</span>'),
('LEB', 'Proverbs', '6', '15', '5', 'Upon <span class=\"idiom\">such a man</span>,<span class=\"note\">“thus”</span>suddenly shall his calamity come;  in a moment he will be damaged and there is no healing.<span class=\"note\">Or “repair”</span>'),
('LEB', 'Proverbs', '6', '16', '7', '<span class=\"verse-title\">What Adonai Hates</span>There are six <span class=\"supplied\">things</span> Adonai hates,  and seven <span class=\"supplied\">things are</span> abominations of his soul:<span class=\"note\">Or “inner self”</span>'),
('LEB', 'Proverbs', '6', '17', '7', 'haughty eyes, a lying tongue,  and hands that shed innocent blood,'),
('LEB', 'Proverbs', '6', '18', '7', 'a devising heart, plans of deception,  feet that hurry to run to evil,<span class=\"note\">Hebrew “the evil”</span>'),
('LEB', 'Proverbs', '6', '19', '7', 'a false witness who breathes lies  and sends out discord<span class=\"note\">Or “quarrels”</span>between brothers.'),
('LEB', 'Proverbs', '6', '20', '9', '<span class=\"verse-title\">Commandment and Instruction as Guardians</span>My child, keep <span class=\"supplied\">the</span> commandment of your father,  and do not disregard <span class=\"supplied\">the</span> instruction of your mother.'),
('LEB', 'Proverbs', '6', '21', '9', 'Bind them on your heart continually;  tie them upon your neck.'),
('LEB', 'Proverbs', '6', '22', '9', '<span class=\"idiom\">When you walk</span>,<span class=\"note\">“In your walking”</span>she<span class=\"note\">That is, “commandment” and “instruction”</span>will lead you,  <span class=\"idiom\">When you lie down</span>,<span class=\"note\">“In your lying down”</span>she will watch over you,  and <span class=\"supplied\">when</span> you awake, she will converse <span class=\"supplied\">with</span> you.'),
('LEB', 'Proverbs', '6', '23', '9', 'For <span class=\"supplied\">like</span> a lamp <span class=\"supplied\">is</span> a commandment, and instruction <span class=\"supplied\">is</span> light,  and the way of life<span class=\"note\">Hebrew “lives”</span><span class=\"supplied\">is the</span> reproof of discipline,'),
('LEB', 'Proverbs', '6', '24', '9', '<span class=\"supplied\">in order</span> to preserve you from an evil woman,<span class=\"note\">Or “wife”</span> from the smoothness of <span class=\"supplied\">the</span> tongue of <span class=\"idiom\">an adulteress</span>.<span class=\"note\">“a foreign woman”</span>'),
('LEB', 'Proverbs', '6', '25', '9', 'Do not desire her beauty in your heart;  may she not capture you with her eyelashes.'),
('LEB', 'Proverbs', '6', '26', '9', 'For <span class=\"supplied\">the</span> price of a woman, a prostitute,<span class=\"note\">Or “whore”</span><span class=\"supplied\">is the</span> price of a loaf of bread,  but the <span class=\"idiom\">woman belonging to a man</span><span class=\"note\">“the woman of a man”</span>hunts precious life.'),
('LEB', 'Proverbs', '6', '27', '11', '<span class=\"verse-title\">Warning Against Relations with a Married Woman</span>Can a man carry fire in his lap,  and his clothes not burn?'),
('LEB', 'Proverbs', '6', '28', '11', 'If a man walks upon the hot coals,  will his feet not be burned?'),
('LEB', 'Proverbs', '6', '29', '11', 'Thus, he who goes to the wife of his neighbor,  any who touches her shall not go unpunished.'),
('LEB', 'Proverbs', '6', '30', '11', '<span class=\"supplied\">People</span> do not despise a thief when he steals  to fill himself<span class=\"note\">Or “soul,” or “inner self”</span>when he is hungry.'),
('LEB', 'Proverbs', '6', '31', '11', 'But <span class=\"supplied\">if</span> he is found, he will pay sevenfold,  every possession of his house he shall give.'),
('LEB', 'Proverbs', '6', '32', '11', 'He who commits adultery <span class=\"supplied\">with</span> a woman lacks <span class=\"idiom\">sense</span>,<span class=\"note\">“heart”</span> he destroys himself<span class=\"note\">Or “his soul,” or “his life”</span>who does it.'),
('LEB', 'Proverbs', '6', '33', '11', 'A wound and dishonor he will find,  and his disgrace will not be wiped out.'),
('LEB', 'Proverbs', '6', '34', '11', 'For jealousy <span class=\"supplied\">is the</span> fury of a husband,  and he will not show restraint on the day of revenge.'),
('LEB', 'Proverbs', '6', '35', '11', 'He will not accept <span class=\"idiom\">any compensation</span>,<span class=\"note\">“<span class=\"supplied\">the</span> face of any compensation”</span> and he will not be willing, though the bribe is large.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '7', '1', '1', '<span class=\"verse-title\">Warning Against the Strange Woman</span>My child, guard my sayings;  store my commandments with you.'),
('LEB', 'Proverbs', '7', '2', '1', 'Keep my commands and live,  and my teaching like <span class=\"supplied\">the</span> <span class=\"idiom\">apple of your eye</span>.<span class=\"note\">A single word meaning “pupil of the eye”</span>'),
('LEB', 'Proverbs', '7', '3', '1', 'Bind them on your fingers;  write them on the tablet of your heart.'),
('LEB', 'Proverbs', '7', '4', '1', 'Say to wisdom,<span class=\"note\">Hebrew “the wisdom”</span>“you are my sister,”  and you shall call insight,<span class=\"note\">Hebrew “the insight”</span>“<span class=\"idiom\">intimate friend</span>.”<span class=\"note\">“one who is known.” To “know” is often a euphemism for intercourse. Therefore “intimate friend” may also be read “lover.”</span>'),
('LEB', 'Proverbs', '7', '5', '1', '<span class=\"supplied\">In order</span> to guard yourself from <span class=\"idiom\">an adulteress</span>,<span class=\"note\">“a strange woman”</span> from the foreigner who <span class=\"idiom\">makes her words smooth</span>.<span class=\"note\">“causes to be smooth her words”</span>'),
('LEB', 'Proverbs', '7', '6', '1', 'For at the window of my house,  through my lattice, I looked down.'),
('LEB', 'Proverbs', '7', '7', '1', 'And I saw among the simple,  I observed among the youth,  a young man lacking <span class=\"idiom\">sense</span>,<span class=\"note\">“heart”</span>'),
('LEB', 'Proverbs', '7', '8', '1', 'passing on the street <span class=\"idiom\">at</span><span class=\"note\">“at the place of”</span>her corner,  and he takes the road <span class=\"supplied\">to</span> her house,'),
('LEB', 'Proverbs', '7', '9', '1', 'at twilight, at <span class=\"supplied\">the</span> day’s evening,  in <span class=\"supplied\">the</span> midst of night and the darkness.'),
('LEB', 'Proverbs', '7', '10', '1', 'Then behold! A woman <span class=\"supplied\">comes</span> to meet him  <span class=\"supplied\">with the</span> garment of a prostitute<span class=\"note\">Or “whore”</span>and <span class=\"idiom\">a secret heart</span>.<span class=\"note\">“secret of heart”</span>'),
('LEB', 'Proverbs', '7', '11', '1', 'She is loud and stubborn;  her feet do not stay at her house.'),
('LEB', 'Proverbs', '7', '12', '1', 'Now in the street, now in the square,  <span class=\"idiom\">at</span><span class=\"note\">“at the place of”</span>every corner she lies in wait.'),
('LEB', 'Proverbs', '7', '13', '1', 'She took hold of <span class=\"note\">Or “strengthened”</span>him and kissed him.  Her face was impudent, and she said to him,'),
('LEB', 'Proverbs', '7', '14', '1', '“Sacrifices of peace offerings <span class=\"supplied\">are</span> upon me;  <span class=\"idiom\">today</span><span class=\"note\">“the day”</span>I completed my vows.'),
('LEB', 'Proverbs', '7', '15', '1', 'So<span class=\"note\">Hebrew “thus”</span>I have come out to meet you,  to seek your face, and I have found you.'),
('LEB', 'Proverbs', '7', '16', '1', '<span class=\"supplied\">With</span> coverings I have adorned my couch,  spreads of the linen of Egypt;'),
('LEB', 'Proverbs', '7', '17', '1', 'I have perfumed my bed <span class=\"supplied\">with</span> myrrh,  aloes, and cinnamon.'),
('LEB', 'Proverbs', '7', '18', '1', 'Come, let us take <span class=\"supplied\">our</span> fill of love making,  until the morning let us delight in love.'),
('LEB', 'Proverbs', '7', '19', '1', 'For there is no man<span class=\"note\">Or “husband”</span>in his home;  he has gone on a <span class=\"idiom\">long journey</span>.<span class=\"note\">“a journey from far”</span>'),
('LEB', 'Proverbs', '7', '20', '1', 'The bag of money he took in his hand,  for <span class=\"supplied\">on</span> the day of the full moon he will come home.”'),
('LEB', 'Proverbs', '7', '21', '1', 'She persuades him with the greatness of her teachings;  with her smooth lips she compels him.'),
('LEB', 'Proverbs', '7', '22', '1', 'He goes after her suddenly;  like an ox to the slaughter he goes,  and like a stag to the instruction of a fool,'),
('LEB', 'Proverbs', '7', '23', '1', 'until an arrow pierces his <span class=\"idiom\">entrails</span>,<span class=\"note\">“heaviness,” often referring to the liver</span> like a bird rushing into a snare,  but he does not know that <span class=\"idiom\">it will cost him his life</span>.<span class=\"note\">“it is against his life”</span>'),
('LEB', 'Proverbs', '7', '24', '3', '<span class=\"verse-title\">Reiteration of the Warning Against a Strange Woman</span>And now, my children, listen to me,  and be attentive to the sayings of my mouth.'),
('LEB', 'Proverbs', '7', '25', '3', 'May your heart not turn aside to her ways;  do not stray into her path.'),
('LEB', 'Proverbs', '7', '26', '3', 'For many slain she has laid low,  and countless<span class=\"note\">Or “strong men”</span><span class=\"supplied\">are</span> all of her killings.'),
('LEB', 'Proverbs', '7', '27', '3', 'The ways of Sheol<span class=\"note\">A term for the place where the dead reside, i.e., the Underworld</span><span class=\"supplied\">are</span> her house,  descending to chambers of death.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '8', '1', '1', '<span class=\"verse-title\">Wisdom Calls</span>Does not wisdom call,  and understanding raise its voice?'),
('LEB', 'Proverbs', '8', '2', '1', '<span class=\"idiom\">Atop the heights</span><span class=\"note\">“At a head of the heights”</span>beside the road,  at the crossroads she stands.'),
('LEB', 'Proverbs', '8', '3', '1', 'Beside gates, before towns,  <span class=\"supplied\">at the</span> entrance of doors, she cries out:'),
('LEB', 'Proverbs', '8', '4', '1', '“To you, O <span class=\"idiom\">people</span>,<span class=\"note\">“men”</span>I call,  and my cry is to the children of humankind.'),
('LEB', 'Proverbs', '8', '5', '1', 'Learn prudence, O simple ones;  fools, learn <span class=\"idiom\">intelligence</span>.<span class=\"note\">“heart”</span>'),
('LEB', 'Proverbs', '8', '6', '1', 'Listen! For noble things I will speak,  and upright things from the opening of my lips.'),
('LEB', 'Proverbs', '8', '7', '1', 'My mouth will utter truth,  and wickedness <span class=\"supplied\">is</span> an abomination to my lips.'),
('LEB', 'Proverbs', '8', '8', '1', 'All sayings of my mouth <span class=\"supplied\">are</span> in righteousness;  none of them are twisted and crooked.'),
('LEB', 'Proverbs', '8', '9', '1', 'All of them are straight to him who understands,  and upright to those who find knowledge.'),
('LEB', 'Proverbs', '8', '10', '1', 'Take my teaching and not silver;  may you choose knowledge rather than choice gold.'),
('LEB', 'Proverbs', '8', '11', '1', 'For wisdom is better than jewels,  and all desires shall not compare with her.'),
('LEB', 'Proverbs', '8', '12', '1', 'I, wisdom, live with prudence,  and I find knowledge and discretion.'),
('LEB', 'Proverbs', '8', '13', '1', 'The fear of Adonai <span class=\"supplied\">is</span> hatred of evil,  pride, and arrogance and an evil way.  And I hate a mouth of perversity.'),
('LEB', 'Proverbs', '8', '14', '1', '<span class=\"idiom\">Advice and sound judgment</span><span class=\"note\">“To/for me are advice and sound judgment”</span>are mine;  I am understanding, <span class=\"idiom\">strength is mine</span>.<span class=\"note\">“to/for me is strength”</span>'),
('LEB', 'Proverbs', '8', '15', '1', 'By me kings reign,  and rulers decree righteousness.'),
('LEB', 'Proverbs', '8', '16', '1', 'By me rulers rule,  and nobles—all judges of righteousness.'),
('LEB', 'Proverbs', '8', '17', '1', 'I love <span class=\"idiom\">those who love me</span>,<span class=\"note\">“her lovers”</span> and those who seek me diligently shall find me.'),
('LEB', 'Proverbs', '8', '18', '1', 'Fortune and glory <span class=\"supplied\">are</span> with me,  enduring wealth and righteousness.'),
('LEB', 'Proverbs', '8', '19', '1', 'My fruit is better than gold,<span class=\"note\">“fine gold”</span>even refined gold,  and my yield than choice silver.'),
('LEB', 'Proverbs', '8', '20', '1', 'In the way of righteousness I walk,  in the midst of paths of justice,'),
('LEB', 'Proverbs', '8', '21', '1', '<span class=\"supplied\">in order</span> to endow those who love me <span class=\"supplied\">with</span> wealth,  and I will fill their treasuries.'),
('LEB', 'Proverbs', '8', '22', '3', '<span class=\"verse-title\">Wisdom at Creation</span>“Adonai possessed<span class=\"note\">Or “gained,” or “acquired”</span>me, <span class=\"supplied\">the</span> first of his ways,  before his acts <span class=\"idiom\">of old</span>.<span class=\"note\">“from before” or “from then”</span>'),
('LEB', 'Proverbs', '8', '23', '3', 'From eternity, I was set up from <span class=\"supplied\">the</span> first,  from the beginning<span class=\"note\">Hebrew “beginnings”</span>of the earth.'),
('LEB', 'Proverbs', '8', '24', '3', 'When there were no depths, I was brought forth,  when there were no springs of <span class=\"idiom\">abounding</span><span class=\"note\">“made heavy”</span>water.'),
('LEB', 'Proverbs', '8', '25', '3', 'Before mountains had been shaped,  before hills, I was brought forth.'),
('LEB', 'Proverbs', '8', '26', '3', 'When he had not yet made earth and fields,  or the first dust of the world,'),
('LEB', 'Proverbs', '8', '27', '3', '<span class=\"idiom\">when he established</span><span class=\"note\">“in his establishing”</span>the heavens, there I <span class=\"supplied\">was</span>,  <span class=\"idiom\">when he drew</span><span class=\"note\">“in his drawing”</span>a circle upon the face of the deep,'),
('LEB', 'Proverbs', '8', '28', '3', '<span class=\"idiom\">when he made</span><span class=\"note\">“in his making”</span>skies from above,  when he founded fountains of the deep,'),
('LEB', 'Proverbs', '8', '29', '3', '<span class=\"idiom\">when he assigned</span><span class=\"note\">“in his assigning”</span>his limits to the sea,  that waters shall not transgress his <span class=\"idiom\">command</span>,<span class=\"note\">“mouth”</span> <span class=\"idiom\">when he marked</span><span class=\"note\">“in his marking”</span>the foundations of the earth,'),
('LEB', 'Proverbs', '8', '30', '3', 'I was <span class=\"idiom\">beside</span><span class=\"note\">“at the place of”</span>him, a master workman,  and I was delighting day by day,  rejoicing before him <span class=\"idiom\">always</span>,<span class=\"note\">“in all <span class=\"supplied\">of</span> time”</span>'),
('LEB', 'Proverbs', '8', '31', '3', 'rejoicing in the world of his earth,  and my delight <span class=\"supplied\">was</span> with the children of humankind.'),
('LEB', 'Proverbs', '8', '32', '5', '<span class=\"verse-title\">Benefits of Following Wisdom</span>“And now, children, listen to me;  happy <span class=\"supplied\">are</span> those who will keep my ways.'),
('LEB', 'Proverbs', '8', '33', '5', 'Hear teaching and be wise;  do not neglect <span class=\"supplied\">it</span>.'),
('LEB', 'Proverbs', '8', '34', '5', 'Happy <span class=\"supplied\">is the</span> person who listens to me,  <span class=\"supplied\">in order</span> to keep watch at my doors day by day,  <span class=\"supplied\">in order</span> to guard the frames of my entrances.'),
('LEB', 'Proverbs', '8', '35', '5', 'For he who finds me <span class=\"supplied\">is</span> he who finds<span class=\"note\">Hebrew “those who find”</span>life,  and he obtains favor from Adonai.'),
('LEB', 'Proverbs', '8', '36', '5', 'But he who misses me injures himself.<span class=\"note\">Or “soul,” or “inner self”</span> All those who hate me love death.”')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '9', '1', '1', '<span class=\"verse-title\">Wisdom’s Banquet</span>Wisdom has built her house;  she has hewn her seven pillars.'),
('LEB', 'Proverbs', '9', '2', '1', 'She has slaughtered her slaughtering, mixed her wine,  <span class=\"supplied\">and</span> also set her table.'),
('LEB', 'Proverbs', '9', '3', '1', 'She has sent her servant girls,<span class=\"note\">Or “young maidens”</span>she calls  upon the wings of the high places of town,'),
('LEB', 'Proverbs', '9', '4', '1', '“Whoever <span class=\"supplied\">is</span> simple, let him turn here.”  As for the one who lacks <span class=\"idiom\">sense</span>,<span class=\"note\">“heart”</span>she says to him,'),
('LEB', 'Proverbs', '9', '5', '1', '“Come, eat with my bread;  drink with the wine I have mixed.'),
('LEB', 'Proverbs', '9', '6', '1', 'Lay aside simplicity and live;  walk in the way of understanding.”'),
('LEB', 'Proverbs', '9', '7', '1', 'He who corrects a scoffer gains abuse for himself,  and he who rebukes the wicked <span class=\"idiom\">gets hurt</span>.<span class=\"note\">“abuse him”</span>'),
('LEB', 'Proverbs', '9', '8', '1', 'Do not rebuke a scoffer, lest he hate you;  rebuke the wise and he will love you.'),
('LEB', 'Proverbs', '9', '9', '1', 'Give to a wise one and he will become more wise;  <span class=\"idiom\">teach</span><span class=\"note\">“make known to”</span>a righteous one and he will increase learning.'),
('LEB', 'Proverbs', '9', '10', '3', '<span class=\"verse-title\">Foolishness’ Banquet</span>The start of wisdom is fear of Adonai,  and knowledge of the Holy One,<span class=\"note\">Or “holy ones”</span>insight.'),
('LEB', 'Proverbs', '9', '11', '3', 'For by me your days shall increase,  and years of life shall multiply for you.'),
('LEB', 'Proverbs', '9', '12', '3', 'If you are wise, you are wise for yourself,  and <span class=\"supplied\">if</span> you scoff, alone you shall bear <span class=\"supplied\">it</span>.'),
('LEB', 'Proverbs', '9', '13', '3', 'A woman of foolishness is loud,  simple, and does not know <span class=\"idiom\">it</span>.<span class=\"note\">“what”</span>'),
('LEB', 'Proverbs', '9', '14', '3', 'She sits at the door of her house,  upon a throne <span class=\"supplied\">at the</span> high places of town,'),
('LEB', 'Proverbs', '9', '15', '3', '<span class=\"supplied\">in order</span> to call to those who pass by the road,  those who go straight <span class=\"supplied\">on</span> their way:'),
('LEB', 'Proverbs', '9', '16', '3', '“Whoever is simple, may he turn here!”  As for he who lacks <span class=\"idiom\">sense</span>,<span class=\"note\">“heart”</span>she says to him,'),
('LEB', 'Proverbs', '9', '17', '3', '“Stolen waters are sweet,  and bread of secrecy is pleasant.”'),
('LEB', 'Proverbs', '9', '18', '3', 'But he does not know that the dead<span class=\"note\">Or “Rephaim”</span><span class=\"supplied\">are</span> there,  in the depths of Sheol<span class=\"note\">A term for the place where the dead reside, i.e., the Underworld</span><span class=\"supplied\">are</span> her guests.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '10', '1', '1', '<span class=\"verse-title\">Proverbs of Solomon</span>The proverbs of Solomon:  A wise child makes a father glad,  but a foolish child grieves his mother.'),
('LEB', 'Proverbs', '10', '2', '1', 'Treasures of wickedness do not profit,  but righteousness delivers from death.'),
('LEB', 'Proverbs', '10', '3', '1', 'Adonai will not cause a righteous person<span class=\"note\">Or “soul,” or “inner self”</span>to go hungry,  but the craving of the wicked he will thwart.'),
('LEB', 'Proverbs', '10', '4', '1', 'A slack hand causes poverty,  but the hand of the diligent enriches.'),
('LEB', 'Proverbs', '10', '5', '1', 'He who gathers in the summer <span class=\"supplied\">is</span> a child who is prudent;  he who sleeps at the harvest <span class=\"supplied\">is</span> a child who brings shame.'),
('LEB', 'Proverbs', '10', '6', '1', 'Blessings <span class=\"supplied\">belong</span> to the head of the righteous,  but the mouth of the wicked conceals violence.'),
('LEB', 'Proverbs', '10', '7', '1', 'The memory of righteousness <span class=\"supplied\">is</span> like a blessing,  but the name of the wicked will rot.'),
('LEB', 'Proverbs', '10', '8', '1', 'A heart of wisdom will heed commandments,  but a babbling fool will come to ruin.'),
('LEB', 'Proverbs', '10', '9', '1', 'Whoever walks in integrity<span class=\"note\">Hebrew “in the integrity”</span>will walk securely,  but whoever follows perversity, his ways will be made known.'),
('LEB', 'Proverbs', '10', '10', '1', 'The winking of an eye causes<span class=\"note\">Or “gives, sets up”</span>trouble,  and the foolishness of lips comes to ruin.'),
('LEB', 'Proverbs', '10', '11', '1', 'A fountain of life <span class=\"supplied\">is</span> a mouth of righteousness,  and a mouth of wickedness conceals violence.'),
('LEB', 'Proverbs', '10', '12', '1', 'Hatred stirs up strife,  but love covers over all offenses.'),
('LEB', 'Proverbs', '10', '13', '1', 'On the lips of one who has understanding, wisdom is found,  but a rod <span class=\"supplied\">is</span> for the back of one who lacks <span class=\"idiom\">sense</span>.<span class=\"note\">“heart”</span>'),
('LEB', 'Proverbs', '10', '14', '1', 'Those who are wise lay up knowledge,  but <span class=\"supplied\">to</span> the mouth of the fool, ruin draws near.'),
('LEB', 'Proverbs', '10', '15', '1', 'The wealth of the rich <span class=\"supplied\">is</span> the city of his strength;  the ruin of the poor <span class=\"supplied\">is</span> their poverty.'),
('LEB', 'Proverbs', '10', '16', '1', 'The wage of the righteous <span class=\"supplied\">leads</span> to life;  the gain of the wicked to sin.'),
('LEB', 'Proverbs', '10', '17', '1', '<span class=\"supplied\">On</span> the path to life is he who guards instruction,  but he who rejects rebuke goes astray.'),
('LEB', 'Proverbs', '10', '18', '1', 'He who conceals hatred <span class=\"supplied\">has</span> lips of deceit,  and he who utters slander—he is a fool.'),
('LEB', 'Proverbs', '10', '19', '1', 'In many words, transgression is not lacking,  but he who restrains his lips is prudent.'),
('LEB', 'Proverbs', '10', '20', '1', 'Choice silver is a tongue of righteousness,  a heart<span class=\"note\">Or “mind”</span>of wickedness is <span class=\"idiom\">of little worth</span>.<span class=\"note\">“like a little”</span>'),
('LEB', 'Proverbs', '10', '21', '1', 'Lips of righteousness feed many,  but fools <span class=\"idiom\">die for lack of sense</span>.<span class=\"note\">“in the lack of heart, they die”</span>'),
('LEB', 'Proverbs', '10', '22', '1', 'The blessing of Adonai makes <span class=\"supplied\">one</span> rich,  and he does not increase sorrow with it.'),
('LEB', 'Proverbs', '10', '23', '1', '<span class=\"supplied\">It is</span> like a sport for a fool to do wrong,  wisdom for a person of understanding.'),
('LEB', 'Proverbs', '10', '24', '1', 'The dread of the wicked will come upon him,  but the desire of the righteous will be granted.'),
('LEB', 'Proverbs', '10', '25', '1', 'With the passing of the tempest, there is no wickedness,  but <span class=\"supplied\">the</span> righteous have a foundation forever.'),
('LEB', 'Proverbs', '10', '26', '1', 'Like vinegar to the tooth and like smoke to the eyes,  thus <span class=\"supplied\">is</span> the lazy to one who employs<span class=\"note\">Or “sends”</span>him.'),
('LEB', 'Proverbs', '10', '27', '1', 'The fear of Adonai adds life,  but the years of the wicked are shortened.'),
('LEB', 'Proverbs', '10', '28', '1', 'The hope of the righteous <span class=\"supplied\">is</span> gladness,  but the expectation of the wicked comes to nothing.'),
('LEB', 'Proverbs', '10', '29', '1', 'A stronghold for the upright is the way of Adonai,  but ruin <span class=\"supplied\">belongs</span> to <span class=\"idiom\">evildoers</span>.<span class=\"note\">“them who do evil”</span>'),
('LEB', 'Proverbs', '10', '30', '1', 'The righteous one is forever; he will not be removed.  But the wicked will not remain in the land.<span class=\"note\">Or “earth”</span>'),
('LEB', 'Proverbs', '10', '31', '1', 'The mouth of the righteous brings forth wisdom,  but a tongue of perversity will be cut off.'),
('LEB', 'Proverbs', '10', '32', '1', 'The lips of the righteous know <span class=\"supplied\">the</span> acceptable,  but the mouth of the wicked, the perverse.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '11', '1', '0', 'Balances of deceit <span class=\"supplied\">are</span> an abomination of Adonai,  but <span class=\"idiom\">an accurate weight</span><span class=\"note\">“a full stone” or “a pure stone”</span>delights him.'),
('LEB', 'Proverbs', '11', '2', '0', 'Pride comes, then disgrace comes,  but wisdom <span class=\"supplied\">is</span> with the humble.'),
('LEB', 'Proverbs', '11', '3', '0', 'The integrity of the upright guides them,  but the crookedness of the treacherous destroys them.'),
('LEB', 'Proverbs', '11', '4', '0', 'Wealth does not profit on the day of wrath,  but righteousness will deliver from death.'),
('LEB', 'Proverbs', '11', '5', '0', 'The righteousness of the blameless will keep his ways straight,  but the wicked will fall by his wickedness.'),
('LEB', 'Proverbs', '11', '6', '0', 'The righteousness of the upright will save them,  but by a scheme the treacherous will be taken captive.'),
('LEB', 'Proverbs', '11', '7', '0', 'With the death of a wicked person, hope will die,  and the expectation of the godless perishes.'),
('LEB', 'Proverbs', '11', '8', '0', 'The righteous is delivered from trouble,  but the wicked enters into it.'),
('LEB', 'Proverbs', '11', '9', '0', 'With a mouth, the godless shall destroy his neighbor,  but by knowledge the righteous are delivered.'),
('LEB', 'Proverbs', '11', '10', '0', 'When good <span class=\"supplied\">is with</span> the righteous, <span class=\"supplied\">the</span> city rejoices,  and with the perishing of the wicked, jubilation.'),
('LEB', 'Proverbs', '11', '11', '0', 'By the blessing of the upright, a city will be exalted,  but by the mouth of the wicked, it will be overthrown.'),
('LEB', 'Proverbs', '11', '12', '0', 'He who lacks <span class=\"idiom\">sense</span><span class=\"note\">“heart”</span>belittles his neighbor,  but a person of intelligence will remain silent.'),
('LEB', 'Proverbs', '11', '13', '0', 'A gossip walks <span class=\"supplied\">about</span> telling a secret,  but the trustworthy in spirit keeps <span class=\"supplied\">the</span> matter.'),
('LEB', 'Proverbs', '11', '14', '0', 'Where there is no guidance, a nation<span class=\"note\">Or “people”</span>shall fall,  but <span class=\"supplied\">there is</span> safety in an abundance of counsel.'),
('LEB', 'Proverbs', '11', '15', '0', 'He will suffer trouble when he loans to a stranger,  but he who refuses a pledge is safe.'),
('LEB', 'Proverbs', '11', '16', '0', 'A woman of grace receives honor,  but the ruthless gets wealth.'),
('LEB', 'Proverbs', '11', '17', '0', 'A person of kindness rewards himself,<span class=\"note\">Or “soul,” or “inner self”</span> but a cruel <span class=\"supplied\">person</span> harms his own flesh.'),
('LEB', 'Proverbs', '11', '18', '0', 'The wicked earns<span class=\"note\">Or “does, makes”</span><span class=\"idiom\">deceptive gain</span>,<span class=\"note\">“gain of deception”</span> but he who sows righteousness, a <span class=\"idiom\">true reward</span>.<span class=\"note\">“a reward of truth”</span>'),
('LEB', 'Proverbs', '11', '19', '0', 'He who is steadfast in righteousness <span class=\"supplied\">is</span> to life  as<span class=\"note\">Hebrew “and”</span>he who pursues evil <span class=\"supplied\">is</span> to death.'),
('LEB', 'Proverbs', '11', '20', '0', 'An abomination of Adonai <span class=\"supplied\">are</span> the <span class=\"idiom\">crooked of heart</span>,<span class=\"note\">“crooked ones of heart”; “heart” may also be translated “mind”</span> but his delight <span class=\"supplied\">are</span> <span class=\"idiom\">those with blameless ways</span>.<span class=\"note\">“blameless ones of ways”</span>'),
('LEB', 'Proverbs', '11', '21', '0', '<span class=\"idiom\">Rest assured</span>,<span class=\"note\">“Hand to hand”</span>the wicked will not go unpunished,  but the offspring<span class=\"note\">Or “seed”</span>of the righteous will escape.'),
('LEB', 'Proverbs', '11', '22', '0', 'A ring of gold in <span class=\"supplied\">the</span> snout of a pig  <span class=\"supplied\">is</span> a woman <span class=\"supplied\">who is</span> beautiful but without discretion.'),
('LEB', 'Proverbs', '11', '23', '0', 'The desire of the righteous <span class=\"supplied\">is</span> only good,  but the expectation of the wicked, wrath.'),
('LEB', 'Proverbs', '11', '24', '0', 'There is one who gives yet <span class=\"idiom\">grows richer</span>,<span class=\"note\">“adding more”</span> but he who withholds <span class=\"idiom\">what is right</span><span class=\"note\">“from what is right”</span>only <span class=\"supplied\">finds</span> need.'),
('LEB', 'Proverbs', '11', '25', '0', 'A person of blessing will be enriched,  and he who gives water also will be refreshed.'),
('LEB', 'Proverbs', '11', '26', '0', 'He who withholds grain, the people curse him,  but a blessing <span class=\"supplied\">is</span> for the head of him who sells.'),
('LEB', 'Proverbs', '11', '27', '0', 'He who diligently seeks good seeks favor,  but he who inquires of evil, it will come to him.'),
('LEB', 'Proverbs', '11', '28', '0', 'He who trusts in his wealth <span class=\"supplied\">is</span> he who will fall,  but like a green leaf the righteous will flourish.'),
('LEB', 'Proverbs', '11', '29', '0', 'He who brings trouble <span class=\"supplied\">to</span> his household, he will inherit wind,<span class=\"note\">Or “breath, spirit”</span> and a fool will serve the wise of heart.'),
('LEB', 'Proverbs', '11', '30', '0', 'The fruit of righteousness is a tree of life,  and he who captures souls<span class=\"note\">Or “persons,” or “inner selves”</span><span class=\"supplied\">is</span> wise.'),
('LEB', 'Proverbs', '11', '31', '0', 'If the righteous on earth will be repaid,  <span class=\"idiom\">how much more</span><span class=\"note\">“more for”</span>the wicked and sinner.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '12', '1', '0', 'He who loves discipline loves knowledge,  but he who hates rebuke <span class=\"supplied\">is</span> stupid.'),
('LEB', 'Proverbs', '12', '2', '0', 'The good obtains favor from Adonai,  but anyone who schemes, he condemns.'),
('LEB', 'Proverbs', '12', '3', '0', 'A person will not be established by wickedness,  but the root of the righteous will not be moved.'),
('LEB', 'Proverbs', '12', '4', '0', 'A woman of strength<span class=\"note\">Or “honor”</span><span class=\"supplied\">is</span> the crown of her master,<span class=\"note\">Or “lord, owner”</span> but like rot in his bones is she who brings shame.'),
('LEB', 'Proverbs', '12', '5', '0', 'The thoughts of the righteous are<span class=\"note\">Hebrew “is”</span>just;  the advice of the wicked is treacherous.'),
('LEB', 'Proverbs', '12', '6', '0', 'The words of the wicked <span class=\"supplied\">are</span> an ambush of blood,  but the mouth of the upright delivers them.'),
('LEB', 'Proverbs', '12', '7', '0', 'The wicked are overthrown and <span class=\"idiom\">are no more</span>,<span class=\"note\">“there is no them”</span> but the house of the righteous shall stand.'),
('LEB', 'Proverbs', '12', '8', '0', 'For his mouth of good sense, a man will be recommended,  but he who is of perverse <span class=\"idiom\">mind</span><span class=\"note\">“heart”</span>will be despised.'),
('LEB', 'Proverbs', '12', '9', '0', 'It is better to be lowly and a servant <span class=\"idiom\">to someone</span><span class=\"note\">“for him,” “<span class=\"supplied\">belonging</span> to him”</span> than self-glorifying and lacking food.'),
('LEB', 'Proverbs', '12', '10', '0', 'The righteous knows the life<span class=\"note\">Or “soul,” or “inner self”</span>of his animal,  but the compassion<span class=\"note\">Hebrew “compassions”</span>of the wicked is cruel.'),
('LEB', 'Proverbs', '12', '11', '0', 'He who works his land will have plenty of food,  but he who follows worthless things lacks <span class=\"idiom\">sense</span>.<span class=\"note\">“heart”</span>'),
('LEB', 'Proverbs', '12', '12', '0', 'The wicked covets the proceeds of evil,  but the root of the righteous <span class=\"idiom\">bears fruit</span>.<span class=\"note\">“gives, sets up”</span>'),
('LEB', 'Proverbs', '12', '13', '0', 'By the transgression of lips, evil is ensnared,  but the righteous escapes from trouble.'),
('LEB', 'Proverbs', '12', '14', '0', 'From the fruit of the mouth of a man, he is filled with good,  and the reward of <span class=\"idiom\">a man’s labor</span><span class=\"note\">“the hands of a man”</span>will return to him.'),
('LEB', 'Proverbs', '12', '15', '0', 'The way of a fool is upright in his <span class=\"supplied\">own</span> eyes,  but he who listens to advice <span class=\"supplied\">is</span> wise.'),
('LEB', 'Proverbs', '12', '16', '0', 'As for a fool, <span class=\"idiom\">on that very day</span><span class=\"note\">“on the day”</span>he makes his anger known,  but he who ignores an insult <span class=\"supplied\">is</span> prudent.'),
('LEB', 'Proverbs', '12', '17', '0', 'He who will speak truth will reveal righteousness,  but the witness of falsehood, deceit.'),
('LEB', 'Proverbs', '12', '18', '0', 'There is one who speaks rashly, like the thrust of a sword,  but the tongue of the wise brings healing.'),
('LEB', 'Proverbs', '12', '19', '0', 'A lip of truth endures forever,  but a tongue of deception lasts only a moment.'),
('LEB', 'Proverbs', '12', '20', '0', 'Deceit <span class=\"supplied\">is</span> in the heart<span class=\"note\">Or “mind”</span>of <span class=\"idiom\">those who plan</span><span class=\"note\">“the planners of”</span>evil,  but to <span class=\"idiom\">those who plan</span><span class=\"note\">“the planners of”</span>peace, <span class=\"supplied\">there is</span> joy.'),
('LEB', 'Proverbs', '12', '21', '0', '<span class=\"idiom\">No evil will happen</span><span class=\"note\">“All evil will not happen”</span>to the righteous,  but the wicked are filled <span class=\"supplied\">with</span> trouble.'),
('LEB', 'Proverbs', '12', '22', '0', 'An abomination of Adonai are lips of deceit,  but they who act faithfully <span class=\"supplied\">are</span> his delight.'),
('LEB', 'Proverbs', '12', '23', '0', 'A clever person conceals knowledge,  but the heart<span class=\"note\">Or “mind”</span>of a fool<span class=\"note\">Hebrew “fools”</span>announces folly.'),
('LEB', 'Proverbs', '12', '24', '0', 'The hand of the diligent ones will rule,  but the lazy will belong to forced labor.'),
('LEB', 'Proverbs', '12', '25', '0', 'Anxiety in the heart<span class=\"note\">Or “mind”</span>of a man will weigh him down,  but a good word will cheer him.'),
('LEB', 'Proverbs', '12', '26', '0', 'A righteous person will seek out his neighbor,  but the way of the wicked will lead them astray.'),
('LEB', 'Proverbs', '12', '27', '0', 'The lazy will not roast his game,  but diligence is the precious wealth of a man.'),
('LEB', 'Proverbs', '12', '28', '0', 'On the road of righteousness is life,  and <span class=\"supplied\">on</span> the way of the path, <span class=\"supplied\">may there be</span> no death.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '13', '1', '0', 'A wise child <span class=\"supplied\">hears</span> the discipline of a father,  but a scoffer does not listen to a rebuke.'),
('LEB', 'Proverbs', '13', '2', '0', 'From the fruit of the mouth of a man, he shall eat what is good,  but the desire<span class=\"note\">Or “soul”</span>of the treacherous, wrongdoing.'),
('LEB', 'Proverbs', '13', '3', '0', 'He who keeps his mouth guards his life;<span class=\"note\">Or “soul,” or “inner self”</span> he who opens his lips, ruin <span class=\"supplied\">belongs</span> to him.'),
('LEB', 'Proverbs', '13', '4', '0', 'The soul<span class=\"note\">Or “life”</span>of the lazy craves, but there is nothing,  but the person of diligence is richly supplied.'),
('LEB', 'Proverbs', '13', '5', '0', 'The righteous hates a word of falsehood;  the wicked will bring shame and <span class=\"idiom\">disgrace</span>.<span class=\"note\">“he will bring disgrace”</span>'),
('LEB', 'Proverbs', '13', '6', '0', 'Righteousness will guard the upright of way,  but wickedness will overthrow sin.'),
('LEB', 'Proverbs', '13', '7', '0', 'There is one who acts rich but <span class=\"idiom\">has nothing</span>;<span class=\"note\">“there is no”</span> another who pretends to be poor but <span class=\"supplied\">has</span> wealth.'),
('LEB', 'Proverbs', '13', '8', '0', 'The ransom of the life of a man <span class=\"supplied\">is</span> his wealth,  but the poor does not receive a threat.'),
('LEB', 'Proverbs', '13', '9', '0', 'The light of the righteous will rejoice,  but the lamp of the wicked will die out.'),
('LEB', 'Proverbs', '13', '10', '0', 'Only by insolence is strife set up,  and wisdom <span class=\"supplied\">is</span> with those who take advice.'),
('LEB', 'Proverbs', '13', '11', '0', 'Wealth <span class=\"supplied\">gained</span> from haste<span class=\"note\">Or “vanity”</span>will dwindle,  but he who gathers <span class=\"idiom\">little by little</span><span class=\"note\">“upon the hand”</span>will increase it.'),
('LEB', 'Proverbs', '13', '12', '0', 'Hope that is deferred makes the heart<span class=\"note\">Or “mind”</span>sick,  but a desire fulfilled <span class=\"supplied\">is</span> a tree of life.'),
('LEB', 'Proverbs', '13', '13', '0', 'He who despises a word will bring destruction on himself,  but he who respects a commandment will be rewarded.'),
('LEB', 'Proverbs', '13', '14', '0', 'The teaching of the wise <span class=\"supplied\">is</span> a fountain of life,  <span class=\"supplied\">in order</span> to avoid the snares of death.'),
('LEB', 'Proverbs', '13', '15', '0', 'Good sense grants favor,  but the way of the faithless is coarse.'),
('LEB', 'Proverbs', '13', '16', '0', 'Anyone who is clever will act with intelligence,  but the fool will display folly.'),
('LEB', 'Proverbs', '13', '17', '0', 'A messenger of wickedness will fall into trouble,  but an envoy of the faithful <span class=\"supplied\">brings</span> healing.'),
('LEB', 'Proverbs', '13', '18', '0', 'Poverty and disgrace <span class=\"supplied\">belong to</span> him who ignores instruction,  but he who guards reproof will be honored.'),
('LEB', 'Proverbs', '13', '19', '0', 'A desire <span class=\"idiom\">fulfilled</span><span class=\"note\">“made to be,” “brought about”</span>will be sweet to the soul,  but an abomination of fools is turning from evil.'),
('LEB', 'Proverbs', '13', '20', '0', 'Walk with the wise and be wise,  but as for the companion of fools, he will suffer harm.'),
('LEB', 'Proverbs', '13', '21', '0', 'Misfortune will pursue sinners,  but the righteous <span class=\"idiom\">will be rewarded with prosperity</span>.<span class=\"note\">“it will reward prosperity”</span>'),
('LEB', 'Proverbs', '13', '22', '0', 'He who is good will leave an inheritance to <span class=\"idiom\">his grandchildren</span>,<span class=\"note\">“sons of sons”</span> and stored up for the righteous <span class=\"supplied\">is</span> the wealth of a sinner.'),
('LEB', 'Proverbs', '13', '23', '0', '<span class=\"supplied\">There is</span> much food <span class=\"supplied\">in</span> the field of the poor,  but <span class=\"idiom\">it is swept away</span><span class=\"note\">“there is sweeping away”</span>by <span class=\"idiom\">injustice</span>.<span class=\"note\">“no justice”</span>'),
('LEB', 'Proverbs', '13', '24', '0', 'He who withholds his rod hates his child,  but he who loves him <span class=\"idiom\">gives him</span><span class=\"note\">“visits him”</span>discipline.'),
('LEB', 'Proverbs', '13', '25', '0', 'The righteous eats to satisfy his life,<span class=\"note\">Or “soul,” or “inner self”</span> but the belly of the wicked will lack.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '14', '1', '0', 'The <span class=\"idiom\">wisest of women</span><span class=\"note\">“wise ones of women”</span>builds her house,  but the foolish tears it down with her hands.'),
('LEB', 'Proverbs', '14', '2', '0', 'He who walks in uprightness fears Adonai,  but he who is devious <span class=\"supplied\">in</span> his ways displeases him.'),
('LEB', 'Proverbs', '14', '3', '0', 'In the mouth of a fool is the rod of pride,  but the lips of the wise preserve them.'),
('LEB', 'Proverbs', '14', '4', '0', '<span class=\"idiom\">When there are no</span><span class=\"note\">“In there is no”</span>oxen the manger is empty,  but an abundance of crops <span class=\"supplied\">comes</span> by the strength of an ox.<span class=\"note\">Hebrew “bull”</span>'),
('LEB', 'Proverbs', '14', '5', '0', 'A faithful witness does not lie,  but he who breathes out falsehood is a witness of deceit.'),
('LEB', 'Proverbs', '14', '6', '0', 'A scoffer seeks wisdom, but there is none,  but knowledge <span class=\"idiom\">comes easily to him who understands</span>.<span class=\"note\">“to one who understands it comes easy”</span>'),
('LEB', 'Proverbs', '14', '7', '0', 'Leave <span class=\"idiom\">the presence of a foolish man</span>,<span class=\"note\">“from the presence <span class=\"supplied\">belonging</span> to a man of foolishness”</span> <span class=\"supplied\">for</span> you will not <span class=\"supplied\">come to</span> know <span class=\"idiom\">words of knowledge</span>.<span class=\"note\">“lips of knowledge”</span>'),
('LEB', 'Proverbs', '14', '8', '0', 'The wisdom of the clever is understanding his ways,  but the folly of fools is deceit.'),
('LEB', 'Proverbs', '14', '9', '0', 'Fools mock<span class=\"note\">Hebrew “he mocks”</span><span class=\"supplied\">the</span> guilt offering,  but among the upright, it is favorable.'),
('LEB', 'Proverbs', '14', '10', '0', 'The heart<span class=\"note\">Or “mind”</span>knows the bitterness of its soul,<span class=\"note\">Or “life,” or “inner self”</span> but in its joy, it will not share itself with a stranger.'),
('LEB', 'Proverbs', '14', '11', '0', 'The house of the wicked will be destroyed,  but the tent of the upright will flourish.'),
('LEB', 'Proverbs', '14', '12', '0', 'There is a way <span class=\"supplied\">that seems</span> upright <span class=\"idiom\">to</span><span class=\"note\">“to the face of”</span>a man,  but its end <span class=\"supplied\">is</span> the way of death.'),
('LEB', 'Proverbs', '14', '13', '0', 'Even in laughter, a heart may be sad,  and the end of joy <span class=\"supplied\">may be</span> grief.'),
('LEB', 'Proverbs', '14', '14', '0', 'From his ways, the perverse of heart will be satisfied,  and <span class=\"idiom\">from his own</span>,<span class=\"note\">“from upon him”</span><span class=\"supplied\">so shall</span> a good man.'),
('LEB', 'Proverbs', '14', '15', '0', 'The simple will believe every word,  but the clever will consider his step.'),
('LEB', 'Proverbs', '14', '16', '0', 'The wise is cautious and turns from evil,  but the fool throws off restraint and is confident.'),
('LEB', 'Proverbs', '14', '17', '0', 'He who is short of <span class=\"idiom\">temper</span><span class=\"note\">“nostril”</span>will act foolishly,  and the man who schemes will be hated.'),
('LEB', 'Proverbs', '14', '18', '0', 'The simple are adorned <span class=\"supplied\">with</span> folly,  but the clever are crowned with knowledge.'),
('LEB', 'Proverbs', '14', '19', '0', 'The evil bow down before the good,  and the wicked at the gates of the righteous.'),
('LEB', 'Proverbs', '14', '20', '0', 'The poor is disliked even by his neighbor,  but the lovers of the rich are many.'),
('LEB', 'Proverbs', '14', '21', '0', 'He who despises his neighbor is a sinner,  but he who has mercy on the poor blesses him.'),
('LEB', 'Proverbs', '14', '22', '0', 'Have they not erred, those who plan evil?  But loyalty and faithfulness <span class=\"supplied\">belong to</span> those who plan good.'),
('LEB', 'Proverbs', '14', '23', '0', 'In all toil, there is profit,  but the <span class=\"idiom\">talk</span><span class=\"note\">“word, matter, thing”</span>of lips <span class=\"supplied\">leads</span> only to poverty.'),
('LEB', 'Proverbs', '14', '24', '0', 'The crown of the wise <span class=\"supplied\">is</span> their wealth;  the folly of fools is folly.'),
('LEB', 'Proverbs', '14', '25', '0', 'He who saves lives<span class=\"note\">Or “souls,” or “inner selves”</span><span class=\"supplied\">is</span> a witness of truth,  but he who utters lies <span class=\"supplied\">is</span> a betrayer.'),
('LEB', 'Proverbs', '14', '26', '0', 'In the fear of Adonai, there is confidence of strength,  and for his children, there will be refuge.'),
('LEB', 'Proverbs', '14', '27', '0', 'The fear of Adonai <span class=\"supplied\">is</span> a fountain of life,  <span class=\"supplied\">in order</span> to turn from the snares of death.'),
('LEB', 'Proverbs', '14', '28', '0', 'In the multitude of people is the glory of the king,  but without a population, a prince <span class=\"supplied\">is</span> ruined.'),
('LEB', 'Proverbs', '14', '29', '0', '<span class=\"supplied\">He who is</span> slow to anger <span class=\"supplied\">has</span> great understanding,  but the hasty of spirit<span class=\"note\">Or “breath”</span>exalts folly.'),
('LEB', 'Proverbs', '14', '30', '0', 'A heart<span class=\"note\">Or “mind”</span>of tranquility<span class=\"note\">Or “he who heals”</span><span class=\"supplied\">is</span> life <span class=\"supplied\">to the</span> flesh,  but causes bones of passion to rot.'),
('LEB', 'Proverbs', '14', '31', '0', 'He who oppresses the poor insults him who made him,  but he who has mercy on the poor honors him.'),
('LEB', 'Proverbs', '14', '32', '0', 'By his evildoing, the wicked will be overthrown,  and the righteous will find refuge in his death.'),
('LEB', 'Proverbs', '14', '33', '0', 'In the heart<span class=\"note\">Or “mind”</span>of him who has understanding, wisdom rests,  but <span class=\"supplied\">even</span> in the midst of fools it becomes known.'),
('LEB', 'Proverbs', '14', '34', '0', 'Righteousness will exalt a nation,  but sin <span class=\"supplied\">is</span> a reproach to a people.'),
('LEB', 'Proverbs', '14', '35', '0', 'The favor of a king <span class=\"supplied\">is</span> for the servant who deals wisely,  but his wrath will be <span class=\"supplied\">on</span> him who acts shamefully.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '15', '1', '0', 'A soft answer will turn away wrath,  but a word of trouble will stir <span class=\"idiom\">anger</span>.<span class=\"note\">“nostril”</span>'),
('LEB', 'Proverbs', '15', '2', '0', 'The tongue of the wise will dispense knowledge,  but the mouth of fools will pour out folly.'),
('LEB', 'Proverbs', '15', '3', '0', 'In every place, the eyes of Adonai  keep watch over the evil and the good.'),
('LEB', 'Proverbs', '15', '4', '0', 'Gentleness<span class=\"note\">Or “Healing”</span>of tongue is a tree of life,  but perverseness in it<span class=\"note\">That is, the tongue</span><span class=\"supplied\">causes</span> a break in spirit.'),
('LEB', 'Proverbs', '15', '5', '0', 'A fool will despise the instruction of his father,  but he who guards reproof is prudent.'),
('LEB', 'Proverbs', '15', '6', '0', '<span class=\"supplied\">In</span> the house of the righteous <span class=\"supplied\">there is</span> much treasure,  but the income of the wicked brings trouble.'),
('LEB', 'Proverbs', '15', '7', '0', 'The lips of the wise will spread knowledge,  but the heart<span class=\"note\">Or “mind”</span>of fools, not so.'),
('LEB', 'Proverbs', '15', '8', '0', 'The sacrifice of the wicked is an abomination of Adonai,  but the prayer of the upright <span class=\"supplied\">is</span> his delight.'),
('LEB', 'Proverbs', '15', '9', '0', 'An abomination of Adonai is the way of the wicked,  but he who pursues righteousness he will love.'),
('LEB', 'Proverbs', '15', '10', '0', 'Severe discipline <span class=\"supplied\">belongs</span> to him who forsakes the way;  he who hates a rebuke will die.'),
('LEB', 'Proverbs', '15', '11', '0', 'Sheol<span class=\"note\">A term for the place where the dead reside, i.e., the Underworld</span>and Abaddon<span class=\"note\">Poetic synonym for “Sheol.” Only mentioned in the ot in relation to Sheol, the grave, or death.</span><span class=\"supplied\">are</span> before Adonai,  <span class=\"idiom\">how much more</span><span class=\"note\">“more for”</span>the hearts of the children of men!'),
('LEB', 'Proverbs', '15', '12', '0', 'A scoffer does not like <span class=\"idiom\">his rebuke</span>;<span class=\"note\">“rebuke <span class=\"supplied\">belonging</span> to him”</span> to the wise he will not go.'),
('LEB', 'Proverbs', '15', '13', '0', 'A heart<span class=\"note\">Or “mind”</span>of gladness will make good <span class=\"idiom\">countenance</span>,<span class=\"note\">“faces”</span> but in sorrow of heart<span class=\"note\">Or “mind”</span>a spirit is broken.'),
('LEB', 'Proverbs', '15', '14', '0', 'The heart of him who understands will seek knowledge,  but the faces of fools, they<span class=\"note\">Hebrew “he/it”</span>will feed on folly.'),
('LEB', 'Proverbs', '15', '15', '0', 'All the days of the poor <span class=\"supplied\">are</span> hard,  but goodness of heart<span class=\"note\">Or “mind”</span><span class=\"supplied\">is</span> a continuous feast.'),
('LEB', 'Proverbs', '15', '16', '0', 'Better <span class=\"supplied\">is</span> little with the fear of Adonai  than great treasure and trouble with it.<span class=\"note\">Or “him”</span>'),
('LEB', 'Proverbs', '15', '17', '0', 'Better <span class=\"supplied\">is</span> a dinner of vegetables when<span class=\"note\">Hebrew “and”</span>love <span class=\"supplied\">is</span> there  than a fattened ox and hatred with it.<span class=\"note\">Or “him”</span>'),
('LEB', 'Proverbs', '15', '18', '0', 'A man who is hot-tempered will stir up strife,  but he who is slow <span class=\"supplied\">to</span> <span class=\"idiom\">anger</span>,<span class=\"note\">“nostrils”</span>he will calm contention.'),
('LEB', 'Proverbs', '15', '19', '0', 'The way of the lazy is like a hedge of thorns,<span class=\"note\">Hebrew “thorn”</span> but the path of the upright <span class=\"supplied\">is</span> a highway.'),
('LEB', 'Proverbs', '15', '20', '0', 'A child of wisdom will make a father glad,  but a <span class=\"idiom\">foolish person</span>,<span class=\"note\">“foolishness of humankind”</span>he despises his mother.'),
('LEB', 'Proverbs', '15', '21', '0', 'Folly is a joy to him who lacks <span class=\"idiom\">sense</span>,<span class=\"note\">“heart”</span> and a person of understanding <span class=\"idiom\">will walk upright</span>.<span class=\"note\">“he will walk upright walking”</span>'),
('LEB', 'Proverbs', '15', '22', '0', 'Plans go wrong when there is no counsel,  but with many advisors it will succeed.'),
('LEB', 'Proverbs', '15', '23', '0', 'Joy <span class=\"supplied\">belongs</span> to a man with answers in his mouth,  and a word in its time, how good <span class=\"supplied\">it is</span>!'),
('LEB', 'Proverbs', '15', '24', '0', 'The path of life <span class=\"supplied\">leads</span> upward for him who has insight,  in order to turn away from Sheol below.'),
('LEB', 'Proverbs', '15', '25', '0', 'The house of the proud, Adonai will tear <span class=\"supplied\">it</span> down,  but he will maintain the <span class=\"idiom\">property line</span><span class=\"note\">“boundary”</span>of the widow.'),
('LEB', 'Proverbs', '15', '26', '0', 'Plans of evil <span class=\"supplied\">are</span> an abomination of Adonai,  but gracious words are pure.'),
('LEB', 'Proverbs', '15', '27', '0', 'He who makes trouble for his house <span class=\"supplied\">is</span> he who is greedy for unjust gain,  but he who hates bribes will live.'),
('LEB', 'Proverbs', '15', '28', '0', 'A heart<span class=\"note\">Or “mind”</span>of righteousness will ponder the answer,  but a mouth of wickedness will pour out deceit.'),
('LEB', 'Proverbs', '15', '29', '0', 'Adonai is far from the wicked,  but the prayers of the righteous he will hear.'),
('LEB', 'Proverbs', '15', '30', '0', 'From the light of the eyes, the heart<span class=\"note\">Or “mind”</span>will rejoice,  and good news will enliven the bones.<span class=\"note\">Hebrew “bone”</span>'),
('LEB', 'Proverbs', '15', '31', '0', 'The ear of him who listens <span class=\"supplied\">to</span> admonitions of life,  in the midst of the wise it will lodge.'),
('LEB', 'Proverbs', '15', '32', '0', 'He who ignores instruction despises himself,<span class=\"note\">Or “soul,” or “inner self”</span> but he who hears admonition gains heart.<span class=\"note\">Or “mind”</span>'),
('LEB', 'Proverbs', '15', '33', '0', 'Fear of Adonai <span class=\"supplied\">is</span> the instruction of the wise,  and before honor <span class=\"supplied\">comes</span> humility.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '16', '1', '0', 'To mortals <span class=\"supplied\">belong</span> the plans of the heart,  but from Adonai <span class=\"supplied\">comes</span> the answer of the tongue.'),
('LEB', 'Proverbs', '16', '2', '0', 'All the ways of a man <span class=\"supplied\">are</span> pure in his own eyes,  but Adonai weighs the spirit.<span class=\"note\">Or “breath”</span>'),
('LEB', 'Proverbs', '16', '3', '0', 'Commit your work to Adonai,  and your plans will be established.'),
('LEB', 'Proverbs', '16', '4', '0', 'All Adonai has made <span class=\"supplied\">is</span> for his<span class=\"note\">Or “its”</span>purpose,  and even the wicked for the day of trouble.'),
('LEB', 'Proverbs', '16', '5', '0', 'An abomination of Adonai <span class=\"supplied\">are</span> all who are arrogant of heart;<span class=\"note\">Or “mind”</span> <span class=\"idiom\">rest assured</span>,<span class=\"note\">“hand to hand”</span>he will not go unpunished.'),
('LEB', 'Proverbs', '16', '6', '0', 'By loyalty and faithfulness, iniquity will be covered over,<span class=\"note\">Or “atoned for”</span> and by fear of Adonai <span class=\"idiom\">one turns</span><span class=\"note\">“turning”</span>from evil.'),
('LEB', 'Proverbs', '16', '7', '0', 'When the ways of a man are pleasing <span class=\"supplied\">to</span> Adonai,  even his enemies he will cause to make peace with him.'),
('LEB', 'Proverbs', '16', '8', '0', 'Better is little with righteousness  than great income with no justice.'),
('LEB', 'Proverbs', '16', '9', '0', 'The <span class=\"idiom\">mind</span><span class=\"note\">“heart”</span>of a person will plan his ways,  and Adonai will direct his steps.'),
('LEB', 'Proverbs', '16', '10', '0', 'A decision is upon the lips of a king;  in judgment his mouth will not sin.'),
('LEB', 'Proverbs', '16', '11', '0', 'A balance and scales of justice <span class=\"supplied\">belong</span> to Adonai;  all the weights of the bag <span class=\"supplied\">are</span> his work.'),
('LEB', 'Proverbs', '16', '12', '0', 'An abomination to kings <span class=\"supplied\">is</span> doing evil,  for by righteousness the throne will be established.'),
('LEB', 'Proverbs', '16', '13', '0', 'The delight of kings <span class=\"supplied\">are</span> the lips of righteousness,  and he who speaks what is upright he will love.'),
('LEB', 'Proverbs', '16', '14', '0', 'The wrath of a king <span class=\"supplied\">is</span> a messenger of death,  but one who is wise will appease<span class=\"note\">Or “atone, cover over”</span>it.'),
('LEB', 'Proverbs', '16', '15', '0', 'In the light of the face of the king <span class=\"supplied\">there is</span> life,  and his favor <span class=\"supplied\">is</span> like a cloud of spring rain.'),
('LEB', 'Proverbs', '16', '16', '0', 'Getting wisdom: <span class=\"idiom\">how much</span><span class=\"note\">“what”</span>better than gold!  And getting understanding: it is chosen <span class=\"idiom\">over</span><span class=\"note\">“from”</span>silver.'),
('LEB', 'Proverbs', '16', '17', '0', 'The highway of the upright, it turns from evil,  he who guards himself<span class=\"note\">Or “soul,” or “inner self”</span>keeps his way.'),
('LEB', 'Proverbs', '16', '18', '0', 'Before destruction <span class=\"supplied\">comes</span> pride,  and before a fall, a haughty spirit.<span class=\"note\">Hebrew “a haughty of spirit”</span>'),
('LEB', 'Proverbs', '16', '19', '0', 'Better a lowly spirit with the poor  than dividing the spoil with the proud.'),
('LEB', 'Proverbs', '16', '20', '0', 'He who is attentive to a matter will find goodness,  and he who trusts in Adonai, his own happiness.'),
('LEB', 'Proverbs', '16', '21', '0', 'The wise of heart<span class=\"note\">Or “mind”</span>is called perceptive,  but he who is pleasant of lips will increase persuasiveness.'),
('LEB', 'Proverbs', '16', '22', '0', 'A fountain of life is wisdom for its owner,<span class=\"note\">Or “master”</span> but the instruction<span class=\"note\">Or “punishment”</span>of fools is folly.'),
('LEB', 'Proverbs', '16', '23', '0', 'The heart<span class=\"note\">Or “mind”</span>of the wise will make his mouth judicious,  and upon his lips, it<span class=\"note\">Or “he”</span>will add persuasiveness.'),
('LEB', 'Proverbs', '16', '24', '0', 'Pleasant sayings <span class=\"supplied\">are</span> a honeycomb,  sweetness to the soul and healing to the bones.'),
('LEB', 'Proverbs', '16', '25', '0', 'There is a way <span class=\"supplied\">that seems</span> upright <span class=\"idiom\">to</span><span class=\"note\">“to the face of”</span>a man,  but its end <span class=\"supplied\">is</span> the way of death.'),
('LEB', 'Proverbs', '16', '26', '0', 'The life<span class=\"note\">Or “soul,” or “inner self”</span>of a worker works for him,  for his <span class=\"idiom\">hunger</span><span class=\"note\">“mouth”</span>urges him.'),
('LEB', 'Proverbs', '16', '27', '0', 'A man of wickedness concocts evil,  and his lips <span class=\"supplied\">are</span> like a scorching fire.'),
('LEB', 'Proverbs', '16', '28', '0', 'A person of perversity will spread dissent,  and he who whispers separates a close friend.'),
('LEB', 'Proverbs', '16', '29', '0', 'A person of violence will entice his neighbor  and cause him to walk on a way <span class=\"supplied\">that is</span> not good.'),
('LEB', 'Proverbs', '16', '30', '0', 'He who winks his eyes <span class=\"supplied\">does so in order</span> to plan perverse things;  he who purses his lips will bring evil to pass.'),
('LEB', 'Proverbs', '16', '31', '0', 'A crown of glory is gray hair;  by a righteous life it is gained.'),
('LEB', 'Proverbs', '16', '32', '0', 'He who is slow to anger<span class=\"note\">“nostrils”</span><span class=\"supplied\">is</span> better than him who is mighty,  and he who controls his spirit than him who captures a city.'),
('LEB', 'Proverbs', '16', '33', '0', 'The lot will be cast into the lap,  but all of its decisions are from Adonai.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '17', '1', '0', 'Better a dry morsel and quiet with it  than a house filled <span class=\"supplied\">with</span> feasts of strife.'),
('LEB', 'Proverbs', '17', '2', '0', 'A slave who deals wisely will rule over a child who acts shamefully,  and in the midst of brothers he will share his inheritance.'),
('LEB', 'Proverbs', '17', '3', '0', 'A crucible <span class=\"supplied\">is</span> for the silver, and a furnace <span class=\"supplied\">is</span> for the gold,  but Adonai will test hearts.'),
('LEB', 'Proverbs', '17', '4', '0', 'He who does evil listens to lips of wickedness,  <span class=\"supplied\">and</span> the liar gives heed to the tongue of mischief.'),
('LEB', 'Proverbs', '17', '5', '0', 'He who mocks the poor insults him who made him;  he who rejoices at calamity will not go unpunished.'),
('LEB', 'Proverbs', '17', '6', '0', 'The crown of the elderly <span class=\"supplied\">are</span> <span class=\"idiom\">grandchildren</span>,<span class=\"note\">“sons of sons”</span> and the glory of children <span class=\"supplied\">is</span> their fathers.'),
('LEB', 'Proverbs', '17', '7', '0', '<span class=\"idiom\">Fine speech</span><span class=\"note\">“A lip of fine<span class=\"supplied\">ness</span>”</span><span class=\"supplied\">is</span> not becoming a fool,  <span class=\"idiom\">still less</span><span class=\"note\">“only for”</span><span class=\"supplied\">is</span> <span class=\"idiom\">false speech</span><span class=\"note\">“lip of deceit”</span>for a ruler.'),
('LEB', 'Proverbs', '17', '8', '0', 'The bribe <span class=\"supplied\">is</span> a stone of magic in the eyes of its owner;<span class=\"note\">Or “master”</span> <span class=\"idiom\">everywhere</span><span class=\"note\">“to all which”</span>he will turn, he will prosper.'),
('LEB', 'Proverbs', '17', '9', '0', 'He who forgives an affront fosters love,  but he who waits on a matter will alienate a friend.'),
('LEB', 'Proverbs', '17', '10', '0', 'A rebuke strikes him who understands  deeper than one hundred blows to a fool.'),
('LEB', 'Proverbs', '17', '11', '0', 'An evil person will seek only rebellion,  and a cruel messenger will be sent against him.'),
('LEB', 'Proverbs', '17', '12', '0', '<span class=\"supplied\">May</span> a man meet a she-bear robbed of offspring  and not a fool in his folly.'),
('LEB', 'Proverbs', '17', '13', '0', '<span class=\"supplied\">For</span> he who returns evil for good,  evil will not depart from his house.'),
('LEB', 'Proverbs', '17', '14', '0', '<span class=\"supplied\">Like</span> the release of water <span class=\"supplied\">is</span> the beginning of strife;  before it breaks out, stop the quarrel.'),
('LEB', 'Proverbs', '17', '15', '0', 'He who justifies the wicked and he who condemns the righteous,  the two of them <span class=\"supplied\">are</span> both abominations of Adonai.'),
('LEB', 'Proverbs', '17', '16', '0', 'Why <span class=\"supplied\">is</span> this? A price in the hand of a fool,  <span class=\"supplied\">in order</span> to buy wisdom where<span class=\"note\">Hebrew “and”</span>there is no <span class=\"idiom\">sense</span>.<span class=\"note\">“heart”</span>'),
('LEB', 'Proverbs', '17', '17', '0', 'The friend loves at all times<span class=\"note\">Hebrew “time”</span>,  but a brother is born for adversity.'),
('LEB', 'Proverbs', '17', '18', '0', 'A person who lacks <span class=\"idiom\">sense</span><span class=\"note\">“heart”</span><span class=\"idiom\">pledges</span>;<span class=\"note\">“pledges a hand”</span> he becomes security before his neighbor.'),
('LEB', 'Proverbs', '17', '19', '0', 'He who loves transgression loves strife;  he who builds his high thresholds seeks destruction.'),
('LEB', 'Proverbs', '17', '20', '0', 'He who is crooked of heart<span class=\"note\">Or “mind”</span>will not find goodness,  and he who is perverse, by his tongue he will fall into calamity.'),
('LEB', 'Proverbs', '17', '21', '0', 'He who begets a fool, <span class=\"supplied\">there is</span> trouble for him;  the father of a fool will not rejoice.'),
('LEB', 'Proverbs', '17', '22', '0', 'A cheerful heart is good medicine,  but a downcast spirit will dry out bones.'),
('LEB', 'Proverbs', '17', '23', '0', 'The wicked will accept a bribe from the lap,  <span class=\"supplied\">in order</span> to pervert the ways of justice.'),
('LEB', 'Proverbs', '17', '24', '0', 'He who understands sets <span class=\"supplied\">his</span> face <span class=\"supplied\">toward</span> wisdom,  but the eyes of a fool, to the end of the earth.<span class=\"note\">Or “land”</span>'),
('LEB', 'Proverbs', '17', '25', '0', 'A grief to his father <span class=\"supplied\">is</span> the child of a fool,  and bitterness to her who bore him.'),
('LEB', 'Proverbs', '17', '26', '0', 'Also, imposing a fine on the righteous is not good,  <span class=\"supplied\">nor</span> to flog nobles for uprightness.'),
('LEB', 'Proverbs', '17', '27', '0', 'He who spares his sayings knows knowledge,  and a cool spirit is a man of understanding.'),
('LEB', 'Proverbs', '17', '28', '0', 'Even a fool who keeps silent <span class=\"idiom\">shall be considered wise</span>;<span class=\"note\">“wise, he shall be considered”</span> he who closes his lips <span class=\"supplied\">is</span> intelligent.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '18', '1', '0', 'He who is selfish seeks a craving;<span class=\"note\">Or “boundary”</span> against all sound judgment he shows contempt.'),
('LEB', 'Proverbs', '18', '2', '0', 'A fool will not take pleasure in understanding,  but in expressing his heart.<span class=\"note\">Or “mind”</span>'),
('LEB', 'Proverbs', '18', '3', '0', 'With the coming of wickedness comes contempt also,  and with dishonor, disgrace.'),
('LEB', 'Proverbs', '18', '4', '0', 'Deep waters <span class=\"supplied\">are</span> words of the mouth of a man;  a gushing stream <span class=\"supplied\">is</span> a fountain of wisdom.'),
('LEB', 'Proverbs', '18', '5', '0', 'Being partial <span class=\"supplied\">to</span> faces of evil <span class=\"supplied\">is</span> not good,  <span class=\"supplied\">nor</span> to subvert the righteous at the judgment.'),
('LEB', 'Proverbs', '18', '6', '0', 'The lips of a fool will bring strife,  and his mouth calls out for a flogging.'),
('LEB', 'Proverbs', '18', '7', '0', 'The mouth of a fool <span class=\"supplied\">is</span> ruin to him,  and his lips <span class=\"supplied\">are</span> a snare to his soul.<span class=\"note\">Or “life,” or “inner self”</span>'),
('LEB', 'Proverbs', '18', '8', '0', 'The words of a whisper <span class=\"supplied\">are</span> like delicious morsels,  and they themselves go down <span class=\"supplied\">to</span> inner parts of the body.'),
('LEB', 'Proverbs', '18', '9', '0', 'Even he who is slack in his work,  he is brother to a master<span class=\"note\">Or “owner”</span>of destruction.'),
('LEB', 'Proverbs', '18', '10', '0', 'A tower of strength is the name of Adonai;  into him<span class=\"note\">Or “it”</span>the righteous will run and be safe.'),
('LEB', 'Proverbs', '18', '11', '0', 'The wealth of the rich <span class=\"supplied\">is</span> <span class=\"idiom\">his strong city</span>,<span class=\"note\">“a city of his strength”</span> and like a wall, it is high in his imagination.'),
('LEB', 'Proverbs', '18', '12', '0', 'In the presence of destruction, the heart<span class=\"note\">Or “mind”</span>of a man will be haughty,  but in the presence of honor, humble.'),
('LEB', 'Proverbs', '18', '13', '0', 'He who returns a word before he will hear,  folly itself <span class=\"supplied\">belongs</span> to him as well as<span class=\"note\">Hebrew “and”</span>shame.'),
('LEB', 'Proverbs', '18', '14', '0', 'The spirit of a man will endure his sickness,  but a broken spirit, who may bear it?'),
('LEB', 'Proverbs', '18', '15', '0', 'An intelligent <span class=\"idiom\">mind</span><span class=\"note\">“heart”</span>will acquire knowledge,  and the ear of the wise will seek knowledge.'),
('LEB', 'Proverbs', '18', '16', '0', 'The gift of a person will open doors for him,  and before the great, it gives him access.'),
('LEB', 'Proverbs', '18', '17', '0', 'The first in his dispute is <span class=\"supplied\">deemed</span> righteous,  <span class=\"supplied\">but</span> his neighbor will come and examine him.'),
('LEB', 'Proverbs', '18', '18', '0', 'The lot will put an end to disputes,  and between powerful contenders it will decide.'),
('LEB', 'Proverbs', '18', '19', '0', 'A brother who is offended <span class=\"supplied\">is worse</span> than a city of strength,  and quarrels <span class=\"supplied\">are</span> like the bars of a fortification.'),
('LEB', 'Proverbs', '18', '20', '0', 'From the fruit of a man’s mouth, his stomach will be satisfied,  as for the yield of his lips, it will satisfy.'),
('LEB', 'Proverbs', '18', '21', '0', 'Death and life <span class=\"supplied\">are</span> in the <span class=\"idiom\">power</span><span class=\"note\">“hand”</span>of the tongue,  and those who love her<span class=\"note\">That is, the power of the tongue</span>will eat of her fruit.'),
('LEB', 'Proverbs', '18', '22', '0', 'He who finds a wife<span class=\"note\">Or “woman”</span>finds good,  and he will obtain favor from Adonai.'),
('LEB', 'Proverbs', '18', '23', '0', 'The poor may speak entreaties,  but the rich will answer roughly.'),
('LEB', 'Proverbs', '18', '24', '0', 'A man of <span class=\"supplied\">many</span> friends will come to ruin,  but there is a friend who sticks closer than a brother.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '19', '1', '0', 'Better a poor person walking in integrity  than one who is perverse <span class=\"idiom\">in his speech</span><span class=\"note\">“in his lips”</span>and is a fool.'),
('LEB', 'Proverbs', '19', '2', '0', 'Also, a life<span class=\"note\">Or “soul,” or “inner self”</span>without knowledge is not good,  and he who moves quickly with <span class=\"supplied\">his</span> feet <span class=\"idiom\">misses the mark</span>.<span class=\"note\">Or “sins”</span>'),
('LEB', 'Proverbs', '19', '3', '0', 'As for the folly of humankind, its way leads to ruin,  and against Adonai his heart<span class=\"note\">Or “mind”</span>will rage.'),
('LEB', 'Proverbs', '19', '4', '0', 'Wealth adds many friends,  but the poor will be left by his friends.'),
('LEB', 'Proverbs', '19', '5', '0', 'A witness of falsehood will not go unpunished,  and he who breathes lies will not escape.'),
('LEB', 'Proverbs', '19', '6', '0', 'Many will seek favor before the generous,  and everyone <span class=\"supplied\">is</span> the friend of a man of gifts.<span class=\"note\">Hebrew “gift”</span>'),
('LEB', 'Proverbs', '19', '7', '0', 'All the brothers of the poor, <span class=\"supplied\">if</span> they hate him,  how much more will his friends keep away from him.  He pursues <span class=\"supplied\">them with</span> words, <span class=\"supplied\">and</span> they are gone.<span class=\"note\">Or “<span class=\"supplied\">when</span> he pursues words <span class=\"supplied\">and</span> not them”</span>'),
('LEB', 'Proverbs', '19', '8', '0', 'He who acquires wisdom loves himself;<span class=\"note\">Or “soul,” or “inner self”</span> he who guards understanding <span class=\"supplied\">loves</span> to find good.'),
('LEB', 'Proverbs', '19', '9', '0', 'A false witness will not go unpunished,  and he who breathes lies will perish.'),
('LEB', 'Proverbs', '19', '10', '0', 'For a fool living in luxury is not fitting,  <span class=\"supplied\">any</span> more <span class=\"supplied\">than it is</span> for a slave to rule over princes.'),
('LEB', 'Proverbs', '19', '11', '0', 'The understanding of a person makes him slow to his <span class=\"idiom\">anger</span>,<span class=\"note\">“nostril”</span> and his glory overlooks offense.'),
('LEB', 'Proverbs', '19', '12', '0', 'The rage of a king growls like a lion,  but his favor is like dew on the grass.'),
('LEB', 'Proverbs', '19', '13', '0', 'A foolish child is a ruin to his father,  and the quarreling of a woman<span class=\"note\">Or “wife”</span>is a continuous dripping.'),
('LEB', 'Proverbs', '19', '14', '0', 'A house and wealth <span class=\"supplied\">are</span> an inheritance <span class=\"supplied\">from</span> fathers,  but from Adonai <span class=\"supplied\">comes</span> a woman<span class=\"note\">Or “wife”</span>who is prudent.'),
('LEB', 'Proverbs', '19', '15', '0', 'Laziness will bring on a deep sleep,  and a person<span class=\"note\">Or “soul,” or “life”</span>of idleness will suffer hunger.'),
('LEB', 'Proverbs', '19', '16', '0', 'He who guards commandments guards his life;<span class=\"note\">Or “soul,” or “inner self”</span> he who is careless of his ways will be killed.'),
('LEB', 'Proverbs', '19', '17', '0', 'He who lends to Adonai <span class=\"supplied\">is</span> he who is kind to the poor,  and his benefits he will repay to him.'),
('LEB', 'Proverbs', '19', '18', '0', 'Discipline your child, for there is hope,  but on his destruction do not set your desire.<span class=\"note\">Or “soul,” or “inner self”</span>'),
('LEB', 'Proverbs', '19', '19', '0', 'A hot-tempered person pays a penalty;  if you rescue <span class=\"supplied\">him</span>, you will do <span class=\"supplied\">it</span> yet again.'),
('LEB', 'Proverbs', '19', '20', '0', 'Listen <span class=\"supplied\">to</span> advice and accept instruction  so that you will gain wisdom for your <span class=\"idiom\">future</span>.<span class=\"note\">“after <span class=\"supplied\">things</span>”</span>'),
('LEB', 'Proverbs', '19', '21', '0', 'Many plans <span class=\"supplied\">are</span> in the heart<span class=\"note\">Or “mind”</span>of a man,  but the purpose of Adonai will be established.'),
('LEB', 'Proverbs', '19', '22', '0', 'The craving of a man <span class=\"supplied\">is</span> his steadfast loyalty,  and <span class=\"supplied\">it is</span> better <span class=\"supplied\">to be</span> poor than a <span class=\"idiom\">liar</span>.<span class=\"note\">“man of lying”</span>'),
('LEB', 'Proverbs', '19', '23', '0', 'Fear of Adonai <span class=\"supplied\">leads</span> to life;  he who is filled <span class=\"supplied\">with it</span> will rest—he will not suffer harm.'),
('LEB', 'Proverbs', '19', '24', '0', 'A lazy person buries his hand in the dish,  and even to his mouth he will not bring it back.'),
('LEB', 'Proverbs', '19', '25', '0', 'The scoffer you shall strike, and the simple, may they learn prudence,  and reprove the intelligent <span class=\"supplied\">and</span> he will gain knowledge.'),
('LEB', 'Proverbs', '19', '26', '0', 'He who does violence to a father, he who chases away a mother,  <span class=\"supplied\">is</span> a child who causes shame and brings reproach.'),
('LEB', 'Proverbs', '19', '27', '0', 'Cease to listen to instruction, my child,  <span class=\"idiom\">and you will stray</span><span class=\"note\">“<span class=\"supplied\">in order</span> to stray”</span>from sayings of knowledge.'),
('LEB', 'Proverbs', '19', '28', '0', 'A worthless witness will mock justice,  and the mouth of the wicked will devour iniquity.'),
('LEB', 'Proverbs', '19', '29', '0', 'Judgments<span class=\"note\">Or “Punishments,” or “Condemnations”</span>are prepared for the scoffers,  and flogging for the back of fools.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '20', '1', '0', 'Wine <span class=\"supplied\">is</span> a mocker, strong drink a brawler,  and any who go astray by it are not wise.'),
('LEB', 'Proverbs', '20', '2', '0', 'Roaring like the lion <span class=\"supplied\">is the</span> dreaded anger of a king;  he who provokes him forfeits his life.<span class=\"note\">Or “soul,” or “inner self”</span>'),
('LEB', 'Proverbs', '20', '3', '0', '<span class=\"supplied\">It is</span> honorable for the man to refrain from strife,  but every fool will be quick to quarrel.'),
('LEB', 'Proverbs', '20', '4', '0', 'The lazy person will not plow in season;  he will expect at the harvest, but there <span class=\"supplied\">will be</span> nothing.'),
('LEB', 'Proverbs', '20', '5', '0', 'Deep waters <span class=\"supplied\">are like</span> purpose in the heart<span class=\"note\">Or “mind”</span>of a man,  and a man of understanding will draw it<span class=\"note\">Or “her”; referring to “purpose”</span>out.'),
('LEB', 'Proverbs', '20', '6', '0', 'Many a person will proclaim his loyalty <span class=\"supplied\">for</span> himself,  but a man <span class=\"supplied\">who is</span> trustworthy, who can find?'),
('LEB', 'Proverbs', '20', '7', '0', 'He who walks in his integrity <span class=\"supplied\">is</span> righteous;  happy are his children who follow him.'),
('LEB', 'Proverbs', '20', '8', '0', 'A king who sits on <span class=\"supplied\">the</span> throne of judgment  winnows all evil with his eyes.'),
('LEB', 'Proverbs', '20', '9', '0', 'Who will say “I have made my heart<span class=\"note\">Or “mind”</span>clean;  I am pure from my sin”?'),
('LEB', 'Proverbs', '20', '10', '0', 'Stone and stone, measure and measure,  <span class=\"idiom\">both of them</span><span class=\"note\">“also <span class=\"supplied\">the</span> two of them”</span><span class=\"supplied\">are</span> an abomination of Adonai.'),
('LEB', 'Proverbs', '20', '11', '0', 'Even by his acts, a <span class=\"idiom\">young man</span><span class=\"note\">Or “young boy,” or “adolescent”</span>will make himself known,  whether his acts are pure and upright.'),
('LEB', 'Proverbs', '20', '12', '0', 'The ear that hears and the eye that sees,  Adonai has made <span class=\"idiom\">them both</span>.<span class=\"note\">“also <span class=\"supplied\">the</span> two of them”</span>'),
('LEB', 'Proverbs', '20', '13', '0', 'Do not love sleep, lest you become poor;  open your eyes <span class=\"supplied\">and</span> have plenty bread.'),
('LEB', 'Proverbs', '20', '14', '0', '“Bad, bad,” the buyer will say,  but <span class=\"supplied\">when</span> one goes to him, then he will boast.'),
('LEB', 'Proverbs', '20', '15', '0', 'There is gold and many costly stones,  but precious jewels are lips of knowledge.'),
('LEB', 'Proverbs', '20', '16', '0', 'Take his garment, for he has given security <span class=\"supplied\">to</span> a stranger,  and on behalf of a foreigner—take it as pledge.'),
('LEB', 'Proverbs', '20', '17', '0', 'Bread gained by deceit is sweet for the man,  but afterward, his mouth will be filled <span class=\"supplied\">with</span> gravel.'),
('LEB', 'Proverbs', '20', '18', '0', 'A plan<span class=\"note\">Hebrew “plans”</span>will be established by advice,  and with guidance make war.'),
('LEB', 'Proverbs', '20', '19', '0', 'He reveals a secret, he who walks <span class=\"supplied\">about with</span> gossip,  and do not associate with a babbler’s lips.'),
('LEB', 'Proverbs', '20', '20', '0', 'He who curses his father and his mother,  his lamp will be extinguished <span class=\"idiom\">in the midst of darkness</span>.<span class=\"note\">“in a pupil of darkness”</span>'),
('LEB', 'Proverbs', '20', '21', '0', 'An inheritance acquired <span class=\"supplied\">hastily</span> <span class=\"idiom\">at the beginning</span><span class=\"note\">“at the first”</span> will not be blessed at its end.'),
('LEB', 'Proverbs', '20', '22', '0', 'Do not say “I will repay evil”;  wait for Adonai and he will deliver you.'),
('LEB', 'Proverbs', '20', '23', '0', 'An abomination of Adonai <span class=\"supplied\">is</span> <span class=\"idiom\">a stone and a weight</span>,<span class=\"note\">“a stone and a stone”</span> and scales of falsehood are not good.'),
('LEB', 'Proverbs', '20', '24', '0', '<span class=\"supplied\">Away</span> from Adonai are the steps of a strong man,  and how will humankind understand his ways?'),
('LEB', 'Proverbs', '20', '25', '0', '<span class=\"supplied\">It is</span> a snare to humankind to say rashly “<span class=\"supplied\">It is</span> holy,”  and after vows, to scrutinize.'),
('LEB', 'Proverbs', '20', '26', '0', 'A wise king winnows the wicked,  and he will drive a wheel over them.'),
('LEB', 'Proverbs', '20', '27', '0', 'The lamp of Adonai <span class=\"supplied\">is</span> the spirit<span class=\"note\">Or “soul”</span>of humankind,  he who searches every <span class=\"idiom\">innermost part</span>.<span class=\"note\">“parts of the inmost”</span>'),
('LEB', 'Proverbs', '20', '28', '0', 'Loyalty and faithfulness will preserve a king,  and he is upheld with the righteousness of his throne.'),
('LEB', 'Proverbs', '20', '29', '0', 'The glory of young men <span class=\"supplied\">is</span> their strength,<span class=\"note\">Or “strengthens them”</span> but the beauty of the aged <span class=\"supplied\">is</span> gray hair.'),
('LEB', 'Proverbs', '20', '30', '0', 'The blows of a wound will cleanse evil,  as will<span class=\"note\">Hebrew “and”</span>beatings of the <span class=\"idiom\">innermost part</span>.<span class=\"note\">“parts of the inmost”</span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '21', '1', '0', 'Streams of water <span class=\"supplied\">are</span> the heart<span class=\"note\">Or “mind”</span>of a king in the hand of Adonai;  <span class=\"idiom\">wherever</span><span class=\"note\">“upon all that”</span>he will desire, he will turn.'),
('LEB', 'Proverbs', '21', '2', '0', 'Every way of a man is upright in his <span class=\"supplied\">own</span> eyes,  but Adonai weighs hearts.<span class=\"note\">Or “minds”</span>'),
('LEB', 'Proverbs', '21', '3', '0', 'Doing righteousness and justice is <span class=\"supplied\">more</span> acceptable  to Adonai than sacrifice.'),
('LEB', 'Proverbs', '21', '4', '0', 'Haughtiness of <span class=\"supplied\">the</span> eyes and pride of heart,  <span class=\"supplied\">the</span> lamp of the wicked <span class=\"supplied\">are</span> sin.'),
('LEB', 'Proverbs', '21', '5', '0', 'The plans of the diligent only <span class=\"supplied\">lead</span> to abundance,  but all who are hasty, only to want.'),
('LEB', 'Proverbs', '21', '6', '0', 'He who makes treasure by a lying tongue  <span class=\"supplied\">is a</span> fleeting vapor <span class=\"supplied\">and</span> seeker<span class=\"note\">Hebrew “seekers”</span>of death.'),
('LEB', 'Proverbs', '21', '7', '0', 'The violence of the wicked will sweep them away,  for they refuse to do justice.'),
('LEB', 'Proverbs', '21', '8', '0', 'Crooked is the way of a man and a foreigner,  but the pure <span class=\"supplied\">is</span> upright <span class=\"supplied\">in</span> his conduct.'),
('LEB', 'Proverbs', '21', '9', '0', 'Better to dwell on the corner of a roof  than <span class=\"supplied\">to</span> share a house with a woman<span class=\"note\">Or “wife”</span>of contention.'),
('LEB', 'Proverbs', '21', '10', '0', 'The soul<span class=\"note\">Or “soul, life, throat”</span>of the wicked desires evil;  his neighbor will not find mercy in his eyes.'),
('LEB', 'Proverbs', '21', '11', '0', 'With the punishment of a scoffer, the simple will become wise,  and with the instruction of the wise, he will obtain knowledge.'),
('LEB', 'Proverbs', '21', '12', '0', 'The righteous observes the house of the wicked;  he throws the wicked to ruin.'),
('LEB', 'Proverbs', '21', '13', '0', 'He who closes his ear from the cry of the poor,  he also will cry out and not be heard.'),
('LEB', 'Proverbs', '21', '14', '0', 'A gift in secret<span class=\"note\">Hebrew “the secret”</span>will avert <span class=\"idiom\">anger</span>,<span class=\"note\">“nostril”</span> and a <span class=\"idiom\">concealed bribe</span>,<span class=\"note\">“bribe in the bosom”</span>strong wrath.'),
('LEB', 'Proverbs', '21', '15', '0', '<span class=\"supplied\">It is</span> a joy to the righteous to do justice,  but dismay to those who do evil.'),
('LEB', 'Proverbs', '21', '16', '0', 'Whoever wanders from the way of understanding,  in the assembly of the dead<span class=\"note\">Or “Rephaim”</span>he will rest.'),
('LEB', 'Proverbs', '21', '17', '0', 'A man of want <span class=\"supplied\">is</span> he who loves pleasure;  he who loves wine and oil<span class=\"note\">Or “fat”</span>will not become rich.'),
('LEB', 'Proverbs', '21', '18', '0', 'A ransom for the righteous is the wicked,  and <span class=\"supplied\">the</span> faithless instead of the upright.'),
('LEB', 'Proverbs', '21', '19', '0', 'Better to live in a land of wilderness  than <span class=\"supplied\">with</span> a wife of quarrels and provocation.'),
('LEB', 'Proverbs', '21', '20', '0', 'Precious treasure and oil <span class=\"supplied\">are</span> in the house of the wise,  but the foolish person<span class=\"note\">Or “foolish of mankind”</span>will devour them.<span class=\"note\">“it”</span>'),
('LEB', 'Proverbs', '21', '21', '0', 'He who pursues righteousness and kindness  will find life, righteousness, and honor.'),
('LEB', 'Proverbs', '21', '22', '0', 'To a city of warriors, the wise ascends,  and he will bring down the stronghold—its object of trust.'),
('LEB', 'Proverbs', '21', '23', '0', 'He who guards his mouth and his tongue,  he guards his life<span class=\"note\">Or “soul,” or “inner self”</span>from danger.'),
('LEB', 'Proverbs', '21', '24', '0', 'The proud, haughty one: “scoffer” <span class=\"supplied\">is</span> his name;  he acts with arrogance of pride.'),
('LEB', 'Proverbs', '21', '25', '0', 'The craving of a lazy person will kill him,  for his hands refuse to work.<span class=\"note\">Or “make”</span>'),
('LEB', 'Proverbs', '21', '26', '0', '<span class=\"idiom\">All day</span><span class=\"note\">“All the day”</span>he craves a craving,  but the righteous will give and not hold back.'),
('LEB', 'Proverbs', '21', '27', '0', 'The sacrifice of the wicked <span class=\"supplied\">is</span> an abomination;  how much more when he brings it in divisiveness!'),
('LEB', 'Proverbs', '21', '28', '0', 'A false witness will perish,  but a man who listens will testify with<span class=\"note\">Hebrew “to the”</span>success.'),
('LEB', 'Proverbs', '21', '29', '0', 'A wicked man is strong in <span class=\"idiom\">his countenance</span>,<span class=\"note\">“his face”</span> but the upright will appoint his paths.'),
('LEB', 'Proverbs', '21', '30', '0', 'There is no wisdom, nor understanding,  nor counsel to oppose Adonai.'),
('LEB', 'Proverbs', '21', '31', '0', 'A horse is prepared for the day of battle,  but to Adonai <span class=\"supplied\">belongs</span> the victory.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '22', '1', '0', 'A <span class=\"idiom\">reputation</span><span class=\"note\">“name”</span><span class=\"supplied\">is</span> to be chosen <span class=\"idiom\">rather than</span><span class=\"note\">“from”</span>great riches;  <span class=\"idiom\">favor is better than silver and gold</span>.<span class=\"note\">“from silver and from gold, favor <span class=\"supplied\">is</span> better”</span>'),
('LEB', 'Proverbs', '22', '2', '0', 'Rich and poor have <span class=\"supplied\">much</span> in common;  Adonai is the maker of all of them.'),
('LEB', 'Proverbs', '22', '3', '0', 'The clever sees danger and hides,  but the simple go on and suffer.'),
('LEB', 'Proverbs', '22', '4', '0', 'The reward of humility <span class=\"supplied\">is</span> the fear of Adonai—  wealth and honor and life.'),
('LEB', 'Proverbs', '22', '5', '0', 'Thorns <span class=\"supplied\">and</span> snares <span class=\"supplied\">are</span> in the way of the perverse;  he who guards himself<span class=\"note\">Or “soul,” or “life”</span>will keep away from them.'),
('LEB', 'Proverbs', '22', '6', '0', 'Train the child <span class=\"idiom\">concerning his way</span>;<span class=\"note\">“on the mouth of his way”</span> even when he is old, he will not stray from it.'),
('LEB', 'Proverbs', '22', '7', '0', 'The rich will rule over the poor,  and the borrower is a slave of <span class=\"idiom\">the lender</span>.<span class=\"note\">“the borrower <span class=\"supplied\">belonging</span> to a man”</span>'),
('LEB', 'Proverbs', '22', '8', '0', 'He who sows injustice will reap calamity,  and the rod of his anger will fail.'),
('LEB', 'Proverbs', '22', '9', '0', 'He <span class=\"supplied\">who</span> <span class=\"idiom\">is generous</span><span class=\"note\">“the generous of eye”</span>will be blessed,  for he gives to the poor from his <span class=\"supplied\">own</span> bread.'),
('LEB', 'Proverbs', '22', '10', '0', 'Drive out a scoffer and strife will go out;  quarrel and abuse will cease.'),
('LEB', 'Proverbs', '22', '11', '0', 'He who loves purity of heart  <span class=\"supplied\">and has</span><span class=\"idiom\">gracious speech</span>,<span class=\"note\">“grace of his lips”</span>his friend <span class=\"supplied\">is the</span> king.'),
('LEB', 'Proverbs', '22', '12', '0', 'The eyes of Adonai keep watch over knowledge,  but he will overthrow the words of the faithless.'),
('LEB', 'Proverbs', '22', '13', '0', 'A lazy person says “A lion in the street!  In the middle of the highway, I shall be killed!”'),
('LEB', 'Proverbs', '22', '14', '0', 'A deep pit is the mouth of an <span class=\"idiom\">adulteress</span>,<span class=\"note\">“a strange woman”</span> <span class=\"supplied\">he with whom</span> Adonai is angry will fall there.'),
('LEB', 'Proverbs', '22', '15', '0', 'Folly is bound up in the heart<span class=\"note\">Or “mind”</span>of a boy;<span class=\"note\">Or “young man,” or “adolescent”</span> <span class=\"supplied\">the</span> rod of discipline will drive it<span class=\"note\">That is, folly</span>from him.'),
('LEB', 'Proverbs', '22', '16', '0', 'He who oppresses the poor <span class=\"supplied\">in order</span> to enrich himself,  <span class=\"supplied\">or</span> gives to the rich, <span class=\"idiom\">will come to poverty</span>.<span class=\"note\">“only loss”</span>'),
('LEB', 'Proverbs', '22', '17', '2', '<span class=\"verse-title\">Words of the Wise</span>Incline your ear and hear the words of the wise;  you shall apply your heart<span class=\"note\">Or “mind”</span>to my teaching.'),
('LEB', 'Proverbs', '22', '18', '2', 'For <span class=\"supplied\">it is</span> pleasant if you guard them <span class=\"idiom\">within you</span>;<span class=\"note\">“in your belly/womb”</span> together they will be ready upon your lips.'),
('LEB', 'Proverbs', '22', '19', '2', '<span class=\"supplied\">In order</span> for your trust to be in Adonai,  I have made <span class=\"supplied\">them</span> known to you <span class=\"idiom\">today</span><span class=\"note\">“the day”</span>—even<span class=\"note\">Or “only”</span>you.'),
('LEB', 'Proverbs', '22', '20', '2', 'Have I not written for you thirty <span class=\"supplied\">sayings</span>  with admonitions and knowledge?'),
('LEB', 'Proverbs', '22', '21', '2', '<span class=\"supplied\">In order</span> to show you what is right—sayings of truth—  <span class=\"supplied\">in order</span> to return a true saying to him who sent you.'),
('LEB', 'Proverbs', '22', '22', '2', 'Do not rob the poor because he is poor,  and do not crush the afflicted at the gate;'),
('LEB', 'Proverbs', '22', '23', '2', 'For Adonai will plead their case  and despoil those who despoil them of life.<span class=\"note\">Or “soul,” or “inner self”</span>'),
('LEB', 'Proverbs', '22', '24', '2', 'Do not befriend an owner<span class=\"note\">Or “master”</span>of <span class=\"idiom\">anger</span>,<span class=\"note\">“nostril”</span> and with a man of wrath you shall not associate;'),
('LEB', 'Proverbs', '22', '25', '2', 'lest you learn his way  and become entangled in a snare to yourself.<span class=\"note\">Or “soul,” or “inner self”</span>'),
('LEB', 'Proverbs', '22', '26', '2', 'Do not be with those who <span class=\"idiom\">give a pledge</span><span class=\"note\">“strike a hand”</span> <span class=\"idiom\">by becoming</span><span class=\"note\">“in the becomings of”</span>surety.'),
('LEB', 'Proverbs', '22', '27', '2', 'If there is nothing for you to pay,<span class=\"note\">Or “complete”</span> why will he take your bed from under you?'),
('LEB', 'Proverbs', '22', '28', '2', 'Do not remove an ancient boundary marker  which your ancestors<span class=\"note\">Or “fathers”</span>made.'),
('LEB', 'Proverbs', '22', '29', '2', 'A man who is skillful in his work, you shall see:  before kings, he will serve;  he will not serve before the commoners.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '23', '1', '0', 'When you sit to eat with a ruler,  you shall surely observe what <span class=\"supplied\">is</span> before you,'),
('LEB', 'Proverbs', '23', '2', '0', 'and you shall put a knife to your throat  if you have a <span class=\"idiom\">big appetite</span>.<span class=\"note\">“lord/master of life”</span>'),
('LEB', 'Proverbs', '23', '3', '0', 'Do not desire his delicacies,  for<span class=\"note\">Hebrew “and”</span>it is food of deception.'),
('LEB', 'Proverbs', '23', '4', '0', 'Do not tire <span class=\"supplied\">in order</span> to become rich;  out of your understanding, <span class=\"supplied\">may you</span> desist.'),
('LEB', 'Proverbs', '23', '5', '0', 'Your eyes will <span class=\"idiom\">alight</span><span class=\"note\">“cause to fly”</span>on it, but there is nothing <span class=\"supplied\">to</span> it,  for suddenly it will make for itself wings  like an eagle and it will be exhausted <span class=\"supplied\">in</span> the heavens.'),
('LEB', 'Proverbs', '23', '6', '0', 'Do not eat the bread of <span class=\"idiom\">the stingy</span>,<span class=\"note\">“stingy of eye”</span> and do not desire his delicacies.'),
('LEB', 'Proverbs', '23', '7', '0', 'For, like hair in his <span class=\"idiom\">throat</span>,<span class=\"note\">“soul,” or “inner self”</span>so it is.<span class=\"note\">Or “is he”</span> “Eat and drink!” he will say to you,  but his heart will not be with you.'),
('LEB', 'Proverbs', '23', '8', '0', 'Your morsel you have eaten, you will vomit it up,  and you will waste your pleasant words.'),
('LEB', 'Proverbs', '23', '9', '0', 'In the ears of a fool do not speak,  for he will despise the wisdom of your words.'),
('LEB', 'Proverbs', '23', '10', '0', 'Do not remove an ancient boundary marker,  and on the fields of orphans do not encroach;'),
('LEB', 'Proverbs', '23', '11', '0', 'For their redeemer is strong,  he himself will plead their cause against you.'),
('LEB', 'Proverbs', '23', '12', '0', 'Apply your heart<span class=\"note\">Or “mind”</span>to instruction,  and your ear to sayings of knowledge.'),
('LEB', 'Proverbs', '23', '13', '0', 'Do not withhold discipline from a child,  if you will beat him with the rod, he will not die.'),
('LEB', 'Proverbs', '23', '14', '0', 'As for you, with the rod you shall beat him,  and his life<span class=\"note\">Or “soul,” or “inner self”</span>you will save from Sheol.<span class=\"note\">A term for the place where the dead reside, i.e., the Underworld</span>'),
('LEB', 'Proverbs', '23', '15', '0', 'My child, if your heart<span class=\"note\">Or “mind”</span>is wise,  my heart will be glad—even me!'),
('LEB', 'Proverbs', '23', '16', '0', 'And my <span class=\"idiom\">insides</span><span class=\"note\">“kidneys”</span>will rejoice  when your lips speak what is upright.'),
('LEB', 'Proverbs', '23', '17', '0', 'May your heart<span class=\"note\">Or “mind”</span>not envy the sinners,  but <span class=\"supplied\">live</span> in fear of Adonai <span class=\"idiom\">all day</span>.<span class=\"note\">“all the day”</span>'),
('LEB', 'Proverbs', '23', '18', '0', '<span class=\"idiom\">Surely</span><span class=\"note\">“For if”</span>there is a future,  and your hope will not be cut off.'),
('LEB', 'Proverbs', '23', '19', '0', 'You, my child, hear and be wise,  and direct your heart<span class=\"note\">Or “mind”</span>on the road.'),
('LEB', 'Proverbs', '23', '20', '0', 'Do not be among drinkers of wine,  among gluttonous eaters of <span class=\"idiom\">their meat</span>.<span class=\"note\">“meat for them”</span>'),
('LEB', 'Proverbs', '23', '21', '0', 'For the drunkard and gluttonous, they will become poor,  and <span class=\"supplied\">with</span> rags, drowsiness will clothe them.'),
('LEB', 'Proverbs', '23', '22', '0', 'Listen to your father—he who <span class=\"idiom\">gave you life</span>,<span class=\"note\">“caused you to be born”</span> and do not despise your mother when<span class=\"note\">Or “for, because”</span>she is old.'),
('LEB', 'Proverbs', '23', '23', '0', 'Buy truth and do not sell <span class=\"supplied\">it</span>,  wisdom and instruction and understanding.'),
('LEB', 'Proverbs', '23', '24', '0', 'The father of the righteous will surely rejoice;  he who bears a wise person will be happy with him.'),
('LEB', 'Proverbs', '23', '25', '0', 'May your father and your mother be glad,  and may she who bore you rejoice.'),
('LEB', 'Proverbs', '23', '26', '0', 'My child, may you give your heart<span class=\"note\">Or “mind”</span>to me,  and may your eyes delight in my ways.'),
('LEB', 'Proverbs', '23', '27', '0', 'For a deep pit is a prostitute,<span class=\"note\">Or “whore”</span> and a narrow well is <span class=\"idiom\">an adulteress</span>.<span class=\"note\">“a foreign woman”</span>'),
('LEB', 'Proverbs', '23', '28', '0', 'She <span class=\"supplied\">is</span> also like a robber lying in wait,  and the faithless among mankind she increases.'),
('LEB', 'Proverbs', '23', '29', '0', 'To whom <span class=\"supplied\">is</span> woe? To whom <span class=\"supplied\">is</span> sorrow? To whom <span class=\"supplied\">are</span> quarrels? To whom <span class=\"supplied\">is</span> complaint?  To whom <span class=\"supplied\">are</span> wounds without cause? To whom <span class=\"supplied\">is</span> redness of <span class=\"supplied\">the</span> eyes?'),
('LEB', 'Proverbs', '23', '30', '0', 'To those who linger over wine,  to those who come to try mixed wine.'),
('LEB', 'Proverbs', '23', '31', '0', 'Do not look at wine when it is red,  when it <span class=\"idiom\">sparkles</span><span class=\"note\">“gives its eye”</span>on the cup,  going down smoothly.'),
('LEB', 'Proverbs', '23', '32', '0', '<span class=\"idiom\">In the end</span>,<span class=\"note\">“end him”</span>it will bite like a serpent,  and it will sting like an adder.'),
('LEB', 'Proverbs', '23', '33', '0', 'Your eyes will see strange things,  and your heart<span class=\"note\">Or “mind”</span>will speak perverse things.'),
('LEB', 'Proverbs', '23', '34', '0', 'And you will be like him who lies down in the heart of the sea,  and like him who lies down on top of a mast.'),
('LEB', 'Proverbs', '23', '35', '0', '“They struck me; I was not hurt. They beat me; I did not know <span class=\"supplied\">it</span>.  When I will awake, I will continue; I will seek it again.”')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '24', '1', '0', 'Do not envy men of evil,  and do not desire to be with them.'),
('LEB', 'Proverbs', '24', '2', '0', 'For their minds will devise violence,  and their lips will speak mischief.'),
('LEB', 'Proverbs', '24', '3', '0', 'By wisdom a house is built,  and by understanding it is established.'),
('LEB', 'Proverbs', '24', '4', '0', 'And by knowledge, rooms are filled  <span class=\"supplied\">with</span> all riches, precious and pleasant.'),
('LEB', 'Proverbs', '24', '5', '0', 'The warrior of wisdom <span class=\"supplied\">is</span> in strength,  and a man of knowledge is strong <span class=\"supplied\">in</span> power.'),
('LEB', 'Proverbs', '24', '6', '0', 'For with wise guidance you shall make war for yourself,  and victory <span class=\"supplied\">is</span> in an abundance of counsel.'),
('LEB', 'Proverbs', '24', '7', '0', 'Wisdom <span class=\"supplied\">is too</span> high for fools;  at the gate he will not open his mouth.'),
('LEB', 'Proverbs', '24', '8', '0', 'He who plans to do evil for<span class=\"note\">Or “to”</span>himself,  they will call <span class=\"supplied\">him</span> “master<span class=\"note\">Or “owner”</span>of mischief.”'),
('LEB', 'Proverbs', '24', '9', '0', 'Devising folly <span class=\"supplied\">is</span> a sin,  and an abomination to humankind <span class=\"supplied\">is</span> a scoffer.'),
('LEB', 'Proverbs', '24', '10', '0', '<span class=\"supplied\">If</span> you faint on the day of adversity,  little <span class=\"supplied\">is</span> your strength.'),
('LEB', 'Proverbs', '24', '11', '0', 'Rescue those who are led away to the death  and those who stagger to the slaughter. If you hold back,'),
('LEB', 'Proverbs', '24', '12', '0', 'if you say, “Look, we do not know this,”  does not he who weighs hearts perceive <span class=\"supplied\">it</span>?  And he who keeps your soul,<span class=\"note\">Or “life,” or “inner self”</span> he knows and will repay humankind according to his deeds.'),
('LEB', 'Proverbs', '24', '13', '0', 'My child, eat honey, for <span class=\"supplied\">it is</span> good,  and the dripping of the honeycomb <span class=\"supplied\">is</span> sweet to your taste.'),
('LEB', 'Proverbs', '24', '14', '0', 'Thus know wisdom for <span class=\"supplied\">the sake of</span> your soul,<span class=\"note\">Or “life,” or “inner self”</span> if you find <span class=\"supplied\">it</span>, then there is a future,  and your hope will not be cut off.'),
('LEB', 'Proverbs', '24', '15', '0', 'Do not lie in wait <span class=\"supplied\">like</span> an outlaw against the home of the righteous;  do not do violence <span class=\"supplied\">to</span> his dwelling place.'),
('LEB', 'Proverbs', '24', '16', '0', 'For seven <span class=\"supplied\">times</span> the righteous will fall, but he will rise,  but the wicked will be overthrown by calamity.'),
('LEB', 'Proverbs', '24', '17', '0', 'While your enemies are falling, do not rejoice;  when he trips himself, may your heart<span class=\"note\">Or “mind”</span>not be glad'),
('LEB', 'Proverbs', '24', '18', '0', 'lest Adonai see and <span class=\"supplied\">it</span> be evil in his eyes,  and turn his anger away from him.'),
('LEB', 'Proverbs', '24', '19', '0', 'Do not fret because of the evildoers;  do not envy the wicked.'),
('LEB', 'Proverbs', '24', '20', '0', 'For there will not be a future for the evil;  the lamp of the wicked will die out.'),
('LEB', 'Proverbs', '24', '21', '0', 'Fear Adonai, my son, and the king;  with those who change, do not associate.'),
('LEB', 'Proverbs', '24', '22', '0', 'For suddenly their disaster will come,  and the ruin of <span class=\"idiom\">both of them</span>,<span class=\"note\">“<span class=\"supplied\">the</span> two of them”</span>who knows?'),
('LEB', 'Proverbs', '24', '23', '0', 'These <span class=\"supplied\">sayings</span> are also for the wise:  <span class=\"idiom\">Partiality</span><span class=\"note\">“acknowledge faces”</span>in judgment is not good.'),
('LEB', 'Proverbs', '24', '24', '0', 'Whoever says to the guilty, “You are righteous,” the people will curse him;  the nations will abhor him.'),
('LEB', 'Proverbs', '24', '25', '0', 'But they who rebuke will have delight,  and upon them blessings of goodness will come.'),
('LEB', 'Proverbs', '24', '26', '0', 'He will kiss <span class=\"supplied\">the</span> lips,  he who gives an honest answer.'),
('LEB', 'Proverbs', '24', '27', '0', 'Prepare your work in the street and get it ready for yourself in the field;  afterward, then<span class=\"note\">Hebrew “and”</span>you shall build your house.'),
('LEB', 'Proverbs', '24', '28', '0', 'Do not be a witness without cause against your neighbor  nor deceive with your lips.'),
('LEB', 'Proverbs', '24', '29', '0', 'Do not say, “Just as he has done to me, so shall I do to him;  I will pay back the man according to his deed.”'),
('LEB', 'Proverbs', '24', '30', '0', 'I passed by the field of a lazy person,  and over the vineyard of a person lacking <span class=\"idiom\">sense</span>;<span class=\"note\">“heart”</span>'),
('LEB', 'Proverbs', '24', '31', '0', 'and behold, it was overgrown—all of it was covered <span class=\"supplied\">with</span> thorns, its surface with nettles,  and <span class=\"idiom\">its stone wall</span><span class=\"note\">“a wall of his/its stones”</span>was broken down.'),
('LEB', 'Proverbs', '24', '32', '0', 'Then I myself saw and my heart<span class=\"note\">Or “mind”</span>considered;  I looked, and I took hold of instruction:'),
('LEB', 'Proverbs', '24', '33', '0', 'A little sleep, a little slumber,  a little folding of the hands for rest,'),
('LEB', 'Proverbs', '24', '34', '0', 'and your poverty will come running,  and your lack like an armed warrior.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '25', '1', '1', '<span class=\"verse-title\">More Proverbs of Solomon</span>These are also proverbs of Solomon which officials of Hezekiah king of Judah copied:'),
('LEB', 'Proverbs', '25', '2', '1', 'The glory of God<span class=\"note\">Or “gods”</span>conceals things,  but the glory of kings searches out things.'),
('LEB', 'Proverbs', '25', '3', '1', 'As heaven is to height and the earth is to depth,  so <span class=\"supplied\">is</span> the heart<span class=\"note\">Or “mind”</span>of kings—there is no searching.'),
('LEB', 'Proverbs', '25', '4', '1', 'Remove the dross from silver,  and it will become a vessel for the smith.'),
('LEB', 'Proverbs', '25', '5', '1', 'Remove the wicked before a king,  and his throne will be established in righteousness.<span class=\"note\">Hebrew “in the righteousness”</span>'),
('LEB', 'Proverbs', '25', '6', '1', 'Do not promote yourself before the king,  and in the place of the great ones do not stand.'),
('LEB', 'Proverbs', '25', '7', '1', 'For <span class=\"supplied\">it is</span> better <span class=\"supplied\">that</span> he say to you, “Ascend here,”  than he humble you before a noble.  What your eyes have seen,'),
('LEB', 'Proverbs', '25', '8', '1', 'do not hastily bring out to court,  for<span class=\"note\">Or “lest”</span>what will you do at its end,  when your neighbor puts you to shame?'),
('LEB', 'Proverbs', '25', '9', '1', 'Argue your argument with your neighbor <span class=\"supplied\">himself</span>,  the secret of another do not disclose,'),
('LEB', 'Proverbs', '25', '10', '1', 'lest he who hears shame you  and your ill repute will not end.'),
('LEB', 'Proverbs', '25', '11', '1', 'Apples of gold in a setting of silver  is a matter spoken at<span class=\"note\">Hebrew “on”</span>its proper time.'),
('LEB', 'Proverbs', '25', '12', '1', 'A ring of gold and an ornament of fine gold  <span class=\"supplied\">is</span> a rebuke of the wise to the ear of a listener.'),
('LEB', 'Proverbs', '25', '13', '1', 'Like the cold of snow on a day<span class=\"note\">Or “at a season”</span>of harvest  <span class=\"supplied\">is</span> a faithful messenger to those who send him,  and the soul<span class=\"note\">Or “life,” or “inner self”</span>of his master is refreshed.<span class=\"note\">Or “returned”</span>'),
('LEB', 'Proverbs', '25', '14', '1', '<span class=\"supplied\">Like</span> clouds and wind when there is no rain,  <span class=\"supplied\">so too is</span> a man who boasts in a gift of deception.'),
('LEB', 'Proverbs', '25', '15', '1', '<span class=\"idiom\">With patience</span><span class=\"note\">“With length of face”</span>a ruler may be persuaded,  and a soft tongue will break a bone.<span class=\"note\">Or “strength”</span>'),
('LEB', 'Proverbs', '25', '16', '1', '<span class=\"supplied\">If</span> you find honey, eat what is sufficient for you,  lest you have your fill of it and vomit it out.'),
('LEB', 'Proverbs', '25', '17', '1', 'Make your foot scarce in the house of your neighbor,  lest he become weary of you and hate you.'),
('LEB', 'Proverbs', '25', '18', '1', '<span class=\"supplied\">Like</span> a club and sword and a sharp arrow  <span class=\"supplied\">is</span> a man who bears false witness against his neighbor.'),
('LEB', 'Proverbs', '25', '19', '1', 'A bad tooth and a lame foot  <span class=\"supplied\">is</span> the trust of a faithless person <span class=\"idiom\">in a time</span><span class=\"note\">“on a day”</span>of trouble.'),
('LEB', 'Proverbs', '25', '20', '1', '<span class=\"supplied\">Like</span> one who removes a garment on a cold day, <span class=\"supplied\">or like</span> vinegar on natron,<span class=\"note\">A mineral salt found on dry lake beds often used as a preservative.</span> <span class=\"supplied\">is</span> he who sings songs to a heavy heart.'),
('LEB', 'Proverbs', '25', '21', '1', 'If your enemy <span class=\"supplied\">is</span> hungry, feed him bread,  and if thirsty, let him drink water.'),
('LEB', 'Proverbs', '25', '22', '1', 'For coals of fire you will heap upon his head,  and Adonai will reward you.'),
('LEB', 'Proverbs', '25', '23', '1', 'The wind of the north produces rain,  and a backbiting tongue, angry faces.'),
('LEB', 'Proverbs', '25', '24', '1', 'Better to live upon the corner of a roof  than <span class=\"supplied\">with</span> a woman<span class=\"note\">Or “wife”</span>of contention and <span class=\"supplied\">in a</span> shared house.'),
('LEB', 'Proverbs', '25', '25', '1', '<span class=\"supplied\">Like</span> cold water<span class=\"note\">Hebrew “waters”</span>upon a weary soul,<span class=\"note\">Or “life,” or “inner self”</span> <span class=\"supplied\">so too is</span> good news from a distant place.'),
('LEB', 'Proverbs', '25', '26', '1', '<span class=\"supplied\">Like</span> a muddied spring or a polluted fountain  is the righteous who gives way before the wicked.'),
('LEB', 'Proverbs', '25', '27', '1', 'To eat much honey is not good,  nor <span class=\"supplied\">is</span> seeking one’s honor<span class=\"note\">Hebrew “their honor”</span>honorable.'),
('LEB', 'Proverbs', '25', '28', '1', 'A breached city <span class=\"supplied\">where</span> there is no wall  <span class=\"supplied\">is like</span> a man who <span class=\"idiom\">has no</span><span class=\"note\">“there is no”</span>self-control for his spirit.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '26', '1', '0', 'Like snow in the summer and like rain at the harvest,  so honor is not fitting for a fool.'),
('LEB', 'Proverbs', '26', '2', '0', 'Like the sparrow <span class=\"supplied\">is</span> to fluttering and like the swallow <span class=\"supplied\">is</span> to flying,  so an undeserved curse does not go forth.'),
('LEB', 'Proverbs', '26', '3', '0', 'A whip for the horse, a bridle for the donkey,  and a rod for the back of fools.'),
('LEB', 'Proverbs', '26', '4', '0', 'Do not answer a fool according to his folly  lest you become like him—even you.'),
('LEB', 'Proverbs', '26', '5', '0', 'Answer a fool according to his folly,  or else he will be wise in his own eyes.'),
('LEB', 'Proverbs', '26', '6', '0', '<span class=\"supplied\">Like</span> cutting off feet <span class=\"supplied\">or</span> drinking violence,  so <span class=\"supplied\">is</span> he who sends messages in the hand of a fool.'),
('LEB', 'Proverbs', '26', '7', '0', '<span class=\"supplied\">Like</span> legs that hang limp from a lame person,  so <span class=\"supplied\">is</span> a proverb in the mouth of fools.'),
('LEB', 'Proverbs', '26', '8', '0', 'Like binding a stone in a sling,  so <span class=\"supplied\">is</span> giving honor to a fool.'),
('LEB', 'Proverbs', '26', '9', '0', '<span class=\"supplied\">Like</span> a thorn that goes up in the hand of a drunkard,  so <span class=\"supplied\">is</span> a proverb in the mouth of fools.'),
('LEB', 'Proverbs', '26', '10', '0', '<span class=\"supplied\">Like</span> an archer who wounds everyone,  so <span class=\"supplied\">is</span> he who hires a fool or he who hires passersby.'),
('LEB', 'Proverbs', '26', '11', '0', 'Like a dog returning to his vomit  <span class=\"supplied\">is</span> a fool reverting to his folly.'),
('LEB', 'Proverbs', '26', '12', '0', '<span class=\"supplied\">Do</span> you see a man wise in his own eyes?  <span class=\"supplied\">There is more</span> hope for a fool than for him.'),
('LEB', 'Proverbs', '26', '13', '0', 'A lazy person says “A lion <span class=\"supplied\">is</span> in the road!  A lion among the streets!”'),
('LEB', 'Proverbs', '26', '14', '0', 'The door turns on its hinge,  and a lazy person on his bed.'),
('LEB', 'Proverbs', '26', '15', '0', 'A lazy person buries his hands in the dish;  he is <span class=\"supplied\">too</span> tired to return it to his mouth.'),
('LEB', 'Proverbs', '26', '16', '0', 'A lazy person is wiser in his eyes  than seven who answer discreetly.'),
('LEB', 'Proverbs', '26', '17', '0', 'Grabbing onto the ears of a dog  passing by <span class=\"supplied\">is</span> one who meddles in a quarrel <span class=\"supplied\">that is</span> not his own.'),
('LEB', 'Proverbs', '26', '18', '0', 'Like a maniac who shoots  firebrands, arrows, and death,'),
('LEB', 'Proverbs', '26', '19', '0', 'so <span class=\"supplied\">is</span> a man who deceives his neighbor,  but says “Am I not joking?”'),
('LEB', 'Proverbs', '26', '20', '0', 'For lack of wood, a fire goes out,  and where there is no whisperer, quarreling will cease.'),
('LEB', 'Proverbs', '26', '21', '0', '<span class=\"supplied\">As</span> charcoal <span class=\"supplied\">is</span> to hot embers and wood <span class=\"supplied\">is</span> to fire,  so a man of quarrels <span class=\"supplied\">is</span> to kindling strife.'),
('LEB', 'Proverbs', '26', '22', '0', 'The words of a whisperer <span class=\"supplied\">are</span> like delicious morsels,  and they go down to the inner parts of the body.'),
('LEB', 'Proverbs', '26', '23', '0', '<span class=\"supplied\">Like</span> <span class=\"idiom\">impure silver</span><span class=\"note\">“silver of impurities”</span>which overlays an earthen vessel,  <span class=\"supplied\">so are</span> smooth lips and an evil heart<span class=\"note\">Or “mind”</span>.'),
('LEB', 'Proverbs', '26', '24', '0', 'On his lips, an enemy will pretend,  but <span class=\"idiom\">inside</span><span class=\"note\">“in his midst”</span>he will harbor deceit.'),
('LEB', 'Proverbs', '26', '25', '0', 'When he makes his voice gracious, do not believe him,  for seven abominations <span class=\"supplied\">are</span> in his heart.'),
('LEB', 'Proverbs', '26', '26', '0', 'Though hatred is covered with guile,  its evil will be exposed in the assembly.'),
('LEB', 'Proverbs', '26', '27', '0', 'He who digs a pit, in it he will fall,  and he who rolls a stone, on him it will come back.'),
('LEB', 'Proverbs', '26', '28', '0', 'A tongue of deceit hates its victim,  and a flattering mouth makes ruin.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '27', '1', '0', 'Do not boast <span class=\"idiom\">about tomorrow</span>,<span class=\"note\">“on the day of tomorrow”</span> for you do not know what the day will bring.'),
('LEB', 'Proverbs', '27', '2', '0', 'May another praise you and not your own mouth,  a stranger and not your own lips.'),
('LEB', 'Proverbs', '27', '3', '0', 'Heavy <span class=\"supplied\">is</span> a stone and weighty <span class=\"supplied\">is</span> sand,  but <span class=\"supplied\">the</span> provocation of a fool <span class=\"supplied\">is</span> heavier than both of them.'),
('LEB', 'Proverbs', '27', '4', '0', 'Cruel <span class=\"supplied\">is</span> wrath and overwhelming <span class=\"supplied\">is</span> anger,  but who will stand before jealousy?'),
('LEB', 'Proverbs', '27', '5', '0', 'Better a rebuke that is open  than a love that is hidden.'),
('LEB', 'Proverbs', '27', '6', '0', 'The wounds of a friend mean well,  but the kisses of an enemy are profane.'),
('LEB', 'Proverbs', '27', '7', '0', 'An <span class=\"idiom\">appetite</span><span class=\"note\">“soul”</span>that is sated spurns honey,  but <span class=\"supplied\">to</span> an <span class=\"idiom\">appetite</span><span class=\"note\">“soul”</span>that is ravenous, all bitterness <span class=\"supplied\">is</span> sweet.'),
('LEB', 'Proverbs', '27', '8', '0', 'Like a bird that strays from its nest,  so is a man who strays from his place.'),
('LEB', 'Proverbs', '27', '9', '0', 'Perfume and incense will gladden a heart,  and the pleasantness of one’s friend <span class=\"supplied\">is</span> <span class=\"idiom\">personal advice</span>.<span class=\"note\">“because of advice of a person”</span>'),
('LEB', 'Proverbs', '27', '10', '0', 'As for your friend and a friend of your father, do not forsake <span class=\"supplied\">them</span>,  and the house of your brother, do not enter on the day of your calamity.  Better is a close neighbor than a distant brother.'),
('LEB', 'Proverbs', '27', '11', '0', 'Be wise, my child, and make my heart glad,  and I will answer him who reproaches me <span class=\"supplied\">with</span> a word.'),
('LEB', 'Proverbs', '27', '12', '0', '<span class=\"supplied\">When</span> the clever sees danger, he hides;  the simple go on <span class=\"supplied\">and</span> suffer.'),
('LEB', 'Proverbs', '27', '13', '0', 'Take his garment, for he gives surety <span class=\"supplied\">to</span> a stranger,  and to <span class=\"idiom\">an adulteress</span><span class=\"note\">“a foreign woman”</span>—<span class=\"supplied\">so</span> take his pledge.'),
('LEB', 'Proverbs', '27', '14', '0', 'He who blesses his neighbor with a loud voice  early in the morning,  a curse will be reckoned to him.'),
('LEB', 'Proverbs', '27', '15', '0', 'Dripping constantly on a day of heavy rain  and a woman<span class=\"note\">Or “wife”</span>of contention are alike.'),
('LEB', 'Proverbs', '27', '16', '0', '<span class=\"supplied\">In</span> restraining her, he restrains wind,<span class=\"note\">Or “breath, or “spirit”</span> and his right hand will grasp oil<span class=\"note\">Or “fat”</span>.'),
('LEB', 'Proverbs', '27', '17', '0', '<span class=\"supplied\">As</span> iron sharpens<span class=\"note\">Or “is united with”</span>iron,  so <span class=\"idiom\">one man sharpens another</span>.<span class=\"note\">“a man sharpens <span class=\"supplied\">the</span> faces of his friend”</span>'),
('LEB', 'Proverbs', '27', '18', '0', 'He who tends a fig tree will eat its fruit,  and he who guards his master<span class=\"note\">Or “lord”</span>will be honored.'),
('LEB', 'Proverbs', '27', '19', '0', 'As the waters <span class=\"supplied\">reflect</span> <span class=\"idiom\">face to face</span>,<span class=\"note\">“the faces to the faces”</span> so the heart of a person <span class=\"supplied\">reflects</span> the person.'),
('LEB', 'Proverbs', '27', '20', '0', 'Sheol<span class=\"note\">A term for the place where the dead reside, i.e., the Underworld</span>and Abaddon<span class=\"note\">Poetic synonym for “Sheol.” Only mentioned in the ot in relation to Sheol, the grave, or death.</span>will not be satisfied,  and the eyes of a person will not be satisfied <span class=\"supplied\">either</span>.'),
('LEB', 'Proverbs', '27', '21', '0', 'A crucible <span class=\"supplied\">is</span> for the silver, and a furnace for the gold,  but a man <span class=\"supplied\">is tested by</span> the mouth of him who praises him.'),
('LEB', 'Proverbs', '27', '22', '0', 'If you crush a fool in the mortar with the pestle <span class=\"idiom\">along with</span><span class=\"note\">Or “in the midst of”</span>the crushed grain,  it will not drive folly from upon him.'),
('LEB', 'Proverbs', '27', '23', '0', 'You will surely know the <span class=\"idiom\">condition</span><span class=\"note\">“faces”</span>of your flock;  your heart<span class=\"note\">Or “mind”</span>attends to the herds.'),
('LEB', 'Proverbs', '27', '24', '0', 'For riches <span class=\"supplied\">are</span> not forever,  nor a crown for generation <span class=\"supplied\">after</span> generation.'),
('LEB', 'Proverbs', '27', '25', '0', 'When the grass is gone, then green growth will appear,  and the herbs of the mountains will be gathered.'),
('LEB', 'Proverbs', '27', '26', '0', 'Lambs <span class=\"supplied\">will be</span> your clothing,  and goats the price of the field.'),
('LEB', 'Proverbs', '27', '27', '0', 'And <span class=\"supplied\">there will be</span> enough goats’ milk for your food,  for <span class=\"supplied\">the</span> food of your household and <span class=\"supplied\">the</span> <span class=\"idiom\">nourishment</span><span class=\"note\">“life”</span>of your maidservants.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '28', '1', '0', 'The wicked flee, but no one pursues,  but the righteous is bold like a lion.'),
('LEB', 'Proverbs', '28', '2', '0', 'By the rebellion of a land, her rulers increase,  but by a person of intelligence who knows justice, it will last.'),
('LEB', 'Proverbs', '28', '3', '0', 'A man <span class=\"supplied\">who is</span> poor and oppresses the impoverished  <span class=\"supplied\">is</span> a beating rain <span class=\"idiom\">that leaves</span><span class=\"note\">“and there is”</span>no food.'),
('LEB', 'Proverbs', '28', '4', '0', 'Those who forsake instruction will praise the wicked,  but they who guard instruction will struggle against them.'),
('LEB', 'Proverbs', '28', '5', '0', 'Men of evil do not understand justice,  but seekers of Adonai understand completely.<span class=\"note\">Or “everything”</span>'),
('LEB', 'Proverbs', '28', '6', '0', 'Better <span class=\"supplied\">to be</span> poor and walking in one’s integrity  than to be crooked of ways when one <span class=\"supplied\">is</span> rich.'),
('LEB', 'Proverbs', '28', '7', '0', 'He who keeps instruction <span class=\"supplied\">is</span> a child of understanding,  but the companion of gluttons will shame his father.'),
('LEB', 'Proverbs', '28', '8', '0', 'He who augments his wealth with interest and with usury  gathers it for him who is kind to the poor.'),
('LEB', 'Proverbs', '28', '9', '0', 'He who turns his ear from listening to instruction,  even his prayer <span class=\"supplied\">is</span> an abomination.'),
('LEB', 'Proverbs', '28', '10', '0', 'He who misleads the upright onto the way of evil,  into his pits he will fall.  But as for the blameless, they will inherit good.'),
('LEB', 'Proverbs', '28', '11', '0', 'A man of wealth <span class=\"supplied\">is</span> wise in his own eyes,  but the intelligent poor sees through him.'),
('LEB', 'Proverbs', '28', '12', '0', 'When the righteous triumphs, great <span class=\"supplied\">is</span> the glory,  but with <span class=\"supplied\">the</span> rising of the wicked, a person will be hidden.'),
('LEB', 'Proverbs', '28', '13', '0', 'He who conceals his transgression will not prosper,  but he who confesses and forsakes will obtain mercy.'),
('LEB', 'Proverbs', '28', '14', '0', 'Happy is the person who fears continuously,  but he who is stubborn of heart,<span class=\"note\">Or “mind”</span>will fall into calamity.'),
('LEB', 'Proverbs', '28', '15', '0', '<span class=\"supplied\">Like</span> a roaring lion and a charging bear  <span class=\"supplied\">is</span> a wicked ruler over a poor people.'),
('LEB', 'Proverbs', '28', '16', '0', 'A ruler who lacks understanding is<span class=\"note\">Hebrew “and”</span>a cruel oppressor,  <span class=\"supplied\">but</span> those who hate unjust gain will have long<span class=\"note\">Or “prolong”</span>days.'),
('LEB', 'Proverbs', '28', '17', '0', 'A person who is burdened with <span class=\"supplied\">the</span> blood of <span class=\"idiom\">another</span>,<span class=\"note\">“life”</span> until death he will flee;  do not take hold of him.'),
('LEB', 'Proverbs', '28', '18', '0', 'He who walks in integrity will be safe,  but he who takes crooked paths will fall in one.'),
('LEB', 'Proverbs', '28', '19', '0', 'He who tills his ground will have plenty bread,  but he who follows fantasies will have plenty <span class=\"supplied\">of</span> poverty.'),
('LEB', 'Proverbs', '28', '20', '0', 'A man of faithfulness <span class=\"supplied\">has</span> abundant blessings,  but he who hurries to become rich will not go unpunished.'),
('LEB', 'Proverbs', '28', '21', '0', '<span class=\"idiom\">Showing partiality</span><span class=\"note\">“Recognizing faces”</span><span class=\"supplied\">is</span> not good,  and over a morsel of bread, a strong man will do wrong.'),
('LEB', 'Proverbs', '28', '22', '0', 'He who hurries for wealth is <span class=\"idiom\">a man with an evil eye</span>,<span class=\"note\">“a man of evil of eye”</span> but he does not know that poverty will come upon him.'),
('LEB', 'Proverbs', '28', '23', '0', 'He who rebukes a person will afterward find <span class=\"supplied\">more</span> favor  than he who flatters <span class=\"supplied\">with the</span> tongue.'),
('LEB', 'Proverbs', '28', '24', '0', 'He who robs his father and his mother and says, “There is no crime,”  is partner to a man who corrupts.'),
('LEB', 'Proverbs', '28', '25', '0', 'The <span class=\"idiom\">greedy person</span><span class=\"note\">“wide soul”</span>will stir up strife,  but he who trusts in Adonai will be enriched.'),
('LEB', 'Proverbs', '28', '26', '0', 'He who trusts in his own heart<span class=\"note\">Or “mind,” or “sense”</span>is a fool,  but he who walks in wisdom will be saved.'),
('LEB', 'Proverbs', '28', '27', '0', '<span class=\"supplied\">For</span> he who gives to the poor, there is no lacking,  but <span class=\"supplied\">for</span> he<span class=\"note\">Hebrew “those”</span>who turns his eyes, <span class=\"supplied\">there are</span> many curses.'),
('LEB', 'Proverbs', '28', '28', '0', 'With <span class=\"supplied\">the</span> rising of the wicked, humankind will be hidden,  and with their perishing, <span class=\"supplied\">the</span> righteous will multiply.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '29', '1', '0', 'A man who is reproved, being stubborn of neck—  suddenly he will be broken, and there will not be healing.'),
('LEB', 'Proverbs', '29', '2', '0', 'When the righteous are numerous, the people will rejoice,  but when the wicked are ruling, people will groan.'),
('LEB', 'Proverbs', '29', '3', '0', 'A man who loves wisdom will make his parents glad,  but the friend of prostitutes<span class=\"note\">Or “whores”</span>will squander his wealth.'),
('LEB', 'Proverbs', '29', '4', '0', 'By justice a king gives stability <span class=\"supplied\">to</span> a land,  but a man of bribes will ruin it.'),
('LEB', 'Proverbs', '29', '5', '0', 'A strong man who flatters his neighbor  is spreading a net for his feet.'),
('LEB', 'Proverbs', '29', '6', '0', 'In transgression, an evil man <span class=\"supplied\">is</span> a snare,  but <span class=\"supplied\">the</span> righteous will sing and rejoice.<span class=\"note\">Hebrew “he will rejoice”</span>'),
('LEB', 'Proverbs', '29', '7', '0', '<span class=\"supplied\">The</span> righteous knows <span class=\"supplied\">the</span> case of the poor,  but <span class=\"supplied\">the</span> wicked does not understand knowledge.'),
('LEB', 'Proverbs', '29', '8', '0', 'Men of scoffing set a city aflame,  but the wise turn away wrath.'),
('LEB', 'Proverbs', '29', '9', '0', '<span class=\"supplied\">If</span> a wise man goes to court with a foolish man,  then there is rankling and ridicule,<span class=\"note\">Hebrew “there is ridicule”</span>but there is no relief.'),
('LEB', 'Proverbs', '29', '10', '0', 'Men of blood hate <span class=\"supplied\">the</span> blameless,  and they seek the life of the upright.<span class=\"note\">Or “soul,” or “inner self”</span>'),
('LEB', 'Proverbs', '29', '11', '0', 'A fool gives all his breath,<span class=\"note\">Or “spirit”</span> but <span class=\"supplied\">the</span> wise holds back in quiet.'),
('LEB', 'Proverbs', '29', '12', '0', 'A ruler listening to a word of falsehood,  all his officials are wicked.'),
('LEB', 'Proverbs', '29', '13', '0', 'The poor and a man of oppression have <span class=\"supplied\">this</span> in common:  Adonai gives <span class=\"idiom\">light to the eyes of them both</span>.<span class=\"note\">“light of the eyes of the two of them”</span>'),
('LEB', 'Proverbs', '29', '14', '0', 'A king who judges with truthfulness <span class=\"supplied\">to</span> the poor,  his throne will be established forever.'),
('LEB', 'Proverbs', '29', '15', '0', 'As for a rod and reproof, they<span class=\"note\">Hebrew “it”</span>will give wisdom,  but a neglected child is disgraced <span class=\"supplied\">by</span> his mother.'),
('LEB', 'Proverbs', '29', '16', '0', 'With the increase of the wicked, transgression will increase,  but the righteous will look on his downfall.'),
('LEB', 'Proverbs', '29', '17', '0', 'Discipline your children, and they will give you rest,  and they will give delight to your soul.<span class=\"note\">Or “life,” or “inner self”</span>'),
('LEB', 'Proverbs', '29', '18', '0', 'When there is no prophecy, the people cast off restraint,  but as for he who guards instruction, happiness <span class=\"supplied\">is</span> his.'),
('LEB', 'Proverbs', '29', '19', '0', 'By words, a servant is not disciplined,  for he will understand, but there is no giving heed.'),
('LEB', 'Proverbs', '29', '20', '0', 'You see a man who is hasty in his words:  <span class=\"supplied\">there is more</span> hope for a fool than him.'),
('LEB', 'Proverbs', '29', '21', '0', 'He who pampers his servant from childhood,  <span class=\"idiom\">arrogance will be his end</span>.<span class=\"note\">“and its/his end will be arrogance”</span>'),
('LEB', 'Proverbs', '29', '22', '0', 'A man of anger will stir strife,  and the owner of anger, much transgression.'),
('LEB', 'Proverbs', '29', '23', '0', 'The pride of a person will bring him humiliation,  and the lowly of spirit will obtain honor.'),
('LEB', 'Proverbs', '29', '24', '0', 'Being a partner with a thief is hating one’s life;<span class=\"note\">Or “soul,” or “inner self”</span> a curse he will hear, but not disclose.'),
('LEB', 'Proverbs', '29', '25', '0', 'The fear of a person will lay a snare,  but he who trusts in Adonai will be secure.'),
('LEB', 'Proverbs', '29', '26', '0', 'Many are those who seek the <span class=\"idiom\">favor</span><span class=\"note\">“faces”</span>of a ruler,  but from Adonai <span class=\"idiom\">one obtains justice</span>.<span class=\"note\">“<span class=\"supplied\">comes</span> justice for a man”</span>'),
('LEB', 'Proverbs', '29', '27', '0', 'A man of injustice is an abomination to the righteous,  but the <span class=\"idiom\">upright</span><span class=\"note\">“upright of way”</span>is an abomination to the wicked.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '30', '1', '1', '<span class=\"verse-title\">The Oracle of Agur</span>The words of Agur, son of Yakeh, the oracle.  Thus says the man to Ithiel, to Ithiel, and Ucal:<span class=\"note\">Or “I am weary, O God; I am weary, O God, and worn out”</span>'),
('LEB', 'Proverbs', '30', '2', '1', 'Certainly I <span class=\"supplied\">am more</span> stupid than a man,  and the understanding of humankind <span class=\"supplied\">is</span> not for me.'),
('LEB', 'Proverbs', '30', '3', '1', 'And I have not learned wisdom,  nor will I know knowledge of the Holy One.<span class=\"note\">Or “holy ones”</span>'),
('LEB', 'Proverbs', '30', '4', '1', 'Who has ascended <span class=\"supplied\">to</span> heaven and come down?  Who has gathered <span class=\"supplied\">the</span> wind in the hollow of his hand?  Who has wrapped water in a<span class=\"note\">Hebrew “the”</span>garment?  Who has established all the ends of the earth?  What <span class=\"supplied\">is</span> his name and what is the name of his child?  For <span class=\"supplied\">surely</span> you know.'),
('LEB', 'Proverbs', '30', '5', '1', 'Every word of God <span class=\"supplied\">is</span> flawless;  he <span class=\"supplied\">is</span> a shield for him who takes refuge in him.'),
('LEB', 'Proverbs', '30', '6', '1', 'Do not add to his words  lest he rebuke you and you be found a liar.'),
('LEB', 'Proverbs', '30', '7', '1', 'Two things I ask from you;  do not deny me before I die:'),
('LEB', 'Proverbs', '30', '8', '1', 'Keep falsehood and a <span class=\"idiom\">lying word</span><span class=\"note\">“word of a lie”</span>far from me;  do not give me poverty or wealth;  provide me <span class=\"supplied\">with</span> food <span class=\"supplied\">only for</span> my need.'),
('LEB', 'Proverbs', '30', '9', '1', 'Or else I will be satisfied and will deny <span class=\"supplied\">him</span>  and say “Who <span class=\"supplied\">is</span> Adonai?”  Or<span class=\"note\">Or “Lest”</span>I will be poor and will steal  and profane the name of my God.'),
('LEB', 'Proverbs', '30', '10', '1', 'Do not slander a servant to his master,  or else he will curse you and you will be guilty.'),
('LEB', 'Proverbs', '30', '11', '1', '<span class=\"supplied\">There is</span> a generation <span class=\"supplied\">that</span> will curse its father,  and its mother it will not bless.'),
('LEB', 'Proverbs', '30', '12', '1', '<span class=\"supplied\">There is</span> a generation <span class=\"supplied\">that is</span> pure in its own eyes,  but from its filthiness it will not be cleansed.'),
('LEB', 'Proverbs', '30', '13', '1', '<span class=\"supplied\">There is</span> a generation—how lofty <span class=\"supplied\">are</span> their eyes!  And their eyelids they will lift.'),
('LEB', 'Proverbs', '30', '14', '1', '<span class=\"supplied\">There is</span> a generation whose teeth <span class=\"supplied\">are</span> swords,  and its jawbones, knives,  <span class=\"supplied\">in order</span> to devour the poor from the earth<span class=\"note\">Or “land”</span> and the needy from humankind.'),
('LEB', 'Proverbs', '30', '15', '1', 'For the leech, <span class=\"supplied\">there are</span> two daughters;  “Give, give!” <span class=\"supplied\">they cry</span>.  As for three of these, they are not satisfied;  as for four, they do not say enough.'),
('LEB', 'Proverbs', '30', '16', '1', 'Sheol<span class=\"note\">A term for the place where the dead reside, i.e., the Underworld</span>and barrenness of womb,  the land is not satisfied <span class=\"supplied\">with</span> water,  and fire does not say “enough!”'),
('LEB', 'Proverbs', '30', '17', '1', 'The eye <span class=\"supplied\">that</span> mocks a father  and scorns the obedience of a mother—  the ravens of the valley will peck it out,  and the offspring of vultures will eat it.'),
('LEB', 'Proverbs', '30', '18', '1', 'Three of these are <span class=\"supplied\">too</span> wonderful for me,  and four, I do not understand them:'),
('LEB', 'Proverbs', '30', '19', '1', 'the way of the eagle in the sky,  the way of a snake on a rock,  the way of a ship in the heart of the sea,  and the way of a man with a young woman.'),
('LEB', 'Proverbs', '30', '20', '1', 'This is the way of a woman committing adultery:  she eats and wipes her mouth,  and says “I have not done wrong.”'),
('LEB', 'Proverbs', '30', '21', '1', 'Under three <span class=\"supplied\">things</span> the earth<span class=\"note\">Or “land”</span>trembles,  and under four, it is not able to bear up:'),
('LEB', 'Proverbs', '30', '22', '1', 'under a slave when he becomes king,  and a fool when he is satisfied <span class=\"supplied\">with</span> food;'),
('LEB', 'Proverbs', '30', '23', '1', 'under an unloved woman when she gets married,  and a maid when she succeeds her mistress.'),
('LEB', 'Proverbs', '30', '24', '1', 'There <span class=\"supplied\">are</span> four small things on the earth,  and they <span class=\"supplied\">are</span> <span class=\"idiom\">exceedingly wise</span>:<span class=\"note\">“wise <span class=\"supplied\">ones</span> from wise <span class=\"supplied\">ones</span>”</span>'),
('LEB', 'Proverbs', '30', '25', '1', 'The ants <span class=\"supplied\">are</span> a people <span class=\"supplied\">who are</span> not strong,  yet they prepare their food in the summer;'),
('LEB', 'Proverbs', '30', '26', '1', 'the badgers <span class=\"supplied\">are</span> a people <span class=\"supplied\">who are</span> not mighty,  yet they set their house on the rock;'),
('LEB', 'Proverbs', '30', '27', '1', 'there is no king for the locust,  yet it marches in rank;'),
('LEB', 'Proverbs', '30', '28', '1', 'a lizard you <span class=\"supplied\">can</span> seize with hands,  yet it is in palaces of kings.'),
('LEB', 'Proverbs', '30', '29', '1', 'There <span class=\"supplied\">are</span> three <span class=\"supplied\">things</span> that are magnificent of stride,  and four that are magnificent <span class=\"supplied\">when</span> moving:'),
('LEB', 'Proverbs', '30', '30', '1', 'a mighty lion among the beasts,<span class=\"note\">Hebrew “beasts”</span> but he will not turn back from <span class=\"idiom\">any face</span>;<span class=\"note\">“faces of all”</span>'),
('LEB', 'Proverbs', '30', '31', '1', 'a strutting rooster or he-goat,  and a king <span class=\"supplied\">whose</span> army <span class=\"supplied\">is</span> with him.'),
('LEB', 'Proverbs', '30', '32', '1', 'If you have been foolish by exalting yourself,  and if you have devised evil, <span class=\"supplied\">put your</span> hand to <span class=\"supplied\">your</span> mouth.'),
('LEB', 'Proverbs', '30', '33', '1', 'For pressing milk produces curd,  and pressing the nose produces blood,  so pressing <span class=\"idiom\">anger</span><span class=\"note\">“nostrils”</span>produces strife.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Proverbs', '31', '1', '1', '<span class=\"verse-title\">The Oracle of King Lemuel</span>The words of Lemuel, <span class=\"supplied\">the</span> king—an oracle  that his mother taught him:'),
('LEB', 'Proverbs', '31', '2', '1', 'What, my son? And what, my son in my womb?  And what, son of my vows?'),
('LEB', 'Proverbs', '31', '3', '1', 'Do not give your strength to the women,  and your ways to destroy kings.'),
('LEB', 'Proverbs', '31', '4', '1', '<span class=\"supplied\">It is</span> not for the kings, O Lemuel;  drinking wine <span class=\"supplied\">is</span> not for the kings,  nor <span class=\"supplied\">is</span> strong drink for rulers.'),
('LEB', 'Proverbs', '31', '5', '1', 'Or else he will drink and forget what has been decreed,  and he will pervert the rights<span class=\"note\">Hebrew “right”</span>of all the <span class=\"idiom\">afflicted</span>.<span class=\"note\">“sons of affliction”</span>'),
('LEB', 'Proverbs', '31', '6', '1', 'Give strong drink to him who is perishing,  and wine to those <span class=\"idiom\">in bitter distress</span>.<span class=\"note\">“to bitterness of soul”</span>'),
('LEB', 'Proverbs', '31', '7', '1', 'He will drink and forget his poverty,  and his misery he will not remember <span class=\"supplied\">any</span> more.'),
('LEB', 'Proverbs', '31', '8', '1', 'Open your mouth for the mute,  for the rights<span class=\"note\">Hebrew “right”</span>of all the <span class=\"idiom\">needy</span>.<span class=\"note\">“the sons of the needy”</span>'),
('LEB', 'Proverbs', '31', '9', '1', 'Open your mouth, judge righteousness,  and defend <span class=\"supplied\">the</span> poor and needy.'),
('LEB', 'Proverbs', '31', '10', '3', '<span class=\"verse-title\">An Excellent Woman<span class=\"note\">Prov 31:10–31 is an acrostic poem</span></span>A woman<span class=\"note\">Or “wife”</span>of excellence,<span class=\"note\">Or “strength,” or “valor,” or “ability,” or “efficiency”</span>who will find?  For her worth <span class=\"supplied\">is</span> far more than precious jewels.'),
('LEB', 'Proverbs', '31', '11', '3', 'The heart of her husband<span class=\"note\">Or “lord”</span>trusts in her,  and gain he will not lack.'),
('LEB', 'Proverbs', '31', '12', '3', 'She does him good, but not harm  all the days of her life.'),
('LEB', 'Proverbs', '31', '13', '3', 'She seeks wool and flax,  and she works with the diligence of <span class=\"idiom\">her hands</span>.<span class=\"note\">“her palms”</span>'),
('LEB', 'Proverbs', '31', '14', '3', 'She is like the ships of a merchant;  from far off she brings her food;<span class=\"note\">Or “bread”</span>'),
('LEB', 'Proverbs', '31', '15', '3', 'And she arises while <span class=\"supplied\">it is</span> still night,  and gives food to her household,  and tasks to her servant girls.'),
('LEB', 'Proverbs', '31', '16', '3', 'She considers a field and buys it,  from the fruit of <span class=\"idiom\">her hand</span><span class=\"note\">“her palm”</span>she plants a vineyard.'),
('LEB', 'Proverbs', '31', '17', '3', 'She girds her waist in strength,  and makes her arms strong.'),
('LEB', 'Proverbs', '31', '18', '3', 'She perceives that her merchandise <span class=\"supplied\">is</span> good;  her lamp does not go out in the night.'),
('LEB', 'Proverbs', '31', '19', '3', 'Her hands she puts onto the distaff,<span class=\"note\">A stick or spindle onto which wool or flax is wound in preparation for spinning</span> and her palms hold a spindle.'),
('LEB', 'Proverbs', '31', '20', '3', 'Her palm she opens to the poor,  and her hand she reaches <span class=\"supplied\">out</span> to the needy.'),
('LEB', 'Proverbs', '31', '21', '3', 'She does not fear for her house <span class=\"supplied\">when</span> it snows,  for <span class=\"idiom\">her entire household</span><span class=\"note\">“all her house, household”</span>is clothed <span class=\"supplied\">in</span> crimson.'),
('LEB', 'Proverbs', '31', '22', '3', 'She makes for herself coverings;  her clothing <span class=\"supplied\">is</span> fine linen and purple.'),
('LEB', 'Proverbs', '31', '23', '3', 'Her husband<span class=\"note\">Or “lord”</span>is known at the gates,  in his seat among the elders of the land.'),
('LEB', 'Proverbs', '31', '24', '3', 'Linen garments she makes and sells,  and she supplies sashes for the merchants.'),
('LEB', 'Proverbs', '31', '25', '3', 'Strength and dignity are her clothing,  and she laughs <span class=\"idiom\">at the future</span>.<span class=\"note\">“to the day/time coming after”</span>'),
('LEB', 'Proverbs', '31', '26', '3', 'She opens her mouth with wisdom,  and instruction of kindness <span class=\"supplied\">is</span> upon her tongue.'),
('LEB', 'Proverbs', '31', '27', '3', 'She looks after the ways of her household,  and the bread of idleness she will not eat.'),
('LEB', 'Proverbs', '31', '28', '3', 'Her children rise and consider her happy,  her husband<span class=\"note\">Or “lord”</span><span class=\"supplied\">also</span>, and he praises her;'),
('LEB', 'Proverbs', '31', '29', '3', '“Many daughters have done excellence,<span class=\"note\">Or “strength,” or “valor,” or “ability,” or “efficiency”</span> but <span class=\"idiom\">you surpass</span><span class=\"note\">“you ascend over”</span>all of them.”'),
('LEB', 'Proverbs', '31', '30', '3', 'Charm<span class=\"note\">Hebrew “The charm”</span><span class=\"supplied\">is</span> deceit and beauty<span class=\"note\">Hebrew “the beauty”</span><span class=\"supplied\">is</span> vain;<span class=\"note\">Or “vapor,” or “emptiness,” or “breath”</span> <span class=\"supplied\">but</span> a woman who fears Adonai shall be praised.'),
('LEB', 'Proverbs', '31', '31', '3', 'Give to her from the fruit of her hand,  and may they praise her works in the city gates.')
