INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '1', '1', '1', '<span class=\"verse-title\">The Ways of the Righteous and the Wicked</span>Blessed is the man who does not walk in the advice of <span class=\"supplied\">the</span> wicked;  nor does he stand in <span class=\"supplied\">the</span> way of sinners;  nor does he sit in <span class=\"supplied\">the</span> assembly of mockers.'),
('LEB', 'Psalms', '1', '2', '1', 'Instead, in the law of Adonai <span class=\"supplied\">is</span> his delight,  and on his law he meditates day and night.'),
('LEB', 'Psalms', '1', '3', '1', 'And so, he is like a tree planted by streams of water  that gives its fruit in its season;  its leaf also does not wither.  Therefore all that he does prospers.'),
('LEB', 'Psalms', '1', '4', '1', 'Not so the wicked.  Instead, <span class=\"supplied\">they are</span> like the chaff that <span class=\"supplied\">the</span> wind scatters.'),
('LEB', 'Psalms', '1', '5', '1', 'Therefore <span class=\"supplied\">the</span> wicked will not stand in the judgment,  nor sinners in the congregation of <span class=\"supplied\">the</span> righteous;'),
('LEB', 'Psalms', '1', '6', '1', 'for Adonai knows <span class=\"supplied\">the</span> way of <span class=\"supplied\">the</span> righteous,<span class=\"note\">Plural</span> but <span class=\"supplied\">the</span> way of <span class=\"supplied\">the</span> wicked will perish.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '2', '1', '1', '<span class=\"verse-title\">The Messiah’s Reign</span>Why are nations in tumult,  and countries plotting<span class=\"note\">Or “muttering”</span>in vain?<span class=\"note\">Or “an empty thing”</span>'),
('LEB', 'Psalms', '2', '2', '1', '<span class=\"supplied\">The</span> kings of <span class=\"supplied\">the</span> earth establish themselves,  and the rulers conspire<span class=\"note\">Or “make themselves firm”</span>together  against Adonai and his anointed:'),
('LEB', 'Psalms', '2', '3', '1', '“Let us tear off their bonds,  and cast their cords from us!”'),
('LEB', 'Psalms', '2', '4', '1', '<span class=\"supplied\">He who</span> sits <span class=\"supplied\">enthroned</span> in the heavens laughs.  The Lord derides them.'),
('LEB', 'Psalms', '2', '5', '1', 'Then he speaks to them in his wrath,  and in his fury he terrifies them:'),
('LEB', 'Psalms', '2', '6', '1', '“But as for me, I have set my king  on Zion, my holy mountain.”'),
('LEB', 'Psalms', '2', '7', '1', 'I will tell the decree;  Adonai said to me:  “You are my son;  today I have begotten you.'),
('LEB', 'Psalms', '2', '8', '1', 'Ask from me and I will make <span class=\"supplied\">the</span> nations your heritage,  and your possession <span class=\"supplied\">the</span> ends of <span class=\"supplied\">the</span> earth.'),
('LEB', 'Psalms', '2', '9', '1', 'You will break them with an iron rod.  Like a potter’s vessel you will shatter them.”'),
('LEB', 'Psalms', '2', '10', '1', 'So then, O kings, be wise.  Be warned, O rulers of <span class=\"supplied\">the</span> earth.'),
('LEB', 'Psalms', '2', '11', '1', 'Serve Adonai with fear,  and rejoice with trembling.'),
('LEB', 'Psalms', '2', '12', '1', 'Kiss the Son  lest he be angry and you perish <span class=\"supplied\">on the</span> way,  for his anger burns quickly.  Blessed are all <span class=\"supplied\">who</span> take refuge in him.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '3', '1', '1', '<span class=\"verse-title\">A psalm of David at his fleeing from the presence of Absalom, his son.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Adonai, how many are my enemies;  many <span class=\"supplied\">are</span> rising against me.'),
('LEB', 'Psalms', '3', '2', '1', 'Many <span class=\"supplied\">are</span> saying about my soul,  “There is no deliverance for him from<span class=\"note\">Or “in”</span>God. <span class=\"italic\">Selah</span><span class=\"note\">Meaning uncertain; probably a musical or liturgical direction</span>'),
('LEB', 'Psalms', '3', '3', '1', 'But<span class=\"note\">Or “And”</span>you, Adonai, <span class=\"supplied\">are</span> a shield around me,  my glory and the one who lifts up my head.'),
('LEB', 'Psalms', '3', '4', '1', '<span class=\"supplied\">With</span> my voice I call to Adonai  and he answers me from <span class=\"idiom\">his holy hill</span>.<span class=\"note\">Literally “the hill of his holiness”</span><span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '3', '5', '1', 'I lay down and slept;  I woke up because Adonai sustains me.'),
('LEB', 'Psalms', '3', '6', '1', 'I am not afraid of the ten thousands of people  who all around have set themselves against me.'),
('LEB', 'Psalms', '3', '7', '1', 'Rise up, O Adonai; deliver me, O my God;  for you strike all my enemies <span class=\"supplied\">on the</span> cheek.  <span class=\"supplied\">The</span> teeth of <span class=\"supplied\">the</span> wicked you break.'),
('LEB', 'Psalms', '3', '8', '1', 'To Adonai belongs deliverance;  <span class=\"supplied\">may</span> your blessing <span class=\"supplied\">be</span> over your people. <span class=\"italic\">Selah</span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '4', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director; with stringed instruments. A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>When I call, answer me, O God of my righteousness.<span class=\"note\">Or “vindication”</span> In trouble <span class=\"idiom\">deliver me</span>.<span class=\"note\">Literally “make wide for me”</span> Be gracious to me and hear my prayer.'),
('LEB', 'Psalms', '4', '2', '1', 'O sons of man, how long will my honor<span class=\"note\">Or “glory”</span>be a disgrace?  <span class=\"supplied\">How long</span> will you love vanity?  <span class=\"supplied\">How long</span> will you seek lies?<span class=\"note\">Or “falsehood”</span><span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '4', '3', '1', 'But know that Adonai has set apart<span class=\"note\">Or “treated specially”</span>for himself <span class=\"supplied\">the</span> faithful one.<span class=\"note\">Or “pious, godly one”</span> Adonai hears when I call to him.'),
('LEB', 'Psalms', '4', '4', '1', 'Be disturbed but do not sin.  Commune in your heart<span class=\"note\">That is, “think it over”</span>on your bed and be silent. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '4', '5', '1', 'Offer correct sacrifices,<span class=\"note\">Or “right sacrifices”</span> and <span class=\"idiom\">trust in</span><span class=\"note\">Literally “trust to”</span>Adonai.'),
('LEB', 'Psalms', '4', '6', '1', 'Many are saying, “Who will show us <span class=\"supplied\">something</span> good?”  Lift up over<span class=\"note\">Or “upon”</span>us the light of your face, O Adonai.'),
('LEB', 'Psalms', '4', '7', '1', 'You have put gladness in my heart  more than <span class=\"idiom\">when</span><span class=\"note\">Literally “the time of”</span>their grain and their wine abound.'),
('LEB', 'Psalms', '4', '8', '1', 'In peace I will lie down and sleep <span class=\"idiom\">at once</span>,<span class=\"note\">Literally “together”</span> for you alone, O Adonai, make me dwell safely.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '5', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director; with the flutes. A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Hear my words, O Adonai.  Give heed to my sighing.<span class=\"note\">Or “murmuring”</span>'),
('LEB', 'Psalms', '5', '2', '1', 'Listen to the sound of my pleading,<span class=\"note\">Or “cry for help”</span>my king and my God,  for to you I pray.'),
('LEB', 'Psalms', '5', '3', '1', 'O Adonai, in <span class=\"supplied\">the</span> morning you will hear my voice.  In <span class=\"supplied\">the</span> morning I will set forth<span class=\"note\">Or “present”</span><span class=\"supplied\">my case</span> to you and I will watch.<span class=\"note\">Or “look expectantly”</span>'),
('LEB', 'Psalms', '5', '4', '1', 'For you <span class=\"supplied\">are</span> not a God <span class=\"supplied\">who</span> desires<span class=\"note\">Or “takes delight in”</span>wickedness.  Evil cannot dwell with you.'),
('LEB', 'Psalms', '5', '5', '1', 'The boastful do not stand before<span class=\"note\">Or “in front of”</span>your eyes.  You hate all evildoers.'),
('LEB', 'Psalms', '5', '6', '1', 'You destroy speakers of lies.<span class=\"note\">Hebrew “lie”</span> <span class=\"idiom\">A man of bloodshed</span><span class=\"note\">Literally “a man of bloods”</span>and deceit Adonai abhors.'),
('LEB', 'Psalms', '5', '7', '1', '<span class=\"idiom\">But as for me</span>,<span class=\"note\">Literally “And I”</span>through the abundance of your steadfast love<span class=\"note\">Or “covenant love”</span> I will enter your house.<span class=\"note\">Or “temple”</span> I will bow down<span class=\"note\">Or “I will worship”</span>toward <span class=\"idiom\">your holy temple</span><span class=\"note\">Literally “the temple of your holiness”</span>in awe <span class=\"supplied\">of</span> you.'),
('LEB', 'Psalms', '5', '8', '1', 'O Adonai, lead me in your righteousness because of my enemies;  make straight before me your way.'),
('LEB', 'Psalms', '5', '9', '1', 'For there is not <span class=\"supplied\">anything</span> reliable in his mouth;  their inner part <span class=\"supplied\">is</span> destruction.<span class=\"note\">Hebrew “destructions”</span> Their throat is an open grave;  <span class=\"supplied\">with</span> their tongue they speak deceit.<span class=\"note\">Or “flattery; smooth words”</span>'),
('LEB', 'Psalms', '5', '10', '1', 'Treat them as guilty, O God;  let them fall because of their plans.  Because of the abundance of their transgressions cast them out,  for they have rebelled against you.'),
('LEB', 'Psalms', '5', '11', '1', 'But let all who take shelter in you rejoice.  Let them ever<span class=\"note\">Or “perpetually”</span>sing for joy,  <span class=\"idiom\">because</span><span class=\"note\">Literally “and”</span>you spread protection over them;  And let those<span class=\"note\">Or “So that those”</span><span class=\"supplied\">who</span> love your name exult in you.'),
('LEB', 'Psalms', '5', '12', '1', 'For you bless <span class=\"supplied\">the</span> righteous.  O Adonai, like a<span class=\"note\">Hebrew “the”</span>shield you surround him <span class=\"supplied\">with</span> good favor.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '6', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director; with stringed instruments, on the Sheminith.<span class=\"note\">Hebrew “upon the eighth”; meaning uncertain</span> A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>O Adonai, do not rebuke me in your anger,  and do not discipline me in your wrath.'),
('LEB', 'Psalms', '6', '2', '1', 'Be gracious to me, O Adonai, because I am feeble.  Heal me, O Adonai, for my bones are terrified.'),
('LEB', 'Psalms', '6', '3', '1', 'My soul is also very terrified.  But you, O Adonai, how long?'),
('LEB', 'Psalms', '6', '4', '1', 'Turn, O Adonai; deliver my life.<span class=\"note\">Or “my soul”; same word as in v. 3</span> Save me for the sake of your steadfast love.<span class=\"note\">Or “loyal love”</span>'),
('LEB', 'Psalms', '6', '5', '1', 'For there is no remembrance of you in death.  In Sheol, who will give thanks to you?'),
('LEB', 'Psalms', '6', '6', '1', 'I am weary with my groaning;  <span class=\"idiom\">I flood</span><span class=\"note\">Literally “I make my bed float”</span>my bed every night.  With my tears <span class=\"idiom\">I drench</span><span class=\"note\">Literally “I cause to melt”</span>my couch.'),
('LEB', 'Psalms', '6', '7', '1', 'My eye wastes away because of vexation;  it grows old because of all my oppressors.'),
('LEB', 'Psalms', '6', '8', '1', 'Depart from me, all workers of evil,  for Adonai has heard the sound of my weeping.'),
('LEB', 'Psalms', '6', '9', '1', 'Adonai has heard my plea;  Adonai has accepted my prayer.'),
('LEB', 'Psalms', '6', '10', '1', 'All my enemies shall be ashamed and shall be very terrified.  They shall turn back; they shall suddenly be ashamed.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '7', '1', '1', '<span class=\"verse-title\">A Shiggaion<span class=\"note\">Meaning uncertain; perhaps a lamentation</span>of David which he sang to Adonai  on account of Cush, a Benjaminite.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>O Adonai, my God, in you I have taken refuge.  Save me from all <span class=\"supplied\">who</span> pursue me, and deliver me.'),
('LEB', 'Psalms', '7', '2', '1', 'Or else he will tear me apart like a lion,  ripping to pieces, with none <span class=\"supplied\">to</span> deliver.'),
('LEB', 'Psalms', '7', '3', '1', 'O Adonai, my God, if I have done this,  if there is wrong in my hands,'),
('LEB', 'Psalms', '7', '4', '1', 'if I have repaid <span class=\"idiom\">my ally</span><span class=\"note\">Literally “<span class=\"supplied\">one</span> being at peace with me”</span><span class=\"supplied\">with</span> harm,  or <span class=\"supplied\">if</span> I have plundered my enemy without cause,'),
('LEB', 'Psalms', '7', '5', '1', 'let the enemy of my soul pursue,  and overtake and trample my life into the ground,  and lay my honor in the dust. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '7', '6', '1', 'Rise up, O Adonai, in your anger,  and lift yourself up against the wrath<span class=\"note\">Or “outbursts”</span>of my oppressors,  and awake for me,  since you have commanded judgment.'),
('LEB', 'Psalms', '7', '7', '1', 'And let <span class=\"supplied\">the</span> assembly of peoples surround you,  and over it return on high.'),
('LEB', 'Psalms', '7', '8', '1', 'Adonai judges the nations.  O Adonai, vindicate me  according to my innocence<span class=\"note\">Or “righteousness”</span>and according to my integrity within me.'),
('LEB', 'Psalms', '7', '9', '1', 'Let the evil of the wicked come to an end,  but establish <span class=\"supplied\">the</span> righteous,  and test the hearts and innermost being, O righteous God.'),
('LEB', 'Psalms', '7', '10', '1', 'My shield <span class=\"supplied\">is</span> with God  who saves the upright of heart.'),
('LEB', 'Psalms', '7', '11', '1', 'God <span class=\"supplied\">is</span> a righteous judge,  and a God who has indignation every day.'),
('LEB', 'Psalms', '7', '12', '1', 'If he<span class=\"note\">That is, a person</span>does not repent, he<span class=\"note\">That is, God</span>will sharpen his sword.  He has bent his bow and he has <span class=\"idiom\">strung</span><span class=\"note\">Literally “prepared”</span>it.'),
('LEB', 'Psalms', '7', '13', '1', 'And he has prepared for him weapons of death.  He has made his arrows fiery shafts.'),
('LEB', 'Psalms', '7', '14', '1', 'See, he<span class=\"note\">That is, the wicked</span>travails with<span class=\"note\">Or “conceives”</span>evil.  He is pregnant with trouble,  and he gives birth to deception.'),
('LEB', 'Psalms', '7', '15', '1', 'He makes a pit and digs it out,  then falls in <span class=\"supplied\">the</span> trap he has made.'),
('LEB', 'Psalms', '7', '16', '1', 'His trouble comes back on his head,  and his violence comes down on his skull.'),
('LEB', 'Psalms', '7', '17', '1', 'I will thank Adonai according to his righteousness,  and I will sing praise to the name of Adonai, <span class=\"supplied\">the</span> Most High.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '8', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, on the Gittith.<span class=\"note\">Meaning uncertain</span>A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Adonai, our Lord, how majestic <span class=\"supplied\">is</span> your name in all the earth,  who put your splendor above the heavens.'),
('LEB', 'Psalms', '8', '2', '1', 'From the mouth of children and infants you have founded<span class=\"note\">Or “ordained”</span>strength  on account of your enemies,  to silence <span class=\"supplied\">the</span> enemy and <span class=\"supplied\">the</span> avenger.'),
('LEB', 'Psalms', '8', '3', '1', 'When I look at your heavens, the work of your fingers,  <span class=\"supplied\">the</span> moon and <span class=\"supplied\">the</span> stars which you set in place—'),
('LEB', 'Psalms', '8', '4', '1', 'what <span class=\"supplied\">is</span> a human being that you think of him?  and a child of humankind that you care for him?'),
('LEB', 'Psalms', '8', '5', '1', 'And you made him a little lower than heavenly beings,<span class=\"note\">Or “than God”</span> and <span class=\"supplied\">with</span> glory and <span class=\"supplied\">with</span> majesty you crowned him.'),
('LEB', 'Psalms', '8', '6', '1', 'You make him over the works of your hands;  all <span class=\"supplied\">things</span> you have placed under his feet:'),
('LEB', 'Psalms', '8', '7', '1', 'sheep and cattle, all of them,  and also <span class=\"supplied\">the</span> wild animals of <span class=\"supplied\">the</span> field,'),
('LEB', 'Psalms', '8', '8', '1', '<span class=\"supplied\">the</span> birds of <span class=\"supplied\">the</span> sky and the fish of the sea,  <span class=\"supplied\">everything</span> that passes along the paths of seas.'),
('LEB', 'Psalms', '8', '9', '1', 'Adonai, our Lord,  how majestic <span class=\"supplied\">is</span> your name in all of the earth!')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '9', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, according to Muth-labben.<span class=\"note\">Meaning uncertain</span>A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>I will thank Adonai with all my heart.  I will tell of all your wonderful deeds.'),
('LEB', 'Psalms', '9', '2', '1', 'I will be glad and rejoice in you.  I will sing <span class=\"supplied\">the praise of</span> your name, O Most High.'),
('LEB', 'Psalms', '9', '3', '1', 'When my enemies turn back,  they will stumble and perish because of your presence.'),
('LEB', 'Psalms', '9', '4', '1', 'For you have maintained my just cause;  you have sat on <span class=\"supplied\">the</span> throne judging correctly.'),
('LEB', 'Psalms', '9', '5', '1', 'You have rebuked <span class=\"supplied\">the</span> nations;  you have destroyed <span class=\"supplied\">the</span> wicked.  Their name you have blotted out  forever and ever.'),
('LEB', 'Psalms', '9', '6', '1', 'The enemies are destroyed <span class=\"supplied\">in</span> ruins forever,  and you have uprooted <span class=\"supplied\">their</span> cities;  their very memory has perished.'),
('LEB', 'Psalms', '9', '7', '1', 'But Adonai sits <span class=\"supplied\">enthroned</span> forever.  He has established his throne for judgment.'),
('LEB', 'Psalms', '9', '8', '1', 'And he will judge <span class=\"supplied\">the</span> world with righteousness.  He will judge <span class=\"supplied\">the</span> peoples with equity.'),
('LEB', 'Psalms', '9', '9', '1', 'And Adonai will be a stronghold for the oppressed,  a stronghold in times of distress.'),
('LEB', 'Psalms', '9', '10', '1', 'And those who know your name will trust in you,  for you do not forsake those <span class=\"supplied\">who</span> seek you, O Adonai.'),
('LEB', 'Psalms', '9', '11', '1', 'Sing <span class=\"supplied\">praises</span> to Adonai <span class=\"supplied\">who</span> sits <span class=\"supplied\">enthroned</span><span class=\"note\">Or “who dwells”</span>in Zion.  Tell his deeds among the peoples,'),
('LEB', 'Psalms', '9', '12', '1', 'for he <span class=\"supplied\">who</span> <span class=\"idiom\">avenges bloodshed</span><span class=\"note\">Literally “seeks bloods”</span>remembers them.  He does not forget the distressed cry of <span class=\"supplied\">the</span> afflicted.'),
('LEB', 'Psalms', '9', '13', '1', 'Be gracious to me, O Adonai.  See my suffering from <span class=\"supplied\">those who</span> hate me,  <span class=\"supplied\">you who</span> lift me up from the gates of death,'),
('LEB', 'Psalms', '9', '14', '1', 'so that I may tell of all your praises.  In the gates of the daughter of Zion  let me rejoice in your salvation.'),
('LEB', 'Psalms', '9', '15', '1', '<span class=\"supplied\">The</span> nations have fallen in <span class=\"supplied\">the</span> pit <span class=\"supplied\">that</span> they made;  their foot is caught in <span class=\"supplied\">the</span> net that they hid.'),
('LEB', 'Psalms', '9', '16', '1', 'Adonai has made himself known;  he has executed judgment.  The wicked is snared by the work of his hands. <span class=\"italic\">Higgaion</span><span class=\"note\">Hebrew musical term</span><span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '9', '17', '1', '<span class=\"supplied\">The</span> wicked shall turn back to Sheol,  all <span class=\"supplied\">the</span> nations forgetting God,'),
('LEB', 'Psalms', '9', '18', '1', 'for <span class=\"supplied\">the</span> needy shall not always be forgotten;  the hope of the poor shall never perish.'),
('LEB', 'Psalms', '9', '19', '1', 'Rise up, O Adonai, let not humans<span class=\"note\">Hebrew “human”</span>prevail;  let <span class=\"supplied\">the</span> nations be judged before you.'),
('LEB', 'Psalms', '9', '20', '1', 'O Adonai, put them in fear.  Let <span class=\"supplied\">the</span> nations know they <span class=\"supplied\">are merely</span> human. <span class=\"italic\">Selah</span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '10', '1', '1', '<span class=\"verse-title\">A Prayer for God to Throw down the Wicked</span>Why, O Adonai, do you stand far off?  <span class=\"supplied\">Why</span> do you hide<span class=\"note\">Or “shut <span class=\"supplied\">your eyes</span>”</span>during times of distress?'),
('LEB', 'Psalms', '10', '2', '1', 'In arrogance <span class=\"supplied\">the</span> wicked persecutes<span class=\"note\">Or “pursues”</span><span class=\"supplied\">the</span> poor.  Let them be caught in <span class=\"supplied\">the</span> schemes that they devised,'),
('LEB', 'Psalms', '10', '3', '1', 'for <span class=\"supplied\">the</span> wicked boasts about the desire of his <span class=\"idiom\">heart</span>,<span class=\"note\">Literally “soul”</span> and <span class=\"supplied\">the</span> one greedy for gain curses <span class=\"supplied\">and</span> treats Adonai with contempt.'),
('LEB', 'Psalms', '10', '4', '1', '<span class=\"idiom\">With bald-faced pride</span><span class=\"note\">Literally “According to the height of his nose”</span><span class=\"supplied\">the</span> wicked will not seek <span class=\"supplied\">God</span>.  There is no God in any of his thoughts.'),
('LEB', 'Psalms', '10', '5', '1', 'His ways<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>endure at all times.<span class=\"note\">Hebrew “time”</span> Your judgments are aloof from him.  <span class=\"supplied\">As for</span> all his enemies, he scoffs at them.'),
('LEB', 'Psalms', '10', '6', '1', 'He says in his heart, “I shall not be moved  <span class=\"idiom\">throughout all generations</span>,<span class=\"note\">Literally “for a generation and a generation”</span><span class=\"supplied\">during</span> which <span class=\"supplied\">I will have</span> no trouble.”'),
('LEB', 'Psalms', '10', '7', '1', 'His mouth is filled with cursing,  with deceits and oppression;  under his tongue are trouble and evil.'),
('LEB', 'Psalms', '10', '8', '1', 'He sits in ambush in villages;  in the hiding places he kills <span class=\"supplied\">the</span> innocent.  His eyes <span class=\"idiom\">lurk</span><span class=\"note\">Literally “lie hid”</span>for <span class=\"supplied\">the</span> helpless.'),
('LEB', 'Psalms', '10', '9', '1', 'He lies in ambush <span class=\"idiom\">secretly,</span><span class=\"note\">Literally “in the secret places”</span>like a lion in a thicket.  He lies in ambush to seize <span class=\"supplied\">the</span> poor;  he seizes <span class=\"supplied\">the</span> poor by catching him in his net.'),
('LEB', 'Psalms', '10', '10', '1', 'He is crushed;<span class=\"note\">Or “and he <span class=\"supplied\">the wicked</span> stoops”</span>he is bowed down;<span class=\"note\">Or “he <span class=\"supplied\">the wicked</span> crouches”</span> so<span class=\"note\">Or “and”</span>the helpless host<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>falls by his <span class=\"idiom\">might</span>.<span class=\"note\">Literally “mighty ones”</span>'),
('LEB', 'Psalms', '10', '11', '1', 'He says in his heart, “God has forgotten.  He has hidden his face.  He never sees.”'),
('LEB', 'Psalms', '10', '12', '1', 'Rise up, O Adonai;  O God, lift up your hand.  Do not forget <span class=\"supplied\">the</span> afflicted.'),
('LEB', 'Psalms', '10', '13', '1', 'Why does the wicked treat God with contempt?  He says in his heart, “You will not call <span class=\"supplied\">me</span> to account.”'),
('LEB', 'Psalms', '10', '14', '1', '<span class=\"supplied\">But</span> you have seen; indeed you have noted trouble and grief  to take <span class=\"supplied\">it</span> into your hand.  The helpless abandons <span class=\"supplied\">himself</span> upon you;  you have been <span class=\"supplied\">the</span> helper <span class=\"supplied\">for the</span> orphan.'),
('LEB', 'Psalms', '10', '15', '1', 'Break <span class=\"supplied\">the</span> arm of <span class=\"supplied\">the</span> wicked,  and <span class=\"supplied\">as for the</span> evil <span class=\"supplied\">man</span>—  seek out his wickedness <span class=\"supplied\">until</span> you find none.'),
('LEB', 'Psalms', '10', '16', '1', 'Adonai <span class=\"supplied\">is</span> king forever and ever;  the nations have perished from his land.'),
('LEB', 'Psalms', '10', '17', '1', '<span class=\"supplied\">The</span> longing of <span class=\"supplied\">the</span> afflicted you have heard, O Adonai.  You will make their heart secure. You will <span class=\"idiom\">listen attentively</span><span class=\"note\">Literally “cause your ear to hear”</span>'),
('LEB', 'Psalms', '10', '18', '1', 'to render judgment <span class=\"supplied\">for the</span> fatherless and <span class=\"supplied\">the</span> oppressed  <span class=\"supplied\">so that</span> a <span class=\"supplied\">mere</span> mortal from the earth will no longer cause terror.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '11', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>In Adonai I have taken refuge;  how can you say to my soul,  “Flee to your mountain <span class=\"supplied\">like</span> a bird”?'),
('LEB', 'Psalms', '11', '2', '1', 'For look: The wicked <span class=\"idiom\">string the bow</span>.<span class=\"note\">Literally “tread the bow,” that is, placing the foot on the bow to bend and string it</span> They have fitted their arrow to <span class=\"supplied\">the</span> string,  to shoot from <span class=\"supplied\">the</span> darkness  at the upright of heart.'),
('LEB', 'Psalms', '11', '3', '1', 'When the foundations are destroyed  what can <span class=\"supplied\">the</span> righteous do?<span class=\"note\">Or “what has the righteous done”</span>'),
('LEB', 'Psalms', '11', '4', '1', 'Adonai <span class=\"supplied\">is</span> in his holy temple;  Adonai <span class=\"supplied\">is</span> in the heavens <span class=\"supplied\">on</span> his throne.  His eyelids see;  they test <span class=\"supplied\">the</span> children of humankind.'),
('LEB', 'Psalms', '11', '5', '1', 'Adonai tests <span class=\"supplied\">the</span> righteous,  but <span class=\"supplied\">the</span> wicked and <span class=\"supplied\">the</span> lover of violence  his soul hates.'),
('LEB', 'Psalms', '11', '6', '1', 'He will rain coals<span class=\"note\">According to Symmachus (compare also <span class=\"italic\">Targum Psalms</span>) a form slightly different from the MT’s “snares”</span>on <span class=\"supplied\">the</span> wicked.  Burning sulfur and scorching wind  <span class=\"supplied\">will be</span> the portion of their cup.'),
('LEB', 'Psalms', '11', '7', '1', 'For Adonai <span class=\"supplied\">is</span> righteous;  he loves righteous deeds.  <span class=\"supplied\">The</span> upright shall see his face.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '12', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director; on the Sheminith.<span class=\"note\">Meaning uncertain</span>A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Save, O Adonai, for <span class=\"supplied\">the</span> pious have ceased <span class=\"supplied\">to be</span>;  for <span class=\"supplied\">the</span> faithful have vanished  from <span class=\"supplied\">among the</span> children of humankind.'),
('LEB', 'Psalms', '12', '2', '1', 'They speak falseness <span class=\"idiom\">to each other</span>.<span class=\"note\">Literally “each with his companion”</span> <span class=\"supplied\">With</span> flattering lips,  <span class=\"supplied\">with</span> <span class=\"idiom\">a double heart</span><span class=\"note\">Literally “a heart and a heart”</span>they speak.'),
('LEB', 'Psalms', '12', '3', '1', 'May Adonai cut off all flattering lips,  <span class=\"supplied\">the</span> tongue speaking great boasts—'),
('LEB', 'Psalms', '12', '4', '1', '<span class=\"supplied\">those</span> who say, “With our tongue we will prevail.  Our lips <span class=\"supplied\">are</span> on our side.  Who <span class=\"supplied\">is</span> master over us?”'),
('LEB', 'Psalms', '12', '5', '1', '“Because of <span class=\"supplied\">the</span> oppression of <span class=\"supplied\">the</span> afflicted,  because of <span class=\"supplied\">the</span> groaning of <span class=\"supplied\">the</span> poor,  now I will rise up,” Adonai says.  “I shall put <span class=\"supplied\">them</span> in <span class=\"supplied\">the</span> safety <span class=\"supplied\">for which</span> they<span class=\"note\">Hebrew “he”</span>long.”'),
('LEB', 'Psalms', '12', '6', '1', 'The words of Adonai <span class=\"supplied\">are</span> pure words  <span class=\"supplied\">like</span> silver refined in the crucible on the ground,  refined seven times.'),
('LEB', 'Psalms', '12', '7', '1', 'You, O Adonai, will protect them.  You will preserve him<span class=\"note\">Or “us”</span> from this generation always.'),
('LEB', 'Psalms', '12', '8', '1', '<span class=\"supplied\">The</span> wicked prowl about  when vileness <span class=\"supplied\">is</span> exalted among <span class=\"supplied\">the</span> children of humankind.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '13', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>How long, O Adonai? Will you forget me forever?  How long will you hide your face from me?'),
('LEB', 'Psalms', '13', '2', '1', 'How long must I take counsel<span class=\"note\">Hebrew pl.; with a slight modification of the Hebrew this reads “set pains”</span>in my soul,  <span class=\"supplied\">and</span> sorrow in my heart <span class=\"supplied\">all the</span> day?  How long will my enemy be exalted over me?'),
('LEB', 'Psalms', '13', '3', '1', 'Consider<span class=\"note\">Literally “Look”</span><span class=\"supplied\">and</span> answer me, O Adonai my God.  Give light to my eyes  lest I sleep <span class=\"supplied\">the sleep of</span> death,'),
('LEB', 'Psalms', '13', '4', '1', '<span class=\"supplied\">and</span> lest my enemy should say, “I have overcome him,”  <span class=\"supplied\">lest</span> my enemies rejoice because I am shaken.'),
('LEB', 'Psalms', '13', '5', '1', 'But as for me, I have trusted in your steadfast love.<span class=\"note\">Or, “loyal love”</span> My heart will rejoice in your deliverance.'),
('LEB', 'Psalms', '13', '6', '1', '<span class=\"note\">In the Hebrew Bible, the previous verse continues</span>I will sing to Adonai  because he has dealt bountifully with me.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '14', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span><span class=\"supplied\">The</span> fool says in his heart, “There is no God.”  They are corrupt.  They do abominable deeds.  There is none who does good.'),
('LEB', 'Psalms', '14', '2', '1', 'Adonai looks down from heaven upon the children of humankind  to see whether there is one who has insight,  one who cares about God.'),
('LEB', 'Psalms', '14', '3', '1', 'All have gone astray;  they are altogether corrupt.  There is not one who does good;  there is not even one.'),
('LEB', 'Psalms', '14', '4', '1', 'All who do evil—do they not know,  <span class=\"supplied\">they</span> who eat my people <span class=\"supplied\">as though</span> they were eating bread?  They do not call on Adonai.'),
('LEB', 'Psalms', '14', '5', '1', 'There <span class=\"idiom\">they are very fearful</span><span class=\"note\">Literally “they feared a fear”</span> because God <span class=\"supplied\">is</span> with <span class=\"supplied\">the</span> generation of <span class=\"supplied\">the</span> righteous.'),
('LEB', 'Psalms', '14', '6', '1', 'You would put to shame <span class=\"supplied\">the</span> plan of <span class=\"supplied\">the</span> poor,  because<span class=\"note\">Or “but”</span>Adonai <span class=\"supplied\">is</span> his refuge.'),
('LEB', 'Psalms', '14', '7', '1', '<span class=\"idiom\">Oh that from Zion</span><span class=\"note\">Literally “Who will give <span class=\"supplied\">that</span> from Zion”</span><span class=\"supplied\">would come</span> salvation <span class=\"supplied\">for</span> Israel!  When Adonai returns the fortunes of his people,  Jacob will rejoice; Israel will be happy.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '15', '1', '1', '<span class=\"verse-title\">A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>O Adonai, who may reside in your tent?  Who may dwell on your holy mountain?'),
('LEB', 'Psalms', '15', '2', '1', 'He who walks blamelessly and does what is right.  He who speaks honestly in his heart.'),
('LEB', 'Psalms', '15', '3', '1', 'He does not slander with his tongue.  He does not harm his friend,  nor <span class=\"idiom\">bring up</span><span class=\"note\">Literally “lift up”</span>a disgrace against his neighbor.'),
('LEB', 'Psalms', '15', '4', '1', 'In his eyes a rejected one <span class=\"supplied\">is</span> contemptible,  but he honors those who respect Adonai.  He takes an oath to <span class=\"supplied\">his own</span> injury and does not retract it.'),
('LEB', 'Psalms', '15', '5', '1', 'He does not lend his money at interest,  and does not take a bribe against <span class=\"supplied\">the</span> innocent.  He who does these <span class=\"supplied\">things</span>  will never be shaken.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '16', '1', '1', '<span class=\"verse-title\">A miktam<span class=\"note\">Meaning uncertain</span>of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Protect me, O God,  for I take refuge in you.'),
('LEB', 'Psalms', '16', '2', '1', '<span class=\"supplied\">Oh my soul</span> you have said<span class=\"note\">Reading a feminine singular verb; some Hebrew manuscripts and translations read “I said”</span>to Adonai, “You <span class=\"supplied\">are</span> my Lord.  I have no good apart from you.”'),
('LEB', 'Psalms', '16', '3', '1', 'With regard to <span class=\"supplied\">the</span> saints who <span class=\"supplied\">are</span> in the land,  they <span class=\"supplied\">are the</span> noble ones in <span class=\"supplied\">whom is</span> all my desire.'),
('LEB', 'Psalms', '16', '4', '1', 'Those <span class=\"supplied\">who</span> hurry <span class=\"supplied\">after</span><span class=\"note\">Or “they who acquire”</span>another <span class=\"supplied\">god</span> increase their sorrows.  I will not pour out their drink offerings of blood,  nor take up their names on my lips.'),
('LEB', 'Psalms', '16', '5', '1', 'Adonai <span class=\"supplied\">is</span> the portion <span class=\"supplied\">which is</span> my share and my cup.  You hold my lot.'),
('LEB', 'Psalms', '16', '6', '1', '<span class=\"supplied\">The</span> measuring lines have fallen for me in pleasant places.  Yes, <span class=\"supplied\">my</span> inheritance is delightful for me.'),
('LEB', 'Psalms', '16', '7', '1', 'I will bless Adonai who advises me;  yes, <span class=\"supplied\">at</span> night my <span class=\"idiom\">innermost being</span><span class=\"note\">Literally “kidneys”</span>instructs me.'),
('LEB', 'Psalms', '16', '8', '1', 'I have set Adonai before me always.  Because <span class=\"supplied\">he is</span> at my right hand I will not be shaken.'),
('LEB', 'Psalms', '16', '9', '1', 'Therefore my heart is glad and my glory rejoices.  Yes, my body will dwell in safety,'),
('LEB', 'Psalms', '16', '10', '1', 'for you will not abandon my soul to Sheol;  you will not give your faithful one to see <span class=\"supplied\">the</span> grave.'),
('LEB', 'Psalms', '16', '11', '1', 'You will make known to me <span class=\"supplied\">the</span> path of life.  In your presence <span class=\"supplied\">is</span> fullness of joy.  At your right hand <span class=\"supplied\">are</span> pleasures forever.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '17', '1', '1', '<span class=\"verse-title\">A prayer of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>O Adonai, hear a just cause.  Hear my cry; heed my prayer  <span class=\"supplied\">I make</span> without deceitful lips.'),
('LEB', 'Psalms', '17', '2', '1', 'Let my vindication come forth from you;  let your eyes see fairness.'),
('LEB', 'Psalms', '17', '3', '1', 'You have tried my heart;  you have examined <span class=\"supplied\">me</span> by night;  you have tested me; you found nothing.  I have decided <span class=\"supplied\">that</span> my mouth will not transgress.'),
('LEB', 'Psalms', '17', '4', '1', 'As for <span class=\"supplied\">the</span> works of humankind,  by the word of your lips,  I have kept <span class=\"supplied\">from</span> the ways of <span class=\"supplied\">the</span> violent.'),
('LEB', 'Psalms', '17', '5', '1', '<span class=\"supplied\">I have</span> held my steps in your path  My feet will not slip.'),
('LEB', 'Psalms', '17', '6', '1', 'As for me, I have called on you  because you will answer me, O God.  Incline your ear to me.  Hear my words.<span class=\"note\">Hebrew “word”</span>'),
('LEB', 'Psalms', '17', '7', '1', 'Show wondrously your acts of loyal love,  O Savior of those who take refuge  at your right hand<span class=\"note\">Or “saving by your right hand those”</span> from those who rise up against <span class=\"supplied\">them</span>.'),
('LEB', 'Psalms', '17', '8', '1', 'Keep me as <span class=\"idiom\">the apple of your eye</span>.<span class=\"note\">Literally “<span class=\"supplied\">the</span> little man of the daughter of <span class=\"supplied\">your</span> eye”</span> Hide me in the shadow of your wings'),
('LEB', 'Psalms', '17', '9', '1', 'from <span class=\"supplied\">the</span> <span class=\"idiom\">presence</span><span class=\"note\">Literally “face”</span>of <span class=\"supplied\">the</span> wicked who destroy me,  <span class=\"supplied\">those</span> enemies against my life,  <span class=\"supplied\">they that</span> surround me.'),
('LEB', 'Psalms', '17', '10', '1', 'They have shut off their calloused<span class=\"note\">Or “fat”</span><span class=\"supplied\">heart</span>;  <span class=\"supplied\">with</span> their mouth they speak arrogantly.'),
('LEB', 'Psalms', '17', '11', '1', 'Now they surround us at our <span class=\"supplied\">every</span> step.  They <span class=\"idiom\">intend</span><span class=\"note\">Literally “set their eyes”</span> <span class=\"idiom\">to pin</span><span class=\"note\">Literally “to stretch”</span><span class=\"supplied\">me</span> to the ground.'),
('LEB', 'Psalms', '17', '12', '1', '<span class=\"idiom\">He is like</span><span class=\"note\">Literally “His image”</span>a lion; he longs to tear apart,  and like a strong lion crouching in hiding places.'),
('LEB', 'Psalms', '17', '13', '1', 'Rise up, O Adonai, confront <span class=\"idiom\">him</span>.<span class=\"note\">Literally “his face”</span> Make him bow down.  Rescue with your sword my life from <span class=\"supplied\">the</span> wicked,'),
('LEB', 'Psalms', '17', '14', '1', 'from men by your hand, O Adonai, from men of <span class=\"supplied\">this</span> world.  Their share <span class=\"supplied\">is</span> in <span class=\"idiom\">this life</span>,<span class=\"note\">Literally “the life”</span>and you fill their stomach <span class=\"supplied\">with</span> your treasure.  They are satisfied <span class=\"supplied\">with</span> children.  They bequeath their excess to their children.'),
('LEB', 'Psalms', '17', '15', '1', '<span class=\"supplied\">By contrast,</span> I in righteousness shall see your face.  Upon awakening I will be satisfied <span class=\"supplied\">seeing</span> your form.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '18', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. Of David, the servant of Adonai,  who spoke to Adonai the words of this song  <span class=\"idiom\">when</span><span class=\"note\">Literally “on the day”</span>Adonai delivered him from the hand of all his enemies,  and from the hand of Saul.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>And he said:  “I love you, O Adonai, my strength.”'),
('LEB', 'Psalms', '18', '2', '1', 'Adonai <span class=\"supplied\">is</span> my rock and my fortress and my deliverer,  my God, my rock in whom I have taken refuge,  my shield and the horn of my deliverance, my stronghold.'),
('LEB', 'Psalms', '18', '3', '1', 'I called upon Adonai, who is worthy to be praised,  and I was saved from my enemies.'),
('LEB', 'Psalms', '18', '4', '1', 'The ropes of death encircled me,  and streams of ruin overwhelmed me.'),
('LEB', 'Psalms', '18', '5', '1', 'The ropes of Sheol surrounded me;  <span class=\"supplied\">the</span> snares of death confronted me.'),
('LEB', 'Psalms', '18', '6', '1', 'In my trouble I called on Adonai,  and to my God I cried for help.  He heard my voice from his temple,  and my cry for help  came up before his presence into his ears.'),
('LEB', 'Psalms', '18', '7', '1', 'Then the earth shook and quaked,  and <span class=\"supplied\">the</span> foundations of <span class=\"supplied\">the</span> mountains trembled,  and they staggered because he was angry.'),
('LEB', 'Psalms', '18', '8', '1', 'Smoke went forth from his nose,  and fire from his mouth consumed.  Burning coals blazed from him.'),
('LEB', 'Psalms', '18', '9', '1', 'So he bowed <span class=\"supplied\">the</span> heavens and came down  with a thick cloud under his feet.'),
('LEB', 'Psalms', '18', '10', '1', 'And he mounted a cherub and flew,  and he swooped down on wings of wind.'),
('LEB', 'Psalms', '18', '11', '1', 'He made darkness his hiding place;  all about him his covering  <span class=\"supplied\">was</span> a darkness of waters, <span class=\"idiom\">thick clouds</span>.<span class=\"note\">Literally “clouds of clouds”</span>'),
('LEB', 'Psalms', '18', '12', '1', 'From the brightness before him  his clouds passed over  <span class=\"supplied\">with</span> hail and coals of fire.'),
('LEB', 'Psalms', '18', '13', '1', 'And Adonai thundered from the heavens,  and the Most High uttered his voice  <span class=\"supplied\">with</span> hail and coals of fire.'),
('LEB', 'Psalms', '18', '14', '1', 'And he shot his arrows and scattered them,<span class=\"note\">That is, the enemy</span> and many lightning bolts and routed them.'),
('LEB', 'Psalms', '18', '15', '1', 'Then the channels of <span class=\"supplied\">the</span> sea became visible,  and <span class=\"supplied\">the</span> foundations of <span class=\"supplied\">the</span> world were uncovered  by your rebuke, O Adonai,  by the <span class=\"idiom\">blast</span><span class=\"note\">Or “breath”</span>of the wind of your nose.'),
('LEB', 'Psalms', '18', '16', '1', 'He reached<span class=\"note\">Or “sent”</span>from on high; he seized me.  He drew me from many waters.'),
('LEB', 'Psalms', '18', '17', '1', 'He delivered me from my strong enemy,  and from <span class=\"supplied\">those who</span> hated me  because they were stronger than I.'),
('LEB', 'Psalms', '18', '18', '1', 'They confronted me on the day of disaster,  but Adonai was my support.'),
('LEB', 'Psalms', '18', '19', '1', 'So he brought me out to a spacious place.  He delivered me because he delighted in me.'),
('LEB', 'Psalms', '18', '20', '1', 'Adonai has rewarded me according to my righteousness;  According to the cleanness of my hands  he has repaid me.'),
('LEB', 'Psalms', '18', '21', '1', 'Because I have kept the ways of Adonai,  and have not acted wickedly against my God,'),
('LEB', 'Psalms', '18', '22', '1', 'because all his ordinances <span class=\"supplied\">are</span> before me,  and his statutes I have not removed from me,'),
('LEB', 'Psalms', '18', '23', '1', 'and I was blameless before him,  and I kept myself from my guilt.'),
('LEB', 'Psalms', '18', '24', '1', 'Therefore Adonai has repaid me according to my righteousness,  according to the cleanness of my hands in his sight.'),
('LEB', 'Psalms', '18', '25', '1', 'To <span class=\"supplied\">the</span> loyal you show yourself loyal.  To <span class=\"supplied\">the</span> blameless you show yourself blameless.'),
('LEB', 'Psalms', '18', '26', '1', 'To <span class=\"supplied\">the</span> pure you show yourself pure.  but to <span class=\"supplied\">the</span> wicked you show yourself shrewd.'),
('LEB', 'Psalms', '18', '27', '1', 'For you deliver humble people,  but haughty eyes you humiliate.'),
('LEB', 'Psalms', '18', '28', '1', 'For you light my lamp;  Adonai, my God, lights up my darkness.'),
('LEB', 'Psalms', '18', '29', '1', 'For with you I can charge a troop,  and with my God I can scale a wall.'),
('LEB', 'Psalms', '18', '30', '1', 'This<span class=\"note\">Hebrew “The”</span>God—his way <span class=\"supplied\">is</span> blameless.  The word of Adonai <span class=\"supplied\">is</span> flawless.<span class=\"note\">Or “refined”</span> He <span class=\"supplied\">is</span> a shield for all those who take refuge in him.'),
('LEB', 'Psalms', '18', '31', '1', 'For who <span class=\"supplied\">is</span> God apart from Adonai,  and who <span class=\"supplied\">is</span> a rock except our God?'),
('LEB', 'Psalms', '18', '32', '1', 'The God who girded me <span class=\"supplied\">with</span> strength,  and made safe<span class=\"note\">Or “blameless”</span>my way,'),
('LEB', 'Psalms', '18', '33', '1', 'he made my feet like the deer’s,  and on my high places he makes me stand.'),
('LEB', 'Psalms', '18', '34', '1', '<span class=\"supplied\">He</span> trains my hands for war,  so that my arms can bend a bow of bronze.'),
('LEB', 'Psalms', '18', '35', '1', 'You also gave me the shield of your salvation,  and your right hand supported me,  and your humility made me great.'),
('LEB', 'Psalms', '18', '36', '1', 'You enlarged my step under me,  and my ankles did not slip.'),
('LEB', 'Psalms', '18', '37', '1', 'I chased my enemies and overtook them;  I did not turn back until they were finished <span class=\"supplied\">off</span>.'),
('LEB', 'Psalms', '18', '38', '1', 'I struck them and they were not able to stand up;  they fell under my feet.'),
('LEB', 'Psalms', '18', '39', '1', 'And you girded me <span class=\"supplied\">with</span> strength for the battle;  <span class=\"supplied\">those</span> rising against me you made to kneel under me.'),
('LEB', 'Psalms', '18', '40', '1', 'And you <span class=\"idiom\">made my enemies turn their backs</span>,<span class=\"note\">Literally “turned to me my enemies’ neck”</span> then I destroyed those <span class=\"supplied\">who</span> hated me.'),
('LEB', 'Psalms', '18', '41', '1', 'They cried out but there was no rescuer,  <span class=\"supplied\">even</span> to Adonai, but he did not answer them.'),
('LEB', 'Psalms', '18', '42', '1', 'And I ground them like dust <span class=\"idiom\">in the wind</span>,<span class=\"note\">Literally “on <span class=\"supplied\">the</span> face of <span class=\"supplied\">the</span> wind”</span> I emptied them<span class=\"note\">Many Hebrew manuscripts, the LXX, Syriac, and 2 Sam 22:43 read “I crushed them”</span>out like mud of <span class=\"supplied\">the</span> streets.'),
('LEB', 'Psalms', '18', '43', '1', 'You rescued me from strife with people;  you set me as head (over) nations.  People I did not know served me;'),
('LEB', 'Psalms', '18', '44', '1', 'as soon as <span class=\"idiom\">they heard</span>,<span class=\"note\">Literally “hearing of ear”</span>they obeyed me.  <span class=\"supplied\">The</span> children of a foreign land feigned obedience before me.'),
('LEB', 'Psalms', '18', '45', '1', '<span class=\"supplied\">The</span> children of a foreign land <span class=\"idiom\">lost heart</span>,<span class=\"note\">Literally “sunk down”</span> and they came trembling from their strongholds.'),
('LEB', 'Psalms', '18', '46', '1', 'Adonai lives, and blessed <span class=\"supplied\">be</span> my rock,  and may the God of my salvation be exalted—'),
('LEB', 'Psalms', '18', '47', '1', 'the God who executed vengeance for me,  and subdued peoples under me,'),
('LEB', 'Psalms', '18', '48', '1', 'who delivered me from my enemies.  Indeed, you exalted me higher than <span class=\"supplied\">those who</span> rose against me.  You delivered me from the man of violence.'),
('LEB', 'Psalms', '18', '49', '1', 'Therefore I will give thanks to you among the nations, O Adonai,  and I will sing <span class=\"supplied\">the praise</span> of your name:'),
('LEB', 'Psalms', '18', '50', '1', 'the one who makes great victories for his king,  and <span class=\"supplied\">who</span> shows loyal love for his anointed,  to David and to his descendants forever.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '19', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>The heavens <span class=\"supplied\">are</span> telling the glory of God,  and the firmament proclaims the work of his hands.'),
('LEB', 'Psalms', '19', '2', '1', '<span class=\"idiom\">Every day</span><span class=\"note\">Literally “day to day”</span><span class=\"supplied\">they</span> pour forth speech,  and <span class=\"idiom\">every night</span><span class=\"note\">Literally “night to night”</span><span class=\"supplied\">they</span> tell knowledge.'),
('LEB', 'Psalms', '19', '3', '1', 'There is no speech and there are no words;  their sound is inaudible.'),
('LEB', 'Psalms', '19', '4', '1', '<span class=\"supplied\">Yet</span> in all <span class=\"supplied\">the</span> world their line<span class=\"note\">LXX “line”</span>goes out,  and their words to <span class=\"supplied\">the</span> end of <span class=\"supplied\">the</span> world.  In them<span class=\"note\">That is, the heavens</span>he has pitched a tent for the sun,'),
('LEB', 'Psalms', '19', '5', '1', 'and it <span class=\"supplied\">is</span> like a bridegroom  who comes out of his bridal chamber.  It is glad like a strong man  to run <span class=\"supplied\">its</span> course.'),
('LEB', 'Psalms', '19', '6', '1', 'Its rising <span class=\"supplied\">is</span> from <span class=\"supplied\">one</span><span class=\"note\">Hebrew “the”</span>end of the heavens  and its circuit to the <span class=\"supplied\">other</span> end<span class=\"note\">Hebrew “ends”</span>of them;  and nothing <span class=\"supplied\">is</span> hidden from its heat.'),
('LEB', 'Psalms', '19', '7', '1', 'The law of Adonai <span class=\"supplied\">is</span> perfect, reviving life.<span class=\"note\">Or “soul”</span> The testimony of Adonai <span class=\"supplied\">is</span> firm, making wise <span class=\"supplied\">the</span> simple.'),
('LEB', 'Psalms', '19', '8', '1', 'The precepts of Adonai <span class=\"supplied\">are</span> right, making <span class=\"supplied\">the</span> heart rejoice.  The command of Adonai <span class=\"supplied\">is</span> pure, enlightening <span class=\"supplied\">the</span> eyes.'),
('LEB', 'Psalms', '19', '9', '1', 'The fear of Adonai <span class=\"supplied\">is</span> pure, enduring forever.  The ordinances of Adonai <span class=\"supplied\">are</span> true; they are righteous altogether,'),
('LEB', 'Psalms', '19', '10', '1', 'more desirable than gold, even much fine gold;  and sweeter than honey, even honey <span class=\"supplied\">in the</span> comb.'),
('LEB', 'Psalms', '19', '11', '1', 'Moreover, your servant <span class=\"supplied\">is</span> warned<span class=\"note\">Or “illumined”</span>by them;  in keeping them <span class=\"supplied\">is</span> great reward.'),
('LEB', 'Psalms', '19', '12', '1', 'Who can perceive <span class=\"supplied\">his</span> errors?  Acquit me from hidden <span class=\"supplied\">faults</span>.'),
('LEB', 'Psalms', '19', '13', '1', 'Also, keep back your servant from arrogant <span class=\"supplied\">sins</span>;<span class=\"note\">Usage elsewhere might suggest “arrogant people”</span> let them not rule over me.  Then I shall be blameless,  and I shall be innocent of great transgression.'),
('LEB', 'Psalms', '19', '14', '1', 'Let the words of my mouth and the meditation of my heart  be acceptable in your presence,  O Adonai, my rock and my redeemer.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '20', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>May Adonai answer you in <span class=\"supplied\">the</span> day of trouble.  May the name of Jacob’s God protect<span class=\"note\">The sense is that of making something inaccessibly high, like a fortress</span>you.'),
('LEB', 'Psalms', '20', '2', '1', 'May he send you help<span class=\"note\">Hebrew “your help”</span>from <span class=\"supplied\">the</span> sanctuary,  and from Zion may he sustain you.'),
('LEB', 'Psalms', '20', '3', '1', 'May he remember all your offerings,  and your burnt offering may he <span class=\"idiom\">regard favorably</span>.<span class=\"note\">Literally “find <span class=\"supplied\">to be</span> fat”</span>'),
('LEB', 'Psalms', '20', '4', '1', 'May he give to you <span class=\"idiom\">your heart’s desire</span>,<span class=\"note\">Literally “according to your heart”</span> and your every plan may he fulfill. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '20', '5', '1', 'May we shout for joy over your victory,  and in the name of our God may we set up banners.  May Adonai fulfill all your requests.'),
('LEB', 'Psalms', '20', '6', '1', 'Now I know that Adonai will help his anointed.<span class=\"note\">Hebrew “messiah”</span> He will answer him from his holy heaven  with the victorious power of his right hand.'),
('LEB', 'Psalms', '20', '7', '1', '<span class=\"idiom\">Some boast in chariots and others in horses</span>,<span class=\"note\">Literally “These in the chariots and those in the horses”</span> but we boast in the name of Adonai, our God.'),
('LEB', 'Psalms', '20', '8', '1', 'They will collapse and fall,  and we will rise and stand firm.'),
('LEB', 'Psalms', '20', '9', '1', 'Rescue,<span class=\"note\">Or “Grant victory”</span>O Adonai.  Let the king answer us when we call.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '21', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>O Adonai, <span class=\"supplied\">the</span> king will rejoice in your strength,  and how greatly he will delight in your help.<span class=\"note\">Or “victory”</span>'),
('LEB', 'Psalms', '21', '2', '1', 'You have given him the desire of his heart,  and have not withheld the request of his lips. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '21', '3', '1', 'For you meet him <span class=\"supplied\">with</span> blessings of good <span class=\"supplied\">things</span>.  You set on his head a crown of fine gold.'),
('LEB', 'Psalms', '21', '4', '1', 'He asked life from you; you gave <span class=\"supplied\">it</span> to him—  length of days forever and ever.'),
('LEB', 'Psalms', '21', '5', '1', 'His honor <span class=\"supplied\">is</span> great because of your help.<span class=\"note\">Or “victory”</span> Splendor and majesty you have bestowed upon him.'),
('LEB', 'Psalms', '21', '6', '1', 'For you set <span class=\"supplied\">on</span> him blessings forever.  You make him glad with <span class=\"supplied\">the</span> joy <span class=\"supplied\">of</span> your presence.'),
('LEB', 'Psalms', '21', '7', '1', 'Because the king trusts Adonai,  and through the steadfast love<span class=\"note\">Or “loyal love”</span>of the Most High  he shall not be moved.'),
('LEB', 'Psalms', '21', '8', '1', 'Your hand will find all your enemies;  your right hand will find those who hate you.'),
('LEB', 'Psalms', '21', '9', '1', 'You will make them like your fiery furnace  at the time of your appearance.  Adonai will swallow them in his wrath  and fire will consume them.'),
('LEB', 'Psalms', '21', '10', '1', 'You will destroy their <span class=\"idiom\">offspring</span><span class=\"note\">Literally “fruit”</span>from <span class=\"supplied\">the</span> earth,  and their <span class=\"idiom\">descendants</span><span class=\"note\">Literally “seed”</span>from among <span class=\"supplied\">the</span> children of humankind.'),
('LEB', 'Psalms', '21', '11', '1', 'Though they have <span class=\"idiom\">plotted</span><span class=\"note\">Literally “spread out”</span>evil against you,  <span class=\"supplied\">though</span> they have planned a scheme, they will not prevail.'),
('LEB', 'Psalms', '21', '12', '1', 'For you will <span class=\"idiom\">turn them to flight</span>;<span class=\"note\">Literally “make them a shoulder”</span> you will aim <span class=\"supplied\">arrows</span> on your bowstrings at their faces.'),
('LEB', 'Psalms', '21', '13', '1', 'Be exalted, O Adonai, in your strength,  and we sing and praise your power.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '22', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director; according to The Doe of the Dawn. A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>My God, my God why have you forsaken me?  <span class=\"supplied\">Why are you</span> far from helping me, <span class=\"supplied\">far from</span> the words of my groaning?'),
('LEB', 'Psalms', '22', '2', '1', 'O my God, I call by day and you do not answer,  and <span class=\"supplied\">by</span> night but I have no rest.<span class=\"note\">Or “I am not silent”</span>'),
('LEB', 'Psalms', '22', '3', '1', 'Yet you <span class=\"supplied\">are</span> holy,  enthroned on the praises of Israel.'),
('LEB', 'Psalms', '22', '4', '1', 'Our ancestors<span class=\"note\">Or “fathers”</span>trusted you;  they trusted and you delivered them.'),
('LEB', 'Psalms', '22', '5', '1', 'They cried to you and were saved;  they trusted you and were not ashamed.'),
('LEB', 'Psalms', '22', '6', '1', 'But I <span class=\"supplied\">am</span> a worm and not a man,  scorned by humankind and despised by people.'),
('LEB', 'Psalms', '22', '7', '1', 'All <span class=\"supplied\">who</span> see me mock me.  They open wide <span class=\"supplied\">their</span> lips;  they shake <span class=\"supplied\">the</span> head, <span class=\"supplied\">saying</span>:'),
('LEB', 'Psalms', '22', '8', '1', '“<span class=\"idiom\">He trusts Adonai</span>.<span class=\"note\">Literally “Commit to Adonai”</span>Let him rescue him.  Let him deliver him because he delights in him.”'),
('LEB', 'Psalms', '22', '9', '1', 'Yet <span class=\"supplied\">you</span> took me from <span class=\"supplied\">the</span> belly;  you made me trust <span class=\"supplied\">while</span> on my mother’s breasts.'),
('LEB', 'Psalms', '22', '10', '1', 'On you I was cast from <span class=\"supplied\">the</span> womb.  From my mother’s belly you <span class=\"supplied\">have been</span> my God.'),
('LEB', 'Psalms', '22', '11', '1', 'Do not be far from me  because trouble <span class=\"supplied\">is</span> near;  because there is no helper.'),
('LEB', 'Psalms', '22', '12', '1', 'Many bulls have encircled me;  mighty <span class=\"supplied\">bulls</span> of Bashan have surrounded me.'),
('LEB', 'Psalms', '22', '13', '1', 'They open their mouth against me  <span class=\"supplied\">like</span> a lion tearing and roaring.'),
('LEB', 'Psalms', '22', '14', '1', 'I am poured out like water,  and all my bones are out of joint.  My heart is like wax;  it is melted <span class=\"idiom\">within me</span>.<span class=\"note\">Literally “in the midst of my intestines”</span>'),
('LEB', 'Psalms', '22', '15', '1', 'My strength is dry like a potsherd,  and my tongue is sticking <span class=\"supplied\">to</span> my jaws;  and you have placed me in <span class=\"supplied\">the</span> dust of death.'),
('LEB', 'Psalms', '22', '16', '1', 'Because dogs have surrounded me;  a gang of evildoers has encircled me.  Like the lion<span class=\"note\">Or “they gouge out” or “they bind.” Not the same word for “lion” found in vv. 14(13), 22(21). The Hebrew text presents such problems that any interpretation must be lightly held.</span><span class=\"supplied\">they are at</span> my hands and my feet.'),
('LEB', 'Psalms', '22', '17', '1', 'I can count all my bones;  they gaze, they look at me.'),
('LEB', 'Psalms', '22', '18', '1', 'They divide my garments among them,  and for my clothing they cast lots.<span class=\"note\">Hebrew “lot”</span>'),
('LEB', 'Psalms', '22', '19', '1', 'But you, O Adonai, do not remain distant.  O my help, hasten to help me.'),
('LEB', 'Psalms', '22', '20', '1', 'Rescue my life from <span class=\"supplied\">the</span> sword,  my only <span class=\"supplied\">life</span> from <span class=\"supplied\">the</span> power of <span class=\"supplied\">the</span> dogs.<span class=\"note\">Hebrew “dog”</span>'),
('LEB', 'Psalms', '22', '21', '1', 'Save me from <span class=\"supplied\">the</span> mouth of <span class=\"supplied\">the</span> lion,  and from <span class=\"supplied\">the</span> horns of <span class=\"supplied\">the</span> wild oxen answer me.'),
('LEB', 'Psalms', '22', '22', '1', 'I will tell your name to my brothers;  inside <span class=\"supplied\">the</span> assembly I will praise you.'),
('LEB', 'Psalms', '22', '23', '1', 'You who revere Adonai, praise him!  Glorify him, all <span class=\"supplied\">you</span> seed of Jacob,  and be in awe of him, all <span class=\"supplied\">you</span> seed of Israel,'),
('LEB', 'Psalms', '22', '24', '1', 'because he has not despised nor abhorred <span class=\"supplied\">the</span> affliction of <span class=\"supplied\">the</span> afflicted,  and has not hid his face from him;  but he listened to him when he cried for help.'),
('LEB', 'Psalms', '22', '25', '1', 'From you <span class=\"supplied\">is</span> my praise.  In <span class=\"supplied\">the</span> great assembly,  I will pay my vows before those who revere him.'),
('LEB', 'Psalms', '22', '26', '1', '<span class=\"supplied\">The</span> afflicted will eat and will be satisfied.  Those who seek him will praise Adonai.  May your<span class=\"note\">Plural</span>heart live forever.'),
('LEB', 'Psalms', '22', '27', '1', 'All <span class=\"supplied\">the</span> ends of <span class=\"supplied\">the</span> earth  will remember and turn to Adonai.  All <span class=\"supplied\">the</span> families of <span class=\"supplied\">the</span> nations  will worship before you.'),
('LEB', 'Psalms', '22', '28', '1', 'Because the kingship belongs to Adonai,  and <span class=\"supplied\">he</span> rules over the nations.'),
('LEB', 'Psalms', '22', '29', '1', 'All <span class=\"supplied\">the</span> <span class=\"idiom\">healthy ones</span><span class=\"note\">Or “prosperous”; Literally “fat ones”</span>of <span class=\"supplied\">the</span> earth will eat and worship.  Before him all <span class=\"supplied\">of</span> those descending <span class=\"supplied\">into the</span> dust will kneel,  even he <span class=\"supplied\">who</span> cannot keep his soul alive.'),
('LEB', 'Psalms', '22', '30', '1', 'Descendants<span class=\"note\">Hebrew “Descendant”</span>will serve him.  Regarding the Lord, it will be told to the <span class=\"supplied\">next</span> generation.'),
('LEB', 'Psalms', '22', '31', '1', 'They will come and tell his saving<span class=\"note\">Or “righteousness”</span><span class=\"supplied\">deeds</span>  to a people <span class=\"supplied\">yet</span> to be born, that he has done <span class=\"supplied\">it</span>.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '23', '1', '1', '<span class=\"verse-title\">A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Adonai is my shepherd;  I will not lack <span class=\"supplied\">for anything</span>.'),
('LEB', 'Psalms', '23', '2', '1', 'In grassy pastures he makes me lie down;  by quiet waters he leads me.'),
('LEB', 'Psalms', '23', '3', '1', 'He restores my life.<span class=\"note\">Or “my soul”</span> He leads me in <span class=\"idiom\">correct paths</span><span class=\"note\">Literally “paths of righteousness”</span> for the sake of his name.'),
('LEB', 'Psalms', '23', '4', '1', 'Even when I walk in a dark valley, I fear no evil  because you <span class=\"supplied\">are</span> with me.  Your rod and your staff, they comfort me.'),
('LEB', 'Psalms', '23', '5', '1', 'You prepare before me a table  in the presence of my oppressors.  You anoint my head with oil;  my cup <span class=\"supplied\">is</span> overflowing.'),
('LEB', 'Psalms', '23', '6', '1', 'Surely goodness and loyal love will pursue me  all the days of my life,  and I will stay<span class=\"note\">Hebrew “return”</span>in the house<span class=\"note\">Or “temple”</span>of Adonai  <span class=\"idiom\">for a very long time</span>.<span class=\"note\">Literally “for length of days”</span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '24', '1', '1', '<span class=\"verse-title\">A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>The earth is Adonai’s, with its fullness,  <span class=\"supplied\">the</span> world and those who live in it,'),
('LEB', 'Psalms', '24', '2', '1', 'because he has founded it on <span class=\"supplied\">the</span> seas,  and has established it on <span class=\"supplied\">the</span> rivers.'),
('LEB', 'Psalms', '24', '3', '1', 'Who may ascend <span class=\"supplied\">the</span> mountain of Adonai?  And who may stand in his holy place?'),
('LEB', 'Psalms', '24', '4', '1', 'He who is innocent of hands and pure of heart,  who does not lift up his soul to falseness,  and does not swear deceitfully.'),
('LEB', 'Psalms', '24', '5', '1', 'He will receive blessing from Adonai,  and justice<span class=\"note\">Or “vindication”</span>from the God of his salvation.'),
('LEB', 'Psalms', '24', '6', '1', 'Such <span class=\"supplied\">is the</span> sort of those who seek<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>him,  those who seek your face, even Jacob. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '24', '7', '1', 'Lift up your heads, O gates,  and rise up, O ancient doorways,  that the king of glory<span class=\"note\">Literally “the king of the glory”</span>may enter.'),
('LEB', 'Psalms', '24', '8', '1', 'Who <span class=\"supplied\">is</span> the king of glory?<span class=\"note\">Literally “the king of the glory”</span> Adonai, strong and mighty;  Adonai, mighty in war!'),
('LEB', 'Psalms', '24', '9', '1', 'Lift up your heads, O gates,  and lift up, O ancient doorways,  that the king of glory<span class=\"note\">Literally “the king of the glory”</span>may enter.'),
('LEB', 'Psalms', '24', '10', '1', 'Who is the king of glory?<span class=\"note\">Literally “the king of the glory”</span> Adonai of hosts,  He <span class=\"supplied\">is</span> the king of glory!<span class=\"note\">Literally “the king of the glory”</span><span class=\"italic\">Selah</span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '25', '1', '1', '<span class=\"verse-title\">Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>To you, O Adonai, I lift up my soul.'),
('LEB', 'Psalms', '25', '2', '1', 'O my God, I trust you; let me not be put to shame.  Do not let my enemies exult over me.'),
('LEB', 'Psalms', '25', '3', '1', 'Indeed, none who wait for you should be put to shame.  Those who betray without cause should be put to shame.'),
('LEB', 'Psalms', '25', '4', '1', 'Make me know your ways, O Adonai.  Teach me your paths.'),
('LEB', 'Psalms', '25', '5', '1', 'Cause me to walk in your truth and teach me,  because you <span class=\"supplied\">are</span> the God of my salvation.  I await you <span class=\"idiom\">all day long</span>.<span class=\"note\">Literally “all of the day”</span>'),
('LEB', 'Psalms', '25', '6', '1', 'Remember your compassion, O Adonai,  and your acts of loyal love,  because they <span class=\"supplied\">are</span> from of old.'),
('LEB', 'Psalms', '25', '7', '1', 'Do not remember  the sins of my youth or my transgressions.  According to your loyal love remember me <span class=\"supplied\">if</span> you <span class=\"supplied\">will</span>,  for the sake of your goodness, O Adonai.'),
('LEB', 'Psalms', '25', '8', '1', 'Good and right <span class=\"supplied\">is</span> Adonai;  therefore he instructs sinners in the way.'),
('LEB', 'Psalms', '25', '9', '1', 'He causes <span class=\"supplied\">the</span> humble to walk in justice,  and teaches <span class=\"supplied\">the</span> humble his way.'),
('LEB', 'Psalms', '25', '10', '1', 'All the paths of Adonai <span class=\"supplied\">are</span> loyal love and faithfulness  for those who keep his covenant and statutes.<span class=\"note\">Or “covenant statutes”</span>'),
('LEB', 'Psalms', '25', '11', '1', 'Also, for the sake of your name, O Adonai,  forgive my sin, because it <span class=\"supplied\">is</span> great.'),
('LEB', 'Psalms', '25', '12', '1', 'Who <span class=\"supplied\">is</span> the man fearing Adonai?  He will instruct him in <span class=\"supplied\">the</span> way he should choose.'),
('LEB', 'Psalms', '25', '13', '1', 'His soul will lodge in prosperity,  and his offspring will possess <span class=\"supplied\">the</span> land.'),
('LEB', 'Psalms', '25', '14', '1', 'Intimate fellowship with Adonai <span class=\"supplied\">is</span> for those who fear him,  and <span class=\"supplied\">he</span> makes known his covenant to them.'),
('LEB', 'Psalms', '25', '15', '1', 'My eyes <span class=\"supplied\">are</span> continually toward Adonai,  because he will take my feet from <span class=\"supplied\">the</span> net.'),
('LEB', 'Psalms', '25', '16', '1', 'Turn to me and have mercy on me  because I <span class=\"supplied\">am</span> lonely and afflicted.'),
('LEB', 'Psalms', '25', '17', '1', 'Remove the troubles of my heart;  bring me out from my distresses.'),
('LEB', 'Psalms', '25', '18', '1', 'Consider my affliction and trouble,  and forgive<span class=\"note\">Or “take away”</span>all my sins.'),
('LEB', 'Psalms', '25', '19', '1', 'Consider that my enemies are many,  and they hate me <span class=\"supplied\">with</span> violent hatred.'),
('LEB', 'Psalms', '25', '20', '1', 'Protect my life and deliver me.  Let me not be put to shame, because I take shelter in you.'),
('LEB', 'Psalms', '25', '21', '1', 'Let integrity and uprightness preserve me,  because I wait for you.'),
('LEB', 'Psalms', '25', '22', '1', 'O God, redeem Israel  from all its troubles.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '26', '1', '1', '<span class=\"verse-title\">Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Judge me, O Adonai, because I have walked in my integrity,  and I have trusted Adonai and not wavered.'),
('LEB', 'Psalms', '26', '2', '1', 'Prove me, O Adonai, and test me.  Try my <span class=\"idiom\">innermost being</span><span class=\"note\">Literally “kidneys,” the locus of intense thoughts and emotions in biblical Hebrew</span>and my <span class=\"idiom\">mind</span>.<span class=\"note\">Literally “heart”</span>'),
('LEB', 'Psalms', '26', '3', '1', 'Because your loyal love <span class=\"supplied\">is</span> before my eyes,  and I walk about in your faithfulness.'),
('LEB', 'Psalms', '26', '4', '1', 'I do not sit with deceitful people,  nor will I go about with hypocrites.'),
('LEB', 'Psalms', '26', '5', '1', 'I hate <span class=\"supplied\">the</span> crowd of evildoers,  and with <span class=\"supplied\">the</span> wicked I will not sit.'),
('LEB', 'Psalms', '26', '6', '1', 'I will wash my hands in innocence,  and I will walk about your altar, O Adonai,'),
('LEB', 'Psalms', '26', '7', '1', 'to declare with a voice of thanksgiving,  and tell of all your wondrous deeds.'),
('LEB', 'Psalms', '26', '8', '1', 'O Adonai, I love <span class=\"supplied\">the</span> dwelling of your house,<span class=\"note\">Or “temple”</span> and <span class=\"idiom\">the place where your glory abides</span>.<span class=\"note\">Literally “the place of the dwelling place of your glory”</span>'),
('LEB', 'Psalms', '26', '9', '1', 'Do not destroy me<span class=\"note\">Or “gather my soul <span class=\"supplied\">for judgment</span>”</span>with <span class=\"supplied\">the</span> sinners,  nor my life with men of bloodshed,'),
('LEB', 'Psalms', '26', '10', '1', 'in whose hands <span class=\"supplied\">is</span> an evil plan,  and whose right hand is full of bribes.<span class=\"note\">Hebrew “bribe”</span>'),
('LEB', 'Psalms', '26', '11', '1', 'But as for me, I will walk in my integrity.  Redeem me and be gracious to me.'),
('LEB', 'Psalms', '26', '12', '1', 'My foot stands on level ground.  In assemblies I will bless Adonai.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '27', '1', '1', '<span class=\"verse-title\">Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Adonai <span class=\"supplied\">is</span> my light and my salvation;  whom shall I fear?  Adonai <span class=\"supplied\">is</span> the refuge of my life;  of whom shall I be afraid?'),
('LEB', 'Psalms', '27', '2', '1', 'When evildoers drew near against me to eat my flesh—  my adversaries and my enemies <span class=\"supplied\">who drew near</span> to me—  they themselves stumbled and fell.'),
('LEB', 'Psalms', '27', '3', '1', 'Though an army encamp against me,  my heart will not fear.  Though war arise against me,  <span class=\"supplied\">even</span> in this I <span class=\"supplied\">will</span> remain confident.'),
('LEB', 'Psalms', '27', '4', '1', 'One <span class=\"supplied\">thing</span> I have asked from Adonai;  it I will seek:  <span class=\"supplied\">that</span> I may dwell in the house<span class=\"note\">Or “temple”</span>of Adonai all the days of my life,  to behold the beauty of Adonai,  and to consider<span class=\"note\">Or “inquire in”</span>his temple.'),
('LEB', 'Psalms', '27', '5', '1', 'Because he will hide me in his shelter in <span class=\"supplied\">the</span> day of trouble.  He will conceal me in the hiding place of his tent.  He will set me high upon a rock.'),
('LEB', 'Psalms', '27', '6', '1', 'And now my head will be high over my enemies round about me.  And I will offer in his tent sacrifices with shouts of joy.  I will sing, yes, I will sing praises to Adonai.'),
('LEB', 'Psalms', '27', '7', '1', 'Hear, O Adonai, my voice <span class=\"supplied\">when</span> I call,  and be gracious to me and answer me.'),
('LEB', 'Psalms', '27', '8', '1', 'On your behalf my heart says, “Seek my face.”  Your face, O Adonai, I do seek.'),
('LEB', 'Psalms', '27', '9', '1', 'Do not hide your face from me;  do not turn your servant away in anger.  You have been my help; do not abandon nor forsake me,  O God of my salvation.'),
('LEB', 'Psalms', '27', '10', '1', 'If my father or my mother forsake me,  then Adonai will receive me.'),
('LEB', 'Psalms', '27', '11', '1', 'Teach me, O Adonai, your way,  and lead me on a level path because of my enemies.'),
('LEB', 'Psalms', '27', '12', '1', 'Do not give me over to the desire of my enemies,  because false witnesses have arisen against me,  and <span class=\"supplied\">each</span> breathing out violence.<span class=\"note\">Or “and <span class=\"supplied\">each</span> a witness of violence”</span>'),
('LEB', 'Psalms', '27', '13', '1', 'Surely<span class=\"note\">Hebrew text is uncertain</span>I believe <span class=\"supplied\">that I will</span> see the goodness of Adonai  in <span class=\"supplied\">the</span> land of <span class=\"supplied\">the</span> living.'),
('LEB', 'Psalms', '27', '14', '1', 'Wait for Adonai.  Be strong and let your heart show strength,  and wait for Adonai.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '28', '1', '1', '<span class=\"verse-title\">A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>To you, O Adonai, I call.  O my rock, do not be deaf to me.  Or else, if you are silent to me,  then I will become like those descending to <span class=\"supplied\">the</span> pit.'),
('LEB', 'Psalms', '28', '2', '1', 'Hear the voice of my supplications  when I cry to you for help,  when I lift up my hands  toward your holy inner sanctuary.'),
('LEB', 'Psalms', '28', '3', '1', 'Do not drag me away with <span class=\"supplied\">the</span> wicked  or with <span class=\"supplied\">the</span> workers of evil,  <span class=\"supplied\">who</span> speak of peace with their neighbors,  while evil <span class=\"supplied\">is</span> in their heart.'),
('LEB', 'Psalms', '28', '4', '1', 'Give to them according to their work,  even according to the evil of their deeds.  Give to them according to the work of their hands;  repay them their rightful due.'),
('LEB', 'Psalms', '28', '5', '1', 'Because they do not regard the works of Adonai,  nor the work of his hands,  he will destroy them  and not rebuild them.'),
('LEB', 'Psalms', '28', '6', '1', 'Blessed <span class=\"supplied\">is</span> Adonai,  because he has heard the voice of my supplications.'),
('LEB', 'Psalms', '28', '7', '1', 'Adonai <span class=\"supplied\">is</span> my strength and my shield.  My heart trusts him and I am helped.  So my heart rejoices,  and with my song I will give thanks to him.'),
('LEB', 'Psalms', '28', '8', '1', 'Adonai <span class=\"supplied\">is</span> their strength,  and he <span class=\"supplied\">is</span> the refuge for the salvation of his anointed one.'),
('LEB', 'Psalms', '28', '9', '1', 'Save your people  and bless your heritage.  Shepherd them also and carry them always.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '29', '1', '1', '<span class=\"verse-title\">A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Ascribe to Adonai, O sons of God,<span class=\"note\">Or “heavenly beings”</span> ascribe to Adonai glory and strength.'),
('LEB', 'Psalms', '29', '2', '1', 'Ascribe to Adonai the glory <span class=\"idiom\">due</span><span class=\"note\">Literally “of”</span>his name.  Worship Adonai in holy array.'),
('LEB', 'Psalms', '29', '3', '1', 'The voice of Adonai <span class=\"supplied\">is</span> over the waters;  the God of glory thunders.  Adonai <span class=\"supplied\">is</span> over many waters.'),
('LEB', 'Psalms', '29', '4', '1', 'The voice of Adonai <span class=\"supplied\">is</span> powerful.  The voice of Adonai <span class=\"supplied\">is</span> majestic.'),
('LEB', 'Psalms', '29', '5', '1', 'The voice of Adonai breaks <span class=\"supplied\">the</span> cedars.  Adonai shatters even the cedars of Lebanon.'),
('LEB', 'Psalms', '29', '6', '1', 'And he makes them skip like a calf;  Lebanon and Sirion like a young wild ox.'),
('LEB', 'Psalms', '29', '7', '1', 'The voice of Adonai flashes<span class=\"note\">Or “divides”</span>flames of fire.'),
('LEB', 'Psalms', '29', '8', '1', 'The voice of Adonai shakes <span class=\"supplied\">the</span> wilderness.  Adonai shakes the wilderness of Kadesh.'),
('LEB', 'Psalms', '29', '9', '1', 'The voice of Adonai causes deer<span class=\"note\">A slightly different Hebrew word here yields, “makes the oaks tremble”</span>to give birth  and strips <span class=\"supplied\">the</span> forests bare.  And at his temple all in it say, “Glory!”'),
('LEB', 'Psalms', '29', '10', '1', 'Adonai sits <span class=\"supplied\">enthroned</span> at the flood,  and Adonai sits <span class=\"supplied\">as</span> king forever.'),
('LEB', 'Psalms', '29', '11', '1', 'May Adonai give strength to his people.  May Adonai bless his people with peace.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '30', '1', '1', '<span class=\"verse-title\">A psalm; a song for the dedication of the house.<span class=\"note\">Or “temple”</span>Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>I will exalt you, O Adonai,  because you have drawn me up,  and have not made my enemies rejoice over me.'),
('LEB', 'Psalms', '30', '2', '1', 'O Adonai, my God,  I cried for help to you,  and you healed me.'),
('LEB', 'Psalms', '30', '3', '1', 'O Adonai, you have brought my life up from Sheol.  You preserved me alive  from among those going down <span class=\"note\">According to the <span class=\"italic\">Kethib</span>; the reading tradition (<span class=\"italic\">Qere</span>) reads “so I would not go down”</span>to <span class=\"supplied\">the</span> pit.'),
('LEB', 'Psalms', '30', '4', '1', 'Sing praises to Adonai, you his faithful ones,  and give thanks <span class=\"idiom\">to his holy fame</span>.<span class=\"note\">Literally “to the fame of his holiness”</span>'),
('LEB', 'Psalms', '30', '5', '1', 'For <span class=\"supplied\">there is</span> a moment in his anger;  <span class=\"supplied\">there is</span> a lifetime in his favor.  Weeping lodges for the evening,  but in the morning <span class=\"supplied\">comes</span> rejoicing.'),
('LEB', 'Psalms', '30', '6', '1', 'But as for me, I had said in my prosperity,  “I shall not be moved ever.”'),
('LEB', 'Psalms', '30', '7', '1', 'O Adonai, by your favor  you caused my strong mountain to stand.  You hid your face. I was bewildered.'),
('LEB', 'Psalms', '30', '8', '1', 'To you, O Adonai, I called,  and to the Lord I pleaded for grace <span class=\"supplied\">saying</span>,'),
('LEB', 'Psalms', '30', '9', '1', '“What gain <span class=\"supplied\">is there</span> in my <span class=\"idiom\">death</span>,<span class=\"note\">Literally “blood”</span> in my going down into <span class=\"supplied\">the</span> pit?  Will <span class=\"supplied\">the</span> dust praise you?  Will it tell of your faithfulness?'),
('LEB', 'Psalms', '30', '10', '1', '“O Adonai, hear and be gracious to me.  O Adonai, be my helper.”'),
('LEB', 'Psalms', '30', '11', '1', 'You have turned my wailing into my dancing.  You have removed my sackcloth  and clothed me <span class=\"supplied\">with</span> joy'),
('LEB', 'Psalms', '30', '12', '1', 'so that <span class=\"idiom\">I</span><span class=\"note\">Literally “my glory”</span>may sing praises to you  and not be quiet.  O Adonai, my God,  I will give thanks to you forever.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '31', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>In you, O Adonai, I have taken refuge.  Let me not be put to shame ever.  Deliver me by your righteousness.'),
('LEB', 'Psalms', '31', '2', '1', 'Incline your ear to me.  Quickly deliver me.  Become my rock of refuge,  <span class=\"idiom\">a fortified keep</span><span class=\"note\">Literally “a house of strongholds”</span>to save me.'),
('LEB', 'Psalms', '31', '3', '1', 'For you <span class=\"supplied\">are</span> my rock and my fortress.  So, for the sake of your name,  lead me and guide me.'),
('LEB', 'Psalms', '31', '4', '1', 'Bring me out of <span class=\"supplied\">the</span> net that they have secretly set for me,  for you <span class=\"supplied\">are</span> my refuge.'),
('LEB', 'Psalms', '31', '5', '1', 'Into your hand I commit my spirit.  You have redeemed me, O Adonai, faithful God.<span class=\"note\">Or “God of truth”</span>'),
('LEB', 'Psalms', '31', '6', '1', 'I hate those devoted to useless idols,  but I trust Adonai.'),
('LEB', 'Psalms', '31', '7', '1', 'I will exult and rejoice in your loyal love.  Because you have seen my misery,  you know the distresses of my life.<span class=\"note\">Or “soul”</span>'),
('LEB', 'Psalms', '31', '8', '1', 'And you have not delivered me  into <span class=\"supplied\">the</span> hand of <span class=\"supplied\">the</span> enemy.  You have set my feet in a broad place.'),
('LEB', 'Psalms', '31', '9', '1', 'Be gracious <span class=\"supplied\">to</span> me, O Adonai,  because I have distress.  My eye wastes away because of vexation,  <span class=\"supplied\">along with</span> my soul and my <span class=\"idiom\">body</span>.<span class=\"note\">Literally “belly”</span>'),
('LEB', 'Psalms', '31', '10', '1', 'For my life is at an end with sorrow,  and my years with sighing.  My strength stumbles because of my iniquity,  and my bones waste away.'),
('LEB', 'Psalms', '31', '11', '1', 'Because of all my adversaries I have become a disgrace,  especially to my neighbors,  and a dread to my acquaintances.  <span class=\"supplied\">Those who</span> see me in the street flee from me.'),
('LEB', 'Psalms', '31', '12', '1', 'I have become forgotten like <span class=\"supplied\">one</span> dead, out of <span class=\"idiom\">mind</span>.<span class=\"note\">Literally “heart”</span> I am like a destroyed vessel.'),
('LEB', 'Psalms', '31', '13', '1', 'For I hear <span class=\"supplied\">the</span> rumor of many,  “Terror on every side!”  When conspiring together against me,  they have plotted to take my life.'),
('LEB', 'Psalms', '31', '14', '1', 'But as for me, I trust you, O Adonai.  I say, “You <span class=\"supplied\">are</span> my God.”'),
('LEB', 'Psalms', '31', '15', '1', 'My times<span class=\"note\">Or “destiny”</span><span class=\"supplied\">are</span> in your hand.  Deliver me from the hand of my enemies  and from <span class=\"supplied\">those who</span> pursue me.'),
('LEB', 'Psalms', '31', '16', '1', 'Shine your face upon your servant.  Save me by your loyal love.'),
('LEB', 'Psalms', '31', '17', '1', 'O Adonai, let me not be put to shame, for I call on you.  Let the wicked be put to shame.  Let them <span class=\"supplied\">go</span> silently<span class=\"note\">Or “let them <span class=\"supplied\">with</span> wailing <span class=\"supplied\">enter</span>”</span>to Sheol.'),
('LEB', 'Psalms', '31', '18', '1', 'Let lying lips be dumb,  that speak against <span class=\"supplied\">the</span> righteous<span class=\"note\">Or “the innocent one”</span>unrestrained  with arrogance and contempt.'),
('LEB', 'Psalms', '31', '19', '1', 'How abundant <span class=\"supplied\">is</span> your goodness  that you have stored up for <span class=\"supplied\">those who</span> fear you,  that you perform for those who take refuge in you  before <span class=\"supplied\">the</span> children of humankind.'),
('LEB', 'Psalms', '31', '20', '1', 'You will hide them in the protection of your presence  from <span class=\"supplied\">the</span> plots of man.  You will hide them in a shelter from <span class=\"supplied\">the</span> strife of tongues.'),
('LEB', 'Psalms', '31', '21', '1', 'Blessed <span class=\"supplied\">is</span> Adonai,  because he has worked marvelously his loyal love to me  in a besieged city.'),
('LEB', 'Psalms', '31', '22', '1', 'As for me, I said in my alarm,  “I am cut off from before your eyes.”  However you heard the voice of my supplications  when I cried to you for help.'),
('LEB', 'Psalms', '31', '23', '1', 'Love Adonai, all you his faithful ones.  Adonai preserves <span class=\"supplied\">the</span> faithful  but repays abundantly <span class=\"supplied\">the</span> one who acts arrogantly.'),
('LEB', 'Psalms', '31', '24', '1', 'Be strong and let your<span class=\"note\">Plural</span>heart show strength,  all <span class=\"supplied\">you</span> who wait for Adonai.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '32', '1', '1', '<span class=\"verse-title\">Of David. A maskil.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Happy <span class=\"supplied\">is</span> he whose transgression is taken away,  whose sin is covered.'),
('LEB', 'Psalms', '32', '2', '1', 'Happy <span class=\"supplied\">is</span> a person to whom Adonai does not impute iniquity  and in whose spirit there is not deceit.'),
('LEB', 'Psalms', '32', '3', '1', 'When I kept silent, my bones were worn out  due to my groaning all the day.'),
('LEB', 'Psalms', '32', '4', '1', 'For day and night your hand was heavy upon me.  My vigor was changed into <span class=\"supplied\">the</span> dry heat of summer. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '32', '5', '1', 'I made known my sin to you, and my iniquity I did not cover.  I said, “I will confess concerning my transgressions to Adonai,”  and you took away the guilt of my sin. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '32', '6', '1', 'Therefore let all <span class=\"supplied\">the</span> faithful pray to you  at <span class=\"supplied\">the</span> time <span class=\"supplied\">for</span> finding <span class=\"supplied\">you</span>.  Surely at <span class=\"supplied\">the</span> flood of many waters they will not reach him.'),
('LEB', 'Psalms', '32', '7', '1', 'You <span class=\"supplied\">are</span> my hiding place;  from trouble you preserve me.  <span class=\"supplied\">With</span> cries of deliverance you surround me. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '32', '8', '1', 'I<span class=\"note\">Adonai is speaking in vv. 8–9</span>will instruct you and teach you  in <span class=\"supplied\">the</span> way that you should go.  I will advise <span class=\"supplied\">you</span> <span class=\"supplied\">with</span> my eye upon you.'),
('LEB', 'Psalms', '32', '9', '1', 'Do not be like a horse <span class=\"supplied\">or</span> like a mule, without understanding;  <span class=\"supplied\">that needs</span> his tackle—bridle and rein—for restraint  <span class=\"supplied\">or he would not</span> come near you.'),
('LEB', 'Psalms', '32', '10', '1', 'Many are <span class=\"supplied\">the</span> pains of the wicked,  but <span class=\"supplied\">for</span> the one who trusts Adonai  loyal love surrounds him.'),
('LEB', 'Psalms', '32', '11', '1', 'Be glad in Adonai and rejoice, <span class=\"supplied\">you</span> righteous,  and shout for joy, all <span class=\"supplied\">you</span> upright of heart.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '33', '1', '1', '<span class=\"verse-title\">Praise to Adonai for His Character and Creation</span>Exult in Adonai, O <span class=\"supplied\">you</span> righteous,  <span class=\"supplied\">for</span> praise <span class=\"supplied\">is</span> fitting for the upright.'),
('LEB', 'Psalms', '33', '2', '1', 'Give thanks to Adonai with <span class=\"supplied\">the</span> lyre;  with a harp of ten strings play to him.'),
('LEB', 'Psalms', '33', '3', '1', 'Sing to him a new song;  play skillfully with jubilant shout.'),
('LEB', 'Psalms', '33', '4', '1', 'For the word of Adonai <span class=\"supplied\">is</span> upright,  and all his work <span class=\"supplied\">is done</span> in faithfulness.'),
('LEB', 'Psalms', '33', '5', '1', '<span class=\"supplied\">He</span> loves righteousness and justice.  The earth is full of the loyal love of Adonai.'),
('LEB', 'Psalms', '33', '6', '1', 'By the word of Adonai <span class=\"supplied\">the</span> heavens were made,  and by the breath of his mouth all their host.'),
('LEB', 'Psalms', '33', '7', '1', '<span class=\"supplied\">He</span> gathered the waters of the sea like a heap.  <span class=\"supplied\">He</span> put <span class=\"supplied\">the</span> deeps in storehouses.'),
('LEB', 'Psalms', '33', '8', '1', 'Let all the earth fear Adonai.  Let all <span class=\"supplied\">the</span> inhabitants of <span class=\"supplied\">the</span> world stand in awe of him.'),
('LEB', 'Psalms', '33', '9', '1', 'For <span class=\"idiom\">he himself spoke</span><span class=\"note\">Literally “he, he spoke”</span>and it came to pass.  <span class=\"idiom\">He himself commanded</span><span class=\"note\">Literally “He, he commanded”</span>and it stood firm.'),
('LEB', 'Psalms', '33', '10', '1', 'Adonai brings to nothing<span class=\"note\">Or “breaks”</span><span class=\"supplied\">the</span> plan of nations;  he frustrates<span class=\"note\">Or “nullifies”</span><span class=\"supplied\">the</span> intents of peoples.'),
('LEB', 'Psalms', '33', '11', '1', 'The plan of Adonai stands firm forever,  the intents of his heart <span class=\"idiom\">from one generation to the next</span>.<span class=\"note\">Literally “to a generation and a generation”</span>'),
('LEB', 'Psalms', '33', '12', '1', 'Blessed <span class=\"supplied\">is</span> the nation whose God <span class=\"supplied\">is</span> Adonai,  the people he has chosen for his inheritance.'),
('LEB', 'Psalms', '33', '13', '1', 'From heaven Adonai looks;  he sees all the children of humankind.'),
('LEB', 'Psalms', '33', '14', '1', 'From his dwelling place<span class=\"note\">Or “the place where he sits enthroned”</span>he gazes  on all the inhabitants of the earth,'),
('LEB', 'Psalms', '33', '15', '1', 'he who fashions altogether their heart,  he who understands all their works.'),
('LEB', 'Psalms', '33', '16', '1', 'The king <span class=\"supplied\">is</span> not saved by <span class=\"supplied\">the</span> greatness of an army.  The warrior is not delivered by <span class=\"supplied\">the</span> greatness of strength.'),
('LEB', 'Psalms', '33', '17', '1', 'The horse <span class=\"supplied\">is</span> a false hope for victory,  nor can it save by the greatness of its power.'),
('LEB', 'Psalms', '33', '18', '1', 'Behold, the eye of Adonai <span class=\"supplied\">is</span> on those who fear him,  on those who hope<span class=\"note\">Or “wait expectantly”</span>for his loyal love'),
('LEB', 'Psalms', '33', '19', '1', 'to deliver their soul from death  and to keep them alive in famine.'),
('LEB', 'Psalms', '33', '20', '1', 'Our soul waits for Adonai;  he <span class=\"supplied\">is</span> our help and our shield.'),
('LEB', 'Psalms', '33', '21', '1', 'For in him our heart rejoices  because in his holy name we trust.'),
('LEB', 'Psalms', '33', '22', '1', 'Let your loyal love, O Adonai, be upon us,  <span class=\"idiom\">even as</span><span class=\"note\">Literally “as which”</span>we hope in you.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '34', '1', '1', '<span class=\"verse-title\">Of David, when he <span class=\"idiom\">pretended to be crazy</span><span class=\"note\">Literally “he changed his discernment”</span>in front of Abimelech<span class=\"note\">Literally “my father is king” whereas 1 Sam 21:10(9) has “Achish.” “Abimelech” is perhaps an honorific title, since its literal meaning suggests a legitimate right to rule</span> so that he drove him out, and <span class=\"supplied\">so</span> he departed.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>I will bless Adonai at all times;  his praise <span class=\"supplied\">shall be</span> in my mouth continually.'),
('LEB', 'Psalms', '34', '2', '1', 'My soul makes its boast in Adonai;  let the humble hear and be glad.'),
('LEB', 'Psalms', '34', '3', '1', 'Magnify Adonai with me,  and let us exalt his name together.'),
('LEB', 'Psalms', '34', '4', '1', 'I sought Adonai and he answered me,  and from all my terrors he delivered me.'),
('LEB', 'Psalms', '34', '5', '1', 'They looked to him and were radiant,  and their faces shall not be ashamed.<span class=\"note\">Or “and let not their faces be ashamed”</span>'),
('LEB', 'Psalms', '34', '6', '1', 'This poor <span class=\"supplied\">man</span> called and Adonai heard,  and saved him from all his troubles.'),
('LEB', 'Psalms', '34', '7', '1', 'The angel of Adonai encamps  around <span class=\"supplied\">those who</span> fear him,  and he rescues them.'),
('LEB', 'Psalms', '34', '8', '1', 'Taste and see that Adonai <span class=\"supplied\">is</span> good;  blessed <span class=\"supplied\">is</span> the man <span class=\"supplied\">who</span> takes refuge in him.'),
('LEB', 'Psalms', '34', '9', '1', 'Fear Adonai, <span class=\"supplied\">you</span> his saints,<span class=\"note\">Or “holy ones”</span> for there is no lack for <span class=\"supplied\">those who</span> fear him.'),
('LEB', 'Psalms', '34', '10', '1', '<span class=\"supplied\">The</span> young lions are in want and suffer hunger,  but those who seek Adonai will not lack for any good thing.'),
('LEB', 'Psalms', '34', '11', '1', 'Come, <span class=\"supplied\">you</span> children, listen to me;  I will teach you the fear of Adonai.'),
('LEB', 'Psalms', '34', '12', '1', 'Who <span class=\"supplied\">is</span> the man who desires life,  who loves <span class=\"supplied\">many</span> days in order to see good?'),
('LEB', 'Psalms', '34', '13', '1', 'Keep your tongue from evil  and your lips from speaking deceit.'),
('LEB', 'Psalms', '34', '14', '1', 'Turn from evil and do good.  Seek peace and pursue it.'),
('LEB', 'Psalms', '34', '15', '1', 'The eyes of Adonai <span class=\"supplied\">are</span> toward <span class=\"supplied\">the</span> righteous,  and his ears toward their cry for help.'),
('LEB', 'Psalms', '34', '16', '1', 'The face of Adonai <span class=\"supplied\">is</span> against evildoers,  to cut off the remembrance of them from <span class=\"supplied\">the</span> earth.'),
('LEB', 'Psalms', '34', '17', '1', 'They<span class=\"note\">That is, the righteous</span>cry out and Adonai hears  and delivers them from all <span class=\"supplied\">of</span> their troubles.'),
('LEB', 'Psalms', '34', '18', '1', 'Adonai <span class=\"supplied\">is</span> near to <span class=\"supplied\">those who are</span> heartbroken  and saves <span class=\"supplied\">those who are</span> crushed in spirit.'),
('LEB', 'Psalms', '34', '19', '1', 'Many <span class=\"supplied\">are the</span> distresses of <span class=\"supplied\">the</span> righteous,  but Adonai delivers him out of them all.'),
('LEB', 'Psalms', '34', '20', '1', '<span class=\"supplied\">He</span> protects all his bones;  not one of them is broken.'),
('LEB', 'Psalms', '34', '21', '1', 'Evil will slay <span class=\"supplied\">the</span> wicked,  and <span class=\"supplied\">those who</span> hate <span class=\"supplied\">the</span> righteous will incur guilt.'),
('LEB', 'Psalms', '34', '22', '1', 'Adonai redeems the life of his servants,  and none who take refuge in him will incur guilt.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '35', '1', '1', '<span class=\"verse-title\">Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Contend, O Adonai, with my contenders;  fight <span class=\"supplied\">those who</span> fight me.'),
('LEB', 'Psalms', '35', '2', '1', 'Grasp buckler and shield  and rise to my aid.'),
('LEB', 'Psalms', '35', '3', '1', 'And draw <span class=\"supplied\">the</span> spear and javelin to meet <span class=\"supplied\">those who</span> pursue me.  Say to my soul, “I <span class=\"supplied\">am</span> your salvation.”'),
('LEB', 'Psalms', '35', '4', '1', 'Let those who seek my life be shamed and humiliated.  Let those who plot calamity against me be repulsed and ashamed.'),
('LEB', 'Psalms', '35', '5', '1', 'Let them be like chaff before <span class=\"supplied\">the</span> wind,  with the angel of Adonai <span class=\"idiom\">driving them</span>.<span class=\"note\">Literally “pushing”</span>'),
('LEB', 'Psalms', '35', '6', '1', 'Let their way be dark and slippery,  with the angel of Adonai pursuing them.'),
('LEB', 'Psalms', '35', '7', '1', 'For without cause they secretly hide the pit with their net for me;  without cause they dug <span class=\"supplied\">it</span> for my life.'),
('LEB', 'Psalms', '35', '8', '1', 'Let <span class=\"idiom\">unforeseen ruin</span><span class=\"note\">Literally “ruin he does not know”</span>come on him,  and his net that he hid, let it catch him.  Let him fall into it in ruin.'),
('LEB', 'Psalms', '35', '9', '1', 'Then my soul will rejoice in Adonai;  it will rejoice in his salvation.'),
('LEB', 'Psalms', '35', '10', '1', 'All of my bones shall say, “O Adonai, who <span class=\"supplied\">is</span> like you,  who delivers <span class=\"supplied\">the</span> poor from <span class=\"supplied\">one</span> stronger than he  and <span class=\"supplied\">the</span> poor and needy from <span class=\"supplied\">the one who</span> robs him?”'),
('LEB', 'Psalms', '35', '11', '1', 'Violent witnesses rise up;  they ask me <span class=\"supplied\">concerning</span> what I do not know.'),
('LEB', 'Psalms', '35', '12', '1', 'They repay me evil in place of good.  <span class=\"supplied\">It is</span> bereavement to my soul.'),
('LEB', 'Psalms', '35', '13', '1', 'But as for me, when they were sick, my clothing <span class=\"supplied\">was</span> sackcloth.  I weakened my soul with fasting,  and my prayer <span class=\"idiom\">returned to me unanswered</span>.<span class=\"note\">Literally “upon my lap it will return”</span>'),
('LEB', 'Psalms', '35', '14', '1', 'I <span class=\"idiom\">behaved</span><span class=\"note\">Literally “walked about”</span>as <span class=\"supplied\">though he were</span> a friend <span class=\"supplied\">or</span> as a brother to me.  As one lamenting a mother, I was bowed down <span class=\"supplied\">in</span> mourning.'),
('LEB', 'Psalms', '35', '15', '1', 'But at my stumbling they rejoiced and gathered together;  smiters whom I did not know gathered against me.  They tore and did not cease.'),
('LEB', 'Psalms', '35', '16', '1', 'Among <span class=\"supplied\">the</span> ungodly of the mockers at feasts,<span class=\"note\">With a slight change the Hebrew might read, “The ungodly boldly mocked”</span> <span class=\"supplied\">they</span> gnashed at me with their teeth.'),
('LEB', 'Psalms', '35', '17', '1', 'My Lord, how long will you watch?  Restore my life from their ravages,<span class=\"note\">Or “roaring”</span> my only <span class=\"supplied\">life</span> from <span class=\"supplied\">the</span> young lions.'),
('LEB', 'Psalms', '35', '18', '1', 'I will give thanks <span class=\"supplied\">to</span> you in <span class=\"supplied\">the</span> great assembly;  among <span class=\"supplied\">the</span> mighty people I will praise you.'),
('LEB', 'Psalms', '35', '19', '1', 'Let not those who <span class=\"supplied\">are</span> wrongfully my enemies<span class=\"note\">Or “those who oppose me <span class=\"supplied\">with</span> falsehood”</span>rejoice over me.  <span class=\"supplied\">Nor let</span> those who hate me without cause wink <span class=\"supplied\">the</span> eye.'),
('LEB', 'Psalms', '35', '20', '1', 'For they do not speak peace,  but against <span class=\"supplied\">the</span> quiet <span class=\"supplied\">ones</span> of <span class=\"supplied\">the</span> land  they plan deceitful words.'),
('LEB', 'Psalms', '35', '21', '1', 'They also made wide their mouths<span class=\"note\">Hebrew “mouth”</span>against me.  They said, “Aha! Aha!  Our eyes have seen <span class=\"supplied\">it</span>.”'),
('LEB', 'Psalms', '35', '22', '1', 'You have seen, O Adonai. Do not be deaf.  O Lord, do not be far from me.'),
('LEB', 'Psalms', '35', '23', '1', 'Wake up and rouse yourself for my right,  for my cause, O my God and my Lord.'),
('LEB', 'Psalms', '35', '24', '1', 'Vindicate me according to your righteousness,  O Adonai my God,  and do not let them rejoice over me.'),
('LEB', 'Psalms', '35', '25', '1', 'Do not let them say in their hearts,<span class=\"note\">Hebrew “heart”</span>“Aha, our desire.”  Let them not say, “We have swallowed him up.”'),
('LEB', 'Psalms', '35', '26', '1', 'Let them be shamed and abashed altogether,  who rejoice at my misfortune.  Let them put on shame and insult,  who magnify themselves against me.'),
('LEB', 'Psalms', '35', '27', '1', 'Let them shout for joy and be glad,  who delight in my vindication;  and let them say continually, “Adonai is great,  who delights in the welfare of his servant.”'),
('LEB', 'Psalms', '35', '28', '1', 'Then my tongue will proclaim your righteousness,  <span class=\"supplied\">and</span> your praise all day.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '36', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. Of David, the servant of Adonai.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>An oracle: the wicked has rebellion  in the midst of his<span class=\"note\">Reading with a few Hebrew manuscripts and some versions; MT reads “my”</span>heart.  There is no fear of God before his eyes.'),
('LEB', 'Psalms', '36', '2', '1', 'For he flatters himself in his eyes,  hating to detect his iniquity.<span class=\"note\">Or “when finding his iniquity and when hating”</span>'),
('LEB', 'Psalms', '36', '3', '1', 'The words of his mouth <span class=\"supplied\">are</span> wickedness and deceit.  He has ceased to have insight <span class=\"supplied\">and</span> to do good.'),
('LEB', 'Psalms', '36', '4', '1', 'He plans sin on his bed.  He puts himself on a way <span class=\"supplied\">that is</span> not good.  He does not reject evil.'),
('LEB', 'Psalms', '36', '5', '1', 'O Adonai, your loyal love <span class=\"supplied\">extends</span> into <span class=\"supplied\">the</span> heavens,  your faithfulness unto <span class=\"supplied\">the</span> clouds.'),
('LEB', 'Psalms', '36', '6', '1', 'Your righteousness <span class=\"supplied\">is</span> <span class=\"idiom\">like the mighty mountains</span>,<span class=\"note\">Literally “like the mountains of God”</span> your judgments <span class=\"supplied\">like the</span> great deep.  You save man and beast, O Adonai.'),
('LEB', 'Psalms', '36', '7', '1', 'How precious <span class=\"supplied\">is</span> your loyal love, O God,  and the children of humankind  take refuge in the shadow of your wings.'),
('LEB', 'Psalms', '36', '8', '1', 'They are refreshed with the <span class=\"idiom\">fullness</span><span class=\"note\">Literally “fatness”</span>of your house,<span class=\"note\">Or “temple”</span> and you give them drink <span class=\"supplied\">from</span> the river of your delights.'),
('LEB', 'Psalms', '36', '9', '1', 'For with you is <span class=\"supplied\">the</span> fountain of life;  in your light we see light.'),
('LEB', 'Psalms', '36', '10', '1', 'Prolong your loyal love to those who know you,  and your righteousness to <span class=\"supplied\">the</span> upright of heart.'),
('LEB', 'Psalms', '36', '11', '1', 'Do not let a foot of pride come against me,  nor let a wicked hand make me to wander homeless.'),
('LEB', 'Psalms', '36', '12', '1', 'There doers of evil have fallen;  they are thrust down and not able to rise.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '37', '1', '1', '<span class=\"verse-title\">Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Do not fret because of evildoers;  do not be envious of doers of wickedness.'),
('LEB', 'Psalms', '37', '2', '1', 'For like the grass they will dry up quickly,  and like green vegetation they will wither.'),
('LEB', 'Psalms', '37', '3', '1', 'Trust Adonai and do good.  Abide <span class=\"supplied\">in the</span> land and feed on faithfulness.<span class=\"note\">That is, God’s faithfulness</span>'),
('LEB', 'Psalms', '37', '4', '1', 'Take pleasure in Adonai as well,  and he will give to you the requests of your heart.'),
('LEB', 'Psalms', '37', '5', '1', '<span class=\"idiom\">Commit to</span><span class=\"note\">Literally “Roll upon”</span>Adonai your way;  Trust also on him and he will act.'),
('LEB', 'Psalms', '37', '6', '1', 'Then he will bring forth your righteousness like the light,  and your justice like the noonday.'),
('LEB', 'Psalms', '37', '7', '1', 'Be quiet before Adonai and wait for him.  Do not fret about one who succeeds <span class=\"supplied\">in</span> his way,  about a man making plots.'),
('LEB', 'Psalms', '37', '8', '1', 'Refrain from anger and forsake wrath.  Do not fret; <span class=\"supplied\">it</span> only brings evil.'),
('LEB', 'Psalms', '37', '9', '1', 'For evildoers will be cut off,  but those who wait for Adonai—  they will possess <span class=\"supplied\">the</span> land.'),
('LEB', 'Psalms', '37', '10', '1', 'And<span class=\"note\">Or “But”</span>yet a little <span class=\"supplied\">while</span> and the wicked will not be,  and you will look carefully upon his place, but he will not be.'),
('LEB', 'Psalms', '37', '11', '1', 'But <span class=\"supplied\">the</span> afflicted will possess <span class=\"supplied\">the</span> land,  and they will take pleasure in an abundance of peace.'),
('LEB', 'Psalms', '37', '12', '1', '<span class=\"supplied\">The</span> wicked plans <span class=\"supplied\">evil</span> against the righteous,  and gnashes at him <span class=\"supplied\">with</span> his teeth.'),
('LEB', 'Psalms', '37', '13', '1', '<span class=\"supplied\">The</span> Lord laughs at him,  for he sees that his day is coming.'),
('LEB', 'Psalms', '37', '14', '1', '<span class=\"supplied\">The</span> wicked have drawn <span class=\"supplied\">the</span> sword and have bent their bow  to throw down <span class=\"supplied\">the</span> poor and <span class=\"supplied\">the</span> needy,  to kill <span class=\"supplied\">those</span> upright in <span class=\"supplied\">their</span> way.'),
('LEB', 'Psalms', '37', '15', '1', 'Their sword will enter into their <span class=\"supplied\">own</span> heart,  and their bows shall be broken.'),
('LEB', 'Psalms', '37', '16', '1', '<span class=\"supplied\">The</span> little belonging to the righteous <span class=\"supplied\">is</span> better  than <span class=\"supplied\">the</span> wealth of many wicked.'),
('LEB', 'Psalms', '37', '17', '1', 'For <span class=\"supplied\">the</span> arms of <span class=\"supplied\">the</span> wicked shall be broken,  but Adonai supports <span class=\"supplied\">the</span> righteous.'),
('LEB', 'Psalms', '37', '18', '1', 'Adonai knows <span class=\"supplied\">the</span> days of <span class=\"supplied\">the</span> blameless,  and their inheritance will be forever.'),
('LEB', 'Psalms', '37', '19', '1', 'They will not be put to shame in distressing times,<span class=\"note\">Hebrew “time”</span> and in <span class=\"supplied\">the</span> days of famine they will be satisfied.'),
('LEB', 'Psalms', '37', '20', '1', 'But <span class=\"supplied\">the</span> wicked will perish,  and the enemies of Adonai <span class=\"supplied\">will be</span> like <span class=\"supplied\">the</span> splendor of <span class=\"supplied\">the</span> pastures.  They vanish; in smoke they vanish.'),
('LEB', 'Psalms', '37', '21', '1', '<span class=\"supplied\">The</span> wicked borrows and does not repay,  but <span class=\"supplied\">the</span> righteous <span class=\"supplied\">is</span> gracious and gives.'),
('LEB', 'Psalms', '37', '22', '1', 'For those blessed <span class=\"supplied\">by</span> him shall possess <span class=\"supplied\">the</span> land,  but those cursed <span class=\"supplied\">by</span> him shall be cut off.'),
('LEB', 'Psalms', '37', '23', '1', '<span class=\"supplied\">The</span> steps of a man are established by Adonai,  and he takes pleasure in his way.'),
('LEB', 'Psalms', '37', '24', '1', 'Though he fall, he will not be cast headlong,  for Adonai supports <span class=\"supplied\">him with</span> his hand.'),
('LEB', 'Psalms', '37', '25', '1', 'I was a youth, but I am <span class=\"supplied\">now</span> old;  yet I have not seen <span class=\"supplied\">the</span> righteous forsaken  or his children <span class=\"idiom\">begging for</span><span class=\"note\">Literally “searching”</span>bread.'),
('LEB', 'Psalms', '37', '26', '1', 'All the day he <span class=\"supplied\">is</span> gracious and lends,  and his children <span class=\"supplied\">are</span> a blessing.'),
('LEB', 'Psalms', '37', '27', '1', 'Turn aside from evil and do good  and <span class=\"supplied\">so</span> abide forever.'),
('LEB', 'Psalms', '37', '28', '1', 'For Adonai loves justice  and will not forsake his faithful ones.  They are protected forever.  But <span class=\"supplied\">the</span> children of <span class=\"supplied\">the</span> wicked will be cut off.'),
('LEB', 'Psalms', '37', '29', '1', '<span class=\"supplied\">The</span> righteous will possess <span class=\"supplied\">the</span> land  and abide in it forever.'),
('LEB', 'Psalms', '37', '30', '1', '<span class=\"supplied\">The</span> mouth of <span class=\"supplied\">the</span> righteous utters wisdom,  and his tongue speaks justice.'),
('LEB', 'Psalms', '37', '31', '1', 'The law of his God <span class=\"supplied\">is</span> in his heart.  His steps will not slip.'),
('LEB', 'Psalms', '37', '32', '1', '<span class=\"supplied\">The</span> wicked watches for the righteous  and seeks to kill him.'),
('LEB', 'Psalms', '37', '33', '1', 'Adonai will not abandon him into his hand,  and he will not let him be condemned when he is judged.'),
('LEB', 'Psalms', '37', '34', '1', 'Wait for Adonai and keep his way,  and he will exalt you to possess <span class=\"supplied\">the</span> land.  When <span class=\"supplied\">the</span> wicked are cut off, you will see <span class=\"supplied\">it</span>.'),
('LEB', 'Psalms', '37', '35', '1', 'I have seen <span class=\"supplied\">the</span> wicked acting violently  and spreading himself out  like a luxuriant native tree.'),
('LEB', 'Psalms', '37', '36', '1', 'Then he passed on<span class=\"note\">Dead Sea Scrolls and the versions read “I passed by”</span>and behold, he was not.  And I sought him, but he could not be found.'),
('LEB', 'Psalms', '37', '37', '1', 'Observe <span class=\"supplied\">the</span> blameless and look at <span class=\"supplied\">the</span> upright,  for <span class=\"supplied\">there is</span> a future for a man of peace.'),
('LEB', 'Psalms', '37', '38', '1', 'But transgressors shall be destroyed altogether.  <span class=\"supplied\">The</span> future of <span class=\"supplied\">the</span> wicked shall be cut off.'),
('LEB', 'Psalms', '37', '39', '1', 'And<span class=\"note\">Some manuscripts omit “and,” preserving the acrostic</span><span class=\"supplied\">the</span> salvation of <span class=\"supplied\">the</span> righteous <span class=\"supplied\">is</span> from Adonai,  their refuge in <span class=\"supplied\">the</span> time of trouble.'),
('LEB', 'Psalms', '37', '40', '1', 'And Adonai helps them and he rescues them.  He rescues them from <span class=\"supplied\">the</span> wicked and saves them,  because they take refuge in him.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '38', '1', '1', '<span class=\"verse-title\">A psalm of David. To bring to remembrance.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>O Adonai, do not rebuke me in your anger  or chastise me in your wrath.'),
('LEB', 'Psalms', '38', '2', '1', 'For your arrows have sunk into me,  and your hand has pressed down on me.'),
('LEB', 'Psalms', '38', '3', '1', 'There is no soundness in my flesh because of your indignation.  There is no health in my bones because of my sin.'),
('LEB', 'Psalms', '38', '4', '1', 'For my iniquities have passed over my head;  like a heavy burden they are too heavy for me.'),
('LEB', 'Psalms', '38', '5', '1', 'My wounds start to stink; they rot  because of my foolishness.'),
('LEB', 'Psalms', '38', '6', '1', 'I am bowed down; I am bent over greatly.  All the day I go about mourning.'),
('LEB', 'Psalms', '38', '7', '1', 'For my loins are full <span class=\"supplied\">of</span> burning,  and there is no soundness in my flesh.'),
('LEB', 'Psalms', '38', '8', '1', 'I am faint and crushed greatly;  I groan because of the roaring of my heart.'),
('LEB', 'Psalms', '38', '9', '1', 'O Lord, all my longing is before you,  and my sighing is not hidden from you.'),
('LEB', 'Psalms', '38', '10', '1', 'My heart throbs violently, my strength leaves me;  and the light of my eyes, that also is not with me.'),
('LEB', 'Psalms', '38', '11', '1', 'My friends and companions stand aloof from my affliction,  and my relatives stand afar off.'),
('LEB', 'Psalms', '38', '12', '1', 'Those who seek my life lay snares as well,  and those intent on my harm speak threats.  They also plot deceit all day.'),
('LEB', 'Psalms', '38', '13', '1', 'But as for me, like <span class=\"supplied\">the</span> deaf I cannot hear,  and <span class=\"supplied\">I am</span> like <span class=\"supplied\">the</span> mute <span class=\"supplied\">who</span> cannot open his mouth.'),
('LEB', 'Psalms', '38', '14', '1', 'And <span class=\"supplied\">so</span> I am like a man who hears not,  and in whose mouth there are no retorts.'),
('LEB', 'Psalms', '38', '15', '1', 'Rather for you I wait, O Adonai.  You will answer, O Lord my God.'),
('LEB', 'Psalms', '38', '16', '1', 'For I said, “<span class=\"supplied\">Help,</span> lest they rejoice over me,  <span class=\"supplied\">lest</span> they boast against me when my foot slips.”'),
('LEB', 'Psalms', '38', '17', '1', 'For I <span class=\"supplied\">am</span> ready to stumble,  and my pain <span class=\"supplied\">is</span> before me continually.'),
('LEB', 'Psalms', '38', '18', '1', 'For my iniquity I confess;  I am anxious because of my sin.'),
('LEB', 'Psalms', '38', '19', '1', 'And my enemies without cause<span class=\"note\">Or “vigorous”</span>are numerous,  and those who hate me wrongfully<span class=\"note\">Or “those who hate me <span class=\"supplied\">with</span> falsehood”</span>are many.'),
('LEB', 'Psalms', '38', '20', '1', 'And those who repay evil in return for good  accuse me in return for my pursuing good.'),
('LEB', 'Psalms', '38', '21', '1', 'Do not forsake me, O Adonai.  O my God, do not be far from me.'),
('LEB', 'Psalms', '38', '22', '1', 'Hurry to help me,  O Lord, my salvation.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '39', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. For Jeduthun.<span class=\"note\">One of David’s musicians (1 Chr 16:41)</span>A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>I said, “I will guard my ways  <span class=\"idiom\">that I may not sin</span><span class=\"note\">Literally “from sinning”</span>with my tongue.  I will keep a muzzle over my mouth  as long as <span class=\"supplied\">the</span> wicked <span class=\"supplied\">are</span> before me.”'),
('LEB', 'Psalms', '39', '2', '1', 'I was mute <span class=\"supplied\">with</span> silence. I was silent <span class=\"supplied\">even</span> from <span class=\"supplied\">saying</span> good <span class=\"supplied\">things</span>,  and my pain was stirred up.'),
('LEB', 'Psalms', '39', '3', '1', 'My heart grew hot inside me;  in my sighing a fire burned.  <span class=\"supplied\">Then</span> I spoke with my tongue,'),
('LEB', 'Psalms', '39', '4', '1', '“Let me know, O Adonai, my end,  and what is the measure of my days.  Let me know how transient I <span class=\"supplied\">am</span>.”'),
('LEB', 'Psalms', '39', '5', '1', 'Look, you have made my days <span class=\"supplied\">mere</span> handbreadths,  and my lifespan as nothing next to you.  Surely every person standing firm <span class=\"supplied\">is</span> complete vanity. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '39', '6', '1', 'Surely a man walks about as a <span class=\"supplied\">mere</span> <span class=\"idiom\">shadow</span>;<span class=\"note\">Literally “image”</span> surely in vain they bustle about.  He heaps up <span class=\"supplied\">possessions</span> but does not know who <span class=\"supplied\">will</span> gather them <span class=\"supplied\">in</span>.'),
('LEB', 'Psalms', '39', '7', '1', 'And now, O Lord, for what do I wait?  My hope is for you.'),
('LEB', 'Psalms', '39', '8', '1', 'From all my transgressions deliver me;  do not make me <span class=\"supplied\">the</span> taunt of <span class=\"supplied\">the</span> fool.'),
('LEB', 'Psalms', '39', '9', '1', 'I am mute. I do not open my mouth,  for you, yourself, have done <span class=\"supplied\">it</span>.'),
('LEB', 'Psalms', '39', '10', '1', 'Remove from me your affliction.  By the opposition of your hand I perish.'),
('LEB', 'Psalms', '39', '11', '1', '<span class=\"supplied\">When</span> with rebukes you chastise a man for sin,  you<span class=\"note\">Hebrew “and/then you”</span><span class=\"idiom\">consume</span><span class=\"note\">Literally “cause to melt”</span>like a moth his delightful <span class=\"supplied\">things</span>.  Surely everyone <span class=\"supplied\">is</span> a <span class=\"supplied\">mere</span> vapor.<span class=\"note\">The word translated “vanity” in vv. 6, 7</span><span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '39', '12', '1', 'Hear my prayer, O Adonai, and listen <span class=\"supplied\">to</span> my cry for help;  do not be deaf to my tears.  For I <span class=\"supplied\">am</span> an alien<span class=\"note\">Or “stranger”</span>with you,  a sojourner like all my ancestors.<span class=\"note\">Or “fathers”</span>'),
('LEB', 'Psalms', '39', '13', '1', 'Look <span class=\"supplied\">away</span> from me that I may be cheerful,  before I depart and I am no more.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '40', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. Of David. A psalm.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>I waited patiently for Adonai,  And he inclined to me  and heard my cry for help.'),
('LEB', 'Psalms', '40', '2', '1', 'And <span class=\"supplied\">so</span> he brought me up from <span class=\"supplied\">the</span> roaring pit,<span class=\"note\">Some read “pit of destruction”</span> from the miry clay.  And he put my feet upon a rock;  he made my steps steady.'),
('LEB', 'Psalms', '40', '3', '1', 'Then he put a new song in my mouth,  a praise to our God.  Many will see and fear,  and will trust Adonai.'),
('LEB', 'Psalms', '40', '4', '1', 'Blessed <span class=\"supplied\">is</span> the man who makes Adonai his trust  and does not turn to <span class=\"supplied\">the</span> proud and <span class=\"supplied\">to those who</span> fall away <span class=\"supplied\">to</span> a lie.<span class=\"note\">Or “false god”</span>'),
('LEB', 'Psalms', '40', '5', '1', 'Many things, O Adonai my God, you have done—  your wonderful deeds and your thoughts toward us.  There is none to compare with you.  If I <span class=\"supplied\">tried to</span> proclaim and tell <span class=\"supplied\">of them</span>,  they would be too numerous to count.'),
('LEB', 'Psalms', '40', '6', '1', 'A sacrifice and offering you do not desire.  <span class=\"idiom\">My ears you have opened</span>.<span class=\"note\">Literally “Ears you have dug for me.”</span> Burnt offering and sin offering you have not demanded.'),
('LEB', 'Psalms', '40', '7', '1', 'Then I said, “Look, I come.  In <span class=\"supplied\">the</span> scroll of <span class=\"supplied\">the</span> book  it is written concerning me:'),
('LEB', 'Psalms', '40', '8', '1', '‘I delight to do your will, O my God,  and your law <span class=\"supplied\">is</span> <span class=\"idiom\">deep within me</span>.’ ”<span class=\"note\">Literally “in the midst of my intestines”</span>'),
('LEB', 'Psalms', '40', '9', '1', 'I have brought good tidings <span class=\"supplied\">of</span> righteousness in the great congregation.  Look, I have not shut my lips.  O Adonai, <span class=\"idiom\">you surely know that</span>.<span class=\"note\">Literally “you, you know”</span>'),
('LEB', 'Psalms', '40', '10', '1', 'Your righteousness I have not hidden in the midst of my heart.  I have spoken of your faithfulness and your salvation.  I have not concealed your loyal love or your faithfulness  from <span class=\"supplied\">the</span> great congregation.'),
('LEB', 'Psalms', '40', '11', '1', 'As for you, O Adonai, do not withhold your mercies from me.  Let your loyal love and your faithfulness  continually preserve me.'),
('LEB', 'Psalms', '40', '12', '1', 'For evils without number have encompassed me.  My iniquities have overtaken me, so that I am not able to see.  They are more than the hairs of my head,  and my heart fails me.'),
('LEB', 'Psalms', '40', '13', '1', 'Be pleased, O Adonai, to deliver me.  O Adonai, hurry to help me.'),
('LEB', 'Psalms', '40', '14', '1', 'Let them be shamed and abashed altogether  who seek to take away my life.<span class=\"note\">Or “soul”</span> Let them be repulsed and humiliated  <span class=\"supplied\">who</span> desire my harm.'),
('LEB', 'Psalms', '40', '15', '1', 'Let them be appalled because of their shame,  those who say to me, “Aha! Aha!”'),
('LEB', 'Psalms', '40', '16', '1', 'Let them rejoice and be glad in you,  all those who seek you.  Let them say continually, “Adonai is great!”  —those who love your salvation.'),
('LEB', 'Psalms', '40', '17', '1', 'But I <span class=\"supplied\">am</span> poor and needy.  Let my Lord consider me.  You <span class=\"supplied\">are</span> my help and my deliverer.  O my God, do not delay.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '41', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Blessed <span class=\"supplied\">is the one who</span> has regard for <span class=\"supplied\">the</span> poor;  in <span class=\"supplied\">the</span> day of disaster, Adonai delivers him.'),
('LEB', 'Psalms', '41', '2', '1', 'Adonai protects him and keeps him alive;  he is blessed in the land,  and you do not give him  into the will<span class=\"note\">Hebrew “soul”</span>of his enemies.'),
('LEB', 'Psalms', '41', '3', '1', 'Adonai sustains him on <span class=\"supplied\">his</span> sick bed.  In his illness, you <span class=\"idiom\">restore to health</span>.<span class=\"note\">Literally “his every bed you change”</span>'),
('LEB', 'Psalms', '41', '4', '1', 'As for me, I said, “O Adonai, be gracious <span class=\"supplied\">to</span> me.  Heal me,<span class=\"note\">Or “my soul”</span>for I have sinned against you.”'),
('LEB', 'Psalms', '41', '5', '1', 'My enemies speak evil about me,  “When will he die and his name perish?”'),
('LEB', 'Psalms', '41', '6', '1', 'And when one comes to see <span class=\"supplied\">me</span>, he speaks falsely;  his heart gathers disaster for itself.  He goes out to the street; he speaks.'),
('LEB', 'Psalms', '41', '7', '1', 'All who hate me speak together against me.  Against me they assume the <span class=\"idiom\">worst</span><span class=\"note\">Literally “evil”</span>for me:'),
('LEB', 'Psalms', '41', '8', '1', '“A ruinous thing is poured out on him,  and <span class=\"supplied\">now</span> that he lies down, he will not rise up again.”'),
('LEB', 'Psalms', '41', '9', '1', 'Even <span class=\"idiom\">my close friend</span>,<span class=\"note\">Literally “the man of my peace”</span>whom I trusted,  <span class=\"supplied\">who</span> ate my bread,  has lifted <span class=\"supplied\">his</span> heel against me.'),
('LEB', 'Psalms', '41', '10', '1', 'But you, O Adonai, be gracious to me and raise me up  that I may repay them.'),
('LEB', 'Psalms', '41', '11', '1', 'By this I know that you delight in me:  because my enemy has not shouted in triumph over me.'),
('LEB', 'Psalms', '41', '12', '1', 'As for me, you have upheld me in my integrity,  and you have set me in your presence forever.'),
('LEB', 'Psalms', '41', '13', '1', 'Blessed <span class=\"supplied\">be</span> Adonai, the God of Israel,  from everlasting to everlasting.  Amen and Amen.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '42', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. A maskil of the sons of Korah.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>As a deer longs for streams of water,  so my soul longs for you, O God.'),
('LEB', 'Psalms', '42', '2', '1', 'My soul thirsts for God, for <span class=\"supplied\">the</span> living God.  When shall I come and appear before God?<span class=\"note\">Some Hebrew manuscripts and versions read “and see the face of God?”</span>'),
('LEB', 'Psalms', '42', '3', '1', 'My tears have been my food day and night,  while <span class=\"supplied\">they</span> say to me all day <span class=\"supplied\">long</span>,  “Where <span class=\"supplied\">is</span> your God?”'),
('LEB', 'Psalms', '42', '4', '1', 'These I remember and I pour out my soul within me:  that I would go with the multitude;  I led them <span class=\"supplied\">in procession</span> to the house<span class=\"note\">Or “temple”</span>of God,  with a voice of rejoicing and thanksgiving,  a crowd celebrating a festival.'),
('LEB', 'Psalms', '42', '5', '1', 'Why are you <span class=\"idiom\">in despair</span>,<span class=\"note\">Literally “bowed down”</span>O my soul,  and disturbed within me?  Hope in God, because I will again praise him,  <span class=\"supplied\">for</span> the salvation of his presence.'),
('LEB', 'Psalms', '42', '6', '1', 'O my God, within me my soul is <span class=\"idiom\">in despair</span>;<span class=\"note\">Literally “bowed down”</span> therefore I remember you from the land of Jordan  and the heights of Hermon, from the mountain of Mizar.'),
('LEB', 'Psalms', '42', '7', '1', 'Deep <span class=\"supplied\">is</span> calling to deep  at<span class=\"note\">Or “to”</span>the thunder of your waterfalls.  All your breakers and your waves  have passed over me.'),
('LEB', 'Psalms', '42', '8', '1', 'By day Adonai commands his loyal love,  and in the night his song <span class=\"supplied\">is</span> with me,  a prayer to the God of my life.'),
('LEB', 'Psalms', '42', '9', '1', 'I say to God, my rock,  “Why have you forgotten me?  Why must I walk about mourning  because of <span class=\"supplied\">the</span> oppression of <span class=\"supplied\">the</span> enemy?”'),
('LEB', 'Psalms', '42', '10', '1', '<span class=\"supplied\">As</span> with a shattering in my bones  my oppressors taunt me,  while they say to me <span class=\"idiom\">all day</span>,<span class=\"note\">Literally “all the day”</span> “Where <span class=\"supplied\">is</span> your God?”'),
('LEB', 'Psalms', '42', '11', '1', 'Why are you <span class=\"idiom\">in despair</span>,<span class=\"note\">Literally “bowed down”</span>O my soul?  And why are you disturbed within me?  Hope in God, because I shall again praise him,  <span class=\"idiom\">my salvation</span><span class=\"note\">Literally “the salvation of my face”</span>and my God.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '43', '1', '1', '<span class=\"verse-title\">A Prayer for Rescue</span>Judge me, O God, and plead my case  against<span class=\"note\">Or “because of”</span>an unfaithful<span class=\"note\">Or “disloyal”</span>nation.  From a man of deceit and wickedness rescue me,'),
('LEB', 'Psalms', '43', '2', '1', 'because you <span class=\"supplied\">are</span> the God of my refuge.  Why have you rejected me?  Why must I go about mourning  because of <span class=\"supplied\">the</span> oppression of <span class=\"supplied\">the</span> enemy?'),
('LEB', 'Psalms', '43', '3', '1', 'Send your light and your truth;  they shall lead<span class=\"note\">Or “let them lead”</span>me.  They shall bring me to <span class=\"idiom\">your holy mountain</span><span class=\"note\">Literally “the mountain of your holiness”</span> and to your dwelling places.'),
('LEB', 'Psalms', '43', '4', '1', 'Then<span class=\"note\">Or “And”</span>I will go to the altar of God,  to God, <span class=\"idiom\">my surpassing joy</span>,<span class=\"note\">Literally “the joy of my gladness”</span> and I will praise you with lyre,  O God, my God.'),
('LEB', 'Psalms', '43', '5', '1', 'Why are you <span class=\"idiom\">in despair</span>,<span class=\"note\">Literally “bowed down”</span>O my soul?  And why are you disturbed within me?  Hope in God, because I will again praise him,  <span class=\"idiom\">my salvation</span><span class=\"note\">Hebrew “the salvation of my face”</span>and my God.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '44', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. Of the sons of Korah. A maskil.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>O God, we have heard with our ears;  our ancestors<span class=\"note\">Or “fathers”</span>have told us  of work you worked in their days,  in days of old.'),
('LEB', 'Psalms', '44', '2', '1', 'You <span class=\"supplied\">with</span> your hand drove out the nations,  but them<span class=\"note\">That is, Israel</span>you planted.  You harmed <span class=\"supplied\">the</span> peoples,  but them<span class=\"note\">That is, Israel</span>you let spread out.'),
('LEB', 'Psalms', '44', '3', '1', 'For not with their sword  did they possess <span class=\"supplied\">the</span> land,  and their arm did not give them victory.  Rather <span class=\"supplied\">it was</span> your right hand and your arm  and the light of your presence,  because you delighted in them.'),
('LEB', 'Psalms', '44', '4', '1', 'You are my king, O God.  Command<span class=\"note\">Or “who commands,” see LXX</span>victories for Jacob.'),
('LEB', 'Psalms', '44', '5', '1', 'By you we push down our enemies;  by your name we tread down those who rise up against us.'),
('LEB', 'Psalms', '44', '6', '1', 'For I do not trust my bow,  and my sword cannot give me victory.'),
('LEB', 'Psalms', '44', '7', '1', 'Rather you have saved us from our enemies,<span class=\"note\">Or “made us victorious over our enemies”</span> and have humiliated those who hate us.'),
('LEB', 'Psalms', '44', '8', '1', 'In God we boast all the day,  and we will give thanks <span class=\"supplied\">to</span> your name forever. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '44', '9', '1', 'Surely you have rejected and disgraced us,  and have not gone out with our armies.'),
('LEB', 'Psalms', '44', '10', '1', 'You have caused us to pull back from <span class=\"supplied\">the</span> enemy,  and <span class=\"supplied\">so</span> those who hate us have plundered for themselves.'),
('LEB', 'Psalms', '44', '11', '1', 'You have given us as sheep <span class=\"supplied\">for</span> food,  and among the nations you have scattered us.'),
('LEB', 'Psalms', '44', '12', '1', 'You have sold your people <span class=\"idiom\">cheaply</span>,<span class=\"note\">Literally “for <span class=\"supplied\">what is</span> not wealth”</span> and did not profit by their price.'),
('LEB', 'Psalms', '44', '13', '1', 'You have made us a taunt to our neighbors,  a derision and a scorn to those around us.'),
('LEB', 'Psalms', '44', '14', '1', 'You have made us a byword among the nations,  a shaking of <span class=\"supplied\">the</span> head among <span class=\"supplied\">the</span> peoples.'),
('LEB', 'Psalms', '44', '15', '1', '<span class=\"idiom\">All day long</span><span class=\"note\">Literally “All of the day”</span>my disgrace <span class=\"supplied\">is</span> before me,  and the shame of my face covers me,'),
('LEB', 'Psalms', '44', '16', '1', 'because of <span class=\"supplied\">the</span> voice of <span class=\"supplied\">the</span> taunter and <span class=\"supplied\">the</span> reviler,  because of <span class=\"supplied\">the</span> enemy and <span class=\"supplied\">the</span> avenger.'),
('LEB', 'Psalms', '44', '17', '1', 'All this has befallen us, though we have not forgotten you,  and we have not been false to your covenant.'),
('LEB', 'Psalms', '44', '18', '1', 'Our heart has not turned back,  and our steps have <span class=\"supplied\">not</span> turned aside from your way.'),
('LEB', 'Psalms', '44', '19', '1', 'But you have crushed us in a place of jackals,  and have covered us with deep shadow.'),
('LEB', 'Psalms', '44', '20', '1', 'If we had forgotten the name of our God,  or had spread out our hands <span class=\"supplied\">in prayer</span> to a foreign god,'),
('LEB', 'Psalms', '44', '21', '1', 'would not God discover this,  for he knows <span class=\"supplied\">the</span> secrets of <span class=\"supplied\">the</span> heart?'),
('LEB', 'Psalms', '44', '22', '1', 'Rather, on account of you we are killed <span class=\"idiom\">all day long</span>;<span class=\"note\">Literally “all of the day”</span> we are accounted as sheep for slaughter.'),
('LEB', 'Psalms', '44', '23', '1', 'Wake up! Why do you sleep, O Lord?  Awake! Do not reject forever.'),
('LEB', 'Psalms', '44', '24', '1', 'Why do you hide your face?  <span class=\"supplied\">Have</span> you forgotten our misery and our oppression?'),
('LEB', 'Psalms', '44', '25', '1', 'For our soul is bowed down to the dust.  Our <span class=\"idiom\">body</span><span class=\"note\">Literally “belly”</span>clings to the ground.'),
('LEB', 'Psalms', '44', '26', '1', 'Rise up! <span class=\"supplied\">Be a</span> help for us,  and redeem us for the sake of your loyal love.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '45', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director; according to <span class=\"supplied\">The</span> Lilies. Of the sons of Korah.  A maskil. A song of love.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>My heart is moved <span class=\"supplied\">with</span> a good word;  I recite my <span class=\"idiom\">compositions</span><span class=\"note\">Literally “works,” that is, the song he has composed</span>to <span class=\"supplied\">the</span> king.  My tongue <span class=\"supplied\">is the</span> pen <span class=\"supplied\">of</span> a skilled scribe.'),
('LEB', 'Psalms', '45', '2', '1', 'You are <span class=\"supplied\">the most</span> handsome of <span class=\"supplied\">the</span> sons of humankind;  grace is poured out on your lips;  therefore God has blessed you forever.'),
('LEB', 'Psalms', '45', '3', '1', 'Gird your sword on <span class=\"supplied\">your</span> thigh, O mighty one,  <span class=\"supplied\">in</span> your splendor and your majesty.'),
('LEB', 'Psalms', '45', '4', '1', 'And <span class=\"supplied\">in</span> your majesty <span class=\"idiom\">ride victoriously</span>,<span class=\"note\">Literally “Succeed! Ride!”</span> because of truth and humility <span class=\"supplied\">and</span> righteousness.  And let your right hand teach you awesome deeds.'),
('LEB', 'Psalms', '45', '5', '1', 'Your arrows <span class=\"supplied\">are</span> sharp;  peoples fall under you  in the midst of the king’s enemies.'),
('LEB', 'Psalms', '45', '6', '1', 'Your throne, O God, <span class=\"supplied\">is</span> forever and ever.  A scepter of uprightness <span class=\"supplied\">is</span>  the scepter of your kingdom.'),
('LEB', 'Psalms', '45', '7', '1', 'You love righteousness and hate wickedness.  Therefore God, your God, has anointed you  from among your companions <span class=\"supplied\">with</span> festive oil.'),
('LEB', 'Psalms', '45', '8', '1', 'All your robes <span class=\"supplied\">are scented with</span> myrrh and aloes <span class=\"supplied\">and</span> cassia.  From palaces of ivory stringed instruments gladden you.'),
('LEB', 'Psalms', '45', '9', '1', 'Kings’ daughters <span class=\"supplied\">are</span> among your noble ladies.  The queen stands at your right hand in gold of Ophir.'),
('LEB', 'Psalms', '45', '10', '1', 'Hear, O daughter, and <span class=\"idiom\">discern</span><span class=\"note\">Literally “see”</span>and incline your ear,  and forget your people and your father’s house.'),
('LEB', 'Psalms', '45', '11', '1', 'Let the king desire your beauty.<span class=\"note\">Or “Should the king desire … then bow down …”</span> Because he <span class=\"supplied\">is</span> your lord, therefore<span class=\"note\">Hebrew “and”</span>bow down to him.'),
('LEB', 'Psalms', '45', '12', '1', 'Even the daughter of Tyre <span class=\"supplied\">will come</span> with a gift.  <span class=\"supplied\">The</span> rich from among people will seek your <span class=\"idiom\">favor</span>.<span class=\"note\">Literally “face”</span>'),
('LEB', 'Psalms', '45', '13', '1', 'The king’s daughter is all glorious within;<span class=\"note\">That is, within her bridal chamber</span> her garment <span class=\"supplied\">is</span> of gold embroidered cloth.'),
('LEB', 'Psalms', '45', '14', '1', 'She is brought to the king <span class=\"supplied\">in</span> colorful garments.  <span class=\"supplied\">The</span> young women behind her, her attendants,  <span class=\"supplied\">are</span> being brought to you.'),
('LEB', 'Psalms', '45', '15', '1', 'They are led with joy and gladness.  They enter <span class=\"supplied\">the</span> palace of <span class=\"supplied\">the</span> king.'),
('LEB', 'Psalms', '45', '16', '1', 'In place of your<span class=\"note\">The pronouns and verbal forms are masculine in this verse</span>fathers will be your sons.  You will make them princes in all the land.<span class=\"note\">Or “earth”</span>'),
('LEB', 'Psalms', '45', '17', '1', 'I will cause your name to be remembered in all generations;  therefore peoples will praise you forever and ever.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '46', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. Of the sons of Korah.  According to Alamoth. A song.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>God <span class=\"supplied\">is</span> our refuge and strength,  a very sufficient help in troubles.'),
('LEB', 'Psalms', '46', '2', '1', 'Therefore we will not fear though <span class=\"supplied\">the</span> earth change,  and though the mountains totter into <span class=\"supplied\">the</span> <span class=\"idiom\">midst</span><span class=\"note\">Literally “heart”</span>of <span class=\"supplied\">the</span> sea,'),
('LEB', 'Psalms', '46', '3', '1', 'though its waters roar <span class=\"supplied\">and</span> foam,  though mountains shake with its surging <span class=\"supplied\">water</span>. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '46', '4', '1', '<span class=\"supplied\">There is</span> a river whose streams gladden the city of God,  the holiest of the dwellings of the Most High.'),
('LEB', 'Psalms', '46', '5', '1', 'God <span class=\"supplied\">is</span> in the midst of her;  she will not be made to totter.  God will help her <span class=\"idiom\">at daybreak</span>.<span class=\"note\">Literally “at the turning of the morning”</span>'),
('LEB', 'Psalms', '46', '6', '1', 'Nations roar, kingdoms shake;  he utters his voice, <span class=\"supplied\">the</span> earth melts.'),
('LEB', 'Psalms', '46', '7', '1', 'Adonai of hosts <span class=\"supplied\">is</span> with us;  our <span class=\"supplied\">high</span> stronghold <span class=\"supplied\">is</span> the God of Jacob. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '46', '8', '1', 'Come, see the works of Adonai,  who has placed desolations on the earth.'),
('LEB', 'Psalms', '46', '9', '1', '<span class=\"supplied\">He</span> makes wars cease to the ends of the earth;  he breaks <span class=\"supplied\">the</span> bow and cuts off <span class=\"supplied\">the</span> spear.  <span class=\"supplied\">The</span> wagons <span class=\"supplied\">of war</span> he burns with fire.'),
('LEB', 'Psalms', '46', '10', '1', 'Be still, and know that I <span class=\"supplied\">am</span> God.  I will be exalted among the nations;  I will be exalted in the earth.'),
('LEB', 'Psalms', '46', '11', '1', 'Adonai of Hosts <span class=\"supplied\">is</span> with us;  the God of Jacob <span class=\"supplied\">is</span> our <span class=\"supplied\">high</span> stronghold.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '47', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. Of the sons of Korah. A psalm.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>All <span class=\"supplied\">you</span> peoples, clap <span class=\"supplied\">your</span> hands.<span class=\"note\">Hebrew “hand”</span> Shout to God with a voice of rejoicing.'),
('LEB', 'Psalms', '47', '2', '1', 'For Adonai Most High <span class=\"supplied\">is</span> awesome,<span class=\"note\">Or “to be feared”</span> a great king over all the earth.'),
('LEB', 'Psalms', '47', '3', '1', 'He subdues peoples under us  and nations under our feet.'),
('LEB', 'Psalms', '47', '4', '1', 'He chooses for us our inheritance,  the pride of Jacob whom he loves.'),
('LEB', 'Psalms', '47', '5', '1', 'God has gone up with a shout,  Adonai with <span class=\"supplied\">the</span> sound of a trumpet.'),
('LEB', 'Psalms', '47', '6', '1', 'Sing praises <span class=\"supplied\">to</span> God, sing praises.  Sing praises to our king, sing praises.'),
('LEB', 'Psalms', '47', '7', '1', 'For God <span class=\"supplied\">is</span> king of all the earth.  Sing praises with understanding.<span class=\"note\">Or “sing a maskil”</span>'),
('LEB', 'Psalms', '47', '8', '1', 'God reigns over nations;  God sits on his holy throne.'),
('LEB', 'Psalms', '47', '9', '1', '<span class=\"supplied\">The</span> princes of <span class=\"supplied\">the</span> peoples are gathered together  <span class=\"supplied\">with</span> the people of Abraham’s God.  For <span class=\"supplied\">the</span> shields of <span class=\"supplied\">the</span> earth belong to God.  He is very exalted.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '48', '1', '1', '<span class=\"verse-title\">A song. A psalm of the sons of Korah.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Adonai <span class=\"supplied\">is</span> great and very worthy of praise  in the city of our God, in his <span class=\"idiom\">holy mountain</span>.<span class=\"note\">Literally “mountain of holiness”</span>'),
('LEB', 'Psalms', '48', '2', '1', 'Beautiful in elevation, the joy of the whole earth,  <span class=\"supplied\">is</span> <span class=\"idiom\">Mount Zion</span>,<span class=\"note\">Literally “the mountain of Zion”</span><span class=\"idiom\">in the far north</span>,<span class=\"note\">Literally “far of north”; the word translated “north” could refer to Mount Zaphon</span> <span class=\"supplied\">the</span> city of <span class=\"supplied\">the</span> great king.'),
('LEB', 'Psalms', '48', '3', '1', 'God <span class=\"supplied\">is</span> in her citadels;  he is known as a <span class=\"supplied\">high</span> stronghold.'),
('LEB', 'Psalms', '48', '4', '1', 'For see, the kings assembled;<span class=\"note\">That is, preparing for battle</span> they advanced together.'),
('LEB', 'Psalms', '48', '5', '1', '<span class=\"idiom\">They themselves saw</span><span class=\"note\">Literally “They, they saw”</span><span class=\"supplied\">it</span>, so they were astonished.  They were terrified; they ran off.'),
('LEB', 'Psalms', '48', '6', '1', 'Trembling seized them there—  pain as of a woman in labor.'),
('LEB', 'Psalms', '48', '7', '1', 'With an east wind  you shatter<span class=\"note\">A few Hebrew manuscripts read “like an east wind <span class=\"supplied\">that</span> shatters”</span>the ships of Tarshish.<span class=\"note\">That is, ships <span class=\"supplied\">that can sail to</span> Tarshish, i.e. well built, able to traverse great distances</span>'),
('LEB', 'Psalms', '48', '8', '1', 'As we have heard, so we have seen  in the city of Adonai of hosts, in the city of our God.  God will establish her forever. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '48', '9', '1', 'We have pondered your loyal love, O God,  in the midst of your temple.'),
('LEB', 'Psalms', '48', '10', '1', 'As <span class=\"supplied\">is</span> your name, O God, so <span class=\"supplied\">is</span> your praise  to <span class=\"supplied\">the</span> ends of <span class=\"supplied\">the</span> earth.  Your right hand is full of righteousness.'),
('LEB', 'Psalms', '48', '11', '1', 'Let <span class=\"idiom\">Mount Zion</span><span class=\"note\">Literally “the mountain of Zion”</span>rejoice;  let the daughters of Judah rejoice  because of your judgments.'),
('LEB', 'Psalms', '48', '12', '1', 'Walk about Zion and circle it;  count her towers.'),
('LEB', 'Psalms', '48', '13', '1', '<span class=\"idiom\">Consider well</span><span class=\"note\">Literally “Set your heart to”</span>her ramparts.  Go through her citadels  so that you can tell <span class=\"supplied\">the</span> next generation'),
('LEB', 'Psalms', '48', '14', '1', 'that this <span class=\"supplied\">is</span> God, our God forever and ever.  <span class=\"idiom\">He himself</span><span class=\"note\">Literally “he, he will …”</span>will guide us until death.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '49', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. Of the sons of Korah. A psalm.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Hear this, all <span class=\"supplied\">you</span> peoples;  give ear, all <span class=\"supplied\">you</span> inhabitants of <span class=\"supplied\">the</span> world,'),
('LEB', 'Psalms', '49', '2', '1', 'both low and high,  rich and poor together.'),
('LEB', 'Psalms', '49', '3', '1', 'My mouth will speak wisdom,  and the meditation of my heart <span class=\"supplied\">will be</span> understanding.'),
('LEB', 'Psalms', '49', '4', '1', 'I will incline my ear to a proverb;  I will <span class=\"idiom\">propound</span><span class=\"note\">Literally “open”</span>my riddle on a lyre.'),
('LEB', 'Psalms', '49', '5', '1', 'Why should I fear in <span class=\"idiom\">times of calamity</span>,<span class=\"note\">Literally “the days of evil”</span> <span class=\"supplied\">when</span> iniquity surrounds me at my heels,'),
('LEB', 'Psalms', '49', '6', '1', 'those who trust their wealth  and boast about the abundance of their riches?'),
('LEB', 'Psalms', '49', '7', '1', 'Surely a man cannot redeem a brother.  He cannot give to God his ransom'),
('LEB', 'Psalms', '49', '8', '1', '(since<span class=\"note\">Hebrew “and”</span>the redemption price for their life is costly  and it always fails),'),
('LEB', 'Psalms', '49', '9', '1', 'so that he may stay alive forever  <span class=\"supplied\">and</span> not see the pit.'),
('LEB', 'Psalms', '49', '10', '1', 'For he sees <span class=\"supplied\">that</span> the wise die,  together <span class=\"supplied\">with the</span> fool and brute they perish,  and leave their wealth to the next <span class=\"supplied\">generation</span>.'),
('LEB', 'Psalms', '49', '11', '1', 'Within them <span class=\"supplied\">they think</span> their houses <span class=\"supplied\">are</span> forever,  their dwelling places from generation to generation.  They <span class=\"idiom\">name</span><span class=\"note\">Literally “call”</span><span class=\"supplied\">their</span> lands by their <span class=\"supplied\">own</span> names.'),
('LEB', 'Psalms', '49', '12', '1', 'But man cannot continue in <span class=\"supplied\">his</span> pomp.  He is like the beasts <span class=\"supplied\">that</span> perish.'),
('LEB', 'Psalms', '49', '13', '1', 'This <span class=\"supplied\">is</span> the journey<span class=\"note\">Hebrew “way”</span><span class=\"supplied\">of those who</span> have foolish <span class=\"supplied\">confidence</span>,  and those after them <span class=\"supplied\">who</span> accept their sayings. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '49', '14', '1', 'Like sheep they are destined to Sheol;  death will shepherd them.  But the upright will rule over them in the morning,  and their forms <span class=\"supplied\">will be for</span> Sheol to consume,  <span class=\"supplied\">far from</span> his lofty abode.'),
('LEB', 'Psalms', '49', '15', '1', 'Surely God will ransom my life  from the <span class=\"idiom\">power</span><span class=\"note\">Literally “hand”</span>of Sheol,  because he will receive me. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '49', '16', '1', 'Do not fear when a man becomes rich,  when the wealth<span class=\"note\">Or “honor”</span>of his house increases,'),
('LEB', 'Psalms', '49', '17', '1', 'because when he dies he will not take <span class=\"supplied\">away</span> any <span class=\"supplied\">of it</span>.  His wealth will not follow down after him.'),
('LEB', 'Psalms', '49', '18', '1', 'Though he congratulated his soul while he was living  —and <span class=\"supplied\">people</span> will praise you when you do well for yourself—'),
('LEB', 'Psalms', '49', '19', '1', 'it<span class=\"note\">That is, his soul</span>will go to the generation of his fathers.  Never will they see light.'),
('LEB', 'Psalms', '49', '20', '1', 'Humankind in <span class=\"supplied\">its</span> pomp, but does not understand,  is like the beasts <span class=\"supplied\">that</span> perish.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '50', '1', '1', '<span class=\"verse-title\">A psalm of Asaph.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>The Supreme God, God, Adonai, has spoken  and summoned <span class=\"supplied\">the</span> earth,  from <span class=\"supplied\">the</span> rising of <span class=\"supplied\">the</span> sun  to its setting.'),
('LEB', 'Psalms', '50', '2', '1', 'From Zion, <span class=\"supplied\">the</span> perfection of beauty,  God shines forth.'),
('LEB', 'Psalms', '50', '3', '1', 'Our God comes and he is not silent.  Before him fire devours,  and around him it is very tempestuous.'),
('LEB', 'Psalms', '50', '4', '1', 'He summons the heavens above  and the earth that he might judge his people:'),
('LEB', 'Psalms', '50', '5', '1', '“Gather to me my loyal ones,  <span class=\"supplied\">those</span> who have made a covenant with me by sacrifice.”'),
('LEB', 'Psalms', '50', '6', '1', 'And <span class=\"supplied\">the</span> heavens declare his righteousness,  because God himself is judge. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '50', '7', '1', '“Hear, O my people, and I will speak,  O Israel, and I will testify against you.  I am God, your God.'),
('LEB', 'Psalms', '50', '8', '1', 'It is not concerning <span class=\"supplied\">a lack of</span> your sacrifices <span class=\"supplied\">that</span> I rebuke you,  and your burnt offerings <span class=\"supplied\">are</span> before me continually.'),
('LEB', 'Psalms', '50', '9', '1', 'I will not take from your house a bull  <span class=\"supplied\">or</span> from your stalls a he-goat,'),
('LEB', 'Psalms', '50', '10', '1', 'because every animal of the forest is mine,  <span class=\"supplied\">the</span> cattle on a thousand hills.'),
('LEB', 'Psalms', '50', '11', '1', 'I know every bird of <span class=\"supplied\">the</span> mountains,  and <span class=\"supplied\">every</span> moving creature in <span class=\"supplied\">the</span> field <span class=\"supplied\">is</span> mine.'),
('LEB', 'Psalms', '50', '12', '1', 'If I were hungry I would not tell you,  because <span class=\"supplied\">the</span> world and its fullness <span class=\"supplied\">are</span> mine.'),
('LEB', 'Psalms', '50', '13', '1', 'Do I eat <span class=\"supplied\">the</span> flesh of bulls  or drink <span class=\"supplied\">the</span> blood of goats?'),
('LEB', 'Psalms', '50', '14', '1', 'Offer to God a thank offering  and pay your vows to <span class=\"supplied\">the</span> Most High.'),
('LEB', 'Psalms', '50', '15', '1', 'And call me in <span class=\"supplied\">the</span> day of trouble;  I will deliver you, and you will glorify me.”'),
('LEB', 'Psalms', '50', '16', '1', 'But to the wicked God says,  “What <span class=\"supplied\">right</span> have you to recite my statutes  and <span class=\"idiom\">mention my covenant with your mouth</span>,<span class=\"note\">Literally “take my covenant on your mouth”</span>'),
('LEB', 'Psalms', '50', '17', '1', 'while you yourself hate discipline,  and cast my words behind you?'),
('LEB', 'Psalms', '50', '18', '1', 'When you see a thief, then you are pleased with him,  and your association <span class=\"supplied\">is</span> with adulterers.'),
('LEB', 'Psalms', '50', '19', '1', 'You give your mouth free rein for evil,  and you harness your tongue to deceit.'),
('LEB', 'Psalms', '50', '20', '1', 'You sit and speak against your brother;  you slander your mother’s son.'),
('LEB', 'Psalms', '50', '21', '1', 'These <span class=\"supplied\">things</span> you have done, and I have been silent;  You imagined that I was just like you.  I will rebuke you and present <span class=\"supplied\">an argument</span> before your eyes.'),
('LEB', 'Psalms', '50', '22', '1', 'Now consider this, you who forget God,  lest I tear <span class=\"supplied\">you</span> apart, and there <span class=\"supplied\">will be</span> none to deliver.'),
('LEB', 'Psalms', '50', '23', '1', 'He who sacrifices a thank offering honors me,  and he <span class=\"supplied\">who</span> orders <span class=\"supplied\">his</span> way;  I will show him the salvation of God.”')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '51', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. A psalm of David.  When Nathan the prophet came to him, after he had gone in to Bathsheba.<span class=\"note\">The Hebrew Bible counts the superscription as the first two verses of the psalm; the English verse number is reduced by two</span></span>Be gracious to me, O God, according to your loyal love.  According to your abundant mercies,  blot out my transgressions.'),
('LEB', 'Psalms', '51', '2', '1', 'Wash me thoroughly from my iniquity,  and from my sin cleanse me.'),
('LEB', 'Psalms', '51', '3', '1', 'For <span class=\"idiom\">I myself know</span><span class=\"note\">Literally “I, I know”</span>my transgressions,<span class=\"note\">Or “rebellions”</span> and my sin <span class=\"supplied\">is</span> ever before me.'),
('LEB', 'Psalms', '51', '4', '1', 'Against you, only you, I have sinned  and have done this evil<span class=\"note\">Hebrew “the evil”</span>in your eyes,  so that you are correct when you speak,  you are blameless when you judge.'),
('LEB', 'Psalms', '51', '5', '1', 'Behold, in iniquity I was born,  and in sin my mother conceived me.'),
('LEB', 'Psalms', '51', '6', '1', 'Behold, you delight <span class=\"supplied\">in</span> truth in the inward parts,  and in <span class=\"supplied\">the</span> hidden <span class=\"supplied\">parts</span> you make me to know wisdom.'),
('LEB', 'Psalms', '51', '7', '1', 'Purify me with hyssop, and I shall be clean.  Wash me, and I shall be whiter than snow.'),
('LEB', 'Psalms', '51', '8', '1', 'Make me hear joy and gladness;  let <span class=\"supplied\">the</span> bones you have crushed rejoice.'),
('LEB', 'Psalms', '51', '9', '1', 'Hide your face from my sins,  and all my iniquities blot out.'),
('LEB', 'Psalms', '51', '10', '1', 'Create a clean heart for me, O God,  and renew a steadfast spirit <span class=\"idiom\">within me</span>.<span class=\"note\">Literally “in my inner parts”</span>'),
('LEB', 'Psalms', '51', '11', '1', 'Do not cast me away from your presence,  and do not take your Holy Spirit from me.'),
('LEB', 'Psalms', '51', '12', '1', 'Restore to me the joy of your salvation,  and with a willing spirit sustain me.'),
('LEB', 'Psalms', '51', '13', '1', '<span class=\"supplied\">Then</span> I will teach transgressors your ways,  and sinners will turn back to you.'),
('LEB', 'Psalms', '51', '14', '1', 'Deliver me from the guilt of bloodshed, O God,  the God of my salvation;  <span class=\"supplied\">then</span> my tongue will sing aloud <span class=\"supplied\">of</span> your righteousness.'),
('LEB', 'Psalms', '51', '15', '1', 'O Lord, open my lips,  and my mouth will proclaim your praise.'),
('LEB', 'Psalms', '51', '16', '1', 'For you do not delight <span class=\"supplied\">in</span> sacrifice or I would give <span class=\"supplied\">it</span>.  <span class=\"supplied\">With</span> a burnt offering you are not pleased.'),
('LEB', 'Psalms', '51', '17', '1', 'The sacrifices of God <span class=\"supplied\">are</span> a broken spirit;  A broken and contrite heart,  O God, you will not despise.'),
('LEB', 'Psalms', '51', '18', '1', 'Do good in your favor toward Zion.  Build the walls of Jerusalem.'),
('LEB', 'Psalms', '51', '19', '1', 'Then you will delight <span class=\"supplied\">in</span> righteous sacrifices,  burnt offering and whole burnt offering.  Then bulls will be offered on your altar.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '52', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. A maskil of David.  When Doeg the Edomite came and informed Saul.  And he said to him, “David has come to the house of Ahimelech.”<span class=\"note\">The Hebrew Bible counts the superscription as the first two verses of the psalm; the English verse number is reduced by two</span></span>Why do you boast about evil, O mighty man?  The loyal love of God <span class=\"supplied\">endures</span> <span class=\"idiom\">continually</span>.<span class=\"note\">Literally “all the day”</span>'),
('LEB', 'Psalms', '52', '2', '1', 'Your tongue plans destruction,  like a sharp razor, working deceit.'),
('LEB', 'Psalms', '52', '3', '1', 'You love evil more than good,  a lie more than speaking <span class=\"idiom\">what is right</span>.<span class=\"note\">Literally “righteousness”</span><span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '52', '4', '1', 'You love all devouring words,  O deceitful tongue,'),
('LEB', 'Psalms', '52', '5', '1', 'but God will pull you down forever.  He will snatch you and tear you away from <span class=\"supplied\">your</span> tent,  and <span class=\"supplied\">he</span> will uproot you from <span class=\"supplied\">the</span> land of <span class=\"supplied\">the</span> living. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '52', '6', '1', 'And <span class=\"supplied\">the</span> righteous will see and fear,  and will laugh at him, <span class=\"supplied\">saying</span>,'),
('LEB', 'Psalms', '52', '7', '1', '“Look, the man <span class=\"supplied\">who</span> would not make God his refuge,  but he trusted in the greatness of his wealth;  he took refuge in his destructiveness.”'),
('LEB', 'Psalms', '52', '8', '1', 'But I <span class=\"supplied\">am</span> like an olive tree flourishing  in the house<span class=\"note\">Or “temple”</span>of God.  I trust the loyal love of God forever and ever.'),
('LEB', 'Psalms', '52', '9', '1', 'I will give thanks to you forever, because <span class=\"supplied\">of what</span> you have done,  and I will wait on your name, because <span class=\"supplied\">it is</span> good,  in the presence of your faithful ones.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '53', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, according to Mahalath.  A maskil of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span><span class=\"supplied\">The</span> fool says in his heart, “There is no God.”  They are corrupt and they have done abominable iniquity.  There is none who does good.'),
('LEB', 'Psalms', '53', '2', '1', 'God looks down from heaven upon the children of humankind  to see whether there is one who has insight,  one who seeks God.'),
('LEB', 'Psalms', '53', '3', '1', '<span class=\"idiom\">All of them</span><span class=\"note\">Literally “The whole of it,” i.e. all of humankind</span>have turned back.  They are altogether corrupt.  There is none who does good;  there is not even one.'),
('LEB', 'Psalms', '53', '4', '1', 'Do not evildoers know,  <span class=\"supplied\">they</span> who eat my people <span class=\"supplied\">as though</span> they were eating bread?  They do not call on God.'),
('LEB', 'Psalms', '53', '5', '1', 'There <span class=\"idiom\">they are very fearful</span><span class=\"note\">Literally “they feared a fear”</span> <span class=\"supplied\">where</span> no fear had been,  because God has scattered the bones of him who encamps <span class=\"supplied\">against</span> you.  You have put <span class=\"supplied\">them</span> to shame, because God has rejected them.'),
('LEB', 'Psalms', '53', '6', '1', '<span class=\"idiom\">Oh, that from Zion</span><span class=\"note\">Literally “Who will give <span class=\"supplied\">that</span> from Zion”</span>would come salvation <span class=\"supplied\">for</span> Israel!  When God returns the fortunes<span class=\"note\">Or “captivity”</span>of his people,  let Jacob rejoice, let Israel be glad.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '54', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, with stringed instruments.  A maskil of David,  when the Ziphites went and said to Saul,  “Is not David hiding himself among us?”<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>O God, by your name save me,  and by your power vindicate me.'),
('LEB', 'Psalms', '54', '2', '1', 'O God, hear my prayer;  heed the words of my mouth.'),
('LEB', 'Psalms', '54', '3', '1', 'For foreigners have risen against me,  and ruthless <span class=\"supplied\">men</span> seek my life.  They have not set God before them. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '54', '4', '1', 'See, God <span class=\"supplied\">is</span> my helper;  The Lord <span class=\"supplied\">is</span> with those who sustain my life.<span class=\"note\">Or “The Lord <span class=\"supplied\">is</span> the sustainer of my life.”</span>'),
('LEB', 'Psalms', '54', '5', '1', 'He will repay<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>my enemies for their<span class=\"note\">Hebrew “the”</span>evil;  in your faithfulness destroy them.'),
('LEB', 'Psalms', '54', '6', '1', 'I will freely sacrifice to you;  I will give thanks <span class=\"supplied\">to</span> your name,  O Adonai, because <span class=\"supplied\">it is</span> good.'),
('LEB', 'Psalms', '54', '7', '1', 'Because he has delivered me from all trouble,  and my eye has looked <span class=\"supplied\">with satisfaction</span> on my enemies.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '55', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, with stringed instruments.  A maskil of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Give ear, O God, <span class=\"supplied\">to</span> my prayer,  and do not hide yourself from my plea.'),
('LEB', 'Psalms', '55', '2', '1', 'Attend to me and answer me.  I am restless in my lamenting and I groan,'),
('LEB', 'Psalms', '55', '3', '1', 'because of <span class=\"supplied\">the</span> voice <span class=\"supplied\">of</span> an enemy,  because of the oppression of the wicked,  because they bring down evil on me,  and in anger they hold a grudge <span class=\"supplied\">against</span> me.'),
('LEB', 'Psalms', '55', '4', '1', 'My heart trembles within me,  and deathly terrors fall on me.'),
('LEB', 'Psalms', '55', '5', '1', 'Fear and trembling come on me,  and horror overwhelms me.'),
('LEB', 'Psalms', '55', '6', '1', 'So I say, “<span class=\"idiom\">Oh, that</span><span class=\"note\">Literally “Who will give that”</span>I had wings like a dove.  I would fly away and be at rest.'),
('LEB', 'Psalms', '55', '7', '1', 'Look, I would flee far away.  I would dwell in the wilderness. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '55', '8', '1', 'I would hurry to my refuge  from <span class=\"supplied\">the</span> raging wind <span class=\"supplied\">and</span> storm.”'),
('LEB', 'Psalms', '55', '9', '1', 'Confuse, O Lord; divide their speech,  because I see violence and strife in the city.'),
('LEB', 'Psalms', '55', '10', '1', 'Day and night they go around it on its walls,  and iniquity and trouble <span class=\"supplied\">are</span> in its midst.'),
('LEB', 'Psalms', '55', '11', '1', 'Destruction <span class=\"supplied\">is</span> within it,  and oppression and deceit  do not depart from its public square.'),
('LEB', 'Psalms', '55', '12', '1', 'For <span class=\"supplied\">it is</span> not an enemy <span class=\"supplied\">that</span> taunts me,  or I could bear <span class=\"supplied\">it</span>.  <span class=\"supplied\">It is not</span> one who hates me <span class=\"supplied\">that</span> magnifies himself over me,  or I could hide myself from him.'),
('LEB', 'Psalms', '55', '13', '1', 'But <span class=\"supplied\">it is</span> you, a man my equal,  my friend and confidant.'),
('LEB', 'Psalms', '55', '14', '1', '<span class=\"supplied\">We</span> who would take sweet counsel together;  in the house<span class=\"note\">Or “temple”</span>of God  we would walk with <span class=\"supplied\">the</span> throng.'),
('LEB', 'Psalms', '55', '15', '1', 'Let death deceive them. May they descend to Sheol alive,  because evil <span class=\"supplied\">is</span> in their home <span class=\"supplied\">and</span> <span class=\"idiom\">heart</span>.<span class=\"note\">Literally “inward part”</span>'),
('LEB', 'Psalms', '55', '16', '1', 'As for me, I will call to God,  and Adonai will save me.'),
('LEB', 'Psalms', '55', '17', '1', '<span class=\"idiom\">Morning, noon and night</span><span class=\"note\">Literally “evening and morning and noon”</span>I will lament and groan loudly,  and he will hear my voice.'),
('LEB', 'Psalms', '55', '18', '1', 'He safely redeems my life from <span class=\"supplied\">the</span> battle against me,<span class=\"note\">Or “my battle”</span> because <span class=\"supplied\">those standing</span> against me are among many.'),
('LEB', 'Psalms', '55', '19', '1', 'God will hear and answer them,<span class=\"note\">Or “and humble them”</span> <span class=\"supplied\">he</span> who <span class=\"supplied\">is</span> enthroned from of old, <span class=\"italic\">Selah</span>  Because they do not change,  and they do not fear God.'),
('LEB', 'Psalms', '55', '20', '1', 'He has put forth his hands against <span class=\"idiom\">his friends</span>;<span class=\"note\">Literally “those at peace with him”</span> he has defiled his covenant.'),
('LEB', 'Psalms', '55', '21', '1', 'The <span class=\"idiom\">buttery words</span><span class=\"note\">Literally “butter”</span>of his mouth were smooth,  but <span class=\"supplied\">there was</span> battle in his heart.  His words were smoother than oil,  but they <span class=\"supplied\">were</span> drawn swords.'),
('LEB', 'Psalms', '55', '22', '1', 'Cast your burden on Adonai,  and he will sustain you.  He will never allow the righteous to be moved.<span class=\"note\">Or “shaken”</span>'),
('LEB', 'Psalms', '55', '23', '1', 'But you, O God, you will bring them down to <span class=\"supplied\">the</span> pit of corruption.  <span class=\"supplied\">The</span> men of bloodshed and deceit will not live half their days,  but I will trust you.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '56', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, according to The Silent Dove of Distant <span class=\"supplied\">Lands</span>.  Of David. A miktam.  When <span class=\"supplied\">the</span> Philistines seized him in Gath.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Be gracious to me, O God, because humankind has trampled me;  fighting all the day he oppresses me.'),
('LEB', 'Psalms', '56', '2', '1', 'My enemies<span class=\"note\">Or “lurking foes”</span>trample all day,  because many <span class=\"supplied\">are</span> attacking me proudly.'),
('LEB', 'Psalms', '56', '3', '1', '<span class=\"idiom\">When</span><span class=\"note\">Literally “<span class=\"supplied\">The</span> day”</span>I fear, I trust you.'),
('LEB', 'Psalms', '56', '4', '1', 'God, whose word I praise,  God I trust; I do not fear.  What can <span class=\"supplied\">mere</span> flesh do to me?'),
('LEB', 'Psalms', '56', '5', '1', 'All day they twist my words;  all their thoughts <span class=\"supplied\">are</span> against me for evil.'),
('LEB', 'Psalms', '56', '6', '1', 'They attack,<span class=\"note\">Or “they stir up strife”</span>they hide, they watch my <span class=\"idiom\">steps</span>,<span class=\"note\">Literally “heels”</span> as they <span class=\"supplied\">lie in</span> wait for my life.'),
('LEB', 'Psalms', '56', '7', '1', 'Because of iniquity will they escape?  In anger cast down <span class=\"supplied\">the</span> peoples, O God.'),
('LEB', 'Psalms', '56', '8', '1', 'You have kept count of my wonderings.  Put my tears in your bottle;  <span class=\"supplied\">are they</span> not in your book?'),
('LEB', 'Psalms', '56', '9', '1', 'Then my enemies will turn back <span class=\"idiom\">when</span><span class=\"note\">Literally “in the day”</span>I call.<span class=\"note\">i.e., call on God</span> This I know because<span class=\"note\">Or “that”</span>God <span class=\"supplied\">is</span> for me.'),
('LEB', 'Psalms', '56', '10', '1', 'God, <span class=\"supplied\">whose</span> word I praise,  Adonai, <span class=\"supplied\">whose</span> word I praise,'),
('LEB', 'Psalms', '56', '11', '1', 'God I trust; I do not fear.  What can <span class=\"supplied\">mere</span> humankind do to me?'),
('LEB', 'Psalms', '56', '12', '1', '<span class=\"supplied\">My</span> vows <span class=\"supplied\">to you</span>, O God, <span class=\"supplied\">are binding</span> upon me.  I will pay thank offerings to you,'),
('LEB', 'Psalms', '56', '13', '1', 'because you have delivered my soul from death.  <span class=\"supplied\">Have you</span> not <span class=\"supplied\">kept</span> my feet from stumbling,  that I may walk before God  in the light of the living?')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '57', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, <span class=\"supplied\">according to</span> Do Not Destroy.  Of David. A miktam.  When he fled from Saul into the cave.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Be gracious to me, O God, be gracious to me,  because in you my soul takes refuge.  In the shadow of your wings I will take refuge  until destruction passes by.'),
('LEB', 'Psalms', '57', '2', '1', 'I will call to God Most High,  to God who accomplishes <span class=\"supplied\">things</span> concerning me.'),
('LEB', 'Psalms', '57', '3', '1', 'He will send from heaven and save me;  he will reproach <span class=\"supplied\">the</span> one who tramples me. <span class=\"italic\">Selah</span>  God will send his loyal love and his faithfulness.'),
('LEB', 'Psalms', '57', '4', '1', 'My soul <span class=\"supplied\">is</span> among lions.  I lie down <span class=\"supplied\">among those who</span> devour—  the children<span class=\"note\">Or “sons”</span>of humankind whose teeth <span class=\"supplied\">are</span> spears and arrows  and whose tongues <span class=\"supplied\">are</span> sharp swords.'),
('LEB', 'Psalms', '57', '5', '1', 'Be exalted above the heavens, O God.  <span class=\"supplied\">Let</span> your glory <span class=\"supplied\">be</span> above all the earth.'),
('LEB', 'Psalms', '57', '6', '1', 'They have set a net for my steps;  my soul is bowed down.  They have dug a pit before me;  they have fallen into the midst of it. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '57', '7', '1', 'My heart <span class=\"supplied\">is</span> steadfast, O God;  My heart <span class=\"supplied\">is</span> steadfast.  I will sing and give praise.'),
('LEB', 'Psalms', '57', '8', '1', 'Awake, my glory;  Awake, harp and lyre.  I will awake <span class=\"supplied\">the</span> dawn.<span class=\"note\">Or “<span class=\"supplied\">at</span> dawn”</span>'),
('LEB', 'Psalms', '57', '9', '1', 'I will give you thanks among the peoples, O Lord;  I will give you praise among <span class=\"supplied\">the</span> nations.'),
('LEB', 'Psalms', '57', '10', '1', 'Because your loyal love <span class=\"supplied\">is</span> high to <span class=\"supplied\">the</span> heavens,  and your faithfulness to <span class=\"supplied\">the</span> clouds.'),
('LEB', 'Psalms', '57', '11', '1', 'Be exalted above <span class=\"supplied\">the</span> heavens, O God.  <span class=\"supplied\">Let</span> your glory <span class=\"supplied\">be</span> above all the earth.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '58', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, <span class=\"supplied\">according to</span> Do not Destroy.  Of David. A miktam.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Do you really speak <span class=\"supplied\">what is</span> right <span class=\"supplied\">when</span> silent?<span class=\"note\">A slight emendation of the Hebrew text yields “what is right, O gods,” or “O mighty ones”</span> <span class=\"supplied\">Do</span> you judge fairly the children of humankind?<span class=\"note\">Or “Do you judge fairly, O sons of humankind?”</span>'),
('LEB', 'Psalms', '58', '2', '1', 'No, in <span class=\"supplied\">your</span> heart you plan injustices;  in the land<span class=\"note\">Or “on the earth”</span>you weigh out the violence of your hands.'),
('LEB', 'Psalms', '58', '3', '1', 'The wicked are estranged from <span class=\"supplied\">the</span> womb.  They go astray from <span class=\"supplied\">the</span> belly, speaking lies.'),
('LEB', 'Psalms', '58', '4', '1', 'Their venom <span class=\"supplied\">is</span> like snake venom;  <span class=\"supplied\">They are</span> like a deaf viper that closes its ear'),
('LEB', 'Psalms', '58', '5', '1', 'so that it does not hear <span class=\"supplied\">the</span> voice of charmers  <span class=\"supplied\">or the</span> skilled caster of spells.'),
('LEB', 'Psalms', '58', '6', '1', 'O God, break their teeth in their mouth.  Break off <span class=\"supplied\">the</span> fangs of <span class=\"supplied\">the</span> young lions, O Adonai.'),
('LEB', 'Psalms', '58', '7', '1', 'Let them run <span class=\"supplied\">away</span> like water <span class=\"supplied\">that</span> runs off.  <span class=\"supplied\">When</span> he bends <span class=\"supplied\">the bow</span>, let his arrows <span class=\"supplied\">be</span> as though they were cut off.<span class=\"note\">Or “as though they were withered <span class=\"supplied\">stalks</span>”</span>'),
('LEB', 'Psalms', '58', '8', '1', '<span class=\"supplied\">Let them be</span> like a snail <span class=\"supplied\">that</span> melts away <span class=\"supplied\">as</span> it goes;  <span class=\"supplied\">like the</span> stillborn of woman <span class=\"supplied\">that</span> do not see <span class=\"supplied\">the</span> sun.'),
('LEB', 'Psalms', '58', '9', '1', 'Before your pots can feel <span class=\"supplied\">the heat of</span> a thornbush,  whether green <span class=\"supplied\">or</span> <span class=\"idiom\">dry</span>,<span class=\"note\">Literally “burning”</span>he will sweep it away.<span class=\"note\">The Hebrew of this verse is difficult to interpret</span>'),
('LEB', 'Psalms', '58', '10', '1', '<span class=\"supplied\">The</span> righteous will rejoice when he sees <span class=\"supplied\">the</span> vengeance;  he will wash his feet in the blood of the wicked.'),
('LEB', 'Psalms', '58', '11', '1', 'And people<span class=\"note\">Hebrew “a person”</span>will say, “Surely <span class=\"supplied\">there is</span> a <span class=\"idiom\">reward</span><span class=\"note\">Literally “fruit”</span>for the righteous.  Surely there is a God <span class=\"supplied\">who</span> judges in the land.”<span class=\"note\">Or “on the earth”</span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '59', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, <span class=\"supplied\">according to</span> Do Not Destroy.  Of David. A miktam.  When Saul dispatched <span class=\"supplied\">men</span> and they watched the house to kill him.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Deliver me from my enemies, O my God.  Protect<span class=\"note\">The sense is that of making something inaccessibly high, like a fortress</span>me from those who rise up against me.'),
('LEB', 'Psalms', '59', '2', '1', 'Deliver me from workers of iniquity,  and from men of bloodshed save me.'),
('LEB', 'Psalms', '59', '3', '1', 'For look, they lie in wait for my life.<span class=\"note\">Hebrew “soul”</span> The mighty attack<span class=\"note\">Or “stir up strife”</span>against me,  not <span class=\"supplied\">because of</span> my transgression or my sin, O Adonai.'),
('LEB', 'Psalms', '59', '4', '1', 'Without guilt on my part they run and ready themselves.  Awake to meet me and see.'),
('LEB', 'Psalms', '59', '5', '1', 'And you, Adonai, God <span class=\"supplied\">of</span> hosts, <span class=\"supplied\">are</span> the God of Israel.  Rouse yourself to punish all the nations.  Do not be gracious to any <span class=\"supplied\">who</span> treacherously plot evil. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '59', '6', '1', 'They return at evening; they howl like dogs<span class=\"note\">Hebrew “dog”</span> while they prowl <span class=\"supplied\">the</span> city.'),
('LEB', 'Psalms', '59', '7', '1', 'Look, they pour out <span class=\"supplied\">talk</span> with their mouth.  Swords <span class=\"supplied\">are</span> on their lips,  for <span class=\"supplied\">they say</span>, “Who hears?”'),
('LEB', 'Psalms', '59', '8', '1', 'But you, O Adonai, will laugh at them;  you will mock all <span class=\"supplied\">the</span> nations.'),
('LEB', 'Psalms', '59', '9', '1', 'O my strength,<span class=\"note\">Reading with a few Hebrew manuscripts and the LXX, Targum and v. 17</span>I will watch for you,  because <span class=\"supplied\">you</span>, O God, <span class=\"supplied\">are</span> my (high) stronghold.'),
('LEB', 'Psalms', '59', '10', '1', 'My<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>), some Hebrew manuscripts and the LXX</span>God of loyal love will meet me;  God will cause me to look <span class=\"supplied\">in triumph</span> on my enemies.'),
('LEB', 'Psalms', '59', '11', '1', 'Do not kill them, lest my people forget.  Make them <span class=\"supplied\">to</span> wander by your power,  and bring them down, O Lord, our shield.'),
('LEB', 'Psalms', '59', '12', '1', '<span class=\"supplied\">By</span> the sin of their mouth <span class=\"supplied\">and</span> the words of their lips,  even in their pride, let them be trapped,  and for <span class=\"supplied\">the</span> curses<span class=\"note\">Hebrew “curse”</span>and lies<span class=\"note\">Hebrew “lie”</span>they proclaim.'),
('LEB', 'Psalms', '59', '13', '1', 'Destroy in anger; destroy so they are no more,  so that they may know that God <span class=\"supplied\">is</span> ruling in Jacob  to the ends of the earth. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '59', '14', '1', 'They return at evening; they howl like dogs<span class=\"note\">Hebrew “dog”</span> while they prowl <span class=\"supplied\">the</span> city.'),
('LEB', 'Psalms', '59', '15', '1', 'As for them, they wander for food.  If they are not satisfied, then they continue all night.<span class=\"note\">Or “then they growl”</span>'),
('LEB', 'Psalms', '59', '16', '1', 'But as for me, I will sing <span class=\"supplied\">of</span> your strength,  and I will hail your loyal love in the morning,  because you have been my <span class=\"supplied\">high</span> stronghold  and a refuge in my <span class=\"idiom\">time</span><span class=\"note\">Literally “day”</span>of trouble.'),
('LEB', 'Psalms', '59', '17', '1', 'O my strength, to you I will give praise,  because God <span class=\"supplied\">is</span> my <span class=\"supplied\">high</span> stronghold,  my God of loyal love.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '60', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, according to Shushan Eduth.<span class=\"note\">Perhaps “The Lily of Testimony”</span> A miktam of David. To teach.  When he fought Mesopotamia and Aram Zobah, and Joab returned and struck Edom in the Valley of Salt, twelve thousand <span class=\"supplied\">persons</span>.<span class=\"note\">The Hebrew Bible counts the superscription as the first two verses of the psalm; the English verse number is reduced by two</span></span>O God, you have rejected us. You have broken us.  You have been angry. Restore us!'),
('LEB', 'Psalms', '60', '2', '1', 'You have made <span class=\"supplied\">the</span> land quake. You have split it open.  Heal its fissures, because it totters.'),
('LEB', 'Psalms', '60', '3', '1', 'You have shown your people hard <span class=\"supplied\">things</span>;  You have given us wine that staggers.'),
('LEB', 'Psalms', '60', '4', '1', 'You have rallied those who fear you round a banner  <span class=\"idiom\">out of bowshot</span>,<span class=\"note\">Literally “from <span class=\"supplied\">the</span> presence of <span class=\"supplied\">the</span> bow”</span><span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '60', '5', '1', 'so that your beloved ones may be rescued.  Save by your right hand and answer us.'),
('LEB', 'Psalms', '60', '6', '1', 'God has spoken in his holiness,  “I will rejoice;  I will divide up Shechem,  and portion out the valley of Succoth.'),
('LEB', 'Psalms', '60', '7', '1', 'Gilead <span class=\"supplied\">is</span> mine, and Manasseh <span class=\"supplied\">is</span> mine,  and Ephraim <span class=\"supplied\">is</span> the <span class=\"idiom\">helmet for</span><span class=\"note\">Literally “protection of”</span>my head;  Judah <span class=\"supplied\">is</span> my scepter.<span class=\"note\">Or “commander’s rod”</span>'),
('LEB', 'Psalms', '60', '8', '1', 'Moab <span class=\"supplied\">is</span> my washing pot;  over Edom, I will cast my sandal.  On account of me, O Philistia, raise a shout.”'),
('LEB', 'Psalms', '60', '9', '1', 'Who will bring me to <span class=\"supplied\">the</span> fortified city?  Who will lead me to Edom?'),
('LEB', 'Psalms', '60', '10', '1', 'Have not you yourself rejected us, O God,  and not gone out with our armies, O God?'),
('LEB', 'Psalms', '60', '11', '1', 'Give us help against <span class=\"supplied\">the</span> adversary,  for the help of humankind <span class=\"supplied\">is</span> futile.'),
('LEB', 'Psalms', '60', '12', '1', 'Through God we will do <span class=\"idiom\">valiantly</span>,<span class=\"note\">Literally “might”</span> and <span class=\"supplied\">it is</span> he <span class=\"supplied\">who</span> will tread down our enemies.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '61', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, upon stringed instruments.<span class=\"note\">Reading with many Hebrew manuscripts and several translations</span>Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Hear, O God, my cry;  attend to my prayer.'),
('LEB', 'Psalms', '61', '2', '1', 'From the end of the earth<span class=\"note\">Or “the far reaches of the land”</span>I call to you  when my heart grows faint.  You will lead me to a rock <span class=\"supplied\">that is</span> higher than I.'),
('LEB', 'Psalms', '61', '3', '1', 'For you have been my refuge,  a strong tower from the enemy.'),
('LEB', 'Psalms', '61', '4', '1', 'Let me reside in your tent forever.  Let me take refuge under the covering of your wings. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '61', '5', '1', 'For you, O God, you have heard my vows.  You have given <span class=\"supplied\">me</span> the heritage of those who fear your name.'),
('LEB', 'Psalms', '61', '6', '1', 'You will add days to the days of the king,  his years like <span class=\"supplied\">those of</span> <span class=\"idiom\">many generations</span>.<span class=\"note\">Literally “a generation and a generation”</span>'),
('LEB', 'Psalms', '61', '7', '1', 'May he live<span class=\"note\">Or “be enthroned”</span>forever in the presence of God.  Appoint loyal love and faithfulness to preserve him.'),
('LEB', 'Psalms', '61', '8', '1', 'So I will sing the praise of your name forever,  that I may fulfill<span class=\"note\">Or “pay”</span>my vows <span class=\"idiom\">daily</span>.<span class=\"note\">Literally “day, day”</span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '62', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, on Jeduthun.<span class=\"note\">One of David’s musicians (1 Chr 16:41)</span> A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Only for God my soul <span class=\"supplied\">waits in</span> silence.  From him <span class=\"supplied\">is</span> my salvation.'),
('LEB', 'Psalms', '62', '2', '1', 'Only he <span class=\"supplied\">is</span> my rock and my salvation,  my <span class=\"supplied\">high</span> stronghold; I shall not be greatly shaken.'),
('LEB', 'Psalms', '62', '3', '1', 'How long will you attack a man?  All of you will be shattered<span class=\"note\">Or “All of you shatter <span class=\"supplied\">him</span>”</span> like a leaning wall, a tottering fence.'),
('LEB', 'Psalms', '62', '4', '1', 'They only<span class=\"note\">This word appears first in the Hebrew verse.</span>plan to thrust <span class=\"supplied\">him</span> down from his high position;  they are pleased <span class=\"supplied\">with</span> deception.  With their mouths<span class=\"note\">Hebrew “his mouth”</span>they bless,  but <span class=\"idiom\">inwardly</span><span class=\"note\">Literally “in the midst of them”</span>they curse. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '62', '5', '1', 'Only for God wait in silence, O my soul,  because my hope <span class=\"supplied\">is</span> from him.'),
('LEB', 'Psalms', '62', '6', '1', 'Only he <span class=\"supplied\">is</span> my rock and my salvation,  my <span class=\"supplied\">high</span> stronghold; I shall not be shaken.'),
('LEB', 'Psalms', '62', '7', '1', 'On God <span class=\"supplied\">rests</span> my salvation and my glory;  God<span class=\"note\">Or “In God”</span>is my strong rock, my refuge.'),
('LEB', 'Psalms', '62', '8', '1', 'Trust him at all times, O people;  pour out before him your heart.  God <span class=\"supplied\">is</span> a refuge for us. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '62', '9', '1', 'Only a vapor<span class=\"note\">Or “breath”</span><span class=\"supplied\">are</span> men of low degree,  a deception <span class=\"supplied\">are</span> men of high degree.  <span class=\"idiom\">Weighed</span><span class=\"note\">Literally “To go up”</span>in a balance,  together they <span class=\"supplied\">are lighter</span> than a vapor.'),
('LEB', 'Psalms', '62', '10', '1', 'Do not trust in extortion,  and do not put vain <span class=\"supplied\">confidence</span> in robbery.  If wealth increases,  do not set <span class=\"supplied\">your</span> heart <span class=\"supplied\">on it</span>.'),
('LEB', 'Psalms', '62', '11', '1', 'Once God has spoken;  twice I have heard this:  that strength <span class=\"supplied\">belongs</span> to God.'),
('LEB', 'Psalms', '62', '12', '1', 'And to you <span class=\"supplied\">belongs</span> loyal love, O Lord,  because you will render to each according to his work.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '63', '1', '1', '<span class=\"verse-title\">A psalm of David, when <span class=\"supplied\">he</span> was in the wilderness of Judah.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>O God, you <span class=\"supplied\">are</span> my God;  I will seek you diligently.  My soul thirsts for you; my flesh longs for you  <span class=\"supplied\">as</span> in a dry and weary land without water.'),
('LEB', 'Psalms', '63', '2', '1', 'Thus I have seen you in the sanctuary,  beholding your strength and glory.'),
('LEB', 'Psalms', '63', '3', '1', 'Because your loyal love <span class=\"supplied\">is</span> better than life,  my lips will praise you.'),
('LEB', 'Psalms', '63', '4', '1', 'So I will bless you while I live.  I will lift up my <span class=\"idiom\">hands</span><span class=\"note\">Hebrew “palms”</span>in your name.'),
('LEB', 'Psalms', '63', '5', '1', 'My soul will be satisfied <span class=\"supplied\">as with the</span> best and richest <span class=\"supplied\">food</span>,  and <span class=\"supplied\">with</span> joyful lips my mouth will praise.'),
('LEB', 'Psalms', '63', '6', '1', 'When I remember you on my bed,  I meditate on you in <span class=\"supplied\">the</span> night watches.'),
('LEB', 'Psalms', '63', '7', '1', 'Because you have been my help,  therefore<span class=\"note\">Hebrew “and”</span>in the shadow of your wings I will sing for joy.'),
('LEB', 'Psalms', '63', '8', '1', 'My soul clings to<span class=\"note\">Or “follows hard after”</span>you;  your right hand upholds me.'),
('LEB', 'Psalms', '63', '9', '1', 'But as for them <span class=\"supplied\">who</span> seek my life for ruin,  they will go into the depths of the earth.'),
('LEB', 'Psalms', '63', '10', '1', 'They <span class=\"supplied\">who</span> would <span class=\"idiom\">deliver him to the sword</span><span class=\"note\">Literally “pour him out to the hands of the sword”</span> will become a portion for jackals.'),
('LEB', 'Psalms', '63', '11', '1', 'However, the king will rejoice in God.  Everyone who swears an oath by him will boast,  because <span class=\"supplied\">the</span> mouth of <span class=\"supplied\">the</span> liars will be stopped.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '64', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Hear my voice, O God, in my lamenting.  Preserve my life from <span class=\"supplied\">the</span> fear of <span class=\"supplied\">the</span> enemy.'),
('LEB', 'Psalms', '64', '2', '1', 'Hide me from <span class=\"supplied\">the</span> secret plots<span class=\"note\">Hebrew “plot”</span>of evildoers,  from <span class=\"supplied\">the</span> unrest of <span class=\"supplied\">those who</span> do iniquity,'),
('LEB', 'Psalms', '64', '3', '1', 'who sharpen their tongue like a sword.  They bend <span class=\"supplied\">their bow to shoot</span> their arrows—bitter words,'),
('LEB', 'Psalms', '64', '4', '1', 'to shoot from<span class=\"note\">Hebrew “in”</span>their<span class=\"note\">Hebrew “the”</span>hiding places <span class=\"supplied\">at the</span> blameless.  Suddenly they shoot <span class=\"supplied\">at</span> him and do not fear.'),
('LEB', 'Psalms', '64', '5', '1', 'They encourage themselves <span class=\"supplied\">in the</span> evil matter.  They talk of secretly laying snares.  They say, “Who will see them?”'),
('LEB', 'Psalms', '64', '6', '1', '<span class=\"idiom\">They carefully plot</span><span class=\"note\">Literally “They search out”</span>injustices <span class=\"supplied\">saying</span>,  “We have perfected <span class=\"idiom\">the perfect plot</span>!”<span class=\"note\">Literally “a plotted plot”</span> Both <span class=\"supplied\">the</span> <span class=\"idiom\">inner thought</span><span class=\"note\">Literally “inward part”</span>and heart of <span class=\"supplied\">a</span> man <span class=\"supplied\">are</span> deep.'),
('LEB', 'Psalms', '64', '7', '1', 'But God will shoot them;  with an arrow they will suddenly be wounded.'),
('LEB', 'Psalms', '64', '8', '1', '<span class=\"note\">The Hebrew of this verse is very difficult</span>And they <span class=\"supplied\">who</span> would cause him<span class=\"note\">That is, the psalmist</span>to stumble,  their <span class=\"supplied\">own</span> tongue <span class=\"supplied\">will be</span> against them.  All who gaze on them will shake.<span class=\"note\">Perhaps a shaking of the head; a similar Hebrew form could be translated “will flee”</span>'),
('LEB', 'Psalms', '64', '9', '1', 'And then<span class=\"note\">Hebrew “and”</span>all men<span class=\"note\">Hebrew “man”</span>will fear,  and will declare the working of God,  and will understand his doing.'),
('LEB', 'Psalms', '64', '10', '1', '<span class=\"supplied\">The</span> righteous will rejoice in Adonai and take refuge in him,  and all <span class=\"supplied\">the</span> upright in heart will boast.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '65', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. A psalm of David. A song.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Praise awaits you, O God, in Zion,  and to you <span class=\"supplied\">the</span> vow shall be fulfilled.'),
('LEB', 'Psalms', '65', '2', '1', 'O <span class=\"supplied\">you who</span> hear prayer,  to you all flesh will come.'),
('LEB', 'Psalms', '65', '3', '1', 'Iniquities<span class=\"note\">Hebrew “matters of iniquities”</span>prevail over me.  As for our transgressions, you will <span class=\"idiom\">forgive</span><span class=\"note\">Literally “provide atonement for”</span>them.'),
('LEB', 'Psalms', '65', '4', '1', 'Blessed <span class=\"supplied\">is one whom</span> you choose and bring near,  <span class=\"supplied\">that</span> he may abide in your courts.  We will be satisfied with the goodness of your house,  your holy temple.'),
('LEB', 'Psalms', '65', '5', '1', 'By awesome deeds in righteousness you will answer us,  O God of our salvation,  <span class=\"supplied\">you who are the</span> confidence <span class=\"supplied\">of</span> all <span class=\"supplied\">the</span> ends of <span class=\"supplied\">the</span> earth  and <span class=\"supplied\">of</span> the far-off seas,'),
('LEB', 'Psalms', '65', '6', '1', '<span class=\"supplied\">who</span> established the mountains by his strength,  <span class=\"supplied\">the one who</span> is girded with might,'),
('LEB', 'Psalms', '65', '7', '1', '<span class=\"supplied\">who</span> stills <span class=\"supplied\">the</span> roar of <span class=\"supplied\">the</span> seas,  the roar of their waves,  and <span class=\"supplied\">the</span> commotion of <span class=\"supplied\">the</span> peoples,'),
('LEB', 'Psalms', '65', '8', '1', 'so that<span class=\"note\">Hebrew “and”</span><span class=\"supplied\">the</span> inhabitants of <span class=\"supplied\">the</span> farthest reaches are in awe of your signs.  You make the dawn and sunset sing for joy.'),
('LEB', 'Psalms', '65', '9', '1', 'You care for<span class=\"note\">Or “visit”</span>the land<span class=\"note\">Or “earth”</span>and water it;  you greatly enrich it.  The stream of God <span class=\"supplied\">is</span> filled with waters.  You provide their grain,  for so you have established it.'),
('LEB', 'Psalms', '65', '10', '1', 'You drench its furrows,  penetrating its ridges.  With rains you soften it;  its growth you bless.'),
('LEB', 'Psalms', '65', '11', '1', 'You crown the year with your <span class=\"idiom\">bounty</span>,<span class=\"note\">Literally “goodness”</span> and your wagon paths drip with <span class=\"idiom\">richness</span>.<span class=\"note\">Literally “fatness”</span>'),
('LEB', 'Psalms', '65', '12', '1', 'They drop <span class=\"supplied\">on the</span> pastures of <span class=\"supplied\">the</span> wilderness,  and the hills gird themselves with joy.'),
('LEB', 'Psalms', '65', '13', '1', '<span class=\"supplied\">The</span> pasturelands put on flocks,  and <span class=\"supplied\">the</span> valleys clothe themselves with grain.  They shout in triumph;  they even sing.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '66', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. A song. A psalm.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Shout joyfully to God, all the earth.'),
('LEB', 'Psalms', '66', '2', '1', 'Sing the glory of his name.  <span class=\"idiom\">Set forth his glorious praise</span>.<span class=\"note\">Literally “set for glory—his praise”</span>'),
('LEB', 'Psalms', '66', '3', '1', 'Say to God, “How awesome are your works!  Because of the greatness of your strength,  your enemies will cringe before you.'),
('LEB', 'Psalms', '66', '4', '1', 'All the earth will bow in worship to you,  and sing <span class=\"supplied\">praise</span> to you.  They will sing <span class=\"supplied\">the praise of</span> your name.” <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '66', '5', '1', 'Come and <span class=\"idiom\">consider</span><span class=\"note\">Literally “see”</span>the works of God;  <span class=\"supplied\">he is</span> awesome <span class=\"supplied\">in his</span> dealings<span class=\"note\">Hebrew “dealing”</span>with the children of humankind.'),
('LEB', 'Psalms', '66', '6', '1', 'He turned <span class=\"supplied\">the</span> sea into dry ground;  they passed through the river on foot.  There we rejoiced in him.'),
('LEB', 'Psalms', '66', '7', '1', '<span class=\"supplied\">He</span> rules by his might forever;  his eyes keep watch on the nations.  Do not let the rebellious exalt themselves.<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span><span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '66', '8', '1', 'Bless our God, O peoples,  and cause the sound of his praise to be heard,'),
('LEB', 'Psalms', '66', '9', '1', 'the one who has kept our soul among the living,  and has not allowed our foot to slip.'),
('LEB', 'Psalms', '66', '10', '1', 'For you have tested us, O God;  you have tried us as silver is tried.'),
('LEB', 'Psalms', '66', '11', '1', 'You brought us into the net;  you placed a <span class=\"idiom\">heavy burden</span><span class=\"note\">Literally “oppression”</span>on our backs.'),
('LEB', 'Psalms', '66', '12', '1', 'You let men<span class=\"note\">Hebrew “man”</span>ride over our heads.  We went through fire and through water,  but you have brought us out to the place of abundance.<span class=\"note\">A slightly different Hebrew word yields “a broad place”</span>'),
('LEB', 'Psalms', '66', '13', '1', 'I will come into your house<span class=\"note\">Or “temple”</span>with burnt offerings.  I will pay to you my vows'),
('LEB', 'Psalms', '66', '14', '1', 'that my lips uttered,  and my mouth spoke in my distress.'),
('LEB', 'Psalms', '66', '15', '1', 'Burnt offerings of fat animals I will offer to you,  with <span class=\"supplied\">the</span> smoke of rams.  I will do<span class=\"note\">That is, “offer”</span>cattle with rams. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '66', '16', '1', 'Come <span class=\"supplied\">and</span> hear, all you God-fearers, and I will tell  what he has done for me.<span class=\"note\">Hebrew “my soul”</span>'),
('LEB', 'Psalms', '66', '17', '1', 'I called to him with my mouth,  and he was extolled with my tongue.<span class=\"note\">A slightly different Hebrew word yields “his exaltation was under my tongue”</span>'),
('LEB', 'Psalms', '66', '18', '1', 'If I had considered evil in my heart,  the Lord would not have listened.'),
('LEB', 'Psalms', '66', '19', '1', 'However, God has listened;  he has attended to the sound of my prayer.'),
('LEB', 'Psalms', '66', '20', '1', 'Blessed be God,  who has not turned aside my prayer,  or his loyal love from me.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '67', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, with stringed instruments. A psalm. A song.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>May God be gracious to us and bless us.  May he cause his face to shine toward us, <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '67', '2', '1', 'that your way may be known on the earth,  your salvation among all nations.'),
('LEB', 'Psalms', '67', '3', '1', 'Let the peoples praise you, O God;  let all of the peoples praise you.'),
('LEB', 'Psalms', '67', '4', '1', 'Let the nations be glad and sing for joy,  because you judge <span class=\"supplied\">the</span> peoples with equity  and guide the nations on the earth. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '67', '5', '1', 'Let <span class=\"supplied\">the</span> peoples praise you, O God;  let all <span class=\"supplied\">the</span> peoples praise you.'),
('LEB', 'Psalms', '67', '6', '1', '<span class=\"supplied\">The</span> earth has yielded its produce.  God, our God, will bless us.'),
('LEB', 'Psalms', '67', '7', '1', 'God will bless us,  and all <span class=\"supplied\">the</span> ends of <span class=\"supplied\">the</span> earth will fear him.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '68', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. Of David. A psalm. A song.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>God will arise; his enemies will be scattered,  and those who hate him will flee from before him.'),
('LEB', 'Psalms', '68', '2', '1', 'As smoke is driven off, you will drive <span class=\"supplied\">them</span> off.  As wax melts before fire,  <span class=\"supplied\">the</span> wicked will perish before God.'),
('LEB', 'Psalms', '68', '3', '1', 'But <span class=\"supplied\">the</span> righteous will be glad; they will exalt before God,  and will rejoice with joy.'),
('LEB', 'Psalms', '68', '4', '1', 'Sing to God, sing praises to his name.  Lift up <span class=\"supplied\">a song</span> to <span class=\"supplied\">the</span> rider on the clouds—his name is Yah—<span class=\"note\">A shortened form of “Adonai”</span> and rejoice before him.'),
('LEB', 'Psalms', '68', '5', '1', 'A father to orphans and a judge for widows  <span class=\"supplied\">is</span> God in his holy habitation.'),
('LEB', 'Psalms', '68', '6', '1', 'God settles the lonely in a home;  he brings prisoners out into prosperity.  But the rebellious abide in a barren land.'),
('LEB', 'Psalms', '68', '7', '1', 'O God, when you went out before your people,  when you marched through <span class=\"supplied\">the</span> desert, <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '68', '8', '1', '<span class=\"supplied\">the</span> earth shook; <span class=\"supplied\">the</span> heavens also dripped <span class=\"supplied\">rain</span>  at the presence of God who <span class=\"supplied\">was</span> at Sinai,  at the presence of God, the God of Israel.'),
('LEB', 'Psalms', '68', '9', '1', 'You caused abundant rain to fall, O God.  When your inheritance was weary you revived it.'),
('LEB', 'Psalms', '68', '10', '1', 'Your community dwelled in it.  You provided in your goodness for the poor, O God.'),
('LEB', 'Psalms', '68', '11', '1', 'The Lord gives <span class=\"supplied\">the</span> word.  Great is the company of women who announce:'),
('LEB', 'Psalms', '68', '12', '1', '“<span class=\"supplied\">The</span> kings of armies flee, they flee,  and she <span class=\"supplied\">who</span> remains at home divides <span class=\"supplied\">the</span> spoil.'),
('LEB', 'Psalms', '68', '13', '1', 'Though you <span class=\"supplied\">men</span> lie down between the sheepfolds,  <span class=\"supplied\">you will be like the</span> wings of a dove covered in silver  and its pinions in yellow gold.”'),
('LEB', 'Psalms', '68', '14', '1', 'When Shaddai scattered kings on it,  on Zalmon it snowed.<span class=\"note\">Or “you caused it to snow on”</span>'),
('LEB', 'Psalms', '68', '15', '1', 'A mountain of God<span class=\"note\">Or “A mighty mountain”</span><span class=\"supplied\">is</span> the mountain of Bashan;  a mountain of <span class=\"supplied\">many</span> peaks <span class=\"supplied\">is</span> the mountain of Bashan.'),
('LEB', 'Psalms', '68', '16', '1', 'Why do you look with hostility, O many-peaked mountains?  This<span class=\"note\">Hebrew “The”</span>mountain God desires for his dwelling.  Yes, Adonai will abide <span class=\"supplied\">in it</span> forever.'),
('LEB', 'Psalms', '68', '17', '1', 'The chariots of God  <span class=\"supplied\">are</span> twice ten thousand, <span class=\"supplied\">with</span> thousands doubled.  The Lord <span class=\"supplied\">is</span> among them <span class=\"supplied\">at</span> Sinai, distinctive <span class=\"supplied\">in victory</span>.<span class=\"note\">Or “<span class=\"supplied\">as at</span> Sinai, in the sanctuary”</span>'),
('LEB', 'Psalms', '68', '18', '1', 'You have ascended on high; you have led away captives.  You have received gifts <span class=\"supplied\">from</span> among humankind,  and even <span class=\"supplied\">from the</span> rebellious, so that Yah<span class=\"note\">A shortened form of “Adonai”</span>God may dwell <span class=\"supplied\">there</span>.'),
('LEB', 'Psalms', '68', '19', '1', 'Blessed be the Lord. Daily he loads us <span class=\"supplied\">with benefits</span>,<span class=\"note\">Or “daily carries our burden”</span> the God <span class=\"supplied\">of</span> our salvation.<span class=\"note\">Or “victory”</span><span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '68', '20', '1', 'Our God <span class=\"supplied\">is</span> a God of deliverances,  and to <span class=\"supplied\">the</span> Adonai the Lord belong escapes from death.'),
('LEB', 'Psalms', '68', '21', '1', 'But God will shatter the head of his enemies,  the hairy scalp <span class=\"supplied\">of the one who</span> <span class=\"idiom\">continues on</span><span class=\"note\">Literally “is walking about”</span>in his guilt.'),
('LEB', 'Psalms', '68', '22', '1', 'The Lord said, “I will bring <span class=\"supplied\">them</span> back from Bashan;  I will bring <span class=\"supplied\">them</span> back from <span class=\"supplied\">the</span> depths of <span class=\"supplied\">the</span> sea,'),
('LEB', 'Psalms', '68', '23', '1', 'so that your foot may shatter <span class=\"supplied\">them</span> bloody  <span class=\"supplied\">for</span> the tongues of your dogs <span class=\"supplied\">to have</span> their share from <span class=\"supplied\">the</span> enemies.”'),
('LEB', 'Psalms', '68', '24', '1', 'They have seen your processions, O God,  the processions of my God, my king, distinctive <span class=\"supplied\">in victory</span>.<span class=\"note\">Or “into the sanctuary”</span>'),
('LEB', 'Psalms', '68', '25', '1', 'Singers went up front, those playing stringed instruments last,  between <span class=\"supplied\">them</span> young women <span class=\"supplied\">playing</span> tambourines.'),
('LEB', 'Psalms', '68', '26', '1', 'Bless God in <span class=\"supplied\">the</span> assemblies,  Adonai from the fountain of Israel.'),
('LEB', 'Psalms', '68', '27', '1', 'There <span class=\"supplied\">is</span> little Benjamin ruling them,  <span class=\"supplied\">with</span> the princes of Judah <span class=\"supplied\">in</span> their throng,  the princes of Zebulun <span class=\"supplied\">and</span> the princes of Naphtali.'),
('LEB', 'Psalms', '68', '28', '1', 'Your God has commanded your strength.  Show yourself strong, O God, <span class=\"supplied\">by</span> what you perform<span class=\"note\">Or “you who have worked”</span>for us.'),
('LEB', 'Psalms', '68', '29', '1', 'Because your temple <span class=\"supplied\">is</span> above Jerusalem,  kings will bring tribute to you.'),
('LEB', 'Psalms', '68', '30', '1', 'Rebuke <span class=\"supplied\">the</span> beasts in<span class=\"note\">Hebrew “of”</span><span class=\"supplied\">the</span> reeds,  <span class=\"supplied\">the</span> herd of bulls with <span class=\"supplied\">the</span> calves of <span class=\"supplied\">the</span> peoples,  trampling the pieces of silver.<span class=\"note\">Or “<span class=\"supplied\">until each</span> humbles himself <span class=\"supplied\">by offering</span> pieces of silver”</span> Scatter <span class=\"supplied\">the</span> nations <span class=\"supplied\">who</span> delight in battles.'),
('LEB', 'Psalms', '68', '31', '1', 'Ambassadors<span class=\"note\">Reading with the LXX; meaning uncertain</span>will come from Egypt;  Cush will quickly stretch out her hands to God.'),
('LEB', 'Psalms', '68', '32', '1', 'O kingdoms of the earth, sing to God;  sing praise to <span class=\"supplied\">the</span> Lord, <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '68', '33', '1', 'to the one who rides in the highest heavens of old.  See, he gives forth his voice, a mighty voice.'),
('LEB', 'Psalms', '68', '34', '1', 'Ascribe strength to God.  His majesty <span class=\"supplied\">is</span> over Israel,  and his strength <span class=\"supplied\">is</span> in the clouds.'),
('LEB', 'Psalms', '68', '35', '1', 'Awesome <span class=\"supplied\">are you</span>, O God, from your sanctuary.<span class=\"note\">Hebrew “sanctuaries”</span> The God of Israel, <span class=\"supplied\">it is</span> he who gives strength and might to the people.  Blessed be God.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '69', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, according to <span class=\"supplied\">The</span> Lilies. Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Save me, O God,  because waters have come up to <span class=\"supplied\">my</span> neck.<span class=\"note\">Hebrew “soul” or “life”</span>'),
('LEB', 'Psalms', '69', '2', '1', 'I sink in <span class=\"supplied\">the</span> mud of the deep, and there is no foothold.  I have come to watery depths,  and <span class=\"supplied\">the</span> torrent floods over me.'),
('LEB', 'Psalms', '69', '3', '1', 'I am weary with my calling <span class=\"supplied\">out</span>; my throat is parched.  My eyes are exhausted <span class=\"supplied\">in my</span> waiting for my God.'),
('LEB', 'Psalms', '69', '4', '1', 'More numerous than the hairs of my head  are <span class=\"supplied\">those</span> hating me without a cause.  <span class=\"supplied\">Those who are</span> destroying me—my enemies wrongfully<span class=\"note\">Or, “those who oppose me <span class=\"supplied\">with</span> falsehood”</span>— are mighty.  What I did not steal, I then must restore.'),
('LEB', 'Psalms', '69', '5', '1', 'O God, <span class=\"idiom\">you yourself know</span><span class=\"note\">Literally “you, you know”</span>my foolishness,  and my guilty deeds are not hidden from you.'),
('LEB', 'Psalms', '69', '6', '1', 'Let those who wait for you not be put to shame because of me,  O Lord Adonai <span class=\"supplied\">of</span> hosts.  Let those who seek you not be disgraced because of me,  O God of Israel.'),
('LEB', 'Psalms', '69', '7', '1', 'Because on account of you I have borne reproach;  disgrace has covered my face.'),
('LEB', 'Psalms', '69', '8', '1', 'I have become a stranger to my brothers  and a foreigner to my mother’s sons,'),
('LEB', 'Psalms', '69', '9', '1', 'because the zeal for your house<span class=\"note\">Or “temple”</span>has consumed me,  and the reproaches of those reproaching you have fallen on me.'),
('LEB', 'Psalms', '69', '10', '1', 'When I wept in the fasting of my soul,  it became reproaches for me.'),
('LEB', 'Psalms', '69', '11', '1', 'When I made sackcloth my clothing,  I became for them a byword.'),
('LEB', 'Psalms', '69', '12', '1', 'Those sitting at <span class=\"supplied\">the</span> gate talk about me  <span class=\"supplied\">as</span> also<span class=\"note\">Hebrew “and”</span><span class=\"supplied\">the</span> songs of <span class=\"supplied\">the</span> drunkards.'),
('LEB', 'Psalms', '69', '13', '1', 'But as for me, my prayer <span class=\"supplied\">is</span> to you, O Adonai, for a favorable time,  O God, according to the abundance of your loyal love.  Answer me with the faithfulness of your salvation.'),
('LEB', 'Psalms', '69', '14', '1', 'Deliver me from <span class=\"supplied\">the</span> mud and do not let me sink.  Let me be delivered from <span class=\"supplied\">those who</span> hate me  and from <span class=\"supplied\">the</span> watery depths.'),
('LEB', 'Psalms', '69', '15', '1', 'Do not let the torrent of waters flood over me,  or <span class=\"supplied\">the</span> deep swallow me,  or the pit close its mouth over me.'),
('LEB', 'Psalms', '69', '16', '1', 'Answer me, O Adonai, because your loyal love <span class=\"supplied\">is</span> good;  according to your abundant mercies, turn to me,'),
('LEB', 'Psalms', '69', '17', '1', 'and do not hide your face from your servant.  Because I am in trouble, answer me quickly.'),
('LEB', 'Psalms', '69', '18', '1', 'Draw near to my soul; redeem it.  Because of my enemies, ransom me.'),
('LEB', 'Psalms', '69', '19', '1', 'You know my reproach, my shame and my disgrace.  <span class=\"idiom\">Fully known</span><span class=\"note\">Literally “<span class=\"supplied\">right</span> in front of”</span>to you are all my adversaries.'),
('LEB', 'Psalms', '69', '20', '1', 'Reproach has broken my heart and I am sick.  And I hoped for sympathy, but there was none,  and for comforters, but I found none.'),
('LEB', 'Psalms', '69', '21', '1', 'They also gave me gall for food,<span class=\"note\">Or “they put poison in my food”</span> and for my thirst they gave me vinegar to drink.'),
('LEB', 'Psalms', '69', '22', '1', 'Let their table before them be a trap,  and their <span class=\"supplied\">times of</span> peace a snare.'),
('LEB', 'Psalms', '69', '23', '1', 'Let their eyes be dark so they cannot see,  and make their loins continually tremble.'),
('LEB', 'Psalms', '69', '24', '1', 'Pour out your indignation on them,  and let your burning anger overtake them.'),
('LEB', 'Psalms', '69', '25', '1', 'Let their camp be desolate.  Let none dwell in their tents,'),
('LEB', 'Psalms', '69', '26', '1', 'because they persecute <span class=\"supplied\">those</span> whom you, yourself, have struck,  and they tell of the pain of <span class=\"supplied\">those</span> you have wounded.'),
('LEB', 'Psalms', '69', '27', '1', '<span class=\"idiom\">Add guilt on top of their guilt</span>,<span class=\"note\">Literally “Give guilt on their guilt”</span> and do not let them <span class=\"idiom\">be acquitted</span>.<span class=\"note\">Literally “come into your righteousness”</span>'),
('LEB', 'Psalms', '69', '28', '1', 'Let them be blotted out of <span class=\"supplied\">the</span> book of <span class=\"supplied\">the</span> living,  and let them not be recorded with <span class=\"supplied\">the</span> righteous.'),
('LEB', 'Psalms', '69', '29', '1', 'But as for me, <span class=\"supplied\">though</span> I <span class=\"supplied\">am</span> afflicted and pained,  your salvation will protect<span class=\"note\">The sense is that of making something inaccessibly high, like a fortress</span>me, O God.'),
('LEB', 'Psalms', '69', '30', '1', 'I will praise the name of God in song,  and magnify him with thanksgiving.'),
('LEB', 'Psalms', '69', '31', '1', 'For Adonai it will be better than an ox <span class=\"supplied\">or</span> bull,  horned <span class=\"supplied\">and</span> hoofed.<span class=\"note\">That is, mature and ritually clean</span>'),
('LEB', 'Psalms', '69', '32', '1', '<span class=\"supplied\">The</span> afflicted will see <span class=\"supplied\">and</span> rejoice.  O God seekers, let your heart revive,<span class=\"note\">Or “live”</span>'),
('LEB', 'Psalms', '69', '33', '1', 'because Adonai hears <span class=\"supplied\">the</span> needy  and does not despise his <span class=\"supplied\">own who are</span> prisoners.'),
('LEB', 'Psalms', '69', '34', '1', 'Let heavens and earth praise him,  <span class=\"supplied\">the</span> seas and all that moves in them,'),
('LEB', 'Psalms', '69', '35', '1', 'because God will save Zion  and build the cities of Judah,  that they may dwell there and possess it.'),
('LEB', 'Psalms', '69', '36', '1', 'And the offspring of his servants will inherit it,  and <span class=\"supplied\">those who</span> love his name will abide in it.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '70', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. Of David. To bring to remembrance.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>O God, <span class=\"supplied\">make haste</span> to deliver me.  O Adonai, make haste to help me.'),
('LEB', 'Psalms', '70', '2', '1', 'Let them be ashamed and abashed  who seek my life.  Let them be repulsed and humiliated  <span class=\"supplied\">who</span> desire my harm.'),
('LEB', 'Psalms', '70', '3', '1', 'let them turn back because of their shame,  those who say, “Aha! Aha!”'),
('LEB', 'Psalms', '70', '4', '1', 'Let them rejoice and be glad in you,  all those who seek you.  Let them say continually, “God is great!”  <span class=\"supplied\">those</span> who love your salvation.'),
('LEB', 'Psalms', '70', '5', '1', 'But I <span class=\"supplied\">am</span> poor and needy;  O God hasten to me.  You <span class=\"supplied\">are</span> my help and my deliverer.  O Adonai, do not delay.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '71', '1', '1', '<span class=\"verse-title\">A Prayer to God the Rock of Refuge</span>In you, O Adonai, I have taken refuge;  let me never be put to shame.'),
('LEB', 'Psalms', '71', '2', '1', 'In your righteousness deliver me and rescue me;  incline your ear to me and save me.'),
('LEB', 'Psalms', '71', '3', '1', 'Be for me a rock of refuge to resort to always;  you have ordained<span class=\"note\">Or “commanded”</span>to save me,  because you <span class=\"supplied\">are</span> my rock and my fortress.'),
('LEB', 'Psalms', '71', '4', '1', 'My God, rescue me from <span class=\"supplied\">the</span> hand of <span class=\"supplied\">the</span> wicked,  from <span class=\"supplied\">the</span> <span class=\"idiom\">grasp</span><span class=\"note\">Literally “palm”</span>of <span class=\"supplied\">the</span> evildoer and <span class=\"supplied\">the</span> oppressor,'),
('LEB', 'Psalms', '71', '5', '1', 'because you <span class=\"supplied\">are</span> my hope,  O Lord Adonai, my confidence from my youth.'),
('LEB', 'Psalms', '71', '6', '1', 'Upon you I have leaned <span class=\"idiom\">from birth</span>.<span class=\"note\">Literally “from the belly”</span> <span class=\"supplied\">It was</span> you who <span class=\"idiom\">took</span><span class=\"note\">Literally “cut,” that is, the umbilical cord</span>me from the womb of my mother.  My praise <span class=\"supplied\">is</span> of you continually.'),
('LEB', 'Psalms', '71', '7', '1', 'I have become a wonder to many,  but you <span class=\"supplied\">are</span> my strong refuge.'),
('LEB', 'Psalms', '71', '8', '1', 'My mouth is filled with your praise,  with your glory all the day.'),
('LEB', 'Psalms', '71', '9', '1', 'Do not cast me away in the time of old age;  when my strength fails do not abandon me.'),
('LEB', 'Psalms', '71', '10', '1', 'For my enemies talk concerning me,  and those who watch for my life conspire together,'),
('LEB', 'Psalms', '71', '11', '1', 'saying, “His God has abandoned him.  Pursue and seize him, because there is no deliverer.”'),
('LEB', 'Psalms', '71', '12', '1', 'O God, do not be far from me.  My God, hurry to help me.'),
('LEB', 'Psalms', '71', '13', '1', 'Let them be ashamed; let them perish  who are <span class=\"idiom\">my adversaries</span>.<span class=\"note\">Literally “adversaries of my soul”</span> Let <span class=\"supplied\">them</span> wrap themselves with scorn and disgrace  who seek my harm.'),
('LEB', 'Psalms', '71', '14', '1', 'But as for me, I will hope continually  and increase your praise.'),
('LEB', 'Psalms', '71', '15', '1', 'My mouth will tell of your righteousness,  your salvation <span class=\"idiom\">all day long</span>,<span class=\"note\">Literally “all the day”</span> though I do not know <span class=\"supplied\">the full</span> sum <span class=\"supplied\">of them</span>.'),
('LEB', 'Psalms', '71', '16', '1', 'I will come in <span class=\"supplied\">to tell</span> the mighty deeds of Lord Adonai.  I will <span class=\"idiom\">make known</span><span class=\"note\">Literally “bring to remembrance”</span>your righteousness, yours only.'),
('LEB', 'Psalms', '71', '17', '1', 'O God, you have taught me from my youth,  and up to now I have proclaimed your wonderful deeds.'),
('LEB', 'Psalms', '71', '18', '1', 'And even when <span class=\"supplied\">I am</span> old and gray,  O God, do not abandon me  until I proclaim your <span class=\"idiom\">strength</span><span class=\"note\">Literally “arm”</span>to <span class=\"supplied\">this</span> generation,  your power to every <span class=\"supplied\">one that</span> comes <span class=\"supplied\">after</span>.'),
('LEB', 'Psalms', '71', '19', '1', 'And your righteousness, O God, <span class=\"supplied\">is</span> to <span class=\"supplied\">the</span> height <span class=\"supplied\">of heaven</span>.  You who have done great things,  O God, who <span class=\"supplied\">is</span> like you?'),
('LEB', 'Psalms', '71', '20', '1', 'You who have caused me<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>to see many troubles and evils,  you will again revive me.<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span> And from the depths of the earth  you will again bring me up.'),
('LEB', 'Psalms', '71', '21', '1', 'You will increase my greatness,  and you will comfort me all around.<span class=\"note\">A slightly different vocalization yields, “… comfort me again”; compare the LXX.</span>'),
('LEB', 'Psalms', '71', '22', '1', 'On my part, I will praise you with a stringed instrument,  <span class=\"supplied\">and</span> your faithfulness, O my God.  I will sing praises to you with a lyre,  O Holy One of Israel.'),
('LEB', 'Psalms', '71', '23', '1', 'My lips will sing for joy when I sing praises to you,  and my soul, which you have redeemed.'),
('LEB', 'Psalms', '71', '24', '1', 'My tongue also  will speak of your righteousness all the day,  because they have been put to shame, because they have been humiliated  who seek my harm.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '72', '1', '1', '<span class=\"verse-title\">Of Solomon.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>O God, give your judgments to <span class=\"supplied\">the</span> king,  and your righteousness to <span class=\"supplied\">the</span> king’s son.'),
('LEB', 'Psalms', '72', '2', '1', 'May he judge your people with righteousness,  and your poor with justice.'),
('LEB', 'Psalms', '72', '3', '1', 'Let <span class=\"supplied\">the</span> mountains yield prosperity<span class=\"note\">Or “peace”</span>for the people,  and <span class=\"supplied\">the</span> hills in righteousness.'),
('LEB', 'Psalms', '72', '4', '1', 'May he provide justice <span class=\"supplied\">for the</span> poor of <span class=\"supplied\">the</span> people,  save <span class=\"supplied\">the</span> children of <span class=\"supplied\">the</span> needy,  and crush <span class=\"supplied\">the</span> oppressor.'),
('LEB', 'Psalms', '72', '5', '1', 'May he live long<span class=\"note\">This presupposes the reading, suggested by the LXX, that might indicate that two of the Hebrew root letters have been inverted. The Hebrew reads “They will/Let them fear”</span><span class=\"idiom\">while the sun endures</span><span class=\"note\">Literally “with <span class=\"supplied\">the</span> sun”</span> <span class=\"idiom\">as long as the moon</span><span class=\"note\">Literally “in the presence of the moon”</span><span class=\"idiom\">for all generations</span>.<span class=\"note\">Literally “generation of generations”</span>'),
('LEB', 'Psalms', '72', '6', '1', 'May he descend like rain on mown grass,  like showers watering <span class=\"supplied\">the</span> earth.'),
('LEB', 'Psalms', '72', '7', '1', 'May <span class=\"supplied\">what is</span> right blossom in his days  and an abundance of peace, until <span class=\"supplied\">the</span> moon is no more.'),
('LEB', 'Psalms', '72', '8', '1', 'And may he rule from sea up to sea,  and from <span class=\"supplied\">the</span> River<span class=\"note\">That is, the Euphrates</span>to <span class=\"supplied\">the</span> edges of <span class=\"supplied\">the</span> land.'),
('LEB', 'Psalms', '72', '9', '1', 'Let <span class=\"supplied\">the</span> desert dwellers bow down before him,  and his enemies lick the dust.'),
('LEB', 'Psalms', '72', '10', '1', 'Let the kings of Tarshish and <span class=\"supplied\">the</span> islands bring tribute.  Let the kings of Sheba and Seba present gifts,'),
('LEB', 'Psalms', '72', '11', '1', 'and let all kings bow down to him.  Let all nations serve him.'),
('LEB', 'Psalms', '72', '12', '1', 'Indeed he will deliver <span class=\"supplied\">the</span> needy <span class=\"supplied\">who is</span> crying for help,  and <span class=\"supplied\">the</span> afflicted <span class=\"supplied\">who</span> has no helper.'),
('LEB', 'Psalms', '72', '13', '1', 'He will take pity on <span class=\"supplied\">the</span> helpless and needy,  and <span class=\"supplied\">the</span> lives of <span class=\"supplied\">the</span> needy he will save.'),
('LEB', 'Psalms', '72', '14', '1', 'From oppression and from violence  he will redeem their lives,  and their blood will be precious in his eyes.'),
('LEB', 'Psalms', '72', '15', '1', 'So may he live, and may gold from Sheba be given to him,  and may prayers be offered for him continually.  May blessings be invoked for him <span class=\"idiom\">all day long</span>.<span class=\"note\">Literally “all the day”</span>'),
('LEB', 'Psalms', '72', '16', '1', 'May there be an abundance of grain in the land  <span class=\"supplied\">even</span> on <span class=\"supplied\">the</span> top of <span class=\"supplied\">the</span> mountains.  May his crop<span class=\"note\">Or “May his descendants”</span>sway like the <span class=\"supplied\">trees of</span> Lebanon,  and may <span class=\"supplied\">those</span> from <span class=\"supplied\">the</span> city blossom like the grass of the earth.'),
('LEB', 'Psalms', '72', '17', '1', 'May his name endure forever.  May his name increase <span class=\"idiom\">as long as the sun shines</span>,<span class=\"note\">Literally “<span class=\"supplied\">in the</span> presence of the sun”</span> and let them be blessed in him.  Let all nations call him blessed.'),
('LEB', 'Psalms', '72', '18', '1', 'Blessed be Adonai, the God of Israel,  <span class=\"supplied\">who</span> alone does wonderful things.'),
('LEB', 'Psalms', '72', '19', '1', 'And blessed be his glorious name forever,  and may the whole earth be filled with his glory.  Amen and Amen.'),
('LEB', 'Psalms', '72', '20', '1', 'The prayers of David, the son of Jesse, are completed.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '73', '1', '1', '<span class=\"verse-title\">A song of Asaph.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Surely God is good to Israel,  to those pure of heart.'),
('LEB', 'Psalms', '73', '2', '1', 'But as for me, my feet had almost stumbled.  My steps had nearly slipped,'),
('LEB', 'Psalms', '73', '3', '1', 'because I envied the boastful  <span class=\"supplied\">when</span> I saw <span class=\"supplied\">the</span> well-being<span class=\"note\">Or “peace” or “prosperity”</span>of <span class=\"supplied\">the</span> wicked.'),
('LEB', 'Psalms', '73', '4', '1', 'For there are no pains up to their death,  and their bodies <span class=\"supplied\">are</span> <span class=\"idiom\">healthy</span>.<span class=\"note\">Literally “fat”</span>'),
('LEB', 'Psalms', '73', '5', '1', 'They do not have <span class=\"idiom\">ordinary trouble</span>,<span class=\"note\">Literally “the trouble of man”</span> and they are not plagued as <span class=\"supplied\">other</span> people.<span class=\"note\">Hebrew “humankind”</span>'),
('LEB', 'Psalms', '73', '6', '1', 'Therefore pride is their necklace;  an outfit of violence covers them.'),
('LEB', 'Psalms', '73', '7', '1', 'Their eye bulges from fat.<span class=\"note\">A slightly different Hebrew reading might be reflected in the LXX’s “their iniquity comes out of fatness”</span> Imaginings overflow <span class=\"supplied\">their</span> heart.'),
('LEB', 'Psalms', '73', '8', '1', 'They mock and speak maliciously of oppression;  they speak <span class=\"supplied\">as though</span> from on high.'),
('LEB', 'Psalms', '73', '9', '1', 'They set their mouth against the heavens,  and their tongue roams the earth.'),
('LEB', 'Psalms', '73', '10', '1', 'Therefore his people turn there,<span class=\"note\">That is, toward the wicked</span> and abundant waters are slurped up by them.'),
('LEB', 'Psalms', '73', '11', '1', 'And they say, “How does God know?”  and, “Does the Most High have knowledge?”'),
('LEB', 'Psalms', '73', '12', '1', 'See, these <span class=\"supplied\">are the</span> wicked,  and they increase wealth, ever carefree.'),
('LEB', 'Psalms', '73', '13', '1', 'Surely in vain I have kept my heart pure,  and washed my hands in innocence.'),
('LEB', 'Psalms', '73', '14', '1', 'And I have been plagued all day  and rebuked <span class=\"idiom\">every morning</span>.<span class=\"note\">Literally “to the mornings”</span>'),
('LEB', 'Psalms', '73', '15', '1', 'If I had said, “I will speak thus,”  behold, I would have acted treacherously  against your children’s generation.'),
('LEB', 'Psalms', '73', '16', '1', 'When I thought about <span class=\"supplied\">how</span> to understand this,  it <span class=\"supplied\">was</span> troubling in my eyes'),
('LEB', 'Psalms', '73', '17', '1', 'until I went into the sanctuary of God.  <span class=\"supplied\">Then</span> I understood their fate.'),
('LEB', 'Psalms', '73', '18', '1', 'Surely you set them on slippery places.  You cause them to fall onto ruin.<span class=\"note\">Or “fall for deceptions”</span>'),
('LEB', 'Psalms', '73', '19', '1', 'How they become a desolation in a moment!  They come to a complete end by terrors.'),
('LEB', 'Psalms', '73', '20', '1', 'Like a dream upon awakening,  when <span class=\"supplied\">you</span> wake up, O Lord,  you will despise their fleeting form.<span class=\"note\">Or “image”</span>'),
('LEB', 'Psalms', '73', '21', '1', 'When my heart was embittered  and I felt stabbed in my kidneys,'),
('LEB', 'Psalms', '73', '22', '1', 'then I was brutish and ignorant.  With you I was <span class=\"supplied\">like</span> the beasts.'),
('LEB', 'Psalms', '73', '23', '1', 'But I <span class=\"supplied\">am</span> continually with you;  you have hold of my right hand.'),
('LEB', 'Psalms', '73', '24', '1', 'You will guide me with your advice,  and afterward you will take me <span class=\"supplied\">into</span> honor.<span class=\"note\">Or “<span class=\"supplied\">to</span> glory”</span>'),
('LEB', 'Psalms', '73', '25', '1', 'Whom do I have in the heavens <span class=\"supplied\">except you</span>?  And with you I have no <span class=\"supplied\">other</span> desire on earth.'),
('LEB', 'Psalms', '73', '26', '1', 'My flesh and heart failed,  <span class=\"supplied\">but</span> God <span class=\"supplied\">is</span> the <span class=\"idiom\">strength</span><span class=\"note\">Literally “rock”</span>of my heart and my reward forever.'),
('LEB', 'Psalms', '73', '27', '1', 'For indeed, <span class=\"supplied\">those</span> distancing themselves from you will be ruined.  You destroy each who abandons you for harlotry.'),
('LEB', 'Psalms', '73', '28', '1', 'But as for me, the approach to God <span class=\"supplied\">is</span> for my good.  I have set the Lord Adonai <span class=\"supplied\">as</span> my refuge,  in order to tell all your works.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '74', '1', '1', '<span class=\"verse-title\">A maskil of Asaph.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Why, O God, have you rejected <span class=\"supplied\">us</span> forever?  <span class=\"supplied\">Why</span> does your anger smoke  against the sheep of your pasture?'),
('LEB', 'Psalms', '74', '2', '1', 'Remember your congregation <span class=\"supplied\">that</span> you bought long ago,  <span class=\"supplied\">when</span> you redeemed the tribe of your inheritance.  <span class=\"supplied\">Remember</span> <span class=\"idiom\">Mount Zion</span><span class=\"note\">Literally “the mountain of Zion”</span>where you have dwelt.'),
('LEB', 'Psalms', '74', '3', '1', 'Lift your steps to the perpetual ruins,  <span class=\"supplied\">to</span> all <span class=\"supplied\">that the</span> enemy has ruined in the sanctuary.'),
('LEB', 'Psalms', '74', '4', '1', 'Your enemies have roared in the midst of your meeting place;  they have set up their signs <span class=\"supplied\">for</span> signs.'),
('LEB', 'Psalms', '74', '5', '1', 'They are<span class=\"note\">Hebrew “He is”</span>known <span class=\"supplied\">to be</span> like <span class=\"supplied\">those who</span> <span class=\"idiom\">wield</span><span class=\"note\">Literally “lift up”</span> axes in a thicket of trees.'),
('LEB', 'Psalms', '74', '6', '1', 'And now<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>its<span class=\"note\">That is, the temple’s</span>carved works altogether  they have smashed with axe and hammer.'),
('LEB', 'Psalms', '74', '7', '1', 'They have set fire to your sanctuary.  They have defiled to the ground,  the dwelling place for your name.'),
('LEB', 'Psalms', '74', '8', '1', 'They have said in their heart,  “We will completely<span class=\"note\">Hebrew “altogether”</span>oppress them.”  They burned all the meeting places of God in the land.'),
('LEB', 'Psalms', '74', '9', '1', 'We do not see our signs,  and there is no longer a prophet.  No one with us knows how long.'),
('LEB', 'Psalms', '74', '10', '1', 'How long, O God, will <span class=\"supplied\">the</span> adversary taunt?  Will <span class=\"supplied\">the</span> enemy treat your name with contempt forever?'),
('LEB', 'Psalms', '74', '11', '1', 'Why do you draw back your hand, even your right hand?  <span class=\"supplied\">Take it</span> from your bosom;<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>destroy <span class=\"supplied\">them</span>!'),
('LEB', 'Psalms', '74', '12', '1', 'But God <span class=\"supplied\">has been</span> my king from long ago,  working salvation in the midst of the earth.'),
('LEB', 'Psalms', '74', '13', '1', 'You split open <span class=\"supplied\">the</span> sea by your strength;  You broke <span class=\"supplied\">the</span> heads of <span class=\"supplied\">the</span> sea monsters in the waters.'),
('LEB', 'Psalms', '74', '14', '1', 'You crushed the heads of Leviathan;  you gave him <span class=\"supplied\">as</span> food to <span class=\"supplied\">the</span> desert dwelling creatures.'),
('LEB', 'Psalms', '74', '15', '1', 'You split open spring and wadi.<span class=\"note\">A seasonal stream that is often dry</span> You dried up ever-flowing rivers.'),
('LEB', 'Psalms', '74', '16', '1', 'Yours <span class=\"supplied\">is the</span> day, yours <span class=\"supplied\">is the</span> night also.  You established light and <span class=\"supplied\">the</span> sun.'),
('LEB', 'Psalms', '74', '17', '1', 'You defined<span class=\"note\">Or “set”</span>all <span class=\"supplied\">the</span> boundaries of <span class=\"supplied\">the</span> earth;  Summer and winter—you formed them.'),
('LEB', 'Psalms', '74', '18', '1', 'O Adonai, remember this: <span class=\"supplied\">the</span> enemy taunts,  and foolish people treat your name with contempt.'),
('LEB', 'Psalms', '74', '19', '1', 'Do not give to beasts the life of your dove;  do not ever forget the life of your afflicted ones.'),
('LEB', 'Psalms', '74', '20', '1', 'Have regard for the covenant,  because <span class=\"supplied\">the</span> dark places of <span class=\"supplied\">the</span> land are full of  <span class=\"supplied\">the</span> haunts of violence.'),
('LEB', 'Psalms', '74', '21', '1', 'Do not let <span class=\"supplied\">the</span> oppressed turn back humiliated;  let <span class=\"supplied\">the</span> poor and needy praise your name.'),
('LEB', 'Psalms', '74', '22', '1', 'Rise up, O God, plead your cause;  remember the reproaching of you by <span class=\"supplied\">the</span> foolish <span class=\"idiom\">all day long</span>.<span class=\"note\">Literally “all the day”</span>'),
('LEB', 'Psalms', '74', '23', '1', 'Do not forget the sound of your adversaries,  the roar of those rising up against you ascending continually.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '75', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, <span class=\"supplied\">according to</span> Do Not Destroy.  A psalm of Asaph. A song.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>We give thanks to you, O God;  we give thanks, and your name is near.  Your wonderful deeds are told.<span class=\"note\">Hebrew “They tell your wonderful deeds”</span>'),
('LEB', 'Psalms', '75', '2', '1', '“I will indeed set an appointed time;  I will judge fairly.<span class=\"note\">Hebrew “I, I will judge” might be emphatic</span>'),
('LEB', 'Psalms', '75', '3', '1', '<span class=\"supplied\">The</span> earth and all its inhabitants <span class=\"supplied\">are</span> shaking;<span class=\"note\">Or “melting away”</span> I steady<span class=\"note\">Hebrew “I, I steady” might be emphatic</span>its columns. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '75', '4', '1', 'I say to the boastful, ‘Do not boast!’  and to the wicked, ‘Do not lift up <span class=\"supplied\">your</span> horn!'),
('LEB', 'Psalms', '75', '5', '1', 'Do not lift up high your<span class=\"note\">Plural</span>horn.  Do <span class=\"supplied\">not</span> speak with <span class=\"idiom\">arrogant pride</span>.’ ”<span class=\"note\">Hebrew “an unrestrained neck”</span>'),
('LEB', 'Psalms', '75', '6', '1', 'For <span class=\"supplied\">it is</span> not from <span class=\"supplied\">the</span> east or <span class=\"supplied\">the</span> west  and not from <span class=\"supplied\">the</span> south<span class=\"note\">Hebrew “wilderness”</span><span class=\"supplied\">that</span> lifting up <span class=\"supplied\">comes</span>,'),
('LEB', 'Psalms', '75', '7', '1', 'rather God <span class=\"supplied\">is the</span> judge;  one he brings low, and another he lifts up.'),
('LEB', 'Psalms', '75', '8', '1', 'For <span class=\"supplied\">there is</span> a cup in the hand of Adonai  with<span class=\"note\">Hebrew “and”</span>wine <span class=\"supplied\">that</span> foams, fully mixed,  and he pours out from this.  Surely all <span class=\"supplied\">the</span> wicked of <span class=\"supplied\">the</span> land  <span class=\"idiom\">will quaff it down <span class=\"supplied\">to</span> its dregs</span>. <span class=\"note\">Literally “they will drain, they will drink its dregs”</span>'),
('LEB', 'Psalms', '75', '9', '1', 'But as for me, I will proclaim forever;  I will sing praise to the God of Jacob.'),
('LEB', 'Psalms', '75', '10', '1', '“And all <span class=\"supplied\">the</span> horns of <span class=\"supplied\">the</span> wicked I will cut off.  <span class=\"supplied\">The</span> horns of <span class=\"supplied\">the</span> righteous will be lifted up.”')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '76', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, with stringed instruments.  A psalm of Asaph. A song.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>God <span class=\"supplied\">is</span> known in Judah.  His name <span class=\"supplied\">is</span> great in Israel.'),
('LEB', 'Psalms', '76', '2', '1', 'His den<span class=\"note\">Or “shelter”; but the image of the lion appears in view</span>has been in Salem,  his lair<span class=\"note\">Or “refuge”</span>in Zion.'),
('LEB', 'Psalms', '76', '3', '1', 'There he broke <span class=\"supplied\">the</span> flames of <span class=\"supplied\">the</span> bow,  <span class=\"supplied\">the</span> shield and sword and battle. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '76', '4', '1', 'Radiant<span class=\"note\">A possible change in the Hebrew text yields, “You are feared, majestic …” a form found in vv. 8(7), 13(12). This is possibly reflected in the reading of Theodotian</span>you <span class=\"supplied\">are</span>, majestic,  from<span class=\"note\">Or “more than”</span><span class=\"supplied\">the</span> mountains of prey.'),
('LEB', 'Psalms', '76', '5', '1', '<span class=\"supplied\">The</span> stouthearted are plundered;  they sleep their sleep,  and all <span class=\"supplied\">the</span> able men cannot <span class=\"idiom\">use</span><span class=\"note\">Literally “find”</span>their hands.'),
('LEB', 'Psalms', '76', '6', '1', 'At your rebuke, O God of Jacob,  both rider<span class=\"note\">Hebrew “chariot,” that is, charioteer</span>and horse slumber.'),
('LEB', 'Psalms', '76', '7', '1', '<span class=\"idiom\">You, indeed, are to be feared</span>,<span class=\"note\">Literally “You, feared you <span class=\"supplied\">are</span>”</span> and who can stand before you  when you <span class=\"supplied\">are</span> angry? <span class=\"note\">A slight change in the vocalization of the MT yields, “from the strength of your anger”</span>'),
('LEB', 'Psalms', '76', '8', '1', 'From heaven you proclaimed judgment.  <span class=\"supplied\">The</span> earth feared and was still'),
('LEB', 'Psalms', '76', '9', '1', 'when God rose for justice,  to save all <span class=\"supplied\">the</span> afflicted of <span class=\"supplied\">the</span> land. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '76', '10', '1', 'For the anger of humankind will praise you.  You will put on <span class=\"supplied\">the</span> remnant of anger.<span class=\"note\">Hebrew “angers”</span>'),
('LEB', 'Psalms', '76', '11', '1', 'Make vows and fulfill them to Adonai, your God;  let all surrounding him  bring tribute to the <span class=\"supplied\">one to be</span> feared.<span class=\"note\">Hebrew “to the fear”</span>'),
('LEB', 'Psalms', '76', '12', '1', 'He cuts off <span class=\"supplied\">the</span> spirit of leaders.  <span class=\"supplied\">He is to be</span> feared by <span class=\"supplied\">the</span> kings of <span class=\"supplied\">the</span> earth.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '77', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, on Jeduthun.<span class=\"note\">One of David’s musicians (1 Chr 16:41)</span> Of Asaph. A psalm.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>I cry out with my voice to God;  with my voice to God, that he may hear me.'),
('LEB', 'Psalms', '77', '2', '1', 'In the day I <span class=\"supplied\">have</span> trouble, I seek<span class=\"note\">Or “make supplication to”</span>the Lord.  At night my hand stretches out <span class=\"idiom\">continually</span>;<span class=\"note\">Literally “it does not grow weary”</span> my soul refuses to be comforted.'),
('LEB', 'Psalms', '77', '3', '1', 'I remember God and I groan loudly;  I meditate and my spirit grows faint.'),
('LEB', 'Psalms', '77', '4', '1', 'You hold <span class=\"supplied\">open</span> my eyelids.  I am troubled and cannot speak.'),
('LEB', 'Psalms', '77', '5', '1', 'I think about <span class=\"supplied\">the</span> days from long ago,  <span class=\"supplied\">the</span> years of ancient times.'),
('LEB', 'Psalms', '77', '6', '1', 'I remember my song in the night.  With my heart I meditate,  and my spirit searches <span class=\"supplied\">to understand</span>.'),
('LEB', 'Psalms', '77', '7', '1', 'Will the Lord reject <span class=\"supplied\">us</span> forever,  and will he never be pleased <span class=\"supplied\">with us</span> again?'),
('LEB', 'Psalms', '77', '8', '1', 'Has his loyal love ceased forever?  Is <span class=\"supplied\">his</span> promise<span class=\"note\">Hebrew “word”</span>ended throughout generations?'),
('LEB', 'Psalms', '77', '9', '1', 'Has God forgotten to have compassion?  Or has he closed off his mercies in anger? <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '77', '10', '1', 'So I said, “This pierces me—<span class=\"note\">Or “this is my sickness”</span> the right <span class=\"supplied\">hand</span> of the Most High <span class=\"supplied\">has</span> changed.”'),
('LEB', 'Psalms', '77', '11', '1', 'I will remember the deeds of Yah.<span class=\"note\">A shortened form of “Adonai”</span> Surely I will remember your wonders<span class=\"note\">Hebrew “wonder”</span>from long ago.'),
('LEB', 'Psalms', '77', '12', '1', 'I will also muse on all your work,  and meditate on your deeds.'),
('LEB', 'Psalms', '77', '13', '1', 'O God, your way <span class=\"supplied\">is</span> distinctive.<span class=\"note\">Or “holy” or “set apart”</span> Who <span class=\"supplied\">is</span> a great god like <span class=\"supplied\">our</span> God?'),
('LEB', 'Psalms', '77', '14', '1', 'You <span class=\"supplied\">are</span> the God who works wonders;<span class=\"note\">Hebrew “wonder”</span> you have made known your might among the peoples.'),
('LEB', 'Psalms', '77', '15', '1', 'With <span class=\"supplied\">your</span> arm you redeemed your people,  the children of Jacob and Joseph. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '77', '16', '1', 'Waters saw you, O God;  waters saw you <span class=\"supplied\">and</span> they trembled.  Surely <span class=\"supplied\">the</span> deeps shook.'),
('LEB', 'Psalms', '77', '17', '1', '<span class=\"supplied\">The</span> clouds poured out water.  <span class=\"supplied\">The</span> skies <span class=\"idiom\">thundered</span>.<span class=\"note\">Literally “gave voice”</span> Your arrows also <span class=\"idiom\">flew about</span>.<span class=\"note\">Literally “walked back and forth”</span>'),
('LEB', 'Psalms', '77', '18', '1', 'The sound of your thunder <span class=\"supplied\">was</span> in the whirlwind;<span class=\"note\">Or “in <span class=\"supplied\">your chariot</span> wheel”</span> lightnings lit <span class=\"supplied\">the</span> world;  the earth shook and quaked.'),
('LEB', 'Psalms', '77', '19', '1', 'Your way <span class=\"supplied\">was</span> through the sea,  and your path<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>through many waters.  Yet your footprints were not discerned.<span class=\"note\">Hebrew “were not known”</span>'),
('LEB', 'Psalms', '77', '20', '1', 'You led your people like a flock  by the hand of Moses and Aaron.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '78', '1', '1', '<span class=\"verse-title\">A maskil of Asaph.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Listen, O my people, to my teaching.  Incline your ears to the words of my mouth.'),
('LEB', 'Psalms', '78', '2', '1', 'I will offer<span class=\"note\">Hebrew “open”</span>a parable <span class=\"supplied\">with</span> my mouth.  I will pour out riddles from long ago,'),
('LEB', 'Psalms', '78', '3', '1', 'that we have heard and known,  and our ancestors<span class=\"note\">Or “fathers”</span>have told us.'),
('LEB', 'Psalms', '78', '4', '1', 'We will not hide <span class=\"supplied\">them</span> from their children,<span class=\"note\">Or “descendants”</span> telling the next generation the praises of Adonai,  and his power and his wonders that he has done.'),
('LEB', 'Psalms', '78', '5', '1', 'For he established a testimony in Jacob,  and appointed a law<span class=\"note\">Hebrew <span class=\"italic\">torah</span></span>in Israel,  which he commanded our ancestors<span class=\"note\">Or “fathers”</span> to teach to their children,'),
('LEB', 'Psalms', '78', '6', '1', 'so that <span class=\"supplied\">the</span> next generation might know—  children <span class=\"supplied\">yet to</span> be born—  <span class=\"supplied\">that</span> they might rise up and tell their children,'),
('LEB', 'Psalms', '78', '7', '1', 'that they might set their confidence in God,  and not forget the deeds of God,  but keep his commandments,'),
('LEB', 'Psalms', '78', '8', '1', 'and not be like their ancestors,<span class=\"note\">Or “fathers”</span> a stubborn and rebellious generation,  a generation <span class=\"supplied\">that</span> did not make ready its heart,  whose spirit was not faithful <span class=\"supplied\">to</span> God.'),
('LEB', 'Psalms', '78', '9', '1', 'The sons of Ephraim, <span class=\"idiom\">armed with archers</span>,<span class=\"note\">Literally “armed with shooters of the bow”</span> turned back on <span class=\"supplied\">the</span> day of battle.'),
('LEB', 'Psalms', '78', '10', '1', 'They did not keep the covenant of God  and refused to go in his law.<span class=\"note\">Hebrew <span class=\"italic\">torah</span></span>'),
('LEB', 'Psalms', '78', '11', '1', 'They also forgot his deeds,  and his wonders that he had shown them.'),
('LEB', 'Psalms', '78', '12', '1', 'In front of their ancestors<span class=\"note\">Or “fathers”</span>he did a wonder,  in the land of Egypt, <span class=\"supplied\">in</span> the region of Zoan.'),
('LEB', 'Psalms', '78', '13', '1', 'He split the sea and caused them to go over,  and he caused waters to stand like a heap.'),
('LEB', 'Psalms', '78', '14', '1', 'And he led them with the cloud by day,  and all night with a fiery light.'),
('LEB', 'Psalms', '78', '15', '1', 'He caused rocks to split in the wilderness  and provided drink abundantly as <span class=\"supplied\">from the</span> depths.'),
('LEB', 'Psalms', '78', '16', '1', 'And he brought streams out of <span class=\"supplied\">the</span> rock  and caused water to flow down like rivers.'),
('LEB', 'Psalms', '78', '17', '1', 'But they sinned still further against him  by rebelling <span class=\"supplied\">against the</span> Most High in the desert.'),
('LEB', 'Psalms', '78', '18', '1', 'And they tested God in their heart  <span class=\"supplied\">by</span> asking food <span class=\"idiom\">for their craving</span>.<span class=\"note\">Literally “for their soul”</span>'),
('LEB', 'Psalms', '78', '19', '1', 'And they spoke against God.  They said, “Is God able  to prepare a table in the wilderness?'),
('LEB', 'Psalms', '78', '20', '1', 'Yes, he struck <span class=\"supplied\">the</span> rock and water flowed  and streams gushed out,  <span class=\"supplied\">but</span> can he also give food  or provide meat for his people?”'),
('LEB', 'Psalms', '78', '21', '1', 'Therefore Adonai heard  and he was very angry,  and a fire was kindled against Jacob,  and <span class=\"supplied\">his</span> anger also rose up against Israel,'),
('LEB', 'Psalms', '78', '22', '1', 'because they did not believe God,  and they did not trust his salvation.'),
('LEB', 'Psalms', '78', '23', '1', 'Nevertheless, he commanded <span class=\"supplied\">the</span> skies above  and opened <span class=\"supplied\">the</span> doors of heaven,'),
('LEB', 'Psalms', '78', '24', '1', 'and rained down on them manna to eat  and gave them the grain of heaven.'),
('LEB', 'Psalms', '78', '25', '1', 'Humankind ate <span class=\"supplied\">the</span> bread of <span class=\"idiom\">angels</span>.<span class=\"note\">Literally “mighty ones”</span> He sent them food <span class=\"supplied\">enough</span> to be satisfied.'),
('LEB', 'Psalms', '78', '26', '1', 'He caused <span class=\"supplied\">the</span> east wind to blow in the heavens  and drove along <span class=\"supplied\">the</span> south wind by his strength.'),
('LEB', 'Psalms', '78', '27', '1', 'Then he rained meat on them like dust,  even winged birds<span class=\"note\">Hebrew “bird”</span>like <span class=\"supplied\">the</span> sand of <span class=\"supplied\">the</span> seas.'),
('LEB', 'Psalms', '78', '28', '1', 'He caused <span class=\"supplied\">them</span> to fall in the midst of his camp,  all around his dwellings.'),
('LEB', 'Psalms', '78', '29', '1', 'So they ate and were well filled,  and he brought about <span class=\"supplied\">what</span> they craved.'),
('LEB', 'Psalms', '78', '30', '1', 'They had not yet turned aside from their craving,  <span class=\"supplied\">while</span> their food <span class=\"supplied\">was</span> still in their mouth,'),
('LEB', 'Psalms', '78', '31', '1', 'the anger of God rose against them,  and he killed some of the stoutest of them,  even the young men of Israel he caused to bow down <span class=\"supplied\">in death</span>.'),
('LEB', 'Psalms', '78', '32', '1', 'In spite of all this they sinned further  and did not believe his wonders.'),
('LEB', 'Psalms', '78', '33', '1', 'And he consumed their days with futility <span class=\"note\">Or “he ended their days like a breath”</span> their years with terror.<span class=\"note\">Or “and their years suddenly”</span>'),
('LEB', 'Psalms', '78', '34', '1', 'When he killed <span class=\"supplied\">some of</span> them, then they sought him,  and repented and earnestly sought God.'),
('LEB', 'Psalms', '78', '35', '1', 'And they remembered that God <span class=\"supplied\">was</span> their rock,  and God Most High their redeemer.'),
('LEB', 'Psalms', '78', '36', '1', 'But they enticed him with their mouth  and lied to him with their tongue.'),
('LEB', 'Psalms', '78', '37', '1', 'For their heart was not steadfast with him,  nor were they faithful to his covenant.'),
('LEB', 'Psalms', '78', '38', '1', 'But he <span class=\"supplied\">was</span> compassionate; he pardoned<span class=\"note\">Or “covered,” or “atoned for”</span><span class=\"supplied\">their</span> guilt  and did not destroy <span class=\"supplied\">them</span>.  And many <span class=\"supplied\">times</span> he turned back his anger  and did not stir up all his wrath,'),
('LEB', 'Psalms', '78', '39', '1', 'for he remembered that they <span class=\"supplied\">were</span> flesh,  a passing wind that does not return.'),
('LEB', 'Psalms', '78', '40', '1', 'How often they rebelled against him in the wilderness  and vexed him in <span class=\"supplied\">the</span> wasteland!'),
('LEB', 'Psalms', '78', '41', '1', 'So they again tested God  and distressed<span class=\"note\">Or perhaps, “pressed to the limit”</span>the Holy One of Israel.'),
('LEB', 'Psalms', '78', '42', '1', 'And they did not remember his <span class=\"idiom\">power</span><span class=\"note\">Literally “hand”</span> <span class=\"idiom\">when</span><span class=\"note\">Literally “the day that”</span>he redeemed them from the enemy,'),
('LEB', 'Psalms', '78', '43', '1', 'how he performed<span class=\"note\">Hebrew “set”</span>his signs in Egypt  and his wonders in the region of Zoan,'),
('LEB', 'Psalms', '78', '44', '1', 'when he turned their rivers<span class=\"note\">Normally the word for the Nile, with the plural here suggesting perhaps it and its canals</span>to blood  so they could not drink <span class=\"supplied\">from</span> their streams.'),
('LEB', 'Psalms', '78', '45', '1', 'He sent among them flies that devoured them  and frogs that destroyed them.'),
('LEB', 'Psalms', '78', '46', '1', 'And he gave their crop to the locusts  and their labor to the locust.<span class=\"note\">Two different words translated “locusts”</span>'),
('LEB', 'Psalms', '78', '47', '1', 'He destroyed their vines with hail  and their sycamore trees with sleet.<span class=\"note\">The word for “sleet” is only used here, and the translation is a guess based on context</span>'),
('LEB', 'Psalms', '78', '48', '1', 'He also handed their cattle over to the hail  and their livestock to the lightning bolts.'),
('LEB', 'Psalms', '78', '49', '1', 'He sent against them his fierce anger,  rage and indignation and trouble,  a band of <span class=\"idiom\">destroying</span><span class=\"note\">Literally “evil”</span>angels.'),
('LEB', 'Psalms', '78', '50', '1', 'He cleared a path for his anger.  He did not spare them<span class=\"note\">Hebrew “their soul”</span>from death  but handed their life over to the plague.'),
('LEB', 'Psalms', '78', '51', '1', 'And he struck down all <span class=\"supplied\">the</span> firstborn in Egypt,  <span class=\"supplied\">the</span> first of <span class=\"supplied\">their</span> virility in the tents of Ham.'),
('LEB', 'Psalms', '78', '52', '1', 'Then he led out his people like sheep  and guided them like a herd in the wilderness.'),
('LEB', 'Psalms', '78', '53', '1', 'And he led them safely and they were not afraid,  but the sea covered their enemies.'),
('LEB', 'Psalms', '78', '54', '1', 'So he brought them to his holy territory,<span class=\"note\">Hebrew “border”</span> this mountain his right hand acquired.<span class=\"note\">Or “had created”</span>'),
('LEB', 'Psalms', '78', '55', '1', 'And he drove out nations before them  and allocated them for an inheritance by <span class=\"supplied\">boundary</span> line,  and settled the tribes of Israel in their tents.'),
('LEB', 'Psalms', '78', '56', '1', 'But they tested and rebelled against God Most High  and did not keep his statutes.'),
('LEB', 'Psalms', '78', '57', '1', 'And they turned and were treacherous like their ancestors.<span class=\"note\">Or “fathers”</span> They twisted like a crooked<span class=\"note\">Or “deceitful”</span>bow.'),
('LEB', 'Psalms', '78', '58', '1', 'For they provoked him to anger with their high places,  and made him jealous with their images.'),
('LEB', 'Psalms', '78', '59', '1', 'God heard and he was very angry  and rejected Israel utterly.'),
('LEB', 'Psalms', '78', '60', '1', 'So he abandoned the dwelling place at Shiloh,  <span class=\"supplied\">the</span> tent he had placed among humankind.'),
('LEB', 'Psalms', '78', '61', '1', 'And he gave his strength into captivity  and his splendor into <span class=\"supplied\">the</span> hand of <span class=\"supplied\">the</span> enemy.'),
('LEB', 'Psalms', '78', '62', '1', 'He also handed his people over to the sword,  and he was very angry with his inheritance.'),
('LEB', 'Psalms', '78', '63', '1', 'Fire devoured his young men,  and his young women<span class=\"note\">Or “virgins”</span>were not praised.'),
('LEB', 'Psalms', '78', '64', '1', 'His priests fell by the sword,  and his widows did not weep.'),
('LEB', 'Psalms', '78', '65', '1', 'Then the Lord awoke like <span class=\"supplied\">one who had been</span> asleep,  <span class=\"supplied\">awoke</span> like a warrior who had been drunk with wine.<span class=\"note\">Or “like a warrior shouting because of wine”</span>'),
('LEB', 'Psalms', '78', '66', '1', 'And he beat back his enemies;  he gave them over to perpetual scorn.'),
('LEB', 'Psalms', '78', '67', '1', 'And he rejected the tent of Joseph,  and did not chose the tribe of Ephraim,'),
('LEB', 'Psalms', '78', '68', '1', 'but chose the tribe of Judah,  <span class=\"idiom\">Mount Zion</span><span class=\"note\">Literally “the mountain of Zion”</span>that he loved.'),
('LEB', 'Psalms', '78', '69', '1', 'And he built his sanctuary like <span class=\"supplied\">the</span> heights,  like <span class=\"supplied\">the</span> earth that he established forever.'),
('LEB', 'Psalms', '78', '70', '1', 'And he chose David his servant  and took him from <span class=\"supplied\">the</span> sheepfolds.'),
('LEB', 'Psalms', '78', '71', '1', 'He brought him from following nursing ewes  to shepherd Jacob, his people,  and Israel, his inheritance.'),
('LEB', 'Psalms', '78', '72', '1', 'And he shepherded them according to the integrity of his heart,  and led them by the skillfulness of his hands.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '79', '1', '1', '<span class=\"verse-title\">A psalm of Asaph.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>O God, <span class=\"supplied\">the</span> nations have entered your inheritance;  they have defiled your holy temple;  they have reduced Jerusalem to ruins.'),
('LEB', 'Psalms', '79', '2', '1', 'They have given the bodies of your servants  as food for the birds of the heavens,  the flesh of your faithful to <span class=\"supplied\">the</span> beasts of <span class=\"supplied\">the</span> earth.'),
('LEB', 'Psalms', '79', '3', '1', 'They have poured out their blood like water  all around Jerusalem,  and there was none to bury <span class=\"supplied\">them</span>.'),
('LEB', 'Psalms', '79', '4', '1', 'We have become a taunt to our neighbors,  a derision and a scorn to those around us.'),
('LEB', 'Psalms', '79', '5', '1', 'How long, O Adonai? Will you be angry forever?  Will your jealousy burn like fire?'),
('LEB', 'Psalms', '79', '6', '1', 'Pour out your anger on the nations  that do not know you,  and on <span class=\"supplied\">the</span> kingdoms  that do not call on your name,'),
('LEB', 'Psalms', '79', '7', '1', 'because they<span class=\"note\">Hebrew “he”</span>have devoured Jacob  and have laid waste his habitation.'),
('LEB', 'Psalms', '79', '8', '1', 'Do not remember against us former iniquities;  let your mercies meet us quickly  because we are brought very low.'),
('LEB', 'Psalms', '79', '9', '1', 'Help us, O God of our salvation,  for the glory of your name;  and deliver us and forgive<span class=\"note\">Hebrew “make atonement for”</span>our sins  for the sake of your name.'),
('LEB', 'Psalms', '79', '10', '1', 'Why should the nations say,  “Where <span class=\"supplied\">is</span> their God?”  Let it<span class=\"note\">That is, God’s name</span>be known among the nations before our eyes,  <span class=\"supplied\">by</span> the avenging of the blood of your servants  that was poured out.'),
('LEB', 'Psalms', '79', '11', '1', 'Let the groaning of the prisoner come before you.  According to the greatness of your <span class=\"idiom\">power</span>,<span class=\"note\">Literally “arm”</span> spare<span class=\"note\">Or “leave a remnant of”</span><span class=\"supplied\">the</span> children <span class=\"supplied\">appointed to</span> death.'),
('LEB', 'Psalms', '79', '12', '1', 'And return to our neighbors sevenfold <span class=\"idiom\">upon them</span><span class=\"note\">Hebrew “into their bosom”</span> their taunts <span class=\"supplied\">with</span> which they taunted you, O Lord.'),
('LEB', 'Psalms', '79', '13', '1', 'Then we, your people and the flock of your pasture,  we will give thanks to you forever.  <span class=\"idiom\">Generation after generation</span><span class=\"note\">Literally “to a generation and a generation”</span> we will tell of your praise.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '80', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director, according to <span class=\"supplied\">The</span> Lilies.  A testimony. Of Asaph. A psalm.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Give ear, O shepherd of Israel,  <span class=\"supplied\">who</span> leads Joseph like a flock.  Shine forth, <span class=\"supplied\">you who</span> sits <span class=\"supplied\">enthroned above</span> the cherubim.'),
('LEB', 'Psalms', '80', '2', '1', 'Before Ephraim, Benjamin, and Manasseh,  stir up your power  and come for our salvation.'),
('LEB', 'Psalms', '80', '3', '1', 'O God, restore us,  and cause your face to shine that we may be saved.'),
('LEB', 'Psalms', '80', '4', '1', 'O Adonai <span class=\"supplied\">of</span> hosts,  <span class=\"idiom\">how long will you be angry</span><span class=\"note\">Literally “until when <span class=\"supplied\">your anger</span> smoke”</span> against the prayer of your people?'),
('LEB', 'Psalms', '80', '5', '1', 'You have fed them <span class=\"supplied\">the</span> bread of tears;  you have given them tears to drink in full measure.<span class=\"note\">The Hebrew “threefold” or “a third of a measure” is difficult</span>'),
('LEB', 'Psalms', '80', '6', '1', 'You have made us <span class=\"supplied\">an object of</span> strife to our neighbors,  and our enemies mock among themselves.'),
('LEB', 'Psalms', '80', '7', '1', 'O God <span class=\"supplied\">of</span> hosts, restore us  and cause your face to shine that we may be saved.'),
('LEB', 'Psalms', '80', '8', '1', 'You uprooted a vine from Egypt;  you drove out <span class=\"supplied\">the</span> nations and planted it.'),
('LEB', 'Psalms', '80', '9', '1', 'You prepared <span class=\"supplied\">a place</span> before it,  and <span class=\"idiom\">it took deep root</span><span class=\"note\">Literally “its root rooted”</span>and filled <span class=\"supplied\">the</span> land.'),
('LEB', 'Psalms', '80', '10', '1', '<span class=\"supplied\">The</span> mountains were covered with its shade,  and <span class=\"supplied\">the</span> mighty cedars <span class=\"supplied\">with</span> its boughs.'),
('LEB', 'Psalms', '80', '11', '1', 'It spread its branches to <span class=\"supplied\">the</span> sea  and its shoots to <span class=\"supplied\">the</span> river.'),
('LEB', 'Psalms', '80', '12', '1', 'Why have you broken down its walls,  so that all <span class=\"supplied\">who</span> pass on <span class=\"supplied\">the</span> road pluck <span class=\"supplied\">fruit from</span> it?'),
('LEB', 'Psalms', '80', '13', '1', 'Swine from the forests devour<span class=\"note\">Hebrew “devours”</span>it  and creatures of <span class=\"supplied\">the</span> field feed on it.'),
('LEB', 'Psalms', '80', '14', '1', 'Please return, O God <span class=\"supplied\">of</span> hosts.  Observe from heaven and see,  and pay attention to this vine,'),
('LEB', 'Psalms', '80', '15', '1', 'even<span class=\"supplied\">the</span> stalk that your right hand planted,  and concerning <span class=\"supplied\">the</span> shoot<span class=\"note\">Hebrew “son”</span>you strengthened for yourself.'),
('LEB', 'Psalms', '80', '16', '1', '<span class=\"supplied\">It is</span> burned with fire, cut down.  They perish at the rebuke of your face.'),
('LEB', 'Psalms', '80', '17', '1', 'Let your hand be on the man of your right hand,  on <span class=\"supplied\">the</span> son of humankind <span class=\"supplied\">whom</span> you made strong for yourself.'),
('LEB', 'Psalms', '80', '18', '1', 'Then we will not turn back from you.  Restore us to life, and we will proclaim your name.'),
('LEB', 'Psalms', '80', '19', '1', 'O Adonai <span class=\"supplied\">of</span> hosts, restore us;  cause your face to shine that we may be saved.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '81', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director; on the Gittith. Of Asaph.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Shout out to God our strength;  shout joyfully to the God of Jacob.'),
('LEB', 'Psalms', '81', '2', '1', 'Lift up a song and strike<span class=\"note\">Hebrew “give”</span><span class=\"supplied\">the</span> tambourine,  <span class=\"supplied\">the</span> pleasant lyre, together with <span class=\"supplied\">the</span> harp.'),
('LEB', 'Psalms', '81', '3', '1', 'Blow <span class=\"supplied\">the</span> horn at new moon,  at full moon, for our feast day,'),
('LEB', 'Psalms', '81', '4', '1', 'because it <span class=\"supplied\">is</span> a statute for Israel,  an ordinance of the God of Jacob.'),
('LEB', 'Psalms', '81', '5', '1', 'He made it a statute<span class=\"note\">Hebrew “testimony” or “witness”</span>in Joseph  when he went out against the land of Egypt,  <span class=\"supplied\">where</span> I heard a language I did not know.<span class=\"note\">The meaning of this line is uncertain. The above is based on perhaps a similar thought in Psa 114:1</span>'),
('LEB', 'Psalms', '81', '6', '1', '“I removed his shoulder from a burden.  His hands were freed from <span class=\"supplied\">the</span> basket.'),
('LEB', 'Psalms', '81', '7', '1', 'In this<span class=\"note\">Hebrew “the”</span>trouble you called, and I rescued you.  Within <span class=\"supplied\">the</span> secret place of thunder I answered you;  I tested you at the waters of Meribah. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '81', '8', '1', 'Hear, O my people, and I will admonish you;  O Israel, if you would <span class=\"supplied\">but</span> listen to me.'),
('LEB', 'Psalms', '81', '9', '1', 'There shall be no strange god among you,  and you shall not bow down to a foreign god.'),
('LEB', 'Psalms', '81', '10', '1', 'I <span class=\"supplied\">am</span> Adonai your God,  who brought you up from the land of Egypt.  Open wide your mouth and I will fill it.'),
('LEB', 'Psalms', '81', '11', '1', 'But my people did not listen to my voice,  and Israel did not yield to me.'),
('LEB', 'Psalms', '81', '12', '1', 'So I let them<span class=\"note\">Hebrew “him”</span>go in the stubbornness of their heart;  they walked in their counsels.'),
('LEB', 'Psalms', '81', '13', '1', 'Oh that my people would listen to me;  <span class=\"supplied\">that</span> Israel would walk in my ways.'),
('LEB', 'Psalms', '81', '14', '1', 'I would subdue their enemies quickly,  and turn my hand against their adversaries.'),
('LEB', 'Psalms', '81', '15', '1', 'Those who hate Adonai would cringe before him,  and their fate<span class=\"note\">Hebrew “time”</span>would be forever.'),
('LEB', 'Psalms', '81', '16', '1', 'But he would feed him<span class=\"note\">That is, Israel</span>from <span class=\"supplied\">the</span> choicest wheat,<span class=\"note\">Hebrew “<span class=\"supplied\">the</span> fat of wheat”</span> and I would satisfy you with honey from a rock.”')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '82', '1', '1', '<span class=\"verse-title\">A psalm of Asaph.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>God stands in the divine assembly;<span class=\"note\">Or “assembly of <span class=\"italic\">El</span> ”</span> he administers judgment in the midst of <span class=\"supplied\">the</span> gods.<span class=\"note\">Hebrew <span class=\"italic\">elohim</span></span>'),
('LEB', 'Psalms', '82', '2', '1', '“How long will you judge unjustly  and <span class=\"idiom\">show favoritism to the wicked</span>?<span class=\"note\">Literally “lift up the faces of the wicked”</span><span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '82', '3', '1', 'Judge <span class=\"supplied\">on behalf of the</span> helpless and <span class=\"supplied\">the</span> orphan;  provide justice <span class=\"supplied\">to the</span> afflicted and <span class=\"supplied\">the</span> poor.'),
('LEB', 'Psalms', '82', '4', '1', 'Rescue <span class=\"supplied\">the</span> helpless and <span class=\"supplied\">the</span> needy;  deliver <span class=\"supplied\">them</span> from the hand of <span class=\"supplied\">the</span> wicked.”'),
('LEB', 'Psalms', '82', '5', '1', 'They do not know or consider.<span class=\"note\">Or “understand”</span> They go about in the darkness,  <span class=\"supplied\">so that</span> all <span class=\"supplied\">the</span> foundations of <span class=\"supplied\">the</span> earth are shaken.'),
('LEB', 'Psalms', '82', '6', '1', 'I have said,<span class=\"note\">Hebrew “I, I have said”</span>“You <span class=\"supplied\">are</span> gods,  and sons of the Most High, all of you.'),
('LEB', 'Psalms', '82', '7', '1', 'However, you will die like men,<span class=\"note\">Singular</span> and you will fall like one of the princes.”'),
('LEB', 'Psalms', '82', '8', '1', 'Rise up, O God, judge the earth,  because you shall inherit<span class=\"note\">Or “receive an inheritance”</span>all the nations.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '83', '1', '1', '<span class=\"verse-title\">A song. A psalm of Asaph.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>O God, do not rest <span class=\"supplied\">silently</span>.  Do not keep silent or be still, O God.'),
('LEB', 'Psalms', '83', '2', '1', 'For look, your enemies roar,  and those who hate you have lifted <span class=\"supplied\">their</span> head.'),
('LEB', 'Psalms', '83', '3', '1', 'They devise cunning schemes<span class=\"note\">Hebrew “scheme”</span>against your people,  and consult together against your protected ones.<span class=\"note\">Or “your treasured ones”</span>'),
('LEB', 'Psalms', '83', '4', '1', 'They say, “Come, let us annihilate them from <span class=\"supplied\">being</span> a nation,  so that the name of Israel will be remembered no more.”'),
('LEB', 'Psalms', '83', '5', '1', 'For they have consulted together <span class=\"idiom\">with a unified purpose</span>.<span class=\"note\">Literally “with a heart together”</span> They have made a covenant against you:'),
('LEB', 'Psalms', '83', '6', '1', 'the tents of Edom and <span class=\"supplied\">the</span> Ishmaelites,  Moab and <span class=\"supplied\">the</span> Hagrites,'),
('LEB', 'Psalms', '83', '7', '1', 'Gebal and Ammon and Amalek,  Philistia, with the inhabitants of Tyre.'),
('LEB', 'Psalms', '83', '8', '1', 'Assyria also has joined with them.  They <span class=\"idiom\">provide help</span><span class=\"note\">Literally “they have become an arm for”</span>to the children of Lot. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '83', '9', '1', 'Do to them as <span class=\"supplied\">you did with</span> Midian,  as <span class=\"supplied\">with</span> Sisera, as <span class=\"supplied\">with</span> Jabin at the wadi of Kishon.'),
('LEB', 'Psalms', '83', '10', '1', 'They were destroyed at En-dor;  they became dung for the ground.'),
('LEB', 'Psalms', '83', '11', '1', 'Make their leaders like Oreb and Zeeb,  and all their chiefs like Zebah and Zalmunna,'),
('LEB', 'Psalms', '83', '12', '1', 'who said, “Let us take <span class=\"supplied\">as</span> our possession  the pastures of God.”'),
('LEB', 'Psalms', '83', '13', '1', 'O my God, make them like the tumbleweed,  like the chaff before wind.'),
('LEB', 'Psalms', '83', '14', '1', 'As fire burns a forest,  and as a flame sets afire mountains,'),
('LEB', 'Psalms', '83', '15', '1', 'so pursue them with your tempest  and terrify them with your storm.'),
('LEB', 'Psalms', '83', '16', '1', 'Fill their faces with shame,  that they may seek your name, O Adonai.'),
('LEB', 'Psalms', '83', '17', '1', 'Let them be ashamed and terrified forever,  and let them be humiliated and perish'),
('LEB', 'Psalms', '83', '18', '1', 'that they may know that you,  <span class=\"supplied\">whose</span> name <span class=\"supplied\">is</span> Adonai, you alone,  <span class=\"supplied\">are the</span> Most High over the whole earth.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '84', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director; on the Gittith.  Of the sons of Korah. A psalm.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>How lovely <span class=\"supplied\">are</span> your dwelling places,  O Adonai of hosts!'),
('LEB', 'Psalms', '84', '2', '1', 'My soul longs and even fails  for the courtyards of Adonai.  My heart and flesh sing for joy  to the living God.'),
('LEB', 'Psalms', '84', '3', '1', 'Even a bird finds a home, and a swallow a nest for herself,  where she may lay her young,  near your altars, O Adonai <span class=\"supplied\">of</span> hosts,  my king and my God.'),
('LEB', 'Psalms', '84', '4', '1', 'Blessed <span class=\"supplied\">are</span> those who dwell in your house;<span class=\"note\">Or “temple”</span> they can ever praise you. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '84', '5', '1', 'Blessed <span class=\"supplied\">is the</span> man whose strength <span class=\"supplied\">is</span> in you;  in their heart <span class=\"supplied\">are the</span> highways <span class=\"supplied\">to Zion</span>.'),
('LEB', 'Psalms', '84', '6', '1', 'Passing through the Valley of Baca,<span class=\"note\">Or “Valley of Weeping”</span> they make it a spring.  The early rain covers <span class=\"supplied\">it with</span> blessings<span class=\"note\">A change in vocalization of the Hebrew yields “<span class=\"supplied\">it with</span> pools”</span>as well.'),
('LEB', 'Psalms', '84', '7', '1', 'They go from strength to strength,  <span class=\"supplied\">until each</span> appears before God in Zion.<span class=\"note\">Or “the God of gods will be seen in Zion”</span>'),
('LEB', 'Psalms', '84', '8', '1', 'O Adonai, God of hosts, hear my prayer;  give ear, O God of Jacob. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '84', '9', '1', 'Look at our shield, O God,  and have regard for the face of your anointed one.'),
('LEB', 'Psalms', '84', '10', '1', 'Because better <span class=\"supplied\">is</span> a day in your courtyards  than a thousand <span class=\"supplied\">elsewhere</span>.  I would rather be at the threshold of the house<span class=\"note\">Or “temple”</span>of my God  than to dwell in <span class=\"supplied\">the</span> tents of wickedness.'),
('LEB', 'Psalms', '84', '11', '1', 'Because Adonai <span class=\"supplied\">is</span> a sun and a shield;  Adonai gives grace and honor.  He does not withhold good from those who walk blamelessly.'),
('LEB', 'Psalms', '84', '12', '1', 'O Adonai <span class=\"supplied\">of</span> hosts,  blessed <span class=\"supplied\">is the</span> man who trusts you.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '85', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. Of the sons of Korah. A psalm.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>O Adonai, you favored your land.  You restored the fortunes<span class=\"note\">Or “captivity”</span>of Jacob.'),
('LEB', 'Psalms', '85', '2', '1', 'You took away the guilt<span class=\"note\">Or “You forgave the iniquity”</span>of your people;  you covered all their sin. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '85', '3', '1', 'You withdrew all your wrath;  you turned from your burning anger.'),
('LEB', 'Psalms', '85', '4', '1', 'Restore us, O God of our salvation,  and annul your vexation with us.'),
('LEB', 'Psalms', '85', '5', '1', 'Will you be angry against us forever?  Will you prolong your anger generation after generation?'),
('LEB', 'Psalms', '85', '6', '1', 'Will you not again revive us,  that your people might rejoice in you?'),
('LEB', 'Psalms', '85', '7', '1', 'Show us, O Adonai, your loyal love,  and grant us your salvation.'),
('LEB', 'Psalms', '85', '8', '1', 'I will hear what God, Adonai, will speak,  because he will speak peace  to his people, even his faithful <span class=\"supplied\">ones</span>,<span class=\"note\">Hebrew “and to his faithful”</span> but let them not return to folly.'),
('LEB', 'Psalms', '85', '9', '1', 'Surely his salvation <span class=\"supplied\">is</span> near for those who fear him,  that glory may abide in our land.'),
('LEB', 'Psalms', '85', '10', '1', 'Loyal love and faithfulness<span class=\"note\">Or “truth”</span>will meet one another;  righteousness and peace will kiss.'),
('LEB', 'Psalms', '85', '11', '1', 'Faithfulness<span class=\"note\">Or “Truth”</span>will sprout from <span class=\"supplied\">the</span> ground,  and righteousness will look down from heaven.'),
('LEB', 'Psalms', '85', '12', '1', 'Yes, Adonai will give <span class=\"supplied\">what is</span> good,  and our land will give its produce.'),
('LEB', 'Psalms', '85', '13', '1', 'Righteousness will go before him,  and it will make his steps a pathway.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '86', '1', '1', '<span class=\"verse-title\">A prayer of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Incline, O Adonai, your ear <span class=\"supplied\">and</span> answer me,  because I <span class=\"supplied\">am</span> poor and needy.'),
('LEB', 'Psalms', '86', '2', '1', 'Watch over my life because I <span class=\"supplied\">am</span> faithful.  You <span class=\"supplied\">are</span> my God; save your servant.  <span class=\"supplied\">I am</span> the one who trusts you.'),
('LEB', 'Psalms', '86', '3', '1', 'Be gracious to me, O Lord,  because I call to you <span class=\"idiom\">all day long</span>.<span class=\"note\">Literally “all the day”</span>'),
('LEB', 'Psalms', '86', '4', '1', 'Make glad the soul of your servant,  because <span class=\"idiom\">I desire you</span>,<span class=\"note\">Literally “I lift up my soul to you”</span>O Lord.'),
('LEB', 'Psalms', '86', '5', '1', 'For you, O Lord, <span class=\"supplied\">are</span> good and forgiving,  and abundant in loyal love<span class=\"note\">Hebrew “and great with respect to loyal love”</span>for all who call to you.'),
('LEB', 'Psalms', '86', '6', '1', 'Heed, O Adonai, my prayer,  and attend to the voice of my supplications.'),
('LEB', 'Psalms', '86', '7', '1', 'In the day of my trouble I call to you,  because you answer me.'),
('LEB', 'Psalms', '86', '8', '1', 'There is none like you among the gods, O Lord,  and there are no works like yours.'),
('LEB', 'Psalms', '86', '9', '1', 'All <span class=\"supplied\">the</span> nations that you have made will come  and bow down<span class=\"note\">Or “worship”</span>before you, O Lord,  and glorify your name.'),
('LEB', 'Psalms', '86', '10', '1', 'For you <span class=\"supplied\">are</span> great and doing wondrous things;  you alone <span class=\"supplied\">are</span> God.'),
('LEB', 'Psalms', '86', '11', '1', 'Teach me, O Adonai, your way,  <span class=\"supplied\">that</span> I may walk in your truth.  Unite my heart<span class=\"note\">That is, give singleness of heart</span>to fear your name.'),
('LEB', 'Psalms', '86', '12', '1', 'I will give you thanks, O Lord my God, with all my heart,  and glorify your name forever,'),
('LEB', 'Psalms', '86', '13', '1', 'because your loyal love <span class=\"supplied\">is</span> great toward me,  and you will have delivered my life from Sheol<span class=\"note\">The nether world, the place of departed spirits.</span>below.'),
('LEB', 'Psalms', '86', '14', '1', 'O God, arrogant men have risen up against me,  even a gang of ruthless men seek my life,  but they do not set you before them.<span class=\"note\">That is, they do not factor God into their actions</span>'),
('LEB', 'Psalms', '86', '15', '1', 'But you, O Lord, <span class=\"supplied\">are</span> a compassionate and gracious God,  <span class=\"idiom\">slow to anger</span><span class=\"note\">Literally “long of nostrils”</span>and abundant in loyal love and faithfulness.'),
('LEB', 'Psalms', '86', '16', '1', 'Turn to me and be gracious to me.  Give your strength to your servant,  and grant victory to the son<span class=\"note\">Or “Save the son”</span>of your maidservant.'),
('LEB', 'Psalms', '86', '17', '1', 'Do a sign <span class=\"supplied\">that</span> benefits me,  that those who hate me may see and be put to shame,  because you, O Adonai, have helped me and comforted me.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '87', '1', '1', '<span class=\"verse-title\">Of the sons of Korah. A psalm. A song.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>His foundation <span class=\"supplied\">is</span> on <span class=\"supplied\">the</span> holy mountains.'),
('LEB', 'Psalms', '87', '2', '1', 'Adonai loves the gates of Zion  more than all the dwellings of Jacob.'),
('LEB', 'Psalms', '87', '3', '1', 'Glorious things <span class=\"supplied\">are</span> spoken about<span class=\"note\">Or “in”</span>you,  O city of God. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '87', '4', '1', 'I will record<span class=\"note\">Or “mention”</span>those who know me  <span class=\"supplied\">in</span> Rahab<span class=\"note\">That is, Egypt; compare Isa 30:7</span>and Babylon,  behold <span class=\"supplied\">in</span> Philistia and Tyre with Cush,  “This one was born there.”'),
('LEB', 'Psalms', '87', '5', '1', 'But with respect to Zion it shall be said,  “<span class=\"idiom\">Each one</span><span class=\"note\">Literally “A man and a man”</span>was born in her,”  for the Most High himself will establish her.'),
('LEB', 'Psalms', '87', '6', '1', 'Adonai will record in writing <span class=\"supplied\">the</span> peoples,  “This <span class=\"supplied\">one</span> was born there.” <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '87', '7', '1', 'And while dancing, singers <span class=\"supplied\">will sing</span>,  “All my springs <span class=\"supplied\">are</span> in you.”')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '88', '1', '1', '<span class=\"verse-title\">A song. A psalm of the sons of Korah.  For the <span class=\"supplied\">music</span> director; according to Mahalath Leannoth.  A maskil of Heman the Ezrahite.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>O Adonai, God of my salvation,  I cry out by day <span class=\"supplied\">and</span> through the night before you.'),
('LEB', 'Psalms', '88', '2', '1', 'Let my prayer come before you;  incline your ear to my cry.'),
('LEB', 'Psalms', '88', '3', '1', 'For my soul is full with troubles,  and my life approaches Sheol.'),
('LEB', 'Psalms', '88', '4', '1', 'I am reckoned with <span class=\"supplied\">those</span> descending to <span class=\"supplied\">the</span> pit.  I am like a man<span class=\"note\">The Hebrew suggests a strong, vigorous man</span>without strength,<span class=\"note\">Or “without help”</span>'),
('LEB', 'Psalms', '88', '5', '1', 'set free<span class=\"note\">Or “forsaken”</span>among the dead,  like <span class=\"supplied\">the</span> slain lying in <span class=\"supplied\">the</span> grave,  whom you no longer remember,  even those cut off from your hand.'),
('LEB', 'Psalms', '88', '6', '1', 'You have set me in <span class=\"supplied\">the</span> pit below,  in dark places, in <span class=\"supplied\">the</span> depths.'),
('LEB', 'Psalms', '88', '7', '1', 'Your wrath presses upon me,  and you afflict <span class=\"supplied\">me with</span> all your waves. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '88', '8', '1', 'You have removed my acquaintances far from me.  You have made me detestable<span class=\"note\">Hebrew “an abomination”</span>to them.  <span class=\"supplied\">I am</span> confined and cannot go out.'),
('LEB', 'Psalms', '88', '9', '1', 'My eye languishes from misery.  I call on you, O Adonai, every day;  I spread out my hands<span class=\"note\">Hebrew “palms”</span>to you.'),
('LEB', 'Psalms', '88', '10', '1', 'Do you work wonders<span class=\"note\">Hebrew “wonder”</span>for the dead?  Or do <span class=\"supplied\">the</span> departed spirits<span class=\"note\">Hebrew <span class=\"italic\">rephaim</span>, sometimes translated “shades”; the weakened state of those who have died</span>rise up <span class=\"supplied\">to</span> praise you? <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '88', '11', '1', 'Is your loyal love told in the grave,  <span class=\"supplied\">or</span> your faithfulness in the underworld?<span class=\"note\">Hebrew <span class=\"italic\">abaddon</span>, the place of destruction</span>'),
('LEB', 'Psalms', '88', '12', '1', 'Are your wonders<span class=\"note\">Hebrew “wonder”</span>known in the darkness,  <span class=\"supplied\">or</span> your righteousness<span class=\"note\">Or “deed of justice”</span>in <span class=\"supplied\">the</span> land of forgetfulness?'),
('LEB', 'Psalms', '88', '13', '1', 'But as for me, I cry for help to you, O Adonai,  and in the morning my prayer comes before you.'),
('LEB', 'Psalms', '88', '14', '1', 'Why do you reject my soul, O Adonai?  <span class=\"supplied\">Why</span> do you hide your face from me?'),
('LEB', 'Psalms', '88', '15', '1', 'I <span class=\"supplied\">am</span> afflicted and perishing from <span class=\"supplied\">my</span> youth.  I bear your terrors. I am distraught.<span class=\"note\">Hebrew meaning uncertain</span>'),
('LEB', 'Psalms', '88', '16', '1', 'Your burning anger has passed over me;  your sudden fears have destroyed me.'),
('LEB', 'Psalms', '88', '17', '1', 'They surround me like water all the day;  they circle about me altogether.'),
('LEB', 'Psalms', '88', '18', '1', 'You have removed loved one and friend far from me,  my acquaintances <span class=\"supplied\">far from my</span> darkness.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '89', '1', '1', '<span class=\"verse-title\">A maskil of Ethan the Ezrahite.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>I will sing forever of Adonai’s acts of loyal love.  <span class=\"idiom\">From generation to generation</span><span class=\"note\">Literally “To a generation and a generation”</span> I will make known your faithfulness with my mouth.'),
('LEB', 'Psalms', '89', '2', '1', 'For I say, “Forever <span class=\"supplied\">your</span> loyal love is built up.  <span class=\"supplied\">The</span> heavens you have established <span class=\"supplied\">with</span> your faithfulness in them.”'),
('LEB', 'Psalms', '89', '3', '1', '“I made a covenant with my chosen one;  I swore <span class=\"supplied\">an oath</span> to David my servant:'),
('LEB', 'Psalms', '89', '4', '1', '‘I will establish your descendants forever,  and I will build up your throne <span class=\"idiom\">from generation to generation</span>.’ ”<span class=\"note\">Literally “To a generation and a generation”</span><span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '89', '5', '1', 'And <span class=\"supplied\">so the</span> heavens will praise your wonderful deed, O Adonai,  even your faithfulness, in <span class=\"supplied\">the</span> assembly of <span class=\"supplied\">the</span> holy ones.'),
('LEB', 'Psalms', '89', '6', '1', 'For who in the sky<span class=\"note\">Hebrew singular collective “clouds”</span>is equal to Adonai?  <span class=\"supplied\">Who is</span> like Adonai among the sons of God,'),
('LEB', 'Psalms', '89', '7', '1', 'a God feared greatly in the council of <span class=\"supplied\">the</span> holy ones,  and awesome above all surrounding him?'),
('LEB', 'Psalms', '89', '8', '1', 'O Adonai <span class=\"supplied\">of</span> hosts,  who <span class=\"supplied\">is</span> mighty like you, O Yah,  with<span class=\"note\">Hebrew “and”</span>your faithfulness surrounding you?'),
('LEB', 'Psalms', '89', '9', '1', 'You are ruling the surging of the sea.  When its waves rise, <span class=\"idiom\">you yourself still</span><span class=\"note\">Literally “you, you calm”</span>them.'),
('LEB', 'Psalms', '89', '10', '1', '<span class=\"idiom\">You yourself crushed</span><span class=\"note\">Literally “You, you crushed”</span>Rahab like one <span class=\"supplied\">who is</span> slain;  you scattered your enemies with your strong arm.'),
('LEB', 'Psalms', '89', '11', '1', '<span class=\"supplied\">The</span> heavens are yours, <span class=\"supplied\">the</span> earth yours as well,  <span class=\"supplied\">the</span> world with its fullness, <span class=\"supplied\">because</span> you founded them.'),
('LEB', 'Psalms', '89', '12', '1', 'North and south, you created them;  Tabor and Hermon shout joyfully over your name.'),
('LEB', 'Psalms', '89', '13', '1', 'You have a mighty arm;  Your hand is strong; your right hand reaches high.'),
('LEB', 'Psalms', '89', '14', '1', 'Righteousness and justice <span class=\"supplied\">are</span> the foundation of your throne.  Loyal love and faithfulness<span class=\"note\">Or “truth”</span>come before your face.<span class=\"note\">That is, enjoy continual access</span>'),
('LEB', 'Psalms', '89', '15', '1', 'Blessed <span class=\"supplied\">are</span> the people who know <span class=\"supplied\">the</span> joyful shout;  O Adonai, they walk about in the light of your countenance.'),
('LEB', 'Psalms', '89', '16', '1', 'In your name they rejoice all the day,  and in your righteousness they rise up.'),
('LEB', 'Psalms', '89', '17', '1', 'For you <span class=\"supplied\">are</span> the glory of their strength,  and by your favor our horn rises up,<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>). The <span class=\"italic\">Kethib</span> reads “you exalt our horn”</span>'),
('LEB', 'Psalms', '89', '18', '1', 'because our shield belongs to Adonai,<span class=\"note\">Or “Adonai <span class=\"supplied\">is</span> our shield.”</span> and our king to the Holy One of Israel.<span class=\"note\">Or “The Holy One of Israel <span class=\"supplied\">is</span> our king.”</span>'),
('LEB', 'Psalms', '89', '19', '1', 'Formerly you spoke in a vision  to your faithful ones, and said,  “I have bestowed help on a mighty man;  I have exalted one chosen from <span class=\"supplied\">the</span> people.'),
('LEB', 'Psalms', '89', '20', '1', 'I have found David, my servant.  With my holy oil I have anointed him,'),
('LEB', 'Psalms', '89', '21', '1', 'with whom my hand will be steadfast.  Surely my arm will strengthen him.'),
('LEB', 'Psalms', '89', '22', '1', '<span class=\"supplied\">The</span> enemy will not deceive him,  and no <span class=\"idiom\">evil man</span><span class=\"note\">Literally “a son of wickedness”</span>will afflict him.'),
('LEB', 'Psalms', '89', '23', '1', 'But I will crush his adversaries before him,  and I will strike those who hate him.'),
('LEB', 'Psalms', '89', '24', '1', 'And <span class=\"supplied\">so</span> my faithfulness  and my loyal love <span class=\"supplied\">will be</span> with him,  and in my name his horn will rise up.'),
('LEB', 'Psalms', '89', '25', '1', 'And I will set his hand on the sea  and his right hand on the rivers.'),
('LEB', 'Psalms', '89', '26', '1', 'He will call out <span class=\"supplied\">to</span> me, ‘You <span class=\"supplied\">are</span> my Father,  my God, and the rock of my salvation.’'),
('LEB', 'Psalms', '89', '27', '1', 'I will also make him <span class=\"supplied\">the</span> firstborn,  <span class=\"supplied\">the</span> highest of <span class=\"supplied\">the</span> kings of <span class=\"supplied\">the</span> earth.'),
('LEB', 'Psalms', '89', '28', '1', 'Forever I will keep my loyal love for him,  and my covenant <span class=\"supplied\">will be</span> reliable for him.'),
('LEB', 'Psalms', '89', '29', '1', 'I will also establish his offspring forever,  and his throne as <span class=\"supplied\">the</span> days of <span class=\"supplied\">the</span> heavens.'),
('LEB', 'Psalms', '89', '30', '1', 'If his sons forsake my law  and do not walk in my judgments,'),
('LEB', 'Psalms', '89', '31', '1', 'if they defile<span class=\"note\">Hebrew “profane; treat as common”</span>my statutes  and do not keep my commandments,'),
('LEB', 'Psalms', '89', '32', '1', 'then I will punish their transgression with <span class=\"supplied\">a</span> rod,  and their guilt with wounds.'),
('LEB', 'Psalms', '89', '33', '1', 'But I will not break off my loyal love from him,  and I will not be false against my faithfulness.'),
('LEB', 'Psalms', '89', '34', '1', 'I will not defile<span class=\"note\">Hebrew “profane; treat as common”</span>my covenant,  or alter what proceeded from my lips.'),
('LEB', 'Psalms', '89', '35', '1', 'Once<span class=\"note\">Hebrew “One”</span>I have sworn by my holiness,  ‘I will surely not lie to David,'),
('LEB', 'Psalms', '89', '36', '1', 'His offspring will endure<span class=\"note\">Hebrew “be”</span>forever,  and his throne as the sun before me.'),
('LEB', 'Psalms', '89', '37', '1', 'Like <span class=\"supplied\">the</span> moon it will be steadfast forever,  and <span class=\"supplied\">like</span> an enduring witness in the sky.’ ” <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '89', '38', '1', 'But you have spurned and rejected;  you are very angry with your anointed one.'),
('LEB', 'Psalms', '89', '39', '1', 'You have repudiated the covenant with your servant;  you have defiled<span class=\"note\">Hebrew “profane; treat as common”</span>his crown in<span class=\"note\">Hebrew “to”</span>the dirt.'),
('LEB', 'Psalms', '89', '40', '1', 'You have broken down all his walls;  you have made his fortifications a ruin.'),
('LEB', 'Psalms', '89', '41', '1', 'All passing along <span class=\"supplied\">the</span> road plunder him;  he has become a taunt to his neighbors.'),
('LEB', 'Psalms', '89', '42', '1', 'You have raised the right hand<span class=\"note\">That is, given them victory</span>of his adversaries.  You have made all his enemies rejoice.'),
('LEB', 'Psalms', '89', '43', '1', 'You have also turned back the edge of his sword  and have not helped him stand <span class=\"supplied\">his ground</span> in the battle.'),
('LEB', 'Psalms', '89', '44', '1', 'You have made his splendor to cease,  and cast his throne to the ground.'),
('LEB', 'Psalms', '89', '45', '1', 'You have cut short the days of his youth;  you have covered him with shame. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '89', '46', '1', 'How long, O Adonai? Will you hide yourself forever?  Will your wrath burn like fire <span class=\"supplied\">forever</span>?'),
('LEB', 'Psalms', '89', '47', '1', 'Remember what my lifespan <span class=\"supplied\">is</span>.  <span class=\"supplied\">Remember</span> for what vanity  you have created all <span class=\"supplied\">the</span> children of humankind.'),
('LEB', 'Psalms', '89', '48', '1', 'What man can live <span class=\"supplied\">on</span> and not see death?  Can he deliver his life from the <span class=\"idiom\">power</span><span class=\"note\">Literally “hand””</span>of Sheol? <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '89', '49', '1', 'Where <span class=\"supplied\">are</span> your former acts of loyal love, O Lord,  <span class=\"supplied\">that</span> you swore to David by your faithfulness?'),
('LEB', 'Psalms', '89', '50', '1', 'Remember, O Lord the taunting of your servants,  <span class=\"supplied\">how</span> I bear in my bosom <span class=\"supplied\">the taunts of</span> all <span class=\"supplied\">the</span> many peoples,'),
('LEB', 'Psalms', '89', '51', '1', '<span class=\"supplied\">by</span> which your enemies taunt, O Adonai,  <span class=\"supplied\">by</span> which they taunt the steps of your anointed one.'),
('LEB', 'Psalms', '89', '52', '1', 'Blessed be Adonai forever.  Amen and Amen.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '90', '1', '1', '<span class=\"verse-title\">A prayer of Moses, the man of God.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>O Lord, you have been our help<span class=\"note\">Or “dwelling place”</span><span class=\"idiom\">in all generations</span>.<span class=\"note\">Literally “in a generation and a generation”</span>'),
('LEB', 'Psalms', '90', '2', '1', 'Before <span class=\"supplied\">the</span> mountains were born  and you brought forth <span class=\"supplied\">the</span> earth and <span class=\"supplied\">the</span> world,  even from everlasting to everlasting, you <span class=\"supplied\">are</span> God.'),
('LEB', 'Psalms', '90', '3', '1', 'You return man to <span class=\"supplied\">the</span> dust,  <span class=\"idiom\">saying</span>,<span class=\"note\">Literally “and you say”</span>“Return, O sons of man.”'),
('LEB', 'Psalms', '90', '4', '1', 'For a thousand years in your eyes  <span class=\"supplied\">are</span> like yesterday when it passes,  or <span class=\"supplied\">like</span> a watch in the night.'),
('LEB', 'Psalms', '90', '5', '1', 'You sweep them away <span class=\"supplied\">like a flood</span>.  They fall asleep.<span class=\"note\">The interpretation of 5a is difficult</span> In the morning <span class=\"supplied\">they are</span> like grass <span class=\"supplied\">that</span> sprouts anew.'),
('LEB', 'Psalms', '90', '6', '1', 'In the morning it blossoms and sprouts anew;  by evening it withers and dries up.'),
('LEB', 'Psalms', '90', '7', '1', 'For we are brought to an end by your anger,  and we hasten <span class=\"supplied\">off</span><span class=\"note\">Or “we are terrified”</span>by your wrath.'),
('LEB', 'Psalms', '90', '8', '1', 'You have put our iniquities before you,  our hidden <span class=\"supplied\">sins</span> into the light of your countenance.'),
('LEB', 'Psalms', '90', '9', '1', 'For all of our days dwindle away in your rage;  we complete our years like a sigh.'),
('LEB', 'Psalms', '90', '10', '1', 'As for the days of our years, within them <span class=\"supplied\">are</span> seventy years  or if by strength eighty years, and their pride<span class=\"note\">Or “span”</span><span class=\"supplied\">is</span> trouble and disaster,  for it passes quickly and we fly <span class=\"supplied\">away</span>.'),
('LEB', 'Psalms', '90', '11', '1', 'Who knows the strength of your anger,  and your rage consistent with<span class=\"note\">Hebrew “according to”</span><span class=\"supplied\">the</span> fear due you?'),
('LEB', 'Psalms', '90', '12', '1', 'So teach <span class=\"supplied\">us</span> to number our days  that we may gain a heart of wisdom.'),
('LEB', 'Psalms', '90', '13', '1', 'Return,<span class=\"note\">Or “Turn back <span class=\"supplied\">your wrath</span>”</span>O Adonai. How long?  And have compassion on<span class=\"note\">Or “change your mind concerning”</span>your servants.'),
('LEB', 'Psalms', '90', '14', '1', 'Satisfy us in the morning with your loyal love,  that we may sing for joy and be glad all our days.'),
('LEB', 'Psalms', '90', '15', '1', 'Make us glad for as many days as you have afflicted us,  <span class=\"supplied\">for as many</span> years <span class=\"supplied\">as</span> we have seen calamity.'),
('LEB', 'Psalms', '90', '16', '1', 'Let your work be visible to your servants,  and your majesty to their children.'),
('LEB', 'Psalms', '90', '17', '1', 'And let the beauty of the Lord our God be upon us,  and establish for us the work of our hands,  yes, the work of our hands, establish it.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '91', '1', '1', '<span class=\"verse-title\">God’s Protection in Times of Crisis</span><span class=\"supplied\">One who</span> lives in the secret place<span class=\"note\">Or “shelter”</span>of the Most High  will lodge in the shadow of Shaddai.<span class=\"note\">Often translated “the Almighty,” this is perhaps better understood as “<span class=\"supplied\">God of the</span> Mountain”</span>'),
('LEB', 'Psalms', '91', '2', '1', 'I will say to Adonai, “<span class=\"supplied\">You are</span> my refuge and my fortress,  my God <span class=\"supplied\">in whom</span> I trust.”'),
('LEB', 'Psalms', '91', '3', '1', 'For he will deliver<span class=\"note\">Hebrew “he, he will deliver” is perhaps emphatic, “He will certainly deliver”</span>you from <span class=\"supplied\">the</span> snare of <span class=\"supplied\">the</span> fowler,  from <span class=\"supplied\">the</span> plague of destruction.'),
('LEB', 'Psalms', '91', '4', '1', 'With his feathers he will cover you,  and under his wings you can take refuge.  His faithfulness <span class=\"supplied\">will be</span> a shield and a buckler.<span class=\"note\">That is, a small, round shield, or perhaps a surrounding bulwark</span>'),
('LEB', 'Psalms', '91', '5', '1', 'You need not fear <span class=\"supplied\">the</span> terror of <span class=\"supplied\">the</span> night,  <span class=\"supplied\">or the</span> arrow <span class=\"supplied\">that</span> flies by day,'),
('LEB', 'Psalms', '91', '6', '1', '<span class=\"supplied\">or the</span> plague <span class=\"supplied\">that</span> spreads in the darkness,  <span class=\"supplied\">or the</span> destruction <span class=\"supplied\">that</span> devastates at noon.'),
('LEB', 'Psalms', '91', '7', '1', 'A thousand may fall at your side,  and ten thousand at your right <span class=\"supplied\">hand</span>,  <span class=\"supplied\">but</span> it will not come near you.'),
('LEB', 'Psalms', '91', '8', '1', 'You will only look with your eyes,  and see <span class=\"supplied\">the</span> punishment of <span class=\"supplied\">the</span> wicked.'),
('LEB', 'Psalms', '91', '9', '1', 'For you, O Adonai, <span class=\"supplied\">are</span> my refuge.  You have made the Most High your dwelling place.<span class=\"note\">This is the most straightforward translation of the Hebrew, though the “you” of each line would refer to a different person. A more unusual rendering would be “Because Adonai, my refuge, even the Most High, you have made your dwelling place …”</span>'),
('LEB', 'Psalms', '91', '10', '1', 'No harm will befall you,  and no plague will come near your tent.'),
('LEB', 'Psalms', '91', '11', '1', 'For he will command his angels concerning you,  to watch over you in all your ways.'),
('LEB', 'Psalms', '91', '12', '1', 'In <span class=\"supplied\">their</span> hands they will bear you up,  lest you strike your foot against a stone.'),
('LEB', 'Psalms', '91', '13', '1', 'You will tread on lion and viper;  you will trample young lion and serpent.'),
('LEB', 'Psalms', '91', '14', '1', 'Because he loves me, therefore I will deliver him;  I will protect<span class=\"note\">The sense is that of making something inaccessibly high, like a fortress</span>him because he knows my name.'),
('LEB', 'Psalms', '91', '15', '1', 'He will call upon me and I will answer him;  I <span class=\"supplied\">will be</span> with him in trouble;  I will rescue and honor him.'),
('LEB', 'Psalms', '91', '16', '1', '<span class=\"supplied\">With</span> <span class=\"idiom\">long life</span><span class=\"note\">Literally “a length of days”</span>I will satisfy him,  and show him my salvation.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '92', '1', '1', '<span class=\"verse-title\">A psalm. A song. For the Sabbath day.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span><span class=\"supplied\">It is</span> good to give thanks to Adonai,  and to sing praise concerning your name, O Most High;'),
('LEB', 'Psalms', '92', '2', '1', 'to declare in the morning your loyal love  and your faithfulness in the night,'),
('LEB', 'Psalms', '92', '3', '1', 'on <span class=\"supplied\">the</span> ten <span class=\"supplied\">string</span>, and on <span class=\"supplied\">the</span> harp,  with a melody on <span class=\"supplied\">the</span> lyre.'),
('LEB', 'Psalms', '92', '4', '1', 'For you, O Adonai, have made me glad by your work;  by the deeds of your hands I sing for joy.'),
('LEB', 'Psalms', '92', '5', '1', 'How great are your deeds, O Adonai;  <span class=\"supplied\">how</span> very deep are your thoughts.'),
('LEB', 'Psalms', '92', '6', '1', '<span class=\"supplied\">The</span> brutish man does not know,  and <span class=\"supplied\">the</span> fool cannot understand this.'),
('LEB', 'Psalms', '92', '7', '1', 'When the wicked flourish like grass  and all <span class=\"supplied\">the</span> workers of evil blossom,  <span class=\"supplied\">it is</span> so they can be destroyed forever.'),
('LEB', 'Psalms', '92', '8', '1', 'But you <span class=\"supplied\">are</span> on high forever, O Adonai.'),
('LEB', 'Psalms', '92', '9', '1', 'For behold, your enemies, O Adonai,  for behold, your enemies will perish.  All <span class=\"supplied\">the</span> workers of evil will be scattered.'),
('LEB', 'Psalms', '92', '10', '1', 'But you have exalted my horn like <span class=\"supplied\">that of</span> a wild ox;  you have anointed me<span class=\"note\">The Hebrew’s “<span class=\"supplied\">that</span> I have moistened with fresh oil” seems unlikely. The above translation presupposes the loss of a letter in the Hebrew textual tradition (<span class=\"italic\">ballotani</span> for the MT’s <span class=\"italic\">balloti</span>)</span>with fresh oil.'),
('LEB', 'Psalms', '92', '11', '1', 'And <span class=\"supplied\">so</span> my eye looks on my enemies.<span class=\"note\">This word for “enemies” suggests those watching for any advantage</span> My ears hear those evildoers who rise up against me.'),
('LEB', 'Psalms', '92', '12', '1', '<span class=\"supplied\">The</span> righteous<span class=\"note\">Hebrew singular, but used as a collective. The verbs of this verse are also singular</span>will flourish like the date palm.  They will grow like a cedar in Lebanon.'),
('LEB', 'Psalms', '92', '13', '1', 'Planted in the house<span class=\"note\">Or “temple”</span>of Adonai,  they will flourish in the courts of our God.'),
('LEB', 'Psalms', '92', '14', '1', 'They will still prosper in old age.  They will be fat and luxuriant,<span class=\"note\">This is the same word used of the oil in v. 11 and translated “fresh”</span>'),
('LEB', 'Psalms', '92', '15', '1', 'to declare that Adonai <span class=\"supplied\">is</span> upright.  <span class=\"supplied\">He is</span> my rock, and <span class=\"supplied\">there is</span> no injustice<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>in him.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '93', '1', '1', '<span class=\"verse-title\">Adonai Is King Over All the Earth</span>Adonai is king; he clothes himself with majesty.  Adonai clothes himself; he girds himself with might.  Yes, <span class=\"supplied\">the</span> world is established <span class=\"supplied\">so that</span> it will not be moved.'),
('LEB', 'Psalms', '93', '2', '1', 'Your throne <span class=\"supplied\">is</span> established from of old;  you <span class=\"supplied\">are</span> from everlasting.'),
('LEB', 'Psalms', '93', '3', '1', '<span class=\"supplied\">The</span> rivers have lifted up, O Adonai;  <span class=\"supplied\">the</span> rivers have lifted up their <span class=\"idiom\">rumbling</span>;<span class=\"note\">Literally “voice”</span> <span class=\"supplied\">the</span> rivers have lifted up their pounding.'),
('LEB', 'Psalms', '93', '4', '1', '<span class=\"supplied\">Mightier</span> than the rumblings of many waters,  <span class=\"supplied\">mightier</span> than <span class=\"supplied\">the</span> mighty breakers of <span class=\"supplied\">the</span> sea,  Adonai on high <span class=\"supplied\">is</span> mighty.'),
('LEB', 'Psalms', '93', '5', '1', 'Your testimonies are fully reliable.  Holiness is fitting for your house,<span class=\"note\">Or “temple”</span> O Adonai, <span class=\"idiom\">forever</span>.<span class=\"note\">Literally “for a length of days”</span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '94', '1', '1', '<span class=\"verse-title\">A Prayer for Retribution against Oppressors</span>O Adonai, God of vengeance,  God of vengeance, shine forth.'),
('LEB', 'Psalms', '94', '2', '1', 'Rise up, O Judge of the earth.  Repay upon the proud what is <span class=\"supplied\">their</span> rightful due.'),
('LEB', 'Psalms', '94', '3', '1', 'How long <span class=\"supplied\">will the</span> wicked, O Adonai,  how long will <span class=\"supplied\">the</span> wicked exult?'),
('LEB', 'Psalms', '94', '4', '1', '<span class=\"idiom\">They gush words</span><span class=\"note\">Literally “They gush, they speak,” which is probably a hendiadys</span>unrestrained.  All <span class=\"supplied\">the</span> evildoers boast.'),
('LEB', 'Psalms', '94', '5', '1', 'They crush your people, O Adonai;  they oppress your inheritance.'),
('LEB', 'Psalms', '94', '6', '1', 'They kill widow and stranger,  and they murder orphans,'),
('LEB', 'Psalms', '94', '7', '1', 'while<span class=\"note\">Hebrew “and”</span>they say, “Yah<span class=\"note\">A shortened form of “Adonai”</span>does not see,”  and “The God of Jacob does not pay attention.”'),
('LEB', 'Psalms', '94', '8', '1', 'You pay attention, O brutes among the people.  And <span class=\"supplied\">you</span> fools, when you will show insight?'),
('LEB', 'Psalms', '94', '9', '1', 'Will <span class=\"supplied\">the</span> one who planted <span class=\"supplied\">the</span> ear not hear?  Will <span class=\"supplied\">the</span> one who formed <span class=\"supplied\">the</span> eye not see?'),
('LEB', 'Psalms', '94', '10', '1', 'Will <span class=\"supplied\">the</span> one who instructs nations not rebuke,  the one who teaches humankind knowledge?'),
('LEB', 'Psalms', '94', '11', '1', 'Adonai knows <span class=\"supplied\">the</span> thoughts of humankind,  that <span class=\"idiom\">they <span class=\"supplied\">are</span> to no purpose</span>.<span class=\"note\">Literally “they <span class=\"supplied\">are</span> a breath” or “a vanity”</span>'),
('LEB', 'Psalms', '94', '12', '1', 'Blessed <span class=\"supplied\">is</span> the man, O Yah, whom you instruct  and teach from your law,'),
('LEB', 'Psalms', '94', '13', '1', 'to give him rest from days of trouble,  until a pit is dug for the wicked.'),
('LEB', 'Psalms', '94', '14', '1', 'For Adonai will not abandon his people,  nor forsake his inheritance.'),
('LEB', 'Psalms', '94', '15', '1', 'For judgment will return to righteousness,<span class=\"note\">That is, the court system will operate correctly</span> and all the upright in heart <span class=\"supplied\">will follow</span> after it.'),
('LEB', 'Psalms', '94', '16', '1', 'Who rose up for me against <span class=\"supplied\">the</span> wicked?  Who stood up for me against <span class=\"supplied\">the</span> workers of iniquity?'),
('LEB', 'Psalms', '94', '17', '1', 'If Adonai had not been my help,  my soul would soon have dwelt in silence.'),
('LEB', 'Psalms', '94', '18', '1', 'When I thought,<span class=\"note\">Hebrew “If/When I said,” that is, said to myself</span>“My foot is slipping,”  your loyal love, O Adonai, supported me.'),
('LEB', 'Psalms', '94', '19', '1', 'When my troubled thoughts were many <span class=\"idiom\">within me</span>,<span class=\"note\">Literally “in my inward parts”</span> your consolations cheered my soul.'),
('LEB', 'Psalms', '94', '20', '1', 'Can there be allied with you a throne of destruction,  one that forms trouble based on statute?'),
('LEB', 'Psalms', '94', '21', '1', 'They band together against <span class=\"supplied\">the</span> life of <span class=\"supplied\">the</span> righteous,  and declare <span class=\"supplied\">the</span> blameless guilty of blood.<span class=\"note\">Or “condemn innocent blood”</span>'),
('LEB', 'Psalms', '94', '22', '1', 'But Adonai has become my <span class=\"supplied\">high</span> stronghold,  and my God <span class=\"supplied\">has become</span> my rock of refuge.'),
('LEB', 'Psalms', '94', '23', '1', 'And he will repay on them their iniquity,  and by<span class=\"note\">Or “in”</span>their evil he will destroy them.  Adonai our God will destroy them.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '95', '1', '1', '<span class=\"verse-title\">A Call to Worship and Obey</span>Come, let us sing for joy to Adonai;  let us shout joyfully to the rock of our salvation.'),
('LEB', 'Psalms', '95', '2', '1', 'Let us come before his presence with thanksgiving;  with songs let us shout joyfully to him.'),
('LEB', 'Psalms', '95', '3', '1', 'For Adonai <span class=\"supplied\">is</span> <span class=\"supplied\">the</span><span class=\"note\">The Hebrew does not have the definite article used twice in the translation of this verse</span>great God,  and <span class=\"supplied\">the</span> great king over all gods,'),
('LEB', 'Psalms', '95', '4', '1', 'in whose hand <span class=\"supplied\">are the</span> unexplored places<span class=\"note\">Or “the deep places”</span>of <span class=\"supplied\">the</span> earth,  and <span class=\"supplied\">the</span> heights of <span class=\"supplied\">the</span> mountains <span class=\"supplied\">are</span> his,'),
('LEB', 'Psalms', '95', '5', '1', 'to whom belongs the sea that he made, <span class=\"note\">Hebrew “and he made it”</span> and <span class=\"supplied\">the</span> dry land <span class=\"supplied\">that</span> his hands formed.'),
('LEB', 'Psalms', '95', '6', '1', 'Come in, let us worship and bow down;  let us kneel before Adonai, our maker.'),
('LEB', 'Psalms', '95', '7', '1', 'For he <span class=\"supplied\">is</span> our God,  and we <span class=\"supplied\">are</span> the people of his pasture  and the sheep of his hand.  <span class=\"idiom\">Today</span><span class=\"note\">Literally “The day”</span>if you will hear his voice:<span class=\"note\">Or “If only you would hear his voice today”</span>'),
('LEB', 'Psalms', '95', '8', '1', '“Do not harden your heart as <span class=\"supplied\">at</span> Meribah,  as <span class=\"supplied\">in</span> the day of Massah in the wilderness,'),
('LEB', 'Psalms', '95', '9', '1', 'when your ancestors<span class=\"note\">Or “fathers”</span>tried me.  They put me to the test,  <span class=\"supplied\">even</span> though they had seen my work.'),
('LEB', 'Psalms', '95', '10', '1', 'For forty years I loathed <span class=\"supplied\">that</span> generation,  and said, ‘<span class=\"supplied\">They are</span> a people <span class=\"supplied\">whose</span> heart wanders.  And my ways they do not know.’'),
('LEB', 'Psalms', '95', '11', '1', 'Therefore I swore in my anger,  ‘They shall surely not enter into my rest.’ ”')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '96', '1', '1', '<span class=\"verse-title\">Adonai the King Comes in Judgment</span>Sing to<span class=\"note\">Or “concerning”</span>Adonai a new song;  sing to<span class=\"note\">Or “concerning”</span>Adonai, all the earth.'),
('LEB', 'Psalms', '96', '2', '1', 'Sing to<span class=\"note\">Or “concerning”</span>Adonai; bless his name.  Announce<span class=\"note\">Or “bring good news concerning”</span>his salvation from day to day.'),
('LEB', 'Psalms', '96', '3', '1', 'Tell his glory among the nations,  his marvelous works among all the peoples.'),
('LEB', 'Psalms', '96', '4', '1', 'For Adonai <span class=\"supplied\">is</span> great and very worthy of praise;  he <span class=\"supplied\">is to be</span> feared above all gods.'),
('LEB', 'Psalms', '96', '5', '1', 'For all the gods of the peoples <span class=\"supplied\">are</span> idols,<span class=\"note\">This word for idols suggests something worthless or insignificant</span> but Adonai made <span class=\"supplied\">the</span> heavens.'),
('LEB', 'Psalms', '96', '6', '1', 'Splendor and majesty <span class=\"supplied\">are</span> before him;  strength and beauty <span class=\"supplied\">are</span> in his sanctuary.'),
('LEB', 'Psalms', '96', '7', '1', 'Ascribe to Adonai, you families of <span class=\"supplied\">the</span> peoples,  ascribe to Adonai glory and strength.'),
('LEB', 'Psalms', '96', '8', '1', 'Ascribe to Adonai the glory due his name;  bring an offering and come into his courts.'),
('LEB', 'Psalms', '96', '9', '1', 'Worship Adonai in holy array;  tremble before him, all the earth.'),
('LEB', 'Psalms', '96', '10', '1', 'Say among the nations, “Adonai is king!  Yes, <span class=\"supplied\">the</span> world is established <span class=\"supplied\">so that</span> it will not be moved.  He will judge <span class=\"supplied\">the</span> peoples fairly.”'),
('LEB', 'Psalms', '96', '11', '1', 'Let the heavens be glad and the earth rejoice.  Let the sea with<span class=\"note\">Hebrew “and”</span>its fullness roar.'),
('LEB', 'Psalms', '96', '12', '1', 'Let <span class=\"supplied\">the</span> field with<span class=\"note\">Hebrew “and”</span>all that <span class=\"supplied\">is</span> in it exult.  Then all <span class=\"supplied\">the</span> trees of <span class=\"supplied\">the</span> forests will sing for joy'),
('LEB', 'Psalms', '96', '13', '1', 'before Adonai, for he is coming;  for he is coming to judge the earth.  He will judge <span class=\"supplied\">the</span> world with righteousness,  and <span class=\"supplied\">the</span> peoples with his faithfulness.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '97', '1', '1', '<span class=\"verse-title\">Adonai’s Glorious Reign</span>Adonai is king! Let the earth rejoice;  let many coastlands be glad.'),
('LEB', 'Psalms', '97', '2', '1', 'Cloud and thick darkness <span class=\"supplied\">are</span> surrounding him;  righteousness and justice <span class=\"supplied\">are</span> the foundation of his throne.'),
('LEB', 'Psalms', '97', '3', '1', 'Fire goes before him,  and devours his enemies round about.'),
('LEB', 'Psalms', '97', '4', '1', 'His lightnings light <span class=\"supplied\">the</span> world;  the earth sees and trembles.'),
('LEB', 'Psalms', '97', '5', '1', '<span class=\"supplied\">The</span> mountains melt like wax at the presence of Adonai,  at the presence of the Lord of all the earth.'),
('LEB', 'Psalms', '97', '6', '1', 'The heavens declare his righteousness,  and all the peoples see his glory.'),
('LEB', 'Psalms', '97', '7', '1', 'Let all who serve an image be ashamed,  those who boast about idols.<span class=\"note\">This word for idols suggests something worthless or insignificant</span> Worship him, all you gods.'),
('LEB', 'Psalms', '97', '8', '1', 'Zion hears and is glad,  and the daughters of Judah rejoice  because of your judgments, O Adonai.'),
('LEB', 'Psalms', '97', '9', '1', 'For you, O Adonai, <span class=\"supplied\">are</span> most high over all the earth.  You are highly<span class=\"note\">Hebrew “very”</span>exalted above all gods.'),
('LEB', 'Psalms', '97', '10', '1', 'You who love Adonai, hate evil.  He protects the lives of his faithful;  he delivers them from <span class=\"supplied\">the</span> hand of <span class=\"supplied\">the</span> wicked.'),
('LEB', 'Psalms', '97', '11', '1', 'Light <span class=\"supplied\">is</span> sown for the righteous,  and joy for <span class=\"supplied\">the</span> upright in heart.'),
('LEB', 'Psalms', '97', '12', '1', 'Be glad in Adonai, <span class=\"supplied\">you</span> righteous,  and give thanks <span class=\"idiom\">to his holy name</span>.<span class=\"note\">Literally “to his holy remembrance” or “to his memorable holiness”</span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '98', '1', '1', '<span class=\"verse-title\">A psalm.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Sing to<span class=\"note\">Or “sing concerning”</span>Adonai a new song, for he has done wonders.  His right hand and his holy arm have secured his victory.'),
('LEB', 'Psalms', '98', '2', '1', 'Adonai has made known his salvation;<span class=\"note\">Or “victory”</span> to the eyes of the nations he has revealed his righteousness.'),
('LEB', 'Psalms', '98', '3', '1', 'He has remembered his loyal love and his faithfulness  to the house of Israel.  All <span class=\"supplied\">the</span> ends of the earth have seen  the salvation<span class=\"note\">Or “victory”</span>of our God.'),
('LEB', 'Psalms', '98', '4', '1', 'Shout joyfully to Adonai, all the earth.  Be cheerful<span class=\"note\">Or “Burst forth”</span>and sing for joy and sing praises.'),
('LEB', 'Psalms', '98', '5', '1', 'Sing praises to Adonai with lyre,  with lyre and melodious sound.'),
('LEB', 'Psalms', '98', '6', '1', 'With trumpets and sound of horn,  shout joyfully before the king, Adonai.'),
('LEB', 'Psalms', '98', '7', '1', 'Let the sea with<span class=\"note\">Hebrew “and”</span>its fullness roar,  <span class=\"supplied\">the</span> world and those who live in it.'),
('LEB', 'Psalms', '98', '8', '1', 'Let <span class=\"supplied\">the</span> rivers clap <span class=\"supplied\">their</span> hands.  Let <span class=\"supplied\">the</span> hills sing joyfully together'),
('LEB', 'Psalms', '98', '9', '1', 'before Adonai, for <span class=\"supplied\">he is</span> coming  to judge the earth.  He will judge <span class=\"supplied\">the</span> world with righteousness  and <span class=\"supplied\">the</span> peoples with equity.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '99', '1', '1', '<span class=\"verse-title\">Adonai Is a Holy King</span>Adonai is king; let <span class=\"supplied\">the</span> peoples tremble.  <span class=\"supplied\">He</span> sits enthroned<span class=\"note\">Or “dwells”</span><span class=\"supplied\">between the</span> cherubim. Let the earth shake.'),
('LEB', 'Psalms', '99', '2', '1', 'Adonai <span class=\"supplied\">is</span> great in Zion,  and he <span class=\"supplied\">is</span> exalted over all the peoples.'),
('LEB', 'Psalms', '99', '3', '1', 'Let them praise your great and fearful name.  He <span class=\"supplied\">is</span> holy.'),
('LEB', 'Psalms', '99', '4', '1', 'And <span class=\"supplied\">the</span> strength of <span class=\"supplied\">the</span> king loves justice.<span class=\"note\">The interpretation of this line is uncertain</span> You have established equity;  you have executed justice and righteousness in Jacob.'),
('LEB', 'Psalms', '99', '5', '1', 'Exalt Adonai our God,  and worship at his footstool.  He <span class=\"supplied\">is</span> holy.'),
('LEB', 'Psalms', '99', '6', '1', 'Moses and Aaron <span class=\"supplied\">were</span> among his priests;  Samuel also <span class=\"supplied\">was</span> among <span class=\"supplied\">those who</span> called on his name.  They called to Adonai, and he answered them.'),
('LEB', 'Psalms', '99', '7', '1', 'He spoke to them in <span class=\"supplied\">the</span> pillar of cloud.  They kept his testimonies and <span class=\"supplied\">the</span> statute he gave to them.'),
('LEB', 'Psalms', '99', '8', '1', 'O Adonai our God, you answered them.  You were a forgiving God to them,  but an avenger of their <span class=\"supplied\">wrong</span> deeds.'),
('LEB', 'Psalms', '99', '9', '1', 'Exalt Adonai our God,  and worship at his holy mountain,  for Adonai our God <span class=\"supplied\">is</span> holy.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '100', '1', '1', '<span class=\"verse-title\">A psalm of thanksgiving.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Shout in triumph to Adonai, all the earth.'),
('LEB', 'Psalms', '100', '2', '1', 'Serve<span class=\"note\">Or “Worship”</span>Adonai with joy;  come into his presence with exultation.'),
('LEB', 'Psalms', '100', '3', '1', 'Know that Adonai, he <span class=\"supplied\">is</span> God;  he made us and we are his.<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>); the <span class=\"italic\">Kethib</span> reads “and not we <span class=\"supplied\">ourselves</span>”</span> <span class=\"supplied\">We are</span> his people and the sheep of his pasture.'),
('LEB', 'Psalms', '100', '4', '1', 'Enter his gates with thanksgiving,  his courts with praise.  Give thanks to him; bless his name.'),
('LEB', 'Psalms', '100', '5', '1', 'For Adonai <span class=\"supplied\">is</span> good; his loyal love is forever,  and his faithfulness <span class=\"supplied\">is</span> <span class=\"idiom\">from generation to generation</span>.<span class=\"note\">Literally “unto a generation and a generation”</span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '101', '1', '1', '<span class=\"verse-title\">Of David. A psalm.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>I will sing of loyal love and justice;  I will sing praises to you, O Adonai.'),
('LEB', 'Psalms', '101', '2', '1', 'I will give attention to <span class=\"supplied\">the</span> way of integrity.  When will you come to me?  I will walk in the integrity of my heart  in the midst of my house.'),
('LEB', 'Psalms', '101', '3', '1', 'I will not set before my eyes  any wicked thing.  I hate the work of those who fall away;<span class=\"note\">The term “those who fall away” is a rare noun. The verb from the same root is found in Psa 40:5(4)</span> it will not cling to me.'),
('LEB', 'Psalms', '101', '4', '1', 'A perverse heart will depart from me;  I will not know evil.'),
('LEB', 'Psalms', '101', '5', '1', 'One who slanders his neighbor in secret,  him I will destroy.  <span class=\"supplied\">One</span> haughty of eyes and <span class=\"idiom\">arrogant</span><span class=\"note\">Literally “wide”</span>of heart,  him I will not endure.'),
('LEB', 'Psalms', '101', '6', '1', 'My eyes <span class=\"supplied\">will be</span> on <span class=\"supplied\">the</span> faithful of <span class=\"supplied\">the</span> land,  that <span class=\"supplied\">they may</span> live with me.  <span class=\"supplied\">He who</span> walks in <span class=\"supplied\">the</span> way of integrity,  he shall minister to me.'),
('LEB', 'Psalms', '101', '7', '1', 'There will not live in the midst of my house  a worker of deceit.  One who speaks lies  will not remain<span class=\"note\">Hebrew “be established”</span>before my eyes.'),
('LEB', 'Psalms', '101', '8', '1', '<span class=\"idiom\">Each morning</span>,<span class=\"note\">Literally “to the mornings”</span> I will destroy all <span class=\"supplied\">the</span> wicked of <span class=\"supplied\">the</span> land,  cutting off from the city of Adonai all evildoers.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '102', '1', '1', '<span class=\"verse-title\">A prayer of <span class=\"supplied\">one</span> afflicted, when he grows faint  and pours out his lament before Adonai.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>O Adonai, hear my prayer,  and let my cry for help come to you.'),
('LEB', 'Psalms', '102', '2', '1', 'Do not hide your face from me  in <span class=\"supplied\">the</span> day <span class=\"supplied\">of</span> my trouble.  Incline your ear to me.  In the day I call, answer me quickly,'),
('LEB', 'Psalms', '102', '3', '1', 'for my days vanish in smoke,  and my bones are charred like a hearth.'),
('LEB', 'Psalms', '102', '4', '1', 'My heart is struck like grass and withers.  Indeed, I forget to eat<span class=\"note\">A rare homonym might yield “I am too wilted to eat”</span>my bread.'),
('LEB', 'Psalms', '102', '5', '1', 'Because of the sound of my groaning  my bones<span class=\"note\">Hebrew “bone”</span>cling to my skin.<span class=\"note\">Hebrew “flesh”</span>'),
('LEB', 'Psalms', '102', '6', '1', 'I am like an owl<span class=\"note\">The identity of the birds in this verse is uncertain</span>of <span class=\"supplied\">the</span> wilderness;  I am like a little owl of <span class=\"supplied\">the</span> ruins.'),
('LEB', 'Psalms', '102', '7', '1', 'I lie awake and I am  like a lone bird on a roof.'),
('LEB', 'Psalms', '102', '8', '1', 'All the day my enemies reproach me;  those who mock me swear <span class=\"supplied\">oaths</span> against me.'),
('LEB', 'Psalms', '102', '9', '1', 'Indeed, I eat ashes like bread  and mix my drink with tears<span class=\"note\">Hebrew “weeping”</span>'),
('LEB', 'Psalms', '102', '10', '1', 'because of your indignation and anger,  for you have picked me up and thrown me away.'),
('LEB', 'Psalms', '102', '11', '1', 'My days <span class=\"supplied\">are</span> like a lengthened shadow,  and I wither like grass.'),
('LEB', 'Psalms', '102', '12', '1', 'But you, O Adonai, abide<span class=\"note\">Or “sit enthroned”</span>forever,  and your remembrance<span class=\"note\">Or “name”</span><span class=\"idiom\">from generation to generation</span>.<span class=\"note\">Literally “to a generation and a generation”</span>'),
('LEB', 'Psalms', '102', '13', '1', 'You rise up and take pity on Zion,  because <span class=\"supplied\">it is</span> time to favor it,  for <span class=\"supplied\">the</span> appointed time has come.'),
('LEB', 'Psalms', '102', '14', '1', 'Your servants take pleasure in her stones,  and show favor to its dust.'),
('LEB', 'Psalms', '102', '15', '1', 'Then <span class=\"supplied\">the</span> nations will fear the name of Adonai,  and all the kings of the earth your glory.'),
('LEB', 'Psalms', '102', '16', '1', 'For Adonai will rebuild Zion;  he will appear in his glory.'),
('LEB', 'Psalms', '102', '17', '1', 'He will turn his attention to the prayer of the destitute  and will not despise their prayer.'),
('LEB', 'Psalms', '102', '18', '1', 'Let this be written for <span class=\"supplied\">the</span> next generation,  so that a people <span class=\"supplied\">yet</span> to be created may praise Yah,<span class=\"note\">A shortened form of “Adonai”</span>'),
('LEB', 'Psalms', '102', '19', '1', 'that he looked down from his holy height.  Adonai looked from heaven over <span class=\"supplied\">the</span> earth'),
('LEB', 'Psalms', '102', '20', '1', 'to hear <span class=\"supplied\">the</span> groaning of <span class=\"supplied\">the</span> prisoner,  to liberate <span class=\"idiom\">those destined to die</span>,<span class=\"note\">Hebrew “<span class=\"supplied\">the</span> sons of death”</span>'),
('LEB', 'Psalms', '102', '21', '1', 'so that <span class=\"supplied\">they<span class=\"note\">That is, the generation that reads what is written</span>may</span> make known in Zion the name of Adonai,  and his praise in Jerusalem,'),
('LEB', 'Psalms', '102', '22', '1', 'when the peoples assemble,  together with<span class=\"note\">Hebrew “and”</span>kingdoms, to serve Adonai.'),
('LEB', 'Psalms', '102', '23', '1', 'He has broken my strength along the way;  he has cut short my days.'),
('LEB', 'Psalms', '102', '24', '1', 'I say, “My God, do not carry me off  from my life in the middle of my days.”  Your years <span class=\"supplied\">continue</span> throughout all generations.'),
('LEB', 'Psalms', '102', '25', '1', 'Long ago you laid the foundation of the earth,  and <span class=\"supplied\">the</span> heavens <span class=\"supplied\">are</span> the work of your hands.'),
('LEB', 'Psalms', '102', '26', '1', 'They will perish, but you will endure.  And like a garment they will all wear out,  you will replace them like clothing, and they will be set aside.'),
('LEB', 'Psalms', '102', '27', '1', 'But you <span class=\"supplied\">are</span> the same,  and your years do not end.'),
('LEB', 'Psalms', '102', '28', '1', 'The children of your servants will continue,  and their <span class=\"idiom\">descendants</span><span class=\"note\">Literally “seed”</span>will be established before you.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '103', '1', '1', '<span class=\"verse-title\">Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Bless Adonai, O my soul,  and all within me, <span class=\"supplied\">bless</span> his holy name.'),
('LEB', 'Psalms', '103', '2', '1', 'Bless Adonai, O my soul,  and do not forget all his benefits:'),
('LEB', 'Psalms', '103', '3', '1', 'who forgives all your iniquity,  who heals all your diseases,'),
('LEB', 'Psalms', '103', '4', '1', 'who redeems your life from <span class=\"supplied\">the</span> pit,  who crowns you with loyal love and mercies,'),
('LEB', 'Psalms', '103', '5', '1', 'who satisfies your life with good  so that your youth is renewed like the eagle’s.'),
('LEB', 'Psalms', '103', '6', '1', 'Adonai does deeds of justice  and judgments for all <span class=\"supplied\">who</span> are oppressed,'),
('LEB', 'Psalms', '103', '7', '1', '<span class=\"supplied\">who</span> made known his ways to Moses,  his deeds to the <span class=\"idiom\">people</span><span class=\"note\">Literally “sons”</span>of Israel.'),
('LEB', 'Psalms', '103', '8', '1', 'Adonai <span class=\"supplied\">is</span> compassionate and gracious,  <span class=\"idiom\">slow to anger</span><span class=\"note\">Literally “long of nostrils”</span>and abundant in loyal love.'),
('LEB', 'Psalms', '103', '9', '1', 'He does not dispute continually,  nor keep <span class=\"supplied\">his anger</span> forever.'),
('LEB', 'Psalms', '103', '10', '1', 'He has not dealt with us according to our sins,  nor repaid us according to our iniquities.'),
('LEB', 'Psalms', '103', '11', '1', 'For as <span class=\"supplied\">the</span> heavens are high above the earth,  <span class=\"supplied\">so</span> his loyal love prevails over those who fear him.'),
('LEB', 'Psalms', '103', '12', '1', 'As far as east <span class=\"supplied\">is</span> from west,  <span class=\"supplied\">so</span> he has removed far from us <span class=\"supplied\">the guilt of</span> our transgressions.'),
('LEB', 'Psalms', '103', '13', '1', 'As a father pities<span class=\"note\">Or “shows compassion to”</span><span class=\"supplied\">his</span> children,  <span class=\"supplied\">so</span> Adonai pities<span class=\"note\">Or “shows compassion to”</span>those who fear him.'),
('LEB', 'Psalms', '103', '14', '1', 'For he knows our frame.<span class=\"note\">That is, how we are formed</span> He remembers that we <span class=\"supplied\">are</span> dust.'),
('LEB', 'Psalms', '103', '15', '1', 'As for man, his days <span class=\"supplied\">are</span> like the grass.  As the flower of the field, so he blossoms.'),
('LEB', 'Psalms', '103', '16', '1', 'When <span class=\"supplied\">the</span> wind passes over it, it is no more,  and its place knows it no longer.'),
('LEB', 'Psalms', '103', '17', '1', 'But the loyal love of Adonai  <span class=\"supplied\">is</span> from everlasting to everlasting on those who fear him,  and his righteousness to <span class=\"supplied\">their</span> children’s children,'),
('LEB', 'Psalms', '103', '18', '1', 'to those who keep his covenant  and remember to do his precepts.'),
('LEB', 'Psalms', '103', '19', '1', 'Adonai has established his throne in the heavens,  and his kingdom rules over all.'),
('LEB', 'Psalms', '103', '20', '1', 'Bless Adonai, you his angels,  <span class=\"supplied\">you</span> mighty heroes who do his word  by <span class=\"idiom\">obeying</span><span class=\"note\">Literally “hearing”</span>the sound of his word.'),
('LEB', 'Psalms', '103', '21', '1', 'Bless Adonai, all you his hosts,  <span class=\"supplied\">you</span> his attendants who do his will.'),
('LEB', 'Psalms', '103', '22', '1', 'Bless Adonai, all his works,<span class=\"note\">That is, created things</span> in all the places of his dominion.  Bless Adonai, O my soul.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '104', '1', '1', '<span class=\"verse-title\">Praise to Adonai for His Creation and Providence</span>Bless Adonai, O my soul.  O Adonai my God, you are very great.  You clothe yourself <span class=\"supplied\">with</span> splendor and majesty,'),
('LEB', 'Psalms', '104', '2', '1', '<span class=\"supplied\">you</span> who cover <span class=\"supplied\">yourself</span> with light as <span class=\"supplied\">with</span> a garment,  who stretch out <span class=\"supplied\">the</span> heavens like a tent curtain,'),
('LEB', 'Psalms', '104', '3', '1', 'the one who sets beams in the waters <span class=\"supplied\">for</span> his upper chambers,  who makes clouds his chariot,  who rides<span class=\"note\">Hebrew “goes”</span>on <span class=\"supplied\">the</span> wings of <span class=\"supplied\">the</span> wind,'),
('LEB', 'Psalms', '104', '4', '1', 'who makes his messengers <span class=\"supplied\">the</span> winds,  his attendants a flame of fire.'),
('LEB', 'Psalms', '104', '5', '1', 'He established <span class=\"supplied\">the</span> earth on her foundations,  <span class=\"supplied\">so that</span> it will not be moved forever and ever.'),
('LEB', 'Psalms', '104', '6', '1', 'You covered it with <span class=\"supplied\">the</span> deep as with a garment.  <span class=\"supplied\">The</span> waters stood above <span class=\"supplied\">the</span> mountains.'),
('LEB', 'Psalms', '104', '7', '1', 'At your rebuke they fled;  at the sound of your thunder they ran off.'),
('LEB', 'Psalms', '104', '8', '1', 'They ascended <span class=\"supplied\">the</span> mountains <span class=\"supplied\">and</span> drained though the valleys  to <span class=\"supplied\">the</span> place that you established<span class=\"note\">Or “appointed”</span>for them.'),
('LEB', 'Psalms', '104', '9', '1', 'You set a boundary that they may not cross over,  <span class=\"supplied\">so that</span> they would not return to cover the earth.'),
('LEB', 'Psalms', '104', '10', '1', '<span class=\"supplied\">You are</span> the one who sends forth springs into the valleys;  they flow between <span class=\"supplied\">the</span> mountains.'),
('LEB', 'Psalms', '104', '11', '1', 'They give drink for every beast of <span class=\"supplied\">the</span> field.  <span class=\"supplied\">The</span> wild donkeys <span class=\"idiom\">quench</span><span class=\"note\">Literally “break”</span>their thirst.'),
('LEB', 'Psalms', '104', '12', '1', 'Along them the birds of the heavens abide.  From among <span class=\"supplied\">the</span> branches they <span class=\"idiom\">sing</span>.<span class=\"note\">Literally “give voice”</span>'),
('LEB', 'Psalms', '104', '13', '1', '<span class=\"supplied\">You are the one who</span> <span class=\"idiom\">waters</span><span class=\"note\">Literally “give drink”</span><span class=\"supplied\">the</span> mountains  from his upper chambers.  The earth is full<span class=\"note\">Or “is sated”</span>with the fruit of your labors:'),
('LEB', 'Psalms', '104', '14', '1', '<span class=\"supplied\">who</span> causes grass to grow for the cattle  and herbs for the service of humankind,  to bring forth food from the earth,'),
('LEB', 'Psalms', '104', '15', '1', 'and wine <span class=\"supplied\">that</span> makes glad <span class=\"supplied\">the</span> heart of man,  so that <span class=\"supplied\">their</span> faces shine from oil,  and bread <span class=\"supplied\">that</span> strengthens the heart of man.'),
('LEB', 'Psalms', '104', '16', '1', 'The trees of Adonai drink their fill,  the cedars of Lebanon that he planted,'),
('LEB', 'Psalms', '104', '17', '1', 'where birds make <span class=\"supplied\">their</span> nest.  <span class=\"supplied\">The</span> stork <span class=\"supplied\">has</span> its home in <span class=\"supplied\">the</span> fir trees.'),
('LEB', 'Psalms', '104', '18', '1', 'The high mountains <span class=\"supplied\">are</span> for the wild goats;  <span class=\"supplied\">the</span> cliffs <span class=\"supplied\">are</span> a refuge for the rock badgers.'),
('LEB', 'Psalms', '104', '19', '1', 'He made <span class=\"supplied\">the</span> moon for appointed times;  <span class=\"supplied\">the</span> sun knows its <span class=\"supplied\">time for</span> setting.'),
('LEB', 'Psalms', '104', '20', '1', 'You make darkness, and it is night,  when<span class=\"note\">Hebrew “in it”</span>all <span class=\"supplied\">the</span> animals of the forest creep about.'),
('LEB', 'Psalms', '104', '21', '1', 'The young lions <span class=\"supplied\">are</span> roaring for the prey  and seeking their food from God.'),
('LEB', 'Psalms', '104', '22', '1', '<span class=\"supplied\">When</span> the sun rises, they gather together  and lie down in their dens.'),
('LEB', 'Psalms', '104', '23', '1', 'Humankind goes out to its work,  and to its labor until evening.'),
('LEB', 'Psalms', '104', '24', '1', 'How many are your works, O Adonai;  all of them you have done in wisdom.  The earth is full of your creatures.'),
('LEB', 'Psalms', '104', '25', '1', 'This <span class=\"supplied\">is</span> the great and <span class=\"idiom\">wide</span><span class=\"note\">Literally “broad of hands”</span>sea,  in which <span class=\"supplied\">are</span> moving animals without number,  living things small and great.'),
('LEB', 'Psalms', '104', '26', '1', 'There <span class=\"supplied\">the</span> ships sail.  Leviathan <span class=\"supplied\">is there</span> that you formed to play with.<span class=\"note\">For this construction meaning “to play with,” see Job 40:29 (Eng: 41:5)</span>'),
('LEB', 'Psalms', '104', '27', '1', 'They all wait for you  to give <span class=\"supplied\">them</span> their food <span class=\"idiom\">at the proper time</span>.<span class=\"note\">Literally “in its season”</span>'),
('LEB', 'Psalms', '104', '28', '1', 'You give to them; they gather <span class=\"supplied\">it</span>.  You open your hand, they are filled with <span class=\"supplied\">what is</span> good.'),
('LEB', 'Psalms', '104', '29', '1', 'You hide your face, they are terrified.  You take away their breath, they die  and return to their dust.'),
('LEB', 'Psalms', '104', '30', '1', 'You send forth your Spirit,<span class=\"note\">Or “breath”</span>they are created,  and you renew the face of <span class=\"supplied\">the</span> ground.'),
('LEB', 'Psalms', '104', '31', '1', 'May the glory of Adonai endure forever.  May Adonai be glad in his works,'),
('LEB', 'Psalms', '104', '32', '1', 'the one who looks at the earth and it quakes,  and touches the mountains and they smoke.'),
('LEB', 'Psalms', '104', '33', '1', 'I will sing to Adonai throughout my life;  I will sing praise to my God while <span class=\"idiom\">I remain alive</span>.<span class=\"note\">Literally “while I am still”</span>'),
('LEB', 'Psalms', '104', '34', '1', 'May my meditation be pleasing to him.  I will be glad in Adonai.'),
('LEB', 'Psalms', '104', '35', '1', 'Let sinners perish completely from the earth,  and <span class=\"supplied\">the</span> wicked <span class=\"idiom\">not remain alive</span>.<span class=\"note\">Literally “they are not still”</span> Bless Adonai, O my soul.  Praise Yah.<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '105', '1', '1', '<span class=\"verse-title\">Praise to Adonai for His Work on Behalf of Israel</span>Give thanks to Adonai; proclaim his name;  make known his deeds among the peoples.'),
('LEB', 'Psalms', '105', '2', '1', 'Sing to him; sing praises concerning him;  tell of all his wonderful works.'),
('LEB', 'Psalms', '105', '3', '1', 'Boast about his holy name.  Let the heart of those who seek Adonai rejoice.'),
('LEB', 'Psalms', '105', '4', '1', 'Make supplication to Adonai and his might;  seek his face continually.'),
('LEB', 'Psalms', '105', '5', '1', 'Remember his wonders that he has done,  his signs and the judgments of his mouth,'),
('LEB', 'Psalms', '105', '6', '1', 'O offspring of Abraham his servant,  <span class=\"supplied\">you</span> descendants<span class=\"note\">Or “children,” or “sons”</span>of Jacob, his chosen ones.'),
('LEB', 'Psalms', '105', '7', '1', 'He <span class=\"supplied\">is</span> Adonai our God;  his judgments <span class=\"supplied\">are</span> in all the earth.'),
('LEB', 'Psalms', '105', '8', '1', 'He remembers his covenant forever,  <span class=\"supplied\">the</span> word <span class=\"supplied\">that</span> he commanded  for a thousand generations,'),
('LEB', 'Psalms', '105', '9', '1', 'that he made with Abraham,  and by his oath <span class=\"supplied\">swore</span> to Isaac.'),
('LEB', 'Psalms', '105', '10', '1', 'He then confirmed it to Jacob as a statute,  to Israel <span class=\"supplied\">as</span> an everlasting covenant,'),
('LEB', 'Psalms', '105', '11', '1', 'saying, “To you I will give the land of Canaan  <span class=\"supplied\">as</span> the allotment <span class=\"supplied\">that is</span> your<span class=\"note\">Hebrew plural</span>inheritance.”'),
('LEB', 'Psalms', '105', '12', '1', 'When they were <span class=\"idiom\">few in number</span><span class=\"note\">Literally “men of number,” that is, so few you could count them</span>—  a trifle—and <span class=\"supplied\">were</span> sojourners in it,'),
('LEB', 'Psalms', '105', '13', '1', 'and they wandered about <span class=\"idiom\">among the nations</span>,<span class=\"note\">Literally “from people to people”</span> from <span class=\"supplied\">one</span> kingdom to another people,'),
('LEB', 'Psalms', '105', '14', '1', 'he allowed no one to oppress them,  and he rebuked kings on account of them,'),
('LEB', 'Psalms', '105', '15', '1', '“Do not touch my anointed ones,  nor harm my prophets.”'),
('LEB', 'Psalms', '105', '16', '1', 'Then he called for a famine upon the land;  he broke every <span class=\"idiom\">supply</span><span class=\"note\">Literally “staff”</span>of bread.'),
('LEB', 'Psalms', '105', '17', '1', 'He sent a man on ahead of them;  Joseph was sold as a slave.'),
('LEB', 'Psalms', '105', '18', '1', 'They clamped<span class=\"note\">Hebrew “oppressed, hurt”</span>his feet in fetters.<span class=\"note\">Hebrew “fetter”</span> His neck<span class=\"note\">Hebrew <span class=\"italic\">nephesh</span> can designate the life, soul, appetite, desire, or throat/neck.</span>went into irons.<span class=\"note\">Hebrew “iron”</span>'),
('LEB', 'Psalms', '105', '19', '1', 'Until <span class=\"supplied\">the</span> time his word came about,  the word of Adonai tested<span class=\"note\">Or “refined”</span>him.'),
('LEB', 'Psalms', '105', '20', '1', '<span class=\"supplied\">The</span> king sent and he freed him;  <span class=\"supplied\">the</span> ruler of <span class=\"supplied\">the</span> peoples <span class=\"supplied\">sent</span> and let him loose.'),
('LEB', 'Psalms', '105', '21', '1', 'He made him lord of his house  and ruler of all his possessions,'),
('LEB', 'Psalms', '105', '22', '1', 'to obligate<span class=\"note\">Hebrew “to bind, imprison” The LXX’s “to instruct” presupposes <span class=\"italic\">ysr</span> instead of <span class=\"italic\">\'sr</span>, and might be correct in light of the parallelism that is fairly tight in this psalm</span>his officials <span class=\"idiom\">as he saw fit</span><span class=\"note\">Literally “in his desire <span class=\"supplied\">soul</span>”</span> and teach his elders wisdom.'),
('LEB', 'Psalms', '105', '23', '1', 'Then Israel came <span class=\"supplied\">to</span> Egypt,  and Jacob was a sojourner in the land of Ham.'),
('LEB', 'Psalms', '105', '24', '1', 'And he<span class=\"note\">That is, God</span>made his people very fruitful.  He also made them<span class=\"note\">Hebrew “him”</span>stronger than their<span class=\"note\">Hebrew “his”</span>enemies.'),
('LEB', 'Psalms', '105', '25', '1', 'He turned their heart to hate his people,  to deal deceitfully against his servants.'),
('LEB', 'Psalms', '105', '26', '1', 'He sent his servant Moses,  together with Aaron whom he had chosen.'),
('LEB', 'Psalms', '105', '27', '1', 'They set before them the words concerning his signs  and portents in the land of Ham.'),
('LEB', 'Psalms', '105', '28', '1', 'He sent darkness and made <span class=\"supplied\">it</span> dark,  and they did not rebel against his words.'),
('LEB', 'Psalms', '105', '29', '1', 'He turned their waters into blood  and it killed their fish.'),
('LEB', 'Psalms', '105', '30', '1', 'Their land swarmed with frogs,  <span class=\"supplied\">even</span> in the chambers of their kings.'),
('LEB', 'Psalms', '105', '31', '1', 'He spoke, and there came flies  <span class=\"supplied\">and</span> gnats throughout all their territory.<span class=\"note\">Hebrew “borders”</span>'),
('LEB', 'Psalms', '105', '32', '1', 'He gave hail for their rains  <span class=\"supplied\">and the</span> fire of lightning in their land.'),
('LEB', 'Psalms', '105', '33', '1', 'And he struck their vines and fig trees,  and shattered the trees of their territory.'),
('LEB', 'Psalms', '105', '34', '1', 'He spoke and there came locusts  and young locusts without number.'),
('LEB', 'Psalms', '105', '35', '1', 'And they ate up all <span class=\"supplied\">the</span> vegetation in their land,  and they ate up the fruit of their ground.'),
('LEB', 'Psalms', '105', '36', '1', 'And he struck down all <span class=\"supplied\">the</span> firstborn in their land,  <span class=\"supplied\">the</span> first of all their virility.'),
('LEB', 'Psalms', '105', '37', '1', 'Then he brought them out with silver and gold,  and there was none among his tribes who stumbled.'),
('LEB', 'Psalms', '105', '38', '1', 'Egypt was glad when they departed,  because the fear of them<span class=\"note\">That is, Israel</span>had fallen upon them.'),
('LEB', 'Psalms', '105', '39', '1', 'He spread out a cloud for a covering,  and fire to give light at night.'),
('LEB', 'Psalms', '105', '40', '1', 'They<span class=\"note\">Hebrew “He”</span>asked, and he brought quail,  and satisfied them with <span class=\"supplied\">the</span> bread of heaven.'),
('LEB', 'Psalms', '105', '41', '1', 'He opened <span class=\"supplied\">the</span> rock and waters flowed;  they coursed<span class=\"note\">Or “surged”</span>through the dry places <span class=\"supplied\">like</span> a river.'),
('LEB', 'Psalms', '105', '42', '1', 'For he remembered his holy <span class=\"idiom\">promise</span>;<span class=\"note\">Literally “word”</span> <span class=\"supplied\">he remembered</span> Abraham his servant.'),
('LEB', 'Psalms', '105', '43', '1', 'And <span class=\"supplied\">so</span> he brought out his people with joy,  his chosen ones with rejoicing.'),
('LEB', 'Psalms', '105', '44', '1', 'And he gave to them <span class=\"supplied\">the</span> lands of <span class=\"supplied\">the</span> nations,  and they inherited <span class=\"supplied\">the</span> labor of <span class=\"supplied\">the</span> peoples,'),
('LEB', 'Psalms', '105', '45', '1', 'so that they might observe his statutes,  and keep his laws.  Praise Yah.<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '106', '1', '1', '<span class=\"verse-title\">Praise to Adonai for His Faithfulness in Israel’s History</span>Praise Yah.<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span>Give thanks to Adonai, for <span class=\"supplied\">he is</span> good,  for his loyal love <span class=\"supplied\">is</span> forever.'),
('LEB', 'Psalms', '106', '2', '1', 'Who can utter the mighty deeds of Adonai,  <span class=\"supplied\">or</span> proclaim all his praise?'),
('LEB', 'Psalms', '106', '3', '1', 'Blessed are <span class=\"supplied\">those who</span> observe justice,  <span class=\"supplied\">he who</span> does righteousness at all times.'),
('LEB', 'Psalms', '106', '4', '1', 'Remember me, O Adonai, when <span class=\"supplied\">you show</span> favor to your people.  Look after me when you deliver,'),
('LEB', 'Psalms', '106', '5', '1', 'that <span class=\"supplied\">I</span> may see the good <span class=\"supplied\">done</span> your chosen ones,  to be glad in the joy of your nation,  to glory together with your inheritance.'),
('LEB', 'Psalms', '106', '6', '1', 'We have sinned <span class=\"supplied\">along</span> with our ancestors.<span class=\"note\">Or “fathers”</span> We have committed iniquity; we have incurred guilt.'),
('LEB', 'Psalms', '106', '7', '1', 'Our ancestors<span class=\"note\">Or “fathers”</span>in Egypt did not understand your wonderful works.  They did not remember your many acts of loyal love,  and <span class=\"supplied\">so</span> they rebelled by <span class=\"supplied\">the</span> sea at <span class=\"idiom\">the Red Sea</span>.<span class=\"note\">Literally “sea of reed”</span>'),
('LEB', 'Psalms', '106', '8', '1', 'Yet he saved them for the sake of his name,  to make known his might.'),
('LEB', 'Psalms', '106', '9', '1', 'So he rebuked <span class=\"idiom\">the Red Sea</span>,<span class=\"note\">Literally “sea of reed”</span>and it dried up,  and he led them through the deep as <span class=\"supplied\">through</span> a desert.'),
('LEB', 'Psalms', '106', '10', '1', 'Thus he saved them from <span class=\"supplied\">the</span> hand of <span class=\"supplied\">the</span> hater  and redeemed them from <span class=\"supplied\">the</span> hand of <span class=\"supplied\">the</span> enemy.'),
('LEB', 'Psalms', '106', '11', '1', 'But waters covered their adversaries;  not one of them survived.'),
('LEB', 'Psalms', '106', '12', '1', 'Then they believed his words;  they sang his praise.'),
('LEB', 'Psalms', '106', '13', '1', 'They quickly forgot his works;  they did not wait for his counsel.'),
('LEB', 'Psalms', '106', '14', '1', 'And they <span class=\"idiom\">craved intensely</span><span class=\"note\">Literally “craved a craving”</span>in the wilderness,  and tested God in <span class=\"supplied\">the</span> desert.'),
('LEB', 'Psalms', '106', '15', '1', 'So he gave to them their request,  but he sent leanness into their souls.<span class=\"note\">Hebrew “soul” or “inner self”</span>'),
('LEB', 'Psalms', '106', '16', '1', 'And they were jealous of Moses in the camp,  <span class=\"supplied\">and</span> of Aaron, the holy one of Adonai.'),
('LEB', 'Psalms', '106', '17', '1', '<span class=\"supplied\">The</span> earth opened and swallowed up Dathan,  and it covered over the gang of Abiram.'),
('LEB', 'Psalms', '106', '18', '1', 'Also fire burned in their assembly;  <span class=\"supplied\">the</span> flame devoured <span class=\"supplied\">the</span> wicked.'),
('LEB', 'Psalms', '106', '19', '1', 'They made a calf at Horeb  and bowed down to a cast image.'),
('LEB', 'Psalms', '106', '20', '1', 'And <span class=\"supplied\">so</span> they exchanged their glory  for an image of an ox that eats grass.'),
('LEB', 'Psalms', '106', '21', '1', 'They forgot God their Savior,  who had done great things in Egypt,'),
('LEB', 'Psalms', '106', '22', '1', 'wonders in the land of Ham,  awesome deeds by <span class=\"idiom\">the Red Sea</span>.<span class=\"note\">Literally “sea of reed”</span>'),
('LEB', 'Psalms', '106', '23', '1', 'So he said he would exterminate them,  had not Moses, his chosen one,  stood in the breach before him,  to reverse his wrath from destroying <span class=\"supplied\">them</span>.'),
('LEB', 'Psalms', '106', '24', '1', 'Then they refused the desirable land;  they did not believe his word,'),
('LEB', 'Psalms', '106', '25', '1', 'but grumbled in their tents.  They did not obey the voice of Adonai.'),
('LEB', 'Psalms', '106', '26', '1', 'So <span class=\"idiom\">he made an oath</span><span class=\"note\">Literally “he lifted his hand,” an act accompanying an oath</span>against them,  to make them drop in the wilderness,'),
('LEB', 'Psalms', '106', '27', '1', 'and to disperse<span class=\"note\">Or “let drop”</span>their <span class=\"idiom\">descendants</span><span class=\"note\">Literally “seed”</span>among the nations  and to scatter them among the lands.'),
('LEB', 'Psalms', '106', '28', '1', 'They also attached themselves to Baal of Peor,  and they ate sacrifices offered to <span class=\"supplied\">the</span> dead.<span class=\"note\">Hebrew “sacrifices of dead”</span>'),
('LEB', 'Psalms', '106', '29', '1', 'Thus they provoked to anger by their deeds,  and a plague broke out among them.'),
('LEB', 'Psalms', '106', '30', '1', 'Then Phinehas stood up and intervened,  and <span class=\"supplied\">so</span> the plague was stopped,'),
('LEB', 'Psalms', '106', '31', '1', 'and it was reckoned to him as righteousness  <span class=\"idiom\">throughout all generations</span>.<span class=\"note\">Literally “for a generation and a generation”</span>'),
('LEB', 'Psalms', '106', '32', '1', 'They also angered <span class=\"supplied\">God</span> at the waters of Meribah,  and it went badly for Moses on account of them,'),
('LEB', 'Psalms', '106', '33', '1', 'because they rebelled against his Spirit,<span class=\"note\">A slightly different verbal form yields “they embittered his spirit”</span> and he spoke thoughtlessly with his lips.'),
('LEB', 'Psalms', '106', '34', '1', 'They did not exterminate the peoples,  as Adonai had commanded them, <span class=\"note\">Hebrew “said to them”</span>'),
('LEB', 'Psalms', '106', '35', '1', 'but they mingled with the nations  and learned their works,'),
('LEB', 'Psalms', '106', '36', '1', 'and served their idols,  which became<span class=\"note\">Hebrew “and they became”</span>a snare to them.'),
('LEB', 'Psalms', '106', '37', '1', 'They even sacrificed their sons and daughters  to the demons,'),
('LEB', 'Psalms', '106', '38', '1', 'and they poured out innocent blood,  the blood of their sons and daughters,  whom they sacrificed to the idols of Canaan,  and <span class=\"supplied\">so</span> the land was defiled with the blood.<span class=\"note\">Hebrew “bloods,” that is, bloodshed</span>'),
('LEB', 'Psalms', '106', '39', '1', 'And they became unclean by their works,  and were unfaithful in their deeds.'),
('LEB', 'Psalms', '106', '40', '1', 'So Adonai’s anger burned against his people,  and he abhorred his inheritance.'),
('LEB', 'Psalms', '106', '41', '1', 'Then he gave them into <span class=\"supplied\">the</span> hand of <span class=\"supplied\">the</span> nations,  and those who hated them ruled over them.'),
('LEB', 'Psalms', '106', '42', '1', 'And their enemies oppressed them,  and they were subdued under their hand.'),
('LEB', 'Psalms', '106', '43', '1', 'Many times he delivered them,  but they rebelled in their counsel,  and were brought low by their iniquity.'),
('LEB', 'Psalms', '106', '44', '1', 'Yet he looked upon their distress  when he heard their cry.'),
('LEB', 'Psalms', '106', '45', '1', 'And he remembered his covenant with them,  and relented based on the abundance of his loyal love.'),
('LEB', 'Psalms', '106', '46', '1', 'And he <span class=\"idiom\">let them find compassion</span><span class=\"note\">Literally “gave them to <span class=\"supplied\">have</span> mercies”</span> before all their captors.'),
('LEB', 'Psalms', '106', '47', '1', 'Save us, O Adonai our God,  and gather us from the nations,  so that <span class=\"supplied\">we may</span> give thanks to your holy name  <span class=\"supplied\">and</span> boast in your praise.'),
('LEB', 'Psalms', '106', '48', '1', 'Blessed <span class=\"supplied\">is</span> Adonai, the God of Israel,  from everlasting and to everlasting.  And let all the people say, “Amen!”  Praise Yah.<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '107', '1', '1', '<span class=\"verse-title\">Thanksgiving to Adonai for His of Deliverance</span>Give thanks to Adonai, for <span class=\"supplied\">he is</span> good,  for his loyal love <span class=\"supplied\">is</span> forever.'),
('LEB', 'Psalms', '107', '2', '1', 'Let the redeemed of Adonai declare<span class=\"note\">Hebrew “say”</span><span class=\"supplied\">it</span>,  <span class=\"supplied\">those</span> whom he has redeemed from <span class=\"supplied\">the</span> hand of <span class=\"supplied\">the</span> enemy'),
('LEB', 'Psalms', '107', '3', '1', 'and gathered from <span class=\"supplied\">the</span> lands,  from east and from west, from north and from south.<span class=\"note\">Hebrew “sea” (<span class=\"italic\">yam</span>), which usually means “west” in a listing of directions. The word “south” is expected (Hebrew <span class=\"italic\">yamin</span>). Either the MT was corrupted early (along with Isa 49:12; the LXX reads “sea” in both) or it looks to the bodies of waters to the south (see also the Targum)</span>'),
('LEB', 'Psalms', '107', '4', '1', 'They wandered in the wilderness, in a desert.  They could find no way<span class=\"note\">The MT reads “way” with the previous line.</span>to a city to inhabit.'),
('LEB', 'Psalms', '107', '5', '1', 'Hungry and thirsty,  their soul grew faint within them.'),
('LEB', 'Psalms', '107', '6', '1', 'Then they cried out to Adonai in their trouble.  He delivered them from their distresses'),
('LEB', 'Psalms', '107', '7', '1', 'and led them by a straight way  to get to a city to inhabit.'),
('LEB', 'Psalms', '107', '8', '1', 'Let them give thanks to Adonai for his loyal love,  and his wonderful deeds for <span class=\"supplied\">the</span> children of humankind,'),
('LEB', 'Psalms', '107', '9', '1', 'for he satisfies <span class=\"supplied\">the</span> longing soul,<span class=\"note\">Thirst is perhaps in view</span> and <span class=\"supplied\">the</span> hungry soul he fills with good.'),
('LEB', 'Psalms', '107', '10', '1', '<span class=\"supplied\">Those who</span> sat in darkness and gloom,  prisoners of misery and iron—'),
('LEB', 'Psalms', '107', '11', '1', 'because they rebelled against the words of God  and spurned the counsel of the Most High,'),
('LEB', 'Psalms', '107', '12', '1', 'he therefore humbled their heart with trouble.  They stumbled and there was no helper.'),
('LEB', 'Psalms', '107', '13', '1', 'Then they called to Adonai for help in their trouble;  he saved them from their distresses.'),
('LEB', 'Psalms', '107', '14', '1', 'He brought them out of darkness and gloom,  and tore off their bonds.'),
('LEB', 'Psalms', '107', '15', '1', 'Let them give thanks to Adonai for his loyal love  and his wonderful deeds for <span class=\"supplied\">the</span> children of humankind,'),
('LEB', 'Psalms', '107', '16', '1', 'for he shatters <span class=\"supplied\">the</span> doors of bronze,  and cuts through <span class=\"supplied\">the</span> bars of iron.'),
('LEB', 'Psalms', '107', '17', '1', 'Fools, because of their rebellious way  and their iniquities, were afflicted.'),
('LEB', 'Psalms', '107', '18', '1', 'Their soul abhorred all food,  and they approached <span class=\"supplied\">the</span> gates of death.'),
('LEB', 'Psalms', '107', '19', '1', 'Then they called to Adonai for help in their trouble.  He saved them from their distresses.'),
('LEB', 'Psalms', '107', '20', '1', 'He sent his word and healed them,  and he delivered <span class=\"supplied\">them</span> from their pits.<span class=\"note\">This word is only found elsewhere in Lam 4:20 with the sense of “traps”</span>'),
('LEB', 'Psalms', '107', '21', '1', 'Let them give thanks to Adonai for his loyal love,  and his wonderful deeds for <span class=\"supplied\">the</span> children of humankind,'),
('LEB', 'Psalms', '107', '22', '1', 'and let them offer sacrifices of thanksgiving,  and tell of his works with rejoicing.'),
('LEB', 'Psalms', '107', '23', '1', '<span class=\"supplied\">Those who</span> went down to the sea into ships,  doing business on <span class=\"supplied\">the</span> <span class=\"idiom\">high seas</span>,<span class=\"note\">Literally “many waters”</span>'),
('LEB', 'Psalms', '107', '24', '1', 'they saw the works of Adonai,  and his wonderful deeds in <span class=\"supplied\">the</span> deep.'),
('LEB', 'Psalms', '107', '25', '1', 'For he spoke and raised up a stormy wind,  and it whipped up its waves.'),
('LEB', 'Psalms', '107', '26', '1', 'They rose to the heavens; they plunged to <span class=\"supplied\">the</span> depths.  Their soul melted in their calamity.'),
('LEB', 'Psalms', '107', '27', '1', 'They reeled and staggered like a drunkard,  and <span class=\"idiom\">they were at their wits’ end</span>.<span class=\"note\">Literally “all their wisdom was swallowed up”</span>'),
('LEB', 'Psalms', '107', '28', '1', 'Then they cried out to Adonai in their trouble,  and he brought them out of their distresses.'),
('LEB', 'Psalms', '107', '29', '1', 'He made <span class=\"supplied\">the</span> storm be still  and their waves became calm.'),
('LEB', 'Psalms', '107', '30', '1', 'Then they were glad because they grew silent,  so he guided them to their desired harbor.'),
('LEB', 'Psalms', '107', '31', '1', 'Let them give thanks to Adonai for his loyal love  and his wonderful deeds for <span class=\"supplied\">the</span> children of humankind,'),
('LEB', 'Psalms', '107', '32', '1', 'and let them exalt him in <span class=\"supplied\">the</span> congregation of <span class=\"supplied\">the</span> people,  and praise him in <span class=\"supplied\">the</span> assembly of <span class=\"supplied\">the</span> elders.'),
('LEB', 'Psalms', '107', '33', '1', 'He turns rivers into a wilderness  and springs of water into thirsty ground,'),
('LEB', 'Psalms', '107', '34', '1', 'a fruitful land into a salty place,  because of the evil of its inhabitants.'),
('LEB', 'Psalms', '107', '35', '1', 'He turns a wilderness into a pool of water  and a dry land into springs of water.'),
('LEB', 'Psalms', '107', '36', '1', 'And he settles <span class=\"supplied\">the</span> hungry there,  so that they may establish a city to inhabit,'),
('LEB', 'Psalms', '107', '37', '1', 'and sow fields and plant vineyards,  <span class=\"idiom\">that they may yield fruit at harvest</span>.<span class=\"note\">Hebrew “and make a fruit of produce”</span>'),
('LEB', 'Psalms', '107', '38', '1', 'And he blesses them and they multiply greatly,  and he does not let their cattle become few.'),
('LEB', 'Psalms', '107', '39', '1', 'When they become few and they are bent down  from <span class=\"supplied\">the</span> oppression of calamity and grief,'),
('LEB', 'Psalms', '107', '40', '1', '<span class=\"supplied\">he</span> pours contempt on princes  and causes them to wander in a trackless waste.'),
('LEB', 'Psalms', '107', '41', '1', 'But he protects<span class=\"note\">The sense is that of making something inaccessibly high, like a fortress</span><span class=\"supplied\">the</span> needy from misery,  and he makes <span class=\"supplied\">their</span> families like a flock.'),
('LEB', 'Psalms', '107', '42', '1', '<span class=\"supplied\">The</span> upright see <span class=\"supplied\">it</span> and are glad,  and all wickedness shuts its mouth.'),
('LEB', 'Psalms', '107', '43', '1', 'Whoever <span class=\"supplied\">is</span> wise, then let him observe these <span class=\"supplied\">things</span>,  and let them consider Adonai’s acts of loyal love.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '108', '1', '1', '<span class=\"verse-title\">A song. A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>My heart <span class=\"supplied\">is</span> steadfast, O God.  I will sing and give praise, even <span class=\"supplied\">with</span> my glory.'),
('LEB', 'Psalms', '108', '2', '1', 'Awake, harp and lyre.  I will awake <span class=\"supplied\">the</span> dawn.<span class=\"note\">Or “I will awake at dawn”</span>'),
('LEB', 'Psalms', '108', '3', '1', 'I will give you thanks among the peoples, O Adonai;  I will give you praise among the nations,'),
('LEB', 'Psalms', '108', '4', '1', 'because your loyal love <span class=\"supplied\">is</span> great above <span class=\"supplied\">the</span> heavens,  and your faithfulness <span class=\"supplied\">reaches</span> to <span class=\"supplied\">the</span> clouds.'),
('LEB', 'Psalms', '108', '5', '1', 'Be exalted above <span class=\"supplied\">the</span> heavens, O God,  and <span class=\"supplied\">let</span> your glory <span class=\"supplied\">be</span> above all the earth,'),
('LEB', 'Psalms', '108', '6', '1', 'so that your beloved ones may be rescued.  Save by your right hand and answer me.'),
('LEB', 'Psalms', '108', '7', '1', 'God has spoken in his holiness,  “I will exult, I will divide up Shechem,  and portion out the valley of Succoth.'),
('LEB', 'Psalms', '108', '8', '1', 'Gilead <span class=\"supplied\">is</span> mine. Manasseh <span class=\"supplied\">is</span> mine.  And Ephraim <span class=\"supplied\">is</span> the <span class=\"idiom\">helmet for</span><span class=\"note\">Literally “protection of”</span>my head.  Judah <span class=\"supplied\">is</span> my scepter;<span class=\"note\">Or “commander’s rod”</span>'),
('LEB', 'Psalms', '108', '9', '1', 'Moab <span class=\"supplied\">is</span> my washing pot.  Over Edom I will cast my sandal;  Over Philistia I will shout in triumph.”'),
('LEB', 'Psalms', '108', '10', '1', 'Who will bring me to <span class=\"supplied\">the</span> fortified city?  Who will lead me to Edom?'),
('LEB', 'Psalms', '108', '11', '1', 'Have you not rejected us, O God,  and not gone out with our armies, O God?'),
('LEB', 'Psalms', '108', '12', '1', 'Give us help against <span class=\"supplied\">the</span> enemy,  for the help of humankind <span class=\"supplied\">is</span> futile.'),
('LEB', 'Psalms', '108', '13', '1', 'Through God we will do valiantly,<span class=\"note\">Hebrew “do might”</span> and <span class=\"supplied\">it is</span> he <span class=\"supplied\">who</span> will tread down our enemies.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '109', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>O God of my praise, do not keep silent,'),
('LEB', 'Psalms', '109', '2', '1', 'for <span class=\"idiom\">wicked and deceitful mouths</span><span class=\"note\">Literally “a wicked mouth and a deceitful mouth”</span> have opened against me.  They speak to me with a lying tongue.'),
('LEB', 'Psalms', '109', '3', '1', 'They also surround me with words of hate,  and fight me without cause.'),
('LEB', 'Psalms', '109', '4', '1', 'In return for my love they accuse me,  though I <span class=\"supplied\">am in</span> prayer.<span class=\"note\">Or perhaps “I <span class=\"supplied\">have</span> a plea <span class=\"supplied\">of innocence</span>”</span>'),
('LEB', 'Psalms', '109', '5', '1', 'So they inflicted evil against me in return for good  and hatred in return for my love.'),
('LEB', 'Psalms', '109', '6', '1', 'Appoint over him a wicked <span class=\"supplied\">man</span>,  and let an accuser stand at his right <span class=\"supplied\">hand</span>.'),
('LEB', 'Psalms', '109', '7', '1', 'When he is judged, let him come out guilty,  and let his prayer<span class=\"note\">Or perhaps “plea <span class=\"supplied\">of innocence</span>”</span>become as sin.'),
('LEB', 'Psalms', '109', '8', '1', 'Let his days be few;  let another take his office.'),
('LEB', 'Psalms', '109', '9', '1', 'Let his children be orphans,  and his wife a widow,'),
('LEB', 'Psalms', '109', '10', '1', 'and let his children wander aimlessly and beg,  and let them plead from their ruins.'),
('LEB', 'Psalms', '109', '11', '1', 'Let <span class=\"supplied\">the</span> creditor seize all that <span class=\"supplied\">is</span> his,  and let strangers plunder his property.'),
('LEB', 'Psalms', '109', '12', '1', 'Let there be none <span class=\"supplied\">who</span> extend to him loyal love,  nor any who pities his orphans.'),
('LEB', 'Psalms', '109', '13', '1', 'Let his descendants<span class=\"note\">Or “posterity”; Hebrew is singular</span>be cut off.  Let their name be blotted out in <span class=\"supplied\">the</span> next generation.'),
('LEB', 'Psalms', '109', '14', '1', 'Let the iniquity of his ancestors<span class=\"note\">Or “fathers”</span>be remembered before Adonai,  and let the sin of his mother not be blotted out.'),
('LEB', 'Psalms', '109', '15', '1', 'Let them be before Adonai continually,  that he may cut off their<span class=\"note\">That is, the family</span>memory from <span class=\"supplied\">the</span> earth,'),
('LEB', 'Psalms', '109', '16', '1', 'because he did not remember to show loyal love,  but he pursued anyone,<span class=\"note\">Hebrew “a man”</span>poor or needy  or brokenhearted, to slay <span class=\"supplied\">them</span>.'),
('LEB', 'Psalms', '109', '17', '1', 'Because he loved cursing, let it come <span class=\"supplied\">upon</span> him.  Because he did not delight in blessing,  let it be far from him.'),
('LEB', 'Psalms', '109', '18', '1', 'Because he wore a curse as his robe,  let it enter <span class=\"idiom\">his body</span><span class=\"note\">Literally “his inward part”</span>like water,  and into his bones like oil.'),
('LEB', 'Psalms', '109', '19', '1', 'May it be for him like a garment <span class=\"supplied\">in which</span> he wraps,  and a belt <span class=\"idiom\">he continually wears</span>.<span class=\"note\">Literally “continually he girds it”</span>'),
('LEB', 'Psalms', '109', '20', '1', 'Let this <span class=\"supplied\">be</span> the punishment for my accusers from Adonai,  even those who speak evil against my life.'),
('LEB', 'Psalms', '109', '21', '1', 'But you, O Adonai my Lord,  deal with me for your name’s sake.  Because your loyal love <span class=\"supplied\">is</span> good, deliver me,'),
('LEB', 'Psalms', '109', '22', '1', 'for I <span class=\"supplied\">am</span> poor and needy,  and my heart is wounded within me.'),
('LEB', 'Psalms', '109', '23', '1', 'Like a lengthening shadow I am passing away;  I am shaken off like a locust.'),
('LEB', 'Psalms', '109', '24', '1', 'My knees <span class=\"idiom\">buckle</span><span class=\"note\">Literally “stumble”</span>from fasting,  and my body grows lean without fat.'),
('LEB', 'Psalms', '109', '25', '1', 'And <span class=\"supplied\">so</span> I am a disgrace to them;  <span class=\"supplied\">when</span> they see me, they shake their heads.<span class=\"note\">Hebrew “head”</span>'),
('LEB', 'Psalms', '109', '26', '1', 'Help me, O Adonai my God;  save me according to your loyal love,'),
('LEB', 'Psalms', '109', '27', '1', 'that they may know that this <span class=\"supplied\">is</span> your hand,  <span class=\"supplied\">that</span> you, O Adonai, you have done it.'),
('LEB', 'Psalms', '109', '28', '1', 'Let them curse, but you bless.  <span class=\"supplied\">When</span> they arise, let them be put to shame,  that your servant may be glad.'),
('LEB', 'Psalms', '109', '29', '1', 'Let my accusers put on disgrace,  and let them cover <span class=\"supplied\">themselves</span> with their shame as with a robe.'),
('LEB', 'Psalms', '109', '30', '1', 'I will give thanks to Adonai exceedingly with my mouth,  and in <span class=\"supplied\">the</span> midst of many I will praise him,'),
('LEB', 'Psalms', '109', '31', '1', 'for he stands at <span class=\"supplied\">the</span> right <span class=\"supplied\">hand</span> of <span class=\"supplied\">the</span> needy,  to save <span class=\"supplied\">him</span> from those judging his life.<span class=\"note\">Or “soul”</span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '110', '1', '1', '<span class=\"verse-title\">Of David. A psalm.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>A declaration of Adonai to my lord,  “Sit at my right <span class=\"supplied\">hand</span>  until I make your enemies your footstool.”'),
('LEB', 'Psalms', '110', '2', '1', 'Adonai will send out your mighty scepter from Zion;  rule in the midst of your enemies.'),
('LEB', 'Psalms', '110', '3', '1', 'Your people <span class=\"idiom\">will volunteer</span><span class=\"note\">Literally “will <span class=\"supplied\">be</span> freewill offerings”; see the related verbal form in Judg 5:2</span>in the day of your power.<span class=\"note\">Or “day of your army,” or “day of battle”</span> In holy splendor, from <span class=\"supplied\">the</span> womb of <span class=\"supplied\">the</span> dawn,  you will have the dew of your youth.<span class=\"note\">The meaning of this verse is obscure</span>'),
('LEB', 'Psalms', '110', '4', '1', 'Adonai has sworn and he will not change <span class=\"supplied\">his mind</span>,  “You <span class=\"supplied\">are</span> a priest forever  according to the manner of Melchizedek.”'),
('LEB', 'Psalms', '110', '5', '1', 'O Lord, at your right <span class=\"supplied\">hand</span>  he will shatter kings in the day of his anger.'),
('LEB', 'Psalms', '110', '6', '1', 'He will judge among the nations;  he will fill <span class=\"supplied\">them</span> with corpses.  He will shatter <span class=\"supplied\">the</span> <span class=\"idiom\">rulers</span><span class=\"note\">Literally “head”</span>of many countries.'),
('LEB', 'Psalms', '110', '7', '1', 'He will drink from <span class=\"supplied\">the</span> stream by the road;  therefore he will lift up <span class=\"supplied\">his</span> head.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '111', '1', '1', '<span class=\"verse-title\">Praise to God for His Work and Commands</span>Praise Yah!<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span> I will give thanks to Adonai with all <span class=\"supplied\">my</span> heart,  in <span class=\"supplied\">the</span> assembly of <span class=\"supplied\">the</span> upright and <span class=\"supplied\">the</span> congregation.'),
('LEB', 'Psalms', '111', '2', '1', 'The works of Adonai <span class=\"supplied\">are</span> great,  studied<span class=\"note\">Or “pondered”</span>by all <span class=\"supplied\">who</span> delight <span class=\"supplied\">in</span> them.'),
('LEB', 'Psalms', '111', '3', '1', '<span class=\"supplied\">Full of</span> splendor and majesty <span class=\"supplied\">is</span> his work,  and his righteousness endures forever.'),
('LEB', 'Psalms', '111', '4', '1', 'He has made his wonders <span class=\"idiom\">to be remembered</span>;<span class=\"note\">Literally “a remembrance”</span> Adonai <span class=\"supplied\">is</span> gracious and compassionate.'),
('LEB', 'Psalms', '111', '5', '1', 'He gives food to those who fear him;  he remembers his covenant forever.'),
('LEB', 'Psalms', '111', '6', '1', 'He has declared the power of his works for his people,  by giving to them <span class=\"supplied\">the</span> inheritance of <span class=\"supplied\">the</span> nations.'),
('LEB', 'Psalms', '111', '7', '1', 'The works of his hands <span class=\"supplied\">are</span> faithfulness and justice;  all his precepts <span class=\"supplied\">are</span> reliable.'),
('LEB', 'Psalms', '111', '8', '1', '<span class=\"supplied\">They are</span> sustained forever <span class=\"supplied\">and</span> ever,  done with faithfulness and uprightness.'),
('LEB', 'Psalms', '111', '9', '1', 'He has sent redemption to his people;  he has commanded his covenant forever;  holy and fearful <span class=\"supplied\">is</span> his name.'),
('LEB', 'Psalms', '111', '10', '1', 'The fear of Adonai <span class=\"supplied\">is the</span> beginning of wisdom;  all who do them<span class=\"note\">That is, his precepts (v. 7), or the stipulations of his covenant (v. 9)</span>have a good understanding.  His praise endures forever.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '112', '1', '1', '<span class=\"verse-title\">The Path of the Righteous and the Path of the Wicked</span>Praise Yah!<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span> Blessed <span class=\"supplied\">is the</span> man <span class=\"supplied\">who</span> fears Adonai;  he takes great delight in his commands.'),
('LEB', 'Psalms', '112', '2', '1', 'His <span class=\"idiom\">descendants</span><span class=\"note\">Literally “seed”</span>will be mighty in the land;  <span class=\"supplied\">the</span> generation of <span class=\"supplied\">the</span> upright will be blessed.'),
('LEB', 'Psalms', '112', '3', '1', 'Wealth and riches <span class=\"supplied\">are</span> in his house,  and his righteousness endures forever.'),
('LEB', 'Psalms', '112', '4', '1', '<span class=\"supplied\">His</span> light rises in the darkness for the upright;  <span class=\"supplied\">he is</span> gracious and compassionate and righteous.'),
('LEB', 'Psalms', '112', '5', '1', '<span class=\"supplied\">It goes</span> well <span class=\"supplied\">for</span> a man <span class=\"supplied\">who</span> is gracious and lends,  <span class=\"supplied\">who</span> conducts his <span class=\"idiom\">business properly</span>.<span class=\"note\">Literally “matters in justice”</span>'),
('LEB', 'Psalms', '112', '6', '1', 'For he will not be shaken forever;  <span class=\"supplied\">the</span> righteous <span class=\"idiom\">will be remembered forever</span>.<span class=\"note\">Literally “will be for an eternal remembrance”</span>'),
('LEB', 'Psalms', '112', '7', '1', 'He is not afraid of bad news;  his heart <span class=\"supplied\">is</span> steadfast, secure<span class=\"note\">Or “full of trust”</span>in Adonai.'),
('LEB', 'Psalms', '112', '8', '1', 'His heart <span class=\"supplied\">is</span> sustained; he will not be afraid,  until he looks <span class=\"supplied\">in triumph</span> on his enemies.'),
('LEB', 'Psalms', '112', '9', '1', '<span class=\"idiom\">He gives lavishly</span><span class=\"note\">Literally “He distributes freely, he gives”</span>to the needy;  his righteousness endures forever.  His horn rises high in honor.'),
('LEB', 'Psalms', '112', '10', '1', '<span class=\"supplied\">The</span> wicked<span class=\"note\">Singular</span>sees <span class=\"supplied\">it</span> and is vexed.  He gnashes his teeth and <span class=\"idiom\">becomes weak</span>.<span class=\"note\">Literally “and melts”</span> <span class=\"supplied\">The</span> desire of <span class=\"supplied\">the</span> wicked<span class=\"note\">Plural</span>perishes.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '113', '1', '1', '<span class=\"verse-title\">God’s Majesty and Care for the Needy</span>Praise Yah!<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span> Praise, O servants of Adonai,  praise the name of Adonai.'),
('LEB', 'Psalms', '113', '2', '1', 'Let the name of Adonai be blessed,  from now until forever.'),
('LEB', 'Psalms', '113', '3', '1', 'From <span class=\"supplied\">the</span> rising of <span class=\"supplied\">the</span> sun to its setting,  <span class=\"supplied\">let</span> the name of Adonai be blessed.'),
('LEB', 'Psalms', '113', '4', '1', 'Adonai <span class=\"supplied\">is</span> high above all nations;  his glory <span class=\"supplied\">is</span> above the heavens.'),
('LEB', 'Psalms', '113', '5', '1', 'Who <span class=\"supplied\">is</span> like Adonai our God,  who <span class=\"supplied\">is</span> enthroned on high,<span class=\"note\">Or “who dwells on high”</span>'),
('LEB', 'Psalms', '113', '6', '1', 'who <span class=\"idiom\">condescends to look at</span><span class=\"note\">Hebrew “makes low to see”</span> <span class=\"supplied\">what is</span> in the heavens and in the earth?'),
('LEB', 'Psalms', '113', '7', '1', '<span class=\"supplied\">He</span> raises <span class=\"supplied\">the</span> helpless from <span class=\"supplied\">the</span> dust;  he lifts <span class=\"supplied\">the</span> needy from <span class=\"supplied\">the</span> ash heap,'),
('LEB', 'Psalms', '113', '8', '1', 'to seat <span class=\"supplied\">them</span> with princes,  with the princes of his people.'),
('LEB', 'Psalms', '113', '9', '1', '<span class=\"supplied\">He</span> causes the barren woman of the house to dwell  <span class=\"supplied\">as</span> the happy mother of children.  Praise Yah!<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '114', '1', '1', '<span class=\"verse-title\">Praise to God for His Works During the Exodus</span>When Israel went out from Egypt,  the house of Jacob from a people of a strange language,'),
('LEB', 'Psalms', '114', '2', '1', 'Judah became his sanctuary,  Israel his dominion.'),
('LEB', 'Psalms', '114', '3', '1', 'The sea looked and fled;  the Jordan turned back.'),
('LEB', 'Psalms', '114', '4', '1', 'The mountains skipped like rams,  <span class=\"supplied\">the</span> hills like <span class=\"idiom\">lambs</span>.<span class=\"note\">Literally “sons of <span class=\"supplied\">the</span> flock”</span>'),
('LEB', 'Psalms', '114', '5', '1', 'What’s with you, O sea, <span class=\"supplied\">that</span> you fled?  O Jordan, <span class=\"supplied\">that</span> you turned back?'),
('LEB', 'Psalms', '114', '6', '1', 'O mountains, <span class=\"supplied\">that</span> you skipped like rams?  O hills, like lambs?'),
('LEB', 'Psalms', '114', '7', '1', 'At the presence of <span class=\"supplied\">the</span> Lord writhe, O earth,<span class=\"note\">Or “O land”</span> at the presence of the God of Jacob,'),
('LEB', 'Psalms', '114', '8', '1', 'who turned the rock into a pool of water,  flinty stone into a spring of water.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '115', '1', '1', '<span class=\"verse-title\">Dead Idols and the Living God</span>Not to us, O Adonai, not to us,  but to your name give glory,  because of your loyal love,  because of your faithfulness.'),
('LEB', 'Psalms', '115', '2', '1', 'Why should the nations say,  “Where, <span class=\"idiom\">pray tell</span>,<span class=\"note\">Literally “please”</span><span class=\"supplied\">is</span> their God?”'),
('LEB', 'Psalms', '115', '3', '1', 'But our God <span class=\"supplied\">is</span> in the heavens;  all that he desires, he does.'),
('LEB', 'Psalms', '115', '4', '1', 'Their idols <span class=\"supplied\">are</span> silver and gold,  <span class=\"supplied\">the</span> work of human hands.'),
('LEB', 'Psalms', '115', '5', '1', 'They have mouths, but cannot speak;  they have eyes, but cannot see;'),
('LEB', 'Psalms', '115', '6', '1', 'they have ears, but cannot hear;  they have noses, but cannot smell;'),
('LEB', 'Psalms', '115', '7', '1', '<span class=\"supplied\">they have</span> their hands, but they cannot feel,  their feet, but they cannot walk;  they cannot utter a sound in their throats.'),
('LEB', 'Psalms', '115', '8', '1', '<span class=\"supplied\">Those who</span> make them become like them,  <span class=\"supplied\">as does</span> everyone who trusts in them.'),
('LEB', 'Psalms', '115', '9', '1', 'O Israel, trust Adonai;  he <span class=\"supplied\">is</span> their help and their shield.'),
('LEB', 'Psalms', '115', '10', '1', 'O house of Aaron, trust in Adonai;  he <span class=\"supplied\">is</span> their help and their shield.'),
('LEB', 'Psalms', '115', '11', '1', '<span class=\"supplied\">You who</span> fear Adonai, trust in Adonai;  he <span class=\"supplied\">is</span> their help and their shield.'),
('LEB', 'Psalms', '115', '12', '1', 'Adonai remembers us; he will bless <span class=\"supplied\">us</span>.  He will bless the house of Israel;  he will bless the house of Aaron.'),
('LEB', 'Psalms', '115', '13', '1', 'He will bless <span class=\"supplied\">those who</span> fear Adonai,  the small with the great.'),
('LEB', 'Psalms', '115', '14', '1', 'May Adonai give you increase,  you and your children.'),
('LEB', 'Psalms', '115', '15', '1', 'May you be blessed by Adonai,  who made heaven and earth.'),
('LEB', 'Psalms', '115', '16', '1', 'The heavens <span class=\"supplied\">are</span> Adonai’s heavens,  but the earth he has given to <span class=\"supplied\">the</span> children of humankind.'),
('LEB', 'Psalms', '115', '17', '1', 'The dead do not praise Yah,<span class=\"note\">A shortened form of “Adonai”</span> nor any descending into silence.'),
('LEB', 'Psalms', '115', '18', '1', '<span class=\"supplied\">It is</span> we, we will bless Yah<span class=\"note\">A shortened form of “Adonai”</span> from now until forever.  Praise Yah!<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '116', '1', '1', '<span class=\"verse-title\">Thanksgiving for God’s Deliverance</span>I love <span class=\"supplied\">him</span>, because Adonai has heard  <span class=\"idiom\">the voice of my supplications</span>.<span class=\"note\">Literally “my voice, my supplications”</span>'),
('LEB', 'Psalms', '116', '2', '1', 'Because he has inclined his ear to me,  I will call <span class=\"idiom\">all my days</span>.<span class=\"note\">Literally “in my days”</span>'),
('LEB', 'Psalms', '116', '3', '1', '<span class=\"supplied\">The</span> ropes of death encircled me,  and the distresses of Sheol found me.  I found trouble and grief.'),
('LEB', 'Psalms', '116', '4', '1', 'Then I called on the name of Adonai:  “O Adonai, please save my life!”'),
('LEB', 'Psalms', '116', '5', '1', 'Gracious <span class=\"supplied\">is</span> Adonai and righteous,  and our God <span class=\"supplied\">is</span> merciful.'),
('LEB', 'Psalms', '116', '6', '1', 'Adonai watches over <span class=\"supplied\">the</span> simple.  I was brought low, but he gave me victory.'),
('LEB', 'Psalms', '116', '7', '1', 'Return, O my soul, to your repose,  for Adonai has dealt bountifully with you.'),
('LEB', 'Psalms', '116', '8', '1', 'For you have rescued me<span class=\"note\">Hebrew “my soul.” If “soul” is accepted, then “death” means “<span class=\"supplied\">the place of</span> death,” that is, the netherworld.</span>from death,  my eyes<span class=\"note\">Hebrew “eye”</span>from tears, my feet from stumbling.'),
('LEB', 'Psalms', '116', '9', '1', 'I will walk before Adonai  in the land<span class=\"note\">Hebrew “lands”</span>of the living.'),
('LEB', 'Psalms', '116', '10', '1', 'I believed when I spoke,  “I am afflicted greatly.”'),
('LEB', 'Psalms', '116', '11', '1', 'I said in my haste,  “<span class=\"idiom\">Everyone</span><span class=\"note\">Literally “All <span class=\"supplied\">of</span> the humankind”</span><span class=\"supplied\">is</span> a liar.”'),
('LEB', 'Psalms', '116', '12', '1', 'What shall I give back to Adonai  <span class=\"supplied\">for</span> all his benefits to me?'),
('LEB', 'Psalms', '116', '13', '1', 'I will lift up <span class=\"supplied\">the</span> cup of salvation  and proclaim the name of Adonai.'),
('LEB', 'Psalms', '116', '14', '1', 'I will pay my vows <span class=\"supplied\">made</span> to Adonai  in the presence of all his people.'),
('LEB', 'Psalms', '116', '15', '1', 'Costly <span class=\"idiom\">in Adonai’s view</span><span class=\"note\">Literally “in the eyes of Adonai”</span><span class=\"supplied\">is</span>  the death of his faithful <span class=\"supplied\">ones</span>.'),
('LEB', 'Psalms', '116', '16', '1', 'Ah, Adonai, I <span class=\"supplied\">am</span> indeed your servant;  I am your servant, the child of your maidservant.  You have loosed my bonds.'),
('LEB', 'Psalms', '116', '17', '1', 'I will offer to you a sacrifice of thanksgiving,  and proclaim the name of Adonai.'),
('LEB', 'Psalms', '116', '18', '1', 'I will pay my vows <span class=\"supplied\">made</span> to Adonai  in the presence of all his people,'),
('LEB', 'Psalms', '116', '19', '1', 'in the courts of the house<span class=\"note\">Or “temple”</span>of Adonai,  in the midst of you, O Jerusalem.  Praise Yah!<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '117', '1', '1', '<span class=\"verse-title\">Let All Peoples Praise Adonai</span>Praise Adonai, all you nations;  extol him, all you peoples.'),
('LEB', 'Psalms', '117', '2', '1', 'For his loyal love <span class=\"supplied\">is</span> mighty on our behalf,  and the faithfulness of Adonai <span class=\"supplied\">is</span> forever.  Praise Yah!<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '118', '1', '1', '<span class=\"verse-title\">Praise to God for His Loyal Love</span>Give thanks to Adonai for <span class=\"supplied\">he is</span> good,  for his loyal love <span class=\"supplied\">is</span> forever.'),
('LEB', 'Psalms', '118', '2', '1', 'Let Israel say,  “His loyal love <span class=\"supplied\">is</span> forever.”'),
('LEB', 'Psalms', '118', '3', '1', 'Let the house of Aaron say,  “His loyal love <span class=\"supplied\">is</span> forever.”'),
('LEB', 'Psalms', '118', '4', '1', 'Let <span class=\"supplied\">those who</span> fear Adonai say,  “His loyal love <span class=\"supplied\">is</span> forever.”'),
('LEB', 'Psalms', '118', '5', '1', 'Out of <span class=\"supplied\">my</span> distress I called to Yah.<span class=\"note\">A shortened form of “Adonai”</span> Yah answered me, <span class=\"supplied\">setting me</span> in a broad place.'),
('LEB', 'Psalms', '118', '6', '1', 'Adonai <span class=\"supplied\">is</span> for me; I do not fear.  What can <span class=\"supplied\">mere</span> humans<span class=\"note\">Or “humankind”</span>do to me?'),
('LEB', 'Psalms', '118', '7', '1', 'Adonai <span class=\"supplied\">is</span> for me as my helper,  and <span class=\"supplied\">so</span> I will look <span class=\"supplied\">in triumph</span> on those who hate me.'),
('LEB', 'Psalms', '118', '8', '1', '<span class=\"supplied\">It is</span> better to take refuge in Adonai  than to trust in humans.<span class=\"note\">Or “humankind”</span>'),
('LEB', 'Psalms', '118', '9', '1', '<span class=\"supplied\">It is</span> better to take refuge in Adonai  than to trust princes.'),
('LEB', 'Psalms', '118', '10', '1', 'All nations surrounded me.  In the name of Adonai I opposed them indeed.'),
('LEB', 'Psalms', '118', '11', '1', 'They surrounded me; yes, they surrounded me.  In the name of Adonai I opposed them indeed.'),
('LEB', 'Psalms', '118', '12', '1', 'They surrounded me like bees;  they were extinguished like a fire of thorns.  In the name of Adonai I opposed them indeed.'),
('LEB', 'Psalms', '118', '13', '1', 'You pushed me hard<span class=\"note\">Hebrew intensive “pushing, you pushed me”</span>to <span class=\"supplied\">make me</span> fall,  but Adonai helped me.'),
('LEB', 'Psalms', '118', '14', '1', 'Yah<span class=\"note\">A shortened form of “Adonai”</span><span class=\"supplied\">is</span> my strength and <span class=\"supplied\">my</span> song,<span class=\"note\">Or “<span class=\"supplied\">my</span> might”</span> and he has become my salvation.<span class=\"note\">Or “victory”</span>'),
('LEB', 'Psalms', '118', '15', '1', '<span class=\"supplied\">The</span> sound of rejoicing and salvation<span class=\"note\">Or “victory”</span> <span class=\"supplied\">is</span> in <span class=\"supplied\">the</span> tents of <span class=\"supplied\">the</span> righteous;  the right hand of Adonai <span class=\"supplied\">has</span> done valiantly.'),
('LEB', 'Psalms', '118', '16', '1', 'The right hand of Adonai <span class=\"supplied\">has</span> exalted;  the right hand of Adonai <span class=\"supplied\">has</span> done valiantly.'),
('LEB', 'Psalms', '118', '17', '1', 'I will not die but live,  and tell of the works of Yah.<span class=\"note\">A shortened form of “Adonai”</span>'),
('LEB', 'Psalms', '118', '18', '1', 'Yah has disciplined me severely,  but he did not consign<span class=\"note\">Hebrew “give”</span>me to death.'),
('LEB', 'Psalms', '118', '19', '1', 'Open to me <span class=\"supplied\">the</span> gates of righteousness,  that I may enter through them  <span class=\"supplied\">and</span> give thanks to Yah.<span class=\"note\">A shortened form of “Adonai”</span>'),
('LEB', 'Psalms', '118', '20', '1', 'This <span class=\"supplied\">is</span> the gate of Adonai,  through <span class=\"supplied\">which the</span> righteous will enter.'),
('LEB', 'Psalms', '118', '21', '1', 'I will give thanks <span class=\"supplied\">to</span> you for you have answered me,  and you have become my salvation.<span class=\"note\">Or “victory”</span>'),
('LEB', 'Psalms', '118', '22', '1', '<span class=\"supplied\">The</span> stone the builders rejected  has become <span class=\"supplied\">the</span> chief cornerstone.'),
('LEB', 'Psalms', '118', '23', '1', 'This is from Adonai;  it is wonderful in our eyes.'),
('LEB', 'Psalms', '118', '24', '1', 'This <span class=\"supplied\">is</span> the day Adonai has worked;<span class=\"note\">Or “made”</span> let us rejoice and be glad in him.<span class=\"note\">Or “in it”</span>'),
('LEB', 'Psalms', '118', '25', '1', 'O Adonai, please save;  O Adonai, please grant success.'),
('LEB', 'Psalms', '118', '26', '1', 'Blessed <span class=\"supplied\">is</span> he who comes in the name of Adonai.  We bless you<span class=\"note\">Hebrew plural</span>from the house<span class=\"note\">Or “temple”</span>of Adonai.'),
('LEB', 'Psalms', '118', '27', '1', 'Adonai is God, and he has given us light.<span class=\"note\">Or “made <span class=\"supplied\">his</span> light to shine upon <span class=\"supplied\">to</span> us”</span> Bind <span class=\"supplied\">the</span> festal sacrifice<span class=\"note\">Or “festal procession”</span>with cords<span class=\"note\">Or “branches”</span> to the horns<span class=\"note\">Or “<span class=\"supplied\">and lead up</span> to the horns”</span>of the altar.'),
('LEB', 'Psalms', '118', '28', '1', 'You <span class=\"supplied\">are</span> my God, and I will give thanks to you.  <span class=\"supplied\">You are</span> my God; I will exalt you.'),
('LEB', 'Psalms', '118', '29', '1', 'Give thanks to Adonai for <span class=\"supplied\">he is</span> good,  for his loyal love <span class=\"supplied\">is</span> forever.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '119', '1', '1', '<span class=\"verse-title\">Aleph<span class=\"note\">Psalm 119 is an acrostic psalm, with twenty-two stanzas corresponding to the letters of the Hebrew alphabet</span></span>Blessed <span class=\"supplied\">are those whose</span> way is blameless,  who walk in the law of Adonai.'),
('LEB', 'Psalms', '119', '2', '1', 'Blessed <span class=\"supplied\">are</span> those who keep his testimonies;  they seek him with a whole heart.'),
('LEB', 'Psalms', '119', '3', '1', 'They also do no wrong;  they walk in his ways.'),
('LEB', 'Psalms', '119', '4', '1', 'You have commanded<span class=\"note\">Perhaps emphatic; Hebrew “you, you have commanded”</span>your precepts,  that <span class=\"supplied\">we</span> should keep <span class=\"supplied\">them</span> diligently.'),
('LEB', 'Psalms', '119', '5', '1', 'Oh that my ways were steadfast,  to keep your statutes!'),
('LEB', 'Psalms', '119', '6', '1', 'Then I would not be ashamed  when I look to all your commands.'),
('LEB', 'Psalms', '119', '7', '1', 'I will give you thanks with an upright heart,  when I learn your righteous laws.'),
('LEB', 'Psalms', '119', '8', '1', 'I will heed your statutes;  do not utterly forsake me.'),
('LEB', 'Psalms', '119', '9', '3', '<span class=\"verse-title\">Beth</span>How can a young man keep his way pure?  By taking heed according to your word.'),
('LEB', 'Psalms', '119', '10', '3', 'With my whole heart I have sought you;  do not let me stray from your commands.'),
('LEB', 'Psalms', '119', '11', '3', 'In my heart I have hidden your word,<span class=\"note\">Or “promise”</span> so that I may not sin against you.'),
('LEB', 'Psalms', '119', '12', '3', 'Blessed <span class=\"supplied\">are</span> you, O Adonai.  Teach me your statutes.'),
('LEB', 'Psalms', '119', '13', '3', 'With my lips I have told <span class=\"supplied\">of</span>  all the ordinances of your mouth.'),
('LEB', 'Psalms', '119', '14', '3', 'In the way of your testimonies I have rejoiced  as <span class=\"supplied\">I would</span> over all riches.'),
('LEB', 'Psalms', '119', '15', '3', 'I will meditate on your precepts  and look at<span class=\"note\">That is, “examine”</span>your ways.'),
('LEB', 'Psalms', '119', '16', '3', 'I will take delight in your statutes;  I will not forget your word.'),
('LEB', 'Psalms', '119', '17', '5', '<span class=\"verse-title\">Gimel</span>Deal bountifully with your servant, that I may live  and heed your word.'),
('LEB', 'Psalms', '119', '18', '5', 'Uncover my eyes, that I may look at<span class=\"note\">That is, “examine”</span> wonderful things from your law.'),
('LEB', 'Psalms', '119', '19', '5', 'I <span class=\"supplied\">am</span> a sojourner on the earth;<span class=\"note\">That is, “here for a short time”</span> do not hide your commands from me.'),
('LEB', 'Psalms', '119', '20', '5', 'My soul is crushed with longing  for your ordinances at all times.'),
('LEB', 'Psalms', '119', '21', '5', 'You rebuke <span class=\"supplied\">the</span> arrogant, <span class=\"supplied\">the</span> accursed,  who stray from your commands.'),
('LEB', 'Psalms', '119', '22', '5', '<span class=\"idiom\">Remove from</span><span class=\"note\">Literally “Roll off of”</span>me scorn and contempt,  for I have kept your testimonies.'),
('LEB', 'Psalms', '119', '23', '5', 'Even <span class=\"supplied\">though</span> princes sit <span class=\"supplied\">and</span> speak against me,  your servant meditates on your statutes.'),
('LEB', 'Psalms', '119', '24', '5', 'Your testimonies <span class=\"supplied\">are</span> my delight,  <span class=\"idiom\">my counselors</span>.<span class=\"note\">Literally “the men of my counsel”</span>'),
('LEB', 'Psalms', '119', '25', '7', '<span class=\"verse-title\">Daleth</span>My soul clings to the dust;  revive me according to your word.'),
('LEB', 'Psalms', '119', '26', '7', 'I told of my ways, and you answered me;  teach me your statutes.'),
('LEB', 'Psalms', '119', '27', '7', 'Make me understand the way of your precepts,  that I may meditate on your wonderful things.'),
('LEB', 'Psalms', '119', '28', '7', 'My soul weeps<span class=\"note\">Or “is sleepless”</span>because of grief;  strengthen me according to your word.'),
('LEB', 'Psalms', '119', '29', '7', 'Remove from me <span class=\"supplied\">the</span> deceptive way,  and graciously give me your law.'),
('LEB', 'Psalms', '119', '30', '7', 'I have chosen <span class=\"supplied\">the</span> faithful way;  I have set your ordinances <span class=\"supplied\">before me</span>.'),
('LEB', 'Psalms', '119', '31', '7', 'I cling to your testimonies;  O Adonai, do not let me be put to shame.'),
('LEB', 'Psalms', '119', '32', '7', 'I will run the way of your commands,  for you will enlarge my heart.<span class=\"note\">That is, “give greater understanding”</span>'),
('LEB', 'Psalms', '119', '33', '9', '<span class=\"verse-title\">Heʼ</span>Teach me, O Adonai, the way of your statutes,  and I will keep it to the end.'),
('LEB', 'Psalms', '119', '34', '9', 'Give me understanding, that I may keep your law  and heed it with <span class=\"supplied\">my</span> whole heart.'),
('LEB', 'Psalms', '119', '35', '9', 'Cause me to walk in the path of your commands,  for I delight in it.'),
('LEB', 'Psalms', '119', '36', '9', 'Incline my heart to your testimonies,  and not to <span class=\"supplied\">dishonest</span> gain.'),
('LEB', 'Psalms', '119', '37', '9', 'Turn away my eyes from looking at <span class=\"supplied\">what is</span> worthless;  revive me in your ways.'),
('LEB', 'Psalms', '119', '38', '9', 'Fulfill your word<span class=\"note\">Or “your promise”</span>to your servant,  which <span class=\"supplied\">is</span> to <span class=\"supplied\">bring about</span> a reverence for you.'),
('LEB', 'Psalms', '119', '39', '9', 'Turn away my disgrace that I dread,  for your ordinances <span class=\"supplied\">are</span> good.'),
('LEB', 'Psalms', '119', '40', '9', 'Look, I have longed for your precepts;  revive me in your righteousness.'),
('LEB', 'Psalms', '119', '41', '11', '<span class=\"verse-title\">Waw</span>Let your acts of loyal love also come to me, O Adonai,  your salvation according to your word.<span class=\"note\">Or “your promise”</span>'),
('LEB', 'Psalms', '119', '42', '11', 'Then I will have an answer <span class=\"supplied\">for the</span> one who taunts me,  for I trust your word.'),
('LEB', 'Psalms', '119', '43', '11', 'And do not snatch <span class=\"supplied\">the</span> word of truth utterly from my mouth,  for I hope in your ordinances.'),
('LEB', 'Psalms', '119', '44', '11', 'So I will heed your law continually,  forever and ever.'),
('LEB', 'Psalms', '119', '45', '11', 'And I will go about <span class=\"idiom\">freely</span>,<span class=\"note\">Literally “in the wide place”</span> for I have sought your precepts.'),
('LEB', 'Psalms', '119', '46', '11', 'And I will speak of your testimonies before kings,  and will not be ashamed.'),
('LEB', 'Psalms', '119', '47', '11', 'And I will take delight in your commands,  which I love.'),
('LEB', 'Psalms', '119', '48', '11', 'And I will lift up my hands<span class=\"note\">Hebrew “palms”</span>to your commands, which I love,  and I will meditate on your statutes.'),
('LEB', 'Psalms', '119', '49', '13', '<span class=\"verse-title\">Zayin</span>Remember <span class=\"supplied\">your</span> word to your servant,  upon which you have caused me to hope.'),
('LEB', 'Psalms', '119', '50', '13', 'This <span class=\"supplied\">is</span> my comfort in my misery:  that your word<span class=\"note\">Or “your promise”</span>preserves my life.'),
('LEB', 'Psalms', '119', '51', '13', '<span class=\"supplied\">The</span> arrogant utterly deride me;  I have not turned aside from your law.'),
('LEB', 'Psalms', '119', '52', '13', 'I remember your ordinances of old, O Adonai,  and I take comfort.'),
('LEB', 'Psalms', '119', '53', '13', 'Rage seizes me because of <span class=\"supplied\">the</span> wicked,  those who forsake your law.'),
('LEB', 'Psalms', '119', '54', '13', 'Your statutes have been my songs  in the house of my sojourning.'),
('LEB', 'Psalms', '119', '55', '13', 'I remember your name in the night, O Adonai,  and I heed your law.'),
('LEB', 'Psalms', '119', '56', '13', 'This has been mine,  that I have kept your precepts.'),
('LEB', 'Psalms', '119', '57', '15', '<span class=\"verse-title\">Heth</span>Adonai <span class=\"supplied\">is</span> my portion;  I intend to heed your words.'),
('LEB', 'Psalms', '119', '58', '15', 'I <span class=\"idiom\">seek your favor</span><span class=\"note\">Literally “entreat your face”</span>with my whole heart;  be gracious <span class=\"supplied\">to</span> me according to your word.<span class=\"note\">Or “your promise”</span>'),
('LEB', 'Psalms', '119', '59', '15', 'I think about my ways,  and turn my feet to your testimonies.'),
('LEB', 'Psalms', '119', '60', '15', 'I hurry and do not delay  to heed your commands.'),
('LEB', 'Psalms', '119', '61', '15', '<span class=\"supplied\">The</span> cords of <span class=\"supplied\">the</span> wicked surround me,  <span class=\"supplied\">but</span> I do not forget your law.'),
('LEB', 'Psalms', '119', '62', '15', '<span class=\"supplied\">In the</span> middle of <span class=\"supplied\">the</span> night I rise to give you thanks,  because of your righteous ordinances.'),
('LEB', 'Psalms', '119', '63', '15', 'I <span class=\"supplied\">am</span> a companion of all <span class=\"supplied\">who</span> fear you  and heed your precepts.'),
('LEB', 'Psalms', '119', '64', '15', 'The earth, O Adonai, is full of your loyal love.  Teach me your statutes.'),
('LEB', 'Psalms', '119', '65', '17', '<span class=\"verse-title\">Teth</span>You have dealt well with your servant,  O Adonai, according to your word.'),
('LEB', 'Psalms', '119', '66', '17', 'Teach me good discernment and knowledge,  for I believe your commands.'),
('LEB', 'Psalms', '119', '67', '17', 'Before I was afflicted, I was going astray,  but now I heed your word.'),
('LEB', 'Psalms', '119', '68', '17', 'You <span class=\"supplied\">are</span> good and do good;  teach me your statutes.'),
('LEB', 'Psalms', '119', '69', '17', '<span class=\"supplied\">The</span> arrogant smear me with lies;<span class=\"note\">Hebrew “lie”</span> I keep your precepts with <span class=\"supplied\">my</span> whole heart.'),
('LEB', 'Psalms', '119', '70', '17', 'Their heart <span class=\"supplied\">is</span> insensitive like fat;<span class=\"note\">See Isa 6:10 for a similar idea, but a different word for “fat”</span> As for me, I take delight in your law.'),
('LEB', 'Psalms', '119', '71', '17', '<span class=\"supplied\">It is</span> good for me that I was afflicted,  so that I might learn your statutes.'),
('LEB', 'Psalms', '119', '72', '17', 'The law of your mouth <span class=\"supplied\">is</span> better to me  than thousands of gold and silver <span class=\"supplied\">coins</span>.'),
('LEB', 'Psalms', '119', '73', '19', '<span class=\"verse-title\">Yod</span>Your hands have made me and established me;  give me understanding that I may learn your commands.'),
('LEB', 'Psalms', '119', '74', '19', 'Those who fear you will see me and be glad,  because I hope in your word.'),
('LEB', 'Psalms', '119', '75', '19', 'I know, O Adonai, that your judgments <span class=\"supplied\">are</span> right,  and <span class=\"supplied\">that</span> in faithfulness you afflicted me.'),
('LEB', 'Psalms', '119', '76', '19', 'Please let your loyal love comfort me,  according to your word<span class=\"note\">Or “your promise”</span>to your servant.'),
('LEB', 'Psalms', '119', '77', '19', 'Let your mercies come <span class=\"supplied\">to</span> me, that I may live,  for your law <span class=\"supplied\">is</span> my delight.'),
('LEB', 'Psalms', '119', '78', '19', 'Let the arrogant be put to shame,  because they have wronged me <span class=\"supplied\">with</span> lies.<span class=\"note\">Hebrew “lie”</span> As for me, I will meditate on your precepts.'),
('LEB', 'Psalms', '119', '79', '19', 'Let those who fear you turn to me,  even those who know<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>your testimonies.'),
('LEB', 'Psalms', '119', '80', '19', 'May my heart be blameless in your statutes,  so that I may not be ashamed.'),
('LEB', 'Psalms', '119', '81', '21', '<span class=\"verse-title\">Kaph</span>My soul languishes for your salvation;  I hope in your word.'),
('LEB', 'Psalms', '119', '82', '21', 'My eyes long for<span class=\"note\">Or “long <span class=\"supplied\">to see</span>”</span>your word,<span class=\"note\">Or “your promise”</span> saying, “When will you comfort me?”'),
('LEB', 'Psalms', '119', '83', '21', 'For I have become like a wineskin in <span class=\"supplied\">the</span> smoke,  <span class=\"supplied\">yet</span> I have not forgotten your statutes.'),
('LEB', 'Psalms', '119', '84', '21', 'How many <span class=\"supplied\">are</span> the days of your servant?  When will you execute justice against those who persecute me?'),
('LEB', 'Psalms', '119', '85', '21', '<span class=\"supplied\">The</span> arrogant have dug pits<span class=\"note\">That is, traps</span>for me,  which <span class=\"supplied\">is</span> not according to<span class=\"note\">Or “who <span class=\"supplied\">are</span> not in accord with”</span>your law.'),
('LEB', 'Psalms', '119', '86', '21', 'All your commands <span class=\"supplied\">are</span> faithful.  They persecute me deceitfully; help me!'),
('LEB', 'Psalms', '119', '87', '21', 'They have almost destroyed me on the earth,  but I have not forsaken your precepts.'),
('LEB', 'Psalms', '119', '88', '21', 'According to your loyal love preserve me alive,  so that I may heed the testimony from your mouth.'),
('LEB', 'Psalms', '119', '89', '23', '<span class=\"verse-title\">Lamed</span>Forever, O Adonai,  your word <span class=\"idiom\">is settled</span><span class=\"note\">Literally “stands firm”</span>in heaven.'),
('LEB', 'Psalms', '119', '90', '23', 'Your faithfulness <span class=\"supplied\">endures</span> <span class=\"idiom\">throughout all generations</span>.<span class=\"note\">Literally “for a generation and a generation”</span> You have established <span class=\"supplied\">the</span> earth and it stands.'),
('LEB', 'Psalms', '119', '91', '23', 'By your ordinances they stand today,  for all <span class=\"supplied\">are</span> your servants.'),
('LEB', 'Psalms', '119', '92', '23', 'Unless your law <span class=\"supplied\">had been</span> my delight,  then I would have perished in my misery.'),
('LEB', 'Psalms', '119', '93', '23', 'I will not ever forget your precepts,  for by them you revive me.<span class=\"note\">Or “give me life”</span>'),
('LEB', 'Psalms', '119', '94', '23', 'I <span class=\"supplied\">am</span> yours. Save me,  for I have sought your precepts.'),
('LEB', 'Psalms', '119', '95', '23', 'For me <span class=\"supplied\">the</span> wicked <span class=\"supplied\">lie in</span> wait to destroy me.  I give attention to your testimonies.'),
('LEB', 'Psalms', '119', '96', '23', 'I have seen a limit to every perfection;<span class=\"note\">The meaning of this line is uncertain.</span> your command <span class=\"supplied\">is</span> very broad.'),
('LEB', 'Psalms', '119', '97', '25', '<span class=\"verse-title\">Mem</span>How I love your law!  The whole day it <span class=\"supplied\">is</span> my meditation.'),
('LEB', 'Psalms', '119', '98', '25', 'Your commands make me wiser than my enemies,  because they<span class=\"note\">Hebrew “it <span class=\"supplied\">is</span>”</span><span class=\"supplied\">are</span> ever with me.'),
('LEB', 'Psalms', '119', '99', '25', 'I have more insight than all my teachers,  for your testimonies <span class=\"supplied\">are</span> my meditation.'),
('LEB', 'Psalms', '119', '100', '25', 'I understand more than <span class=\"supplied\">the</span> elders,  for I keep your precepts.'),
('LEB', 'Psalms', '119', '101', '25', 'I have restrained my feet from every evil way,  so that I may heed your word.'),
('LEB', 'Psalms', '119', '102', '25', 'I have not turned aside from your ordinances,  for you have taught me.'),
('LEB', 'Psalms', '119', '103', '25', 'How smooth<span class=\"note\">Or “sweet”</span><span class=\"supplied\">are</span> your words<span class=\"note\">Hebrew “word”</span>to my palate,  more than honey in my mouth.'),
('LEB', 'Psalms', '119', '104', '25', 'From your precepts I get understanding,  therefore I hate every false way.'),
('LEB', 'Psalms', '119', '105', '27', '<span class=\"verse-title\">Nun</span>Your word <span class=\"supplied\">is</span> a lamp to my feet  and a light to my path.'),
('LEB', 'Psalms', '119', '106', '27', 'I have sworn <span class=\"supplied\">an oath</span> and confirmed <span class=\"supplied\">it</span>,  to heed <span class=\"idiom\">your righteous ordinances</span>.<span class=\"note\">Literally “the ordinances of your righteousness”</span>'),
('LEB', 'Psalms', '119', '107', '27', 'I am very much afflicted;  O Adonai, revive me according to your word.'),
('LEB', 'Psalms', '119', '108', '27', 'Please accept the freewill offerings of my mouth, O Adonai,  and teach me your ordinances.'),
('LEB', 'Psalms', '119', '109', '27', 'My life <span class=\"supplied\">is</span> <span class=\"idiom\">in danger</span><span class=\"note\">Literally “in my hand”</span>continually,  yet I do not forget your law.'),
('LEB', 'Psalms', '119', '110', '27', '<span class=\"supplied\">The</span> wicked have laid a snare for me,  yet I do not wander from your precepts.'),
('LEB', 'Psalms', '119', '111', '27', 'I have taken as my own your testimonies forever,  for they <span class=\"supplied\">are</span> the joy of my heart.'),
('LEB', 'Psalms', '119', '112', '27', 'I have inclined my heart to do your statutes  forever, to the end.'),
('LEB', 'Psalms', '119', '113', '29', '<span class=\"verse-title\">Samek</span>I hate <span class=\"idiom\">the double-minded</span>,<span class=\"note\">Literally “<span class=\"supplied\">the</span> divided”</span> but I love your law.'),
('LEB', 'Psalms', '119', '114', '29', 'You <span class=\"supplied\">are</span> my hiding place and my shield;  I hope in your word.'),
('LEB', 'Psalms', '119', '115', '29', 'Turn aside from me, <span class=\"supplied\">you</span> evildoers,  for I will keep the commands of my God.'),
('LEB', 'Psalms', '119', '116', '29', 'Sustain me according to your word,<span class=\"note\">Or “promise”</span>that I may live,  and do not let me be ashamed of my hope.'),
('LEB', 'Psalms', '119', '117', '29', 'Uphold me, that I may be delivered,  and have regard for your statutes continually.'),
('LEB', 'Psalms', '119', '118', '29', 'You reject all <span class=\"supplied\">who</span> stray from your statutes,  for their deceit <span class=\"supplied\">is</span> a breach of faith.'),
('LEB', 'Psalms', '119', '119', '29', 'You remove all the wicked of <span class=\"supplied\">the</span> earth <span class=\"supplied\">like</span> dross,  therefore I love your testimonies.'),
('LEB', 'Psalms', '119', '120', '29', 'My flesh trembles for fear of you,  and I am afraid of your judgments.'),
('LEB', 'Psalms', '119', '121', '31', '<span class=\"verse-title\">ʽAyin</span>I have done justice and righteousness;<span class=\"note\">Or “done <span class=\"supplied\">what is</span> proper and right”</span> do not leave me to my oppressors.'),
('LEB', 'Psalms', '119', '122', '31', 'Pledge good for your servant;  do not let <span class=\"supplied\">the</span> arrogant oppress me.'),
('LEB', 'Psalms', '119', '123', '31', 'My eyes long for<span class=\"note\">Or “long <span class=\"supplied\">to see</span>”</span>your salvation,  and your righteous word.<span class=\"note\">Or “promise”</span>'),
('LEB', 'Psalms', '119', '124', '31', 'Deal with your servant according to your loyal love,  and teach me your statutes.'),
('LEB', 'Psalms', '119', '125', '31', 'I <span class=\"supplied\">am</span> your servant; give me understanding,  that I may know your statutes.'),
('LEB', 'Psalms', '119', '126', '31', '<span class=\"supplied\">It is</span> time for Adonai to act,<span class=\"note\">Or “time to act, O Adonai”</span> <span class=\"supplied\">for</span> they have broken your law.'),
('LEB', 'Psalms', '119', '127', '31', 'Therefore I love your commands  more than gold, even fine gold.'),
('LEB', 'Psalms', '119', '128', '31', 'Therefore all <span class=\"supplied\">your</span> precepts of everything I regard as right;  I hate every false way.'),
('LEB', 'Psalms', '119', '129', '33', '<span class=\"verse-title\">Pe</span>Your testimonies <span class=\"supplied\">are</span> miracles,  therefore my soul keeps them.'),
('LEB', 'Psalms', '119', '130', '33', 'The unfolding of your words gives light,  giving understanding to <span class=\"supplied\">the</span> simple.'),
('LEB', 'Psalms', '119', '131', '33', 'I open my mouth and pant,  because I long for your commands.'),
('LEB', 'Psalms', '119', '132', '33', 'Turn to me and be gracious to me,  as <span class=\"supplied\">is</span> proper for those who love your name.'),
('LEB', 'Psalms', '119', '133', '33', 'Direct my steps in your word,<span class=\"note\">Or “through your promise”</span> and do not let any iniquity gain power over me.'),
('LEB', 'Psalms', '119', '134', '33', 'Redeem me from <span class=\"supplied\">the</span> oppression of humankind,  that I may heed your precepts.'),
('LEB', 'Psalms', '119', '135', '33', 'Shine your face on your servant,  and teach me your statutes.'),
('LEB', 'Psalms', '119', '136', '33', 'Streams of water flow down <span class=\"supplied\">from</span> my eyes,  because <span class=\"supplied\">people</span> do not heed your law.'),
('LEB', 'Psalms', '119', '137', '35', '<span class=\"verse-title\">Tsade</span>You <span class=\"supplied\">are</span> righteous, O Adonai,  and your ordinances <span class=\"supplied\">are</span> upright.'),
('LEB', 'Psalms', '119', '138', '35', 'You have ordained your testimonies <span class=\"supplied\">in</span> righteousness,  and <span class=\"supplied\">in</span> exceeding faithfulness.'),
('LEB', 'Psalms', '119', '139', '35', 'My zeal consumes me,  because my enemies have forgotten your words.'),
('LEB', 'Psalms', '119', '140', '35', 'Your word <span class=\"supplied\">is</span> very pure,  and your servant loves it.'),
('LEB', 'Psalms', '119', '141', '35', 'I <span class=\"supplied\">am</span> small and despised,  <span class=\"supplied\">yet</span> I do not forget your precepts.'),
('LEB', 'Psalms', '119', '142', '35', 'Your righteousness <span class=\"supplied\">is</span> an everlasting righteousness,  and your law <span class=\"supplied\">is</span> truth.'),
('LEB', 'Psalms', '119', '143', '35', 'Trouble and anguish have found me;  your commands delight me.'),
('LEB', 'Psalms', '119', '144', '35', 'The righteousness of your testimonies <span class=\"supplied\">is</span> forever;  give me understanding that I may live.'),
('LEB', 'Psalms', '119', '145', '37', '<span class=\"verse-title\">Qoph</span>I call with <span class=\"supplied\">my</span> whole heart; answer me, O Adonai.  I will keep your statutes.'),
('LEB', 'Psalms', '119', '146', '37', 'I call to you. Save me,  and I will heed your testimonies.'),
('LEB', 'Psalms', '119', '147', '37', 'I rise early in the dawn and cry for help;  I hope in your word.<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>'),
('LEB', 'Psalms', '119', '148', '37', 'My eyes anticipate <span class=\"supplied\">the</span> night watches,  that <span class=\"supplied\">I may</span> meditate on your word.'),
('LEB', 'Psalms', '119', '149', '37', 'Hear my voice according to your loyal love;  O Adonai, preserve my life according to your justice.'),
('LEB', 'Psalms', '119', '150', '37', '<span class=\"supplied\">Those who</span> pursue a <span class=\"supplied\">wicked</span> purpose draw near;  they are far from your law.'),
('LEB', 'Psalms', '119', '151', '37', 'You <span class=\"supplied\">are</span> near, O Adonai,  and all your commands <span class=\"supplied\">are</span> truth.'),
('LEB', 'Psalms', '119', '152', '37', 'Concerning your testimonies, I knew long ago  that you have established them forever.'),
('LEB', 'Psalms', '119', '153', '39', '<span class=\"verse-title\">Resh</span>See my misery and rescue me,  for I do not forget your law.'),
('LEB', 'Psalms', '119', '154', '39', 'Plead my cause and redeem me;  preserve my life according to your word.<span class=\"note\">Or “promise”</span>'),
('LEB', 'Psalms', '119', '155', '39', 'Salvation <span class=\"supplied\">is</span> far from <span class=\"supplied\">the</span> wicked,  for they do not seek your statutes.'),
('LEB', 'Psalms', '119', '156', '39', 'Your mercies <span class=\"supplied\">are</span> great, O Adonai;  preserve my life according to your ordinances.'),
('LEB', 'Psalms', '119', '157', '39', 'My persecutors and enemies <span class=\"supplied\">are</span> many,  <span class=\"supplied\">yet</span> I do not turn aside from your testimonies.'),
('LEB', 'Psalms', '119', '158', '39', 'I see <span class=\"supplied\">the</span> treacherous and I feel disgust,  because they do not heed your word.'),
('LEB', 'Psalms', '119', '159', '39', 'Consider<span class=\"note\">Hebrew “See”</span>that I love your precepts;  O Adonai, according to your loyal love preserve my life.'),
('LEB', 'Psalms', '119', '160', '39', 'The <span class=\"idiom\">whole</span><span class=\"note\">Literally “head”</span>of your word <span class=\"supplied\">is</span> truth,  and your every righteous judgment <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '119', '161', '41', '<span class=\"verse-title\">Sin/Shin</span>Princes persecute me without cause,  but my heart trembles because of your word.<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>'),
('LEB', 'Psalms', '119', '162', '41', 'I <span class=\"supplied\">am</span> rejoicing over your word,  like one who finds great spoil.'),
('LEB', 'Psalms', '119', '163', '41', 'I hate and abhor falsehood;  I love your law.'),
('LEB', 'Psalms', '119', '164', '41', 'I praise you seven <span class=\"supplied\">times</span> in the day  for your righteous ordinances.'),
('LEB', 'Psalms', '119', '165', '41', 'Great peace <span class=\"supplied\">is</span> for those who love your law,  and they do not have a cause for stumbling.'),
('LEB', 'Psalms', '119', '166', '41', 'I hope for your salvation, O Adonai,  and do your commands.'),
('LEB', 'Psalms', '119', '167', '41', 'My soul heeds your testimonies,  and I love them exceedingly.'),
('LEB', 'Psalms', '119', '168', '41', 'I heed your precepts and testimonies,  because all my ways <span class=\"supplied\">are</span> before you.'),
('LEB', 'Psalms', '119', '169', '43', '<span class=\"verse-title\">Taw</span>Let my cry come before you, O Adonai;  give me understanding according to your word.'),
('LEB', 'Psalms', '119', '170', '43', 'Let my plea come before you;  Deliver me according to your word.<span class=\"note\">Or “promise”</span>'),
('LEB', 'Psalms', '119', '171', '43', 'Let my lips pour out praise,  because you teach me your statutes.'),
('LEB', 'Psalms', '119', '172', '43', 'Let my tongue sing <span class=\"supplied\">of</span> your word,<span class=\"note\">Or “promise”</span> because all your commands <span class=\"supplied\">are</span> right.'),
('LEB', 'Psalms', '119', '173', '43', 'Let your hand be my help,<span class=\"note\">Hebrew “to help me”</span> because I have chosen your precepts.'),
('LEB', 'Psalms', '119', '174', '43', 'I long for your salvation, O Adonai,  and your law <span class=\"supplied\">is</span> my delight.'),
('LEB', 'Psalms', '119', '175', '43', 'Let my soul<span class=\"note\">Or “my life”</span>live that it may praise you,  and let your ordinances help me.'),
('LEB', 'Psalms', '119', '176', '43', 'I have wandered like a lost sheep; seek your servant,  because I do not forget your commands.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '120', '1', '1', '<span class=\"verse-title\">A song of ascents.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>In my distress I called to Adonai,  and he answered me.'),
('LEB', 'Psalms', '120', '2', '1', '“Deliver my life, O Adonai, from lying lips,  from a deceitful tongue.”'),
('LEB', 'Psalms', '120', '3', '1', 'What shall be given to you,<span class=\"note\">That is, by God against the false accuser</span> and what more shall be done to you,  deceitful tongue?'),
('LEB', 'Psalms', '120', '4', '1', '<span class=\"supplied\">The</span> sharpened arrows of a warrior,  with burning charcoals from broom trees.'),
('LEB', 'Psalms', '120', '5', '1', 'Woe to me, that I sojourn in Meshech,  <span class=\"supplied\">that</span> I dwell among the tents of Kedar.'),
('LEB', 'Psalms', '120', '6', '1', 'Too long my soul has had its dwelling  near <span class=\"supplied\">one who</span> hates peace.'),
('LEB', 'Psalms', '120', '7', '1', 'I <span class=\"supplied\">am for</span> peace, but when I speak,  they <span class=\"supplied\">are</span> for war.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '121', '1', '1', '<span class=\"verse-title\">A song for the ascents.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>I lift up my eyes to the mountains;  whence will my help come?'),
('LEB', 'Psalms', '121', '2', '1', 'My help <span class=\"supplied\">is</span> from Adonai,  maker of heaven and earth.'),
('LEB', 'Psalms', '121', '3', '1', 'He will not allow your foot to be moved;  he who protects you will not slumber.'),
('LEB', 'Psalms', '121', '4', '1', 'Look, he will not slumber and he will not sleep—  he who protects Israel.'),
('LEB', 'Psalms', '121', '5', '1', 'Adonai <span class=\"supplied\">is</span> your protector;  Adonai <span class=\"supplied\">is</span> your shade at your right hand.'),
('LEB', 'Psalms', '121', '6', '1', 'The sun will not strike you by day,  nor <span class=\"supplied\">the</span> moon by night.'),
('LEB', 'Psalms', '121', '7', '1', 'Adonai will protect you from all evil;<span class=\"note\">Or “all calamity”</span> he will protect your life.'),
('LEB', 'Psalms', '121', '8', '1', 'Adonai will protect your going out and your coming in  from now until forever.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '122', '1', '1', '<span class=\"verse-title\">A song of ascents. Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>I rejoiced in <span class=\"supplied\">those who</span> said to me,  “Let us go to the house<span class=\"note\">Or “temple”</span>of Adonai.”'),
('LEB', 'Psalms', '122', '2', '1', 'Our feet are standing  within your gates, O Jerusalem—'),
('LEB', 'Psalms', '122', '3', '1', 'Jerusalem that is built  as a city that is joined together,'),
('LEB', 'Psalms', '122', '4', '1', 'where <span class=\"supplied\">the</span> tribes go up,  the tribes of Yah<span class=\"note\">A shortened form of “Adonai”</span><span class=\"supplied\">as</span> a testimony for Israel,  to give thanks to the name of Adonai.'),
('LEB', 'Psalms', '122', '5', '1', 'For there <span class=\"supplied\">the</span> thrones sit<span class=\"note\">That is, rulers or judges on thrones</span>for judgment,  thrones of David’s house.'),
('LEB', 'Psalms', '122', '6', '1', 'Pray<span class=\"note\">Hebrew “Ask”</span>for the peace of Jerusalem:  “May those who love you be at ease.'),
('LEB', 'Psalms', '122', '7', '1', 'May peace be within your walls,  security within your palaces.”'),
('LEB', 'Psalms', '122', '8', '1', 'For the sake of my brothers and my friends,  I will say, “Peace <span class=\"supplied\">be</span> within you.”'),
('LEB', 'Psalms', '122', '9', '1', 'For the sake of the house<span class=\"note\">Or “temple”</span>of Adonai our God,  I will seek your good.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '123', '1', '1', '<span class=\"verse-title\">A song of ascents.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>I lift up my eyes to you,  the one enthroned<span class=\"note\">Or “the one who dwells”</span>in the heavens.'),
('LEB', 'Psalms', '123', '2', '1', 'Behold, as <span class=\"supplied\">the</span> eyes of servants  <span class=\"supplied\">look</span> to the hand of their master,<span class=\"note\">Hebrew “masters”</span> as <span class=\"supplied\">the</span> eyes of a maid to the hand of her mistress,  so our eyes <span class=\"supplied\">look</span> to Adonai our God,  until he is gracious to us.'),
('LEB', 'Psalms', '123', '3', '1', 'Be gracious to us, O Adonai, be gracious to us,  for long enough we have had our fill of contempt.'),
('LEB', 'Psalms', '123', '4', '1', 'For long enough our soul has had its fill of  the derision of the self-confident,  the contempt of <span class=\"supplied\">the</span> arrogant.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '124', '1', '1', '<span class=\"verse-title\">A song of ascents. Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>“If <span class=\"supplied\">it had</span> not <span class=\"supplied\">been</span> Adonai who was on our side,”  do let Israel say,'),
('LEB', 'Psalms', '124', '2', '1', '“If <span class=\"supplied\">it had</span> not <span class=\"supplied\">been</span> Adonai who was on our side,  when men rose up against us,'),
('LEB', 'Psalms', '124', '3', '1', 'then they would have swallowed us alive,  when their anger was kindled against us.'),
('LEB', 'Psalms', '124', '4', '1', 'Then the waters would have flooded over us,  <span class=\"supplied\">the</span> torrent would have passed over our soul.'),
('LEB', 'Psalms', '124', '5', '1', 'Then over our soul would have passed  the raging waters.”'),
('LEB', 'Psalms', '124', '6', '1', 'Blessed <span class=\"supplied\">be</span> Adonai,  who has not made<span class=\"note\">Hebrew “given”</span>us prey for their teeth.'),
('LEB', 'Psalms', '124', '7', '1', 'Our soul has escaped like a bird  from <span class=\"supplied\">the</span> snare of fowlers.  The snare <span class=\"supplied\">is</span> broken, and we have escaped.'),
('LEB', 'Psalms', '124', '8', '1', 'Our help <span class=\"supplied\">is</span> in the name of Adonai,  maker of heaven and earth.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '125', '1', '1', '<span class=\"verse-title\">A song of ascents.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Those who trust in Adonai  <span class=\"supplied\">are</span> like Mount Zion, which cannot be moved,  <span class=\"supplied\">but</span> abides forever.'),
('LEB', 'Psalms', '125', '2', '1', '<span class=\"supplied\">As</span> mountains <span class=\"supplied\">are</span> round about Jerusalem,  so Adonai <span class=\"supplied\">is</span> round about his people,  from now until forever.'),
('LEB', 'Psalms', '125', '3', '1', 'For the scepter of wickedness shall not rest  on the <span class=\"idiom\">land</span><span class=\"note\">Literally “lot,” that is, land allotted</span>of the righteous,  lest the righteous stretch out  their hands to do wickedness.<span class=\"note\">Hebrew “in wickedness”</span>'),
('LEB', 'Psalms', '125', '4', '1', 'Do good, O Adonai, to the good,  and to <span class=\"supplied\">those</span> upright in their hearts.'),
('LEB', 'Psalms', '125', '5', '1', 'But regarding those who turn aside to their crooked ways,  Adonai will lead them away with the evildoers.  Peace <span class=\"supplied\">be</span> upon Israel.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '126', '1', '1', '<span class=\"verse-title\">A song of ascents.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>When Adonai restored the fortunes<span class=\"note\">Or “captivity”</span>of Zion,<span class=\"note\">Or “<span class=\"supplied\">those</span> returned of <span class=\"supplied\">belonging to</span> Zion”</span> we were like dreamers.'),
('LEB', 'Psalms', '126', '2', '1', 'Then our mouth was filled with laughter,  and our tongue with rejoicing.  Then they said among the nations,  “Adonai has done great <span class=\"supplied\">things</span> for these <span class=\"supplied\">people</span>.”'),
('LEB', 'Psalms', '126', '3', '1', 'Adonai has done great <span class=\"supplied\">things</span> for us;  we are glad.'),
('LEB', 'Psalms', '126', '4', '1', 'Restore, O Adonai, our fortunes<span class=\"note\">Or “captivity”</span> like the streams in the Negeb.<span class=\"note\">Or “South”</span>'),
('LEB', 'Psalms', '126', '5', '1', 'Those who sow with tears  shall reap with rejoicing.'),
('LEB', 'Psalms', '126', '6', '1', 'He who diligently goes <span class=\"supplied\">out</span> with weeping,  carrying the seed bag,  shall certainly come <span class=\"supplied\">in</span> with rejoicing,  carrying his sheaves.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '127', '1', '1', '<span class=\"verse-title\">A song of ascents. Of Solomon.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Unless Adonai builds a house,  its builders labor at it in vain.  Unless Adonai guards a city,  a guard watches in vain.'),
('LEB', 'Psalms', '127', '2', '1', '<span class=\"supplied\">It is</span> in vain for you who rise early <span class=\"supplied\">and</span> sit late,  eating the bread of anxious toil,  <span class=\"supplied\">when</span> thus he<span class=\"note\">That is, Adonai</span>provides<span class=\"note\">Hebrew “gives”</span>for his beloved in <span class=\"supplied\">his</span> sleep.<span class=\"note\">Or “so he gives sleep to his beloved”</span>'),
('LEB', 'Psalms', '127', '3', '1', 'Look, children<span class=\"note\">Hebrew “sons”</span><span class=\"supplied\">are</span> the heritage<span class=\"note\">That is, gift</span>of Adonai;  the fruit of the womb <span class=\"supplied\">is</span> a reward.'),
('LEB', 'Psalms', '127', '4', '1', 'Like arrows in <span class=\"supplied\">the</span> hand of a warrior,  so <span class=\"supplied\">are</span> the children<span class=\"note\">Hebrew “sons”</span>of <span class=\"supplied\">one’s</span> youth.'),
('LEB', 'Psalms', '127', '5', '1', 'Blessed <span class=\"supplied\">is</span> the man who fills his quiver with them.<span class=\"note\">Or “whose quiver he <span class=\"supplied\">Adonai</span> fills with them”</span> They shall not be put to shame  when they speak with enemies at the gate.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '128', '1', '1', '<span class=\"verse-title\">A song of ascents.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Blessed <span class=\"supplied\">is</span> everyone who fears Adonai,  who walks in his ways.'),
('LEB', 'Psalms', '128', '2', '1', 'You will indeed eat of the labor of your hands;  you <span class=\"supplied\">will be</span> happy and <span class=\"supplied\">it will be</span> well with you.'),
('LEB', 'Psalms', '128', '3', '1', 'Your wife <span class=\"supplied\">will be</span> like a fruitful vine  <span class=\"idiom\">within</span><span class=\"note\">Literally “in the recesses of”</span>your house.  Your children<span class=\"note\">Hebrew “sons”</span><span class=\"supplied\">will be</span> like olive shoots  about your table.'),
('LEB', 'Psalms', '128', '4', '1', 'Look, for thus shall a man be blessed  <span class=\"idiom\">who fears Adonai</span>.<span class=\"note\">Literally “a fearer of Adonai”</span>'),
('LEB', 'Psalms', '128', '5', '1', 'May Adonai bless you from Zion,  that you may see the good of Jerusalem  all the days of your life,'),
('LEB', 'Psalms', '128', '6', '1', 'and that you may see your children’s children.  <span class=\"supplied\">May</span> peace <span class=\"supplied\">be</span> upon Israel.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '129', '1', '1', '<span class=\"verse-title\">A song of ascents.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>“Too often<span class=\"note\">Or “Greatly”</span>they have attacked me from my youth.”  Let Israel say,'),
('LEB', 'Psalms', '129', '2', '1', '“Too often<span class=\"note\">Or “Greatly”</span>they have attacked me from my youth,  yet they have not prevailed against me.'),
('LEB', 'Psalms', '129', '3', '1', 'On my back plowmen have plowed.  They have made their furrows<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>long.”'),
('LEB', 'Psalms', '129', '4', '1', 'Adonai <span class=\"supplied\">is</span> righteous.  He has cut <span class=\"supplied\">the</span> ropes<span class=\"note\">That is, the yoke and tackle on a beast of burden</span>of <span class=\"supplied\">the</span> wicked.'),
('LEB', 'Psalms', '129', '5', '1', 'Let all be put to shame and repulsed  who hate Zion.'),
('LEB', 'Psalms', '129', '6', '1', 'Let them be like grass on <span class=\"supplied\">the</span> housetops,  that withers before it grows up,'),
('LEB', 'Psalms', '129', '7', '1', '<span class=\"supplied\">with</span> which a reaper cannot fill his hand,  nor the binder of sheaves his <span class=\"idiom\">arms</span>,<span class=\"note\">Literally “bosom”</span>'),
('LEB', 'Psalms', '129', '8', '1', 'so that passersby do not say,  “The blessing of Adonai <span class=\"supplied\">be</span> upon you.  We bless you in the name of Adonai.”')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '130', '1', '1', '<span class=\"verse-title\">A song of ascents.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Out of <span class=\"supplied\">the</span> depths I call to you, O Adonai.'),
('LEB', 'Psalms', '130', '2', '1', '“O Lord, hear my voice.  Let your ears be attentive  to the voice of my supplications.'),
('LEB', 'Psalms', '130', '3', '1', 'If you, O Yah,<span class=\"note\">A shortened form of “Adonai”</span>should <span class=\"idiom\">keep track of</span><span class=\"note\">Literally “keep”</span>iniquities,  O Lord, who could stand?'),
('LEB', 'Psalms', '130', '4', '1', 'But with you <span class=\"supplied\">is</span> forgiveness,  so that you may be feared.”'),
('LEB', 'Psalms', '130', '5', '1', 'I await Adonai; my soul awaits,  and I wait<span class=\"note\">Or “hope,” here and v. 7, though the Hebrew uses a different verbal stem in v. 7</span>for his word.'),
('LEB', 'Psalms', '130', '6', '1', 'My soul <span class=\"supplied\">waits</span> for the Lord  more than watchmen for the morning.  Yes, <span class=\"supplied\">more than</span> watchmen for the morning.'),
('LEB', 'Psalms', '130', '7', '1', 'O Israel, wait<span class=\"note\">Or “hope”</span>for Adonai.  For with Adonai <span class=\"supplied\">there is</span> loyal love,  and with him <span class=\"supplied\">there is</span> abundant redemption.'),
('LEB', 'Psalms', '130', '8', '1', 'And he will redeem Israel  from all its iniquities.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '131', '1', '1', '<span class=\"verse-title\">A song of ascents. Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>My heart is not haughty nor my eyes <span class=\"idiom\">arrogant</span>.<span class=\"note\">Literally “raised”</span> And I do not <span class=\"idiom\">concern myself</span><span class=\"note\">Literally “walk about”</span> with things too great and difficult for me.'),
('LEB', 'Psalms', '131', '2', '1', 'Rather I have soothed and quieted my soul,  like a weaned child with its mother,  like the weaned child <span class=\"supplied\">is</span> my soul with me.'),
('LEB', 'Psalms', '131', '3', '1', 'O Israel, hope in Adonai  from now until forever.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '132', '1', '1', '<span class=\"verse-title\">A song of ascents.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>O Adonai, remember with regard to David  all his affliction.<span class=\"note\">Hebrew “his being afflicted”</span>'),
('LEB', 'Psalms', '132', '2', '1', 'How he swore to Adonai,  he vowed to the Mighty One of Jacob:'),
('LEB', 'Psalms', '132', '3', '1', '“I will not enter into the tent of my house,  I will not go up to the couch of my bed,'),
('LEB', 'Psalms', '132', '4', '1', 'I will give no sleep to my eyes,  <span class=\"supplied\">no</span> slumber to my eyelids,'),
('LEB', 'Psalms', '132', '5', '1', 'until I find a place for Adonai,  a dwelling place for the Mighty One of Jacob.”'),
('LEB', 'Psalms', '132', '6', '1', 'Look, we heard of it in Ephrathah;  we found her in the fields of Jaar.<span class=\"note\">Or “the fields of the woods”</span>'),
('LEB', 'Psalms', '132', '7', '1', 'Let us go to his dwelling places;<span class=\"note\">That is, the temple</span> Let us worship at his footstool.'),
('LEB', 'Psalms', '132', '8', '1', 'Arise, O Adonai, to your resting place,  you and your mighty ark.'),
('LEB', 'Psalms', '132', '9', '1', 'Let your priests clothe themselves with righteousness,  and let your faithful sing for joy.'),
('LEB', 'Psalms', '132', '10', '1', 'For the sake of your servant David,  do not turn away the face of your anointed one.'),
('LEB', 'Psalms', '132', '11', '1', 'Adonai swore <span class=\"supplied\">in</span> truth to David;  he will not turn back from it.  “One from the fruit of your <span class=\"idiom\">body</span><span class=\"note\">Literally “belly”</span> I will set on your throne.'),
('LEB', 'Psalms', '132', '12', '1', 'If your sons will heed my covenant  and my testimonies that I will teach them,  their sons also forever  will sit on your throne.”'),
('LEB', 'Psalms', '132', '13', '1', 'For Adonai has chosen Zion;  he has desired it for his habitation.'),
('LEB', 'Psalms', '132', '14', '1', '“This <span class=\"supplied\">is</span> my resting place forever;  here I will dwell, for I have desired it.'),
('LEB', 'Psalms', '132', '15', '1', 'I will bless its provisions abundantly;  I will satisfy its poor with bread.'),
('LEB', 'Psalms', '132', '16', '1', 'I will also clothe its priests with salvation,  and its faithful will shout exuberantly for joy.'),
('LEB', 'Psalms', '132', '17', '1', 'There I will cause a horn to grow for David;  I will set a lamp for my anointed one.'),
('LEB', 'Psalms', '132', '18', '1', 'I will clothe his enemies with shame,  but on him his crown will flourish.”<span class=\"note\">Or “shine”</span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '133', '1', '1', '<span class=\"verse-title\">A song of ascents. Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Look, how good and how pleasant <span class=\"supplied\">it is</span>  <span class=\"supplied\">when</span> brothers dwell together in unity.'),
('LEB', 'Psalms', '133', '2', '1', '<span class=\"supplied\">It is</span> like the fragrant oil upon the head,  running down upon the beard, the beard of Aaron,  that runs down upon the edges of his robes.'),
('LEB', 'Psalms', '133', '3', '1', '<span class=\"supplied\">It is</span> like the dew of Hermon that runs down  upon the mountains of Zion,  because there Adonai commanded the blessing—  life forever.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '134', '1', '1', '<span class=\"verse-title\">A song of ascents.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Look, bless Adonai, all <span class=\"supplied\">you</span> the servants of Adonai,  who <span class=\"idiom\">serve</span><span class=\"note\">Literally “stand”</span>in the house<span class=\"note\">Or “temple”</span>of Adonai by night.'),
('LEB', 'Psalms', '134', '2', '1', 'Lift up your hands in <span class=\"supplied\">the</span> sanctuary  and bless Adonai.'),
('LEB', 'Psalms', '134', '3', '1', 'May Adonai bless you from Zion,  <span class=\"supplied\">he who</span> made heaven and earth.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '135', '1', '1', '<span class=\"verse-title\">Praise to God for His Power and Redemption</span>Praise Yah!<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span>Praise the name of Adonai.  Praise <span class=\"supplied\">him</span>, O servants of Adonai,'),
('LEB', 'Psalms', '135', '2', '1', 'who stand in the house<span class=\"note\">Or “temple”</span>of Adonai,  in the courts of the house<span class=\"note\">Or “temple”</span>of our God.'),
('LEB', 'Psalms', '135', '3', '1', 'Praise Yah,<span class=\"note\">A shortened form of “Adonai”</span>for Adonai <span class=\"supplied\">is</span> good.  Sing praises concerning his name, for <span class=\"supplied\">it is</span> pleasant.'),
('LEB', 'Psalms', '135', '4', '1', 'For Yah has chosen Jacob for himself,  Israel as his special possession.'),
('LEB', 'Psalms', '135', '5', '1', 'For I know that Adonai <span class=\"supplied\">is</span> great,  and our Lord <span class=\"supplied\">is greater</span> than all gods.'),
('LEB', 'Psalms', '135', '6', '1', 'All that Adonai desires, he does,  in heaven and on earth,  in the seas and all <span class=\"supplied\">the</span> depths.'),
('LEB', 'Psalms', '135', '7', '1', '<span class=\"supplied\">He</span> causes clouds to rise from the edge of the earth;  he makes lightnings for the rain;  <span class=\"supplied\">he</span> brings out <span class=\"supplied\">the</span> wind from his storehouses.'),
('LEB', 'Psalms', '135', '8', '1', 'Who struck the firstborn of Egypt,  both of humans<span class=\"note\">Hebrew “human”</span>and animals.<span class=\"note\">Hebrew “animal”</span>'),
('LEB', 'Psalms', '135', '9', '1', 'He sent signs and wonders into your midst, O Egypt,  against Pharaoh and all his servants.'),
('LEB', 'Psalms', '135', '10', '1', 'Who struck many nations  and killed mighty kings—'),
('LEB', 'Psalms', '135', '11', '1', 'Sihon the king of the Amorites,  and Og the king of Bashan,  and all the kingdoms of Canaan—'),
('LEB', 'Psalms', '135', '12', '1', 'and he gave their land as an inheritance,  an inheritance for his people Israel.'),
('LEB', 'Psalms', '135', '13', '1', 'O Adonai, your name <span class=\"supplied\">endures</span> forever,  your <span class=\"idiom\">renown</span><span class=\"note\">Literally “remembrance”</span><span class=\"idiom\">throughout all generations</span>,<span class=\"note\">Literally “for a generation and a generation”</span>'),
('LEB', 'Psalms', '135', '14', '1', 'for Adonai will plead <span class=\"supplied\">the</span> cause <span class=\"supplied\">of</span> his people  and will have compassion on his servants.'),
('LEB', 'Psalms', '135', '15', '1', 'The idols of the nations <span class=\"supplied\">are</span> silver and gold,  <span class=\"supplied\">the</span> work of the hands of humankind.'),
('LEB', 'Psalms', '135', '16', '1', 'They have mouths, but cannot speak;  they have eyes, but cannot see;'),
('LEB', 'Psalms', '135', '17', '1', 'they have ears, but cannot hear;  there is not even breath in their mouths.'),
('LEB', 'Psalms', '135', '18', '1', '<span class=\"supplied\">Those who</span> make them become like them,  <span class=\"supplied\">as does</span> everyone who trusts in them.'),
('LEB', 'Psalms', '135', '19', '1', 'O house of Israel, bless Adonai.  O house of Aaron, bless Adonai.'),
('LEB', 'Psalms', '135', '20', '1', 'O house of Levi, bless Adonai.  <span class=\"supplied\">You who</span> fear Adonai, bless Adonai.'),
('LEB', 'Psalms', '135', '21', '1', 'Blessed <span class=\"supplied\">be</span> Adonai from Zion,  who dwells <span class=\"supplied\">in</span> Jerusalem. Praise Yah!<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '136', '1', '1', '<span class=\"verse-title\">Praise to God for His Creation and Deliverance</span>Give thanks to Adonai, for <span class=\"supplied\">he is</span> good,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '2', '1', 'Give thanks to the God of gods,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '3', '1', 'Give thanks to the Lord of lords,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '4', '1', 'To <span class=\"supplied\">him who</span> alone does great wonders,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '5', '1', 'To <span class=\"supplied\">him who</span> made the heavens with skill,<span class=\"note\">Or “understanding”</span> for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '6', '1', 'To <span class=\"supplied\">him who</span> spread out the earth above the waters,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '7', '1', 'To <span class=\"supplied\">him who</span> made <span class=\"supplied\">the</span> great lights,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '8', '1', 'The sun to rule the day,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '9', '1', 'The moon and stars to rule the night,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '10', '1', 'To <span class=\"supplied\">him who</span> struck Egypt through their firstborn,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '11', '1', 'And he brought Israel out from among them,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '12', '1', 'With a strong hand and an outstretched arm,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '13', '1', 'To <span class=\"supplied\">him who</span> divided <span class=\"supplied\">the</span> <span class=\"idiom\">Red Sea</span><span class=\"note\">Literally “sea of reed”</span>in two,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '14', '1', 'And he let Israel cross over through the midst of it,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '15', '1', 'But he tossed Pharaoh and his army into <span class=\"supplied\">the</span> <span class=\"idiom\">Red Sea</span>,<span class=\"note\">Literally “sea of reed”</span> for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '16', '1', 'To <span class=\"supplied\">him who</span> led his people through the wilderness,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '17', '1', 'To <span class=\"supplied\">him who</span> struck great kings,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '18', '1', 'And he killed mighty kings,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '19', '1', 'Sihon the king of the Amorites,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '20', '1', 'And Og the king of Bashan,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '21', '1', 'And he gave their land as an inheritance,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '22', '1', 'An inheritance to Israel his servant,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '23', '1', 'Who remembered us in our low estate,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '24', '1', 'And he rescued us from our enemies,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '25', '1', '<span class=\"supplied\">The one who</span> gives food to all flesh,  for his loyal love <span class=\"supplied\">endures</span> forever.'),
('LEB', 'Psalms', '136', '26', '1', 'Give thanks to the God of heaven,  for his loyal love <span class=\"supplied\">endures</span> forever.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '137', '1', '1', '<span class=\"verse-title\">Lament During the Babylonian Exile</span>By the rivers of Babylon,  there we sat, yes, we wept,  when we remembered Zion.'),
('LEB', 'Psalms', '137', '2', '1', 'On <span class=\"supplied\">the</span> willows<span class=\"note\">Species of tree uncertain</span>in her midst,  we hung up our lyres.'),
('LEB', 'Psalms', '137', '3', '1', 'For there our captors asked of us  words of a song,  and our tormentors<span class=\"note\">Only occurring once in the MT, this interpretation is uncertain. The LXX’s “carried us captive” is a guess. If related to the Hebrew root <span class=\"italic\">yll</span> it might have the idea of “mockers/yammerers”</span><span class=\"supplied\">asked of us</span> jubilation,  “Sing for us from a song of Zion.”'),
('LEB', 'Psalms', '137', '4', '1', 'How could we sing the song of Adonai  in <span class=\"idiom\">a foreign land</span>?<span class=\"note\">Literally “a land of a foreigner”</span>'),
('LEB', 'Psalms', '137', '5', '1', 'If I forget you, O Jerusalem,  let my right hand forget.<span class=\"note\">That is, how to play a musical instrument</span>'),
('LEB', 'Psalms', '137', '6', '1', 'Let my tongue cling to the roof of my mouth,  if I do not remember you,  if do not I exalt Jerusalem  above my highest joy.'),
('LEB', 'Psalms', '137', '7', '1', 'Remember, O Adonai, against the sons of Edom  the day of Jerusalem,  the ones who said, “Lay <span class=\"supplied\">it</span> bare! Lay <span class=\"supplied\">it</span> bare  to its foundation!”'),
('LEB', 'Psalms', '137', '8', '1', 'O daughter of Babylon, about to be devastated,  happy <span class=\"supplied\">shall be he</span> who pays back to you  <span class=\"idiom\">what you paid out to us</span>.<span class=\"note\">Literally “<span class=\"supplied\">with</span> what you treated us”</span>'),
('LEB', 'Psalms', '137', '9', '1', 'Happy <span class=\"supplied\">shall be he</span> who seizes  and smashes your children  against the rock.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '138', '1', '1', '<span class=\"verse-title\">Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>I give you thanks with my whole heart;  before <span class=\"supplied\">the</span> gods I sing your praise.'),
('LEB', 'Psalms', '138', '2', '1', 'I bow down toward your holy temple  and give thanks to your name,  because of your loyal love and faithfulness,  for you have magnified your word<span class=\"note\">Or “promise”</span>according to all your name.<span class=\"note\">Or “beyond all your name”</span>'),
('LEB', 'Psalms', '138', '3', '1', '<span class=\"idiom\">When</span><span class=\"note\">Literally “In the day”</span>I called and you answered me,  you emboldened me in my soul <span class=\"supplied\">with</span> strength.'),
('LEB', 'Psalms', '138', '4', '1', 'All <span class=\"supplied\">the</span> kings of <span class=\"supplied\">the</span> earth  will praise you, O Adonai,  when they have heard the words<span class=\"note\">Or “heard about the words”</span>of your mouth,'),
('LEB', 'Psalms', '138', '5', '1', 'and they will sing of the ways of Adonai,  because Adonai’s glory <span class=\"supplied\">is</span> great.'),
('LEB', 'Psalms', '138', '6', '1', 'Though Adonai <span class=\"supplied\">is</span> high, yet he sees <span class=\"supplied\">the</span> lowly,  but <span class=\"supplied\">the</span> proud he perceives<span class=\"note\">Hebrew “knows”</span>at a distance.'),
('LEB', 'Psalms', '138', '7', '1', 'Though I walk in the midst of trouble,  you preserve me alive.  You stretch out your hand against the anger of my enemies,  and you save me <span class=\"supplied\">with</span> your right hand.'),
('LEB', 'Psalms', '138', '8', '1', 'Adonai accomplishes <span class=\"supplied\">things</span> for my benefit.  O Adonai, your loyal love <span class=\"supplied\">endures</span> forever;  do not abandon the works of your hands.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '139', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. Of David. A psalm.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>O Adonai, you have searched me, and you know <span class=\"supplied\">me</span>.'),
('LEB', 'Psalms', '139', '2', '1', 'You know my sitting down and my rising up.  You understand my thought from afar.'),
('LEB', 'Psalms', '139', '3', '1', 'You search out<span class=\"note\">Hebrew “winnow”; or perhaps from another root, “measure”</span>my wandering and my lying down,  and are acquainted with all my ways.'),
('LEB', 'Psalms', '139', '4', '1', 'For <span class=\"supplied\">there is</span> not a word <span class=\"supplied\">yet</span> on my tongue,  <span class=\"supplied\">but</span> behold, O Adonai, you know it completely.'),
('LEB', 'Psalms', '139', '5', '1', 'You barricade me behind and in front,  and set your hand upon me.'),
('LEB', 'Psalms', '139', '6', '1', '<span class=\"supplied\">Such</span> knowledge is too wonderful for me.  It is set high; I cannot <span class=\"supplied\">prevail</span> against it.'),
('LEB', 'Psalms', '139', '7', '1', 'Where I can go from your Spirit,  or where can I flee from your presence?'),
('LEB', 'Psalms', '139', '8', '1', 'If I ascend to heaven, there you <span class=\"supplied\">are</span>,  and if I make my bed <span class=\"supplied\">in</span> Sheol, look! <span class=\"supplied\">There</span> you <span class=\"supplied\">are</span>.'),
('LEB', 'Psalms', '139', '9', '1', 'If I lift up <span class=\"supplied\">the</span> wings of <span class=\"supplied\">the</span> dawn,  <span class=\"supplied\">and</span> I alight on <span class=\"supplied\">the</span> far side of <span class=\"supplied\">the</span> sea,'),
('LEB', 'Psalms', '139', '10', '1', 'even there your hand would lead me,  and your right hand would hold me fast.'),
('LEB', 'Psalms', '139', '11', '1', 'And if I should say, “Surely darkness will cover me,  and <span class=\"supplied\">the</span> light around me <span class=\"supplied\">will be as</span> night,”'),
('LEB', 'Psalms', '139', '12', '1', 'even <span class=\"supplied\">the</span> darkness is not too dark for you,<span class=\"note\">Or “does not obscure from you”</span> and <span class=\"supplied\">the</span> night shines as the day—  the darkness <span class=\"supplied\">and</span> the light are alike <span class=\"supplied\">for you</span>.'),
('LEB', 'Psalms', '139', '13', '1', 'Indeed you created my <span class=\"idiom\">inward parts</span>;<span class=\"note\">Literally “kidneys”</span> you wove me in my mother’s womb.'),
('LEB', 'Psalms', '139', '14', '1', 'I praise you, because I am fearfully  <span class=\"supplied\">and</span> wonderfully <span class=\"supplied\">made</span>.<span class=\"note\">Or “I am made wonderful fearfully”</span> Wonderful <span class=\"supplied\">are</span> your works,  and my soul knows <span class=\"supplied\">it</span> well.'),
('LEB', 'Psalms', '139', '15', '1', 'My <span class=\"idiom\">frame</span><span class=\"note\">Literally “bone”</span>was not hidden from you,  when I was created secretly,<span class=\"note\">Or “in the secret <span class=\"supplied\">place</span>”</span> <span class=\"supplied\">and</span> intricately woven  in <span class=\"supplied\">the</span> depths of <span class=\"supplied\">the</span> earth.'),
('LEB', 'Psalms', '139', '16', '1', 'Your eyes saw my embryo,<span class=\"note\">Hebrew “unformed substance”</span> and in your book they all were written—  days fashioned <span class=\"supplied\">for me</span> when <span class=\"supplied\">there was</span> not one of them.'),
('LEB', 'Psalms', '139', '17', '1', 'And to me, how precious<span class=\"note\">Or “difficult”</span>are your thoughts, O God;  how vast is their sum.'),
('LEB', 'Psalms', '139', '18', '1', 'If I should count them,  they would outnumber <span class=\"supplied\">the</span> sand.  I awaken, and I <span class=\"supplied\">am</span> still with you.'),
('LEB', 'Psalms', '139', '19', '1', 'If only you would kill the wicked, O God—  so get away from me, <span class=\"supplied\">you</span> <span class=\"idiom\">bloodthirsty men</span>—<span class=\"note\">Literally “men of bloods”</span>'),
('LEB', 'Psalms', '139', '20', '1', 'who speak against you deceitfully.  Your enemies take <span class=\"supplied\">your name</span> in vain.'),
('LEB', 'Psalms', '139', '21', '1', 'Do I not hate those who hate you, O Adonai?  And do I <span class=\"supplied\">not</span> loathe those who rise up against you?'),
('LEB', 'Psalms', '139', '22', '1', 'I hate them with a complete hatred;  they have become my enemies.'),
('LEB', 'Psalms', '139', '23', '1', 'Search me, O God, and know my heart;  test me and know my anxious thoughts.'),
('LEB', 'Psalms', '139', '24', '1', 'And see if <span class=\"supplied\">there is</span> in me <span class=\"idiom\">the worship of false gods</span>,<span class=\"note\">Literally “a way of an idol”</span> and lead me in <span class=\"supplied\">the</span> way everlasting.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '140', '1', '1', '<span class=\"verse-title\">For the <span class=\"supplied\">music</span> director. A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>Rescue me, O Adonai, from evil men.<span class=\"note\">The several uses of the singular “man” in this psalm are probably collective</span> Preserve me from violent men,'),
('LEB', 'Psalms', '140', '2', '1', 'who plan evil <span class=\"supplied\">things</span> in <span class=\"supplied\">their</span> heart.  They stir up wars continually.'),
('LEB', 'Psalms', '140', '3', '1', 'They sharpen their tongue as <span class=\"supplied\">sharp as</span> a snake’s;  <span class=\"supplied\">the</span> venom of a viper <span class=\"supplied\">is</span> under their lips. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '140', '4', '1', 'Protect me, O Adonai, from <span class=\"supplied\">the</span> hands of <span class=\"supplied\">the</span> wicked.  Preserve me from violent men,  who have planned to <span class=\"idiom\">make me stumble</span>.<span class=\"note\">Literally “push my steps”</span>'),
('LEB', 'Psalms', '140', '5', '1', '<span class=\"supplied\">The</span> proud have hidden a trap for me, and cords.  They have spread out a net along <span class=\"supplied\">the</span> <span class=\"idiom\">side</span><span class=\"note\">Literally “hand”</span>of <span class=\"supplied\">the</span> path.  They have set snares for me. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '140', '6', '1', 'I say to Adonai, “You <span class=\"supplied\">are</span> my God.”  Listen, O Adonai, to the voice of my supplications.'),
('LEB', 'Psalms', '140', '7', '1', 'O Adonai, my Lord, the strength of my salvation,  you have covered my head in <span class=\"supplied\">the</span> day of battle.'),
('LEB', 'Psalms', '140', '8', '1', 'Do not grant, O Adonai, <span class=\"supplied\">the</span> desires of <span class=\"supplied\">the</span> wicked.  Do not allow <span class=\"supplied\">them</span> to attain their<span class=\"note\">Hebrew “his”</span>plan, lest they be exalted. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '140', '9', '1', '<span class=\"supplied\">With respect to the</span> head of <span class=\"supplied\">those who</span> surround me,  may the harm of their lips cover them.'),
('LEB', 'Psalms', '140', '10', '1', 'Let burning coals fall on them;  let them be dropped into the fire,  into bottomless pits <span class=\"supplied\">from which</span> they cannot rise.'),
('LEB', 'Psalms', '140', '11', '1', 'Do not let a <span class=\"idiom\">slanderer</span><span class=\"note\">Literally “man of tongue”</span>be established in the land;  as for <span class=\"supplied\">the</span> man of violence, let evil hunt him quickly.'),
('LEB', 'Psalms', '140', '12', '1', 'I know<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>that Adonai will maintain <span class=\"supplied\">the</span> cause of <span class=\"supplied\">the</span> afflicted,  <span class=\"supplied\">and</span> justice for <span class=\"supplied\">the</span> needy.'),
('LEB', 'Psalms', '140', '13', '1', 'Surely <span class=\"supplied\">the</span> righteous will give thanks to your name;  <span class=\"supplied\">the</span> upright will dwell in your presence.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '141', '1', '1', '<span class=\"verse-title\">A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>I call on you, O Adonai; hasten to me.  Listen to my voice when I call to you.'),
('LEB', 'Psalms', '141', '2', '1', 'Let my prayer be set before you <span class=\"supplied\">as</span> incense,  the lifting up of my palms <span class=\"supplied\">as the</span> evening offering.'),
('LEB', 'Psalms', '141', '3', '1', 'Set a guard, O Adonai, over my mouth;  keep watch over the door of my lips.'),
('LEB', 'Psalms', '141', '4', '1', 'Do not incline my heart to any evil thing,  to practice wicked deeds  with men <span class=\"supplied\">who</span> do iniquity;  and do not let me eat of their delicacies.'),
('LEB', 'Psalms', '141', '5', '1', 'Let a righteous one strike me in kindness,  and let him chasten me.  <span class=\"supplied\">It is</span> oil for <span class=\"supplied\">my</span> head;<span class=\"note\">Or “It is choice oil”</span>let not my head refuse.  For still my prayer <span class=\"supplied\">is</span> against their evil deeds.'),
('LEB', 'Psalms', '141', '6', '1', '<span class=\"supplied\">When</span> their judges are thrown down <span class=\"supplied\">the</span> sides of a cliff,<span class=\"note\">Or “are dropped into the hands of <span class=\"supplied\">the</span> Rock,” that is, Adonai. The interpretation of vv. 6–7 is uncertain.</span> then they will understand that my words were pleasant.'),
('LEB', 'Psalms', '141', '7', '1', 'As <span class=\"supplied\">when one</span> plows and breaks up the earth,  <span class=\"supplied\">so</span> our bones are scattered at the mouth of Sheol.'),
('LEB', 'Psalms', '141', '8', '1', 'But<span class=\"note\">Or “Because”</span>my eyes <span class=\"supplied\">are</span> toward you, O Adonai, my Lord;  I have taken refuge in you. Do not lay bare my soul.<span class=\"note\">Or “Do not raze my life”</span>'),
('LEB', 'Psalms', '141', '9', '1', 'Protect me from <span class=\"supplied\">the</span> <span class=\"idiom\">grasp</span><span class=\"note\">Literally “hand”</span>of <span class=\"supplied\">the</span> trap they have laid for me,  and <span class=\"supplied\">from the</span> snares of evildoers.'),
('LEB', 'Psalms', '141', '10', '1', 'Let <span class=\"supplied\">the</span> wicked fall into their<span class=\"note\">Hebrew “his”</span>nets,  while I escape altogether.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '142', '1', '1', '<span class=\"verse-title\">A maskil of David.  When he was in the cave. A prayer.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm; the English verse number is reduced by one</span></span>I cry out with my voice to Adonai;  I implore favor with my voice to Adonai.'),
('LEB', 'Psalms', '142', '2', '1', 'I pour out my complaint before him;  I declare my trouble before him.'),
('LEB', 'Psalms', '142', '3', '1', 'When my spirit faints within me,  you know my way.  On the path where I walk,  they have hidden a trap for me;'),
('LEB', 'Psalms', '142', '4', '1', 'look to <span class=\"supplied\">my</span> right and see.  There is no one looking out for me;  there is no escape for me;<span class=\"note\">Hebrew “a refuge has perished from me”</span> no one cares for my soul.<span class=\"note\">Or “life”</span>'),
('LEB', 'Psalms', '142', '5', '1', 'I cry out to you, O Adonai.  I say, “You <span class=\"supplied\">are</span> my refuge,  my portion in the land of the living.”'),
('LEB', 'Psalms', '142', '6', '1', 'Attend to my cry,  for I am brought very low.  Deliver me from my pursuers,  for they are stronger than I.'),
('LEB', 'Psalms', '142', '7', '1', 'Bring me<span class=\"note\">Hebrew “my soul”</span>out of prison,  that I may give thanks to your name.  <span class=\"supplied\">The</span> righteous will encircle me,  because you will deal bountifully with me.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '143', '1', '1', '<span class=\"verse-title\">A psalm of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>O Adonai, hear my prayer;  listen to my supplications.  In your faithfulness answer me,  <span class=\"supplied\">and</span> in your righteousness.'),
('LEB', 'Psalms', '143', '2', '1', 'And do not enter into judgment with your servant,  because no one alive <span class=\"supplied\">is</span> righteous before you.'),
('LEB', 'Psalms', '143', '3', '1', 'For <span class=\"supplied\">the</span> enemy has pursued my soul;  he has crushed my life to the ground.  He has made me dwell in dark places  like <span class=\"supplied\">those</span> long dead.'),
('LEB', 'Psalms', '143', '4', '1', 'And <span class=\"supplied\">so</span> my spirit grows faint within me;  my heart <span class=\"idiom\">within</span><span class=\"note\">Literally “in the midst of”</span>me is desolate.'),
('LEB', 'Psalms', '143', '5', '1', 'I remember <span class=\"supplied\">the</span> days of long ago;  I meditate on all your doings.<span class=\"note\">Hebrew “doing”</span> I muse on the labor of your hands.'),
('LEB', 'Psalms', '143', '6', '1', 'I stretch out my hands to you;  my soul <span class=\"supplied\">longs</span> for you like a dry land. <span class=\"italic\">Selah</span>'),
('LEB', 'Psalms', '143', '7', '1', 'Quickly answer me, O Adonai;  my spirit fails.  Do not hide your face from me,  or I will become  like <span class=\"supplied\">those</span> descending to <span class=\"supplied\">the</span> pit.'),
('LEB', 'Psalms', '143', '8', '1', 'Cause me to hear your loyal love in the morning,  for I trust you.  Cause me to know the way that I should go,  for I lift up my soul to you.'),
('LEB', 'Psalms', '143', '9', '1', 'Deliver me from my enemies, O Adonai.  I take refuge in you.<span class=\"note\">This reading for the difficult Hebrew “I cover to you” is suggested by the LXX</span>'),
('LEB', 'Psalms', '143', '10', '1', 'Teach me to do your will, for you <span class=\"supplied\">are</span> my God;  your Spirit <span class=\"supplied\">is</span> good.  Lead me onto level ground.'),
('LEB', 'Psalms', '143', '11', '1', 'For your name’s sake, O Adonai, preserve my life;  in your righteousness bring me<span class=\"note\">Hebrew “my soul”</span>out of trouble.'),
('LEB', 'Psalms', '143', '12', '1', 'And in your loyal love destroy my enemies,  and exterminate all <span class=\"supplied\">the</span> adversaries of my soul,  for I <span class=\"supplied\">am</span> your servant.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '144', '1', '1', '<span class=\"verse-title\">Of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>Blessed be Adonai, my rock,  the one who trains my hands for battle,  my fingers for war—'),
('LEB', 'Psalms', '144', '2', '1', 'my loyal love and my fortress,  my stronghold and my deliverer,  my shield, and <span class=\"supplied\">one</span> in whom I take refuge,  the one who subdues peoples<span class=\"note\">Reading with many Hebrew manuscripts and versions; MT reads “my people”</span>under me.'),
('LEB', 'Psalms', '144', '3', '1', 'O Adonai, what <span class=\"supplied\">is</span> humankind that you take knowledge of him,  <span class=\"supplied\">or the</span> son of man that you take thought of him?'),
('LEB', 'Psalms', '144', '4', '1', 'Humankind is like a breath,  his days like a passing shadow.'),
('LEB', 'Psalms', '144', '5', '1', 'O Adonai, bow the heavens and come down;  touch the mountains so that they smoke.'),
('LEB', 'Psalms', '144', '6', '1', 'Flash forth lightning and scatter them;  dispatch your arrows and rout them.'),
('LEB', 'Psalms', '144', '7', '1', 'Stretch out your hands from on high;  Rescue me and deliver me from many waters,  from <span class=\"supplied\">the</span> hand of foreigners,'),
('LEB', 'Psalms', '144', '8', '1', 'whose mouth speaks falsely,  and their right hand <span class=\"supplied\">is</span> a false right hand.'),
('LEB', 'Psalms', '144', '9', '1', 'O God, I will sing a new song to you.  With a lyre of ten <span class=\"supplied\">strings</span> I will sing praise to you,'),
('LEB', 'Psalms', '144', '10', '1', 'who gives victory to kings,  who rescues David his servant  from <span class=\"supplied\">the</span> evil sword.'),
('LEB', 'Psalms', '144', '11', '1', 'Rescue me and deliver me  from <span class=\"supplied\">the</span> hand of foreigners,  whose mouth speaks falsely,  and whose right hand <span class=\"supplied\">is</span> a false right hand,'),
('LEB', 'Psalms', '144', '12', '1', 'that our sons <span class=\"supplied\">may be</span> like plants,  full grown in their youth,  our daughters like corner pillars,  carved in the style of a palace,'),
('LEB', 'Psalms', '144', '13', '1', '<span class=\"supplied\">that</span> our granaries <span class=\"supplied\">may be</span> full,  providing <span class=\"supplied\">produce</span> of all kinds,<span class=\"note\">Hebrew “from kind to kind”</span> <span class=\"supplied\">that</span> our sheep <span class=\"supplied\">may</span> produce by the thousands,  by the tens of thousands in our open fields,'),
('LEB', 'Psalms', '144', '14', '1', '<span class=\"supplied\">that</span> our cattle <span class=\"supplied\">may be</span> pregnant;  <span class=\"supplied\">that there be</span> no breach <span class=\"supplied\">in our walls</span>,  and no going out <span class=\"supplied\">in exile</span>,  and no outcry in our plazas.'),
('LEB', 'Psalms', '144', '15', '1', 'Blessed <span class=\"supplied\">are</span> the people who have <span class=\"supplied\">it</span> thus.  Blessed <span class=\"supplied\">are</span> the people whose God <span class=\"supplied\">is</span> Adonai.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '145', '1', '1', '<span class=\"verse-title\">A praise of David.<span class=\"note\">The Hebrew Bible counts the superscription as the first verse of the psalm</span></span>I will exalt you, my God, O king,  and I will bless<span class=\"note\">That is, praise; here and v. 2</span>your name forever and ever.'),
('LEB', 'Psalms', '145', '2', '1', 'Every day I will bless you  and praise your name forever and ever.'),
('LEB', 'Psalms', '145', '3', '1', 'Great <span class=\"supplied\">is</span> Adonai, and very worthy of praise,  and his greatness is unsearchable.'),
('LEB', 'Psalms', '145', '4', '1', 'One generation will laud your works to another,  and will declare your mighty deeds.'),
('LEB', 'Psalms', '145', '5', '1', 'On the splendor of the glory of your majesty,  and on your wonderful deeds, I will meditate.'),
('LEB', 'Psalms', '145', '6', '1', 'And they will speak of the power of your awesome deeds,<span class=\"note\">That is, deeds that evoke a fearful reverence</span> and I will tell of your greatness.<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>'),
('LEB', 'Psalms', '145', '7', '1', 'They will utter <span class=\"note\">Hebrew “pour forth”</span>the renown of your abundant goodness,  and they will proclaim with joy your righteousness.'),
('LEB', 'Psalms', '145', '8', '1', 'Adonai <span class=\"supplied\">is</span> gracious and compassionate,  <span class=\"idiom\">slow to anger</span><span class=\"note\">Literally “long of nostrils”</span>and great in loyal love.'),
('LEB', 'Psalms', '145', '9', '1', 'Adonai <span class=\"supplied\">is</span> good to all,  and his mercies <span class=\"supplied\">are</span> over all his works.'),
('LEB', 'Psalms', '145', '10', '1', 'All your works will praise you, O Adonai,  and your faithful ones will bless<span class=\"note\">That is, praise</span>you.'),
('LEB', 'Psalms', '145', '11', '1', 'They will speak of the glory of your kingdom,  and talk of your power,'),
('LEB', 'Psalms', '145', '12', '1', 'to make known to the children of humankind his mighty deeds,  and the glory of the majesty of his kingdom.'),
('LEB', 'Psalms', '145', '13', '1', 'Your kingdom <span class=\"supplied\">is</span> an everlasting kingdom,  and your dominion <span class=\"supplied\">endures</span> <span class=\"idiom\">throughout all generations</span>.<span class=\"note\">Literally “for a generation and a generation”</span>'),
('LEB', 'Psalms', '145', '14', '1', 'Adonai upholds all who are falling,  and raises up all who are bowed down.'),
('LEB', 'Psalms', '145', '15', '1', '<span class=\"supplied\">The</span> eyes of all look hopefully to you,  and you give them their food in due time,'),
('LEB', 'Psalms', '145', '16', '1', 'opening your hand,  and satisfying <span class=\"supplied\">the</span> desire <span class=\"supplied\">of</span> every living creature.'),
('LEB', 'Psalms', '145', '17', '1', 'Adonai <span class=\"supplied\">is</span> righteous in all his ways,  and loyal in all his works.'),
('LEB', 'Psalms', '145', '18', '1', 'Adonai <span class=\"supplied\">is</span> near to all who call on him,  to all who call on him in truth.'),
('LEB', 'Psalms', '145', '19', '1', 'He fulfills the desire of those who fear him;  he also hears their cry for help, and saves them.'),
('LEB', 'Psalms', '145', '20', '1', 'Adonai protects all those who love him,  but all the wicked he will exterminate.'),
('LEB', 'Psalms', '145', '21', '1', 'My mouth will speak the praise of Adonai,  and all flesh will bless his holy name  forever and ever.')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '146', '1', '1', '<span class=\"verse-title\">Praise to Adonai for His Help</span>Praise Yah.<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span> Praise Adonai, O my soul.'),
('LEB', 'Psalms', '146', '2', '1', 'I will praise Adonai while I live;  I will sing praises to my God while I <span class=\"supplied\">am</span> still <span class=\"supplied\">alive</span>.'),
('LEB', 'Psalms', '146', '3', '1', 'Do not place trust in princes,  in a son of humankind with whom there is no deliverance.'),
('LEB', 'Psalms', '146', '4', '1', 'His breath departs; he returns to his plot;<span class=\"note\">A Hebrew wordplay: “humankind,” <span class=\"italic\">adam</span> (v. 3), returns to his ground/plot, <span class=\"italic\">adamah</span></span> on that day his plans perish.'),
('LEB', 'Psalms', '146', '5', '1', 'Blessed <span class=\"supplied\">is the one</span> whose help <span class=\"supplied\">is</span> the God of Jacob,  whose hope <span class=\"supplied\">is</span> on Adonai <span class=\"supplied\">as</span> his God,'),
('LEB', 'Psalms', '146', '6', '1', 'who made heaven and earth,  the sea and all that <span class=\"supplied\">is</span> in them,  the one who keeps faith<span class=\"note\">Or “remains faithful”</span>forever,'),
('LEB', 'Psalms', '146', '7', '1', '<span class=\"supplied\">who</span> executes justice for the oppressed,  <span class=\"supplied\">who</span> gives food for the hungry.  Adonai sets prisoners free;'),
('LEB', 'Psalms', '146', '8', '1', 'Adonai opens <span class=\"supplied\">the eyes of the</span> blind;  Adonai raises up <span class=\"supplied\">those</span> bowed down;  Adonai loves <span class=\"supplied\">the</span> righteous;'),
('LEB', 'Psalms', '146', '9', '1', 'Adonai protects<span class=\"note\">Or “watches over”</span><span class=\"supplied\">the</span> strangers.  He helps up <span class=\"supplied\">the</span> orphan and <span class=\"supplied\">the</span> widow,  but <span class=\"supplied\">the</span> way of <span class=\"supplied\">the</span> wicked he <span class=\"idiom\">thwarts</span>.<span class=\"note\">Literally “makes crooked”</span>'),
('LEB', 'Psalms', '146', '10', '1', 'Adonai will reign forever,  Your God, O Zion, <span class=\"idiom\">throughout all generations</span>.<span class=\"note\">Literally “for a generation and a generation”</span> Praise Yah.<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '147', '1', '1', '<span class=\"verse-title\">Praise to Adonai for His Providence</span>Praise Yah.<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span>; here and v. 20</span> For <span class=\"supplied\">it is</span> good to sing praises <span class=\"supplied\">to</span> our God;  for <span class=\"supplied\">it is</span> pleasant; praise is fitting.'),
('LEB', 'Psalms', '147', '2', '1', 'Adonai <span class=\"supplied\">is</span> building Jerusalem;  he gathers <span class=\"supplied\">the</span> scattered ones of Israel.'),
('LEB', 'Psalms', '147', '3', '1', '<span class=\"supplied\">He is</span> the one who heals the brokenhearted,  and binds up their wounds.'),
('LEB', 'Psalms', '147', '4', '1', '<span class=\"supplied\">He</span> counts <span class=\"supplied\">the</span> number of the stars;  he gives names to all of them.'),
('LEB', 'Psalms', '147', '5', '1', 'Great <span class=\"supplied\">is</span> our Lord, and abundant in power;  his understanding is unlimited.'),
('LEB', 'Psalms', '147', '6', '1', 'Adonai helps the afflicted up;  <span class=\"supplied\">he</span> brings <span class=\"supplied\">the</span> wicked down to <span class=\"supplied\">the</span> ground.'),
('LEB', 'Psalms', '147', '7', '1', 'Sing to Adonai with thanksgiving;  sing praises to our God with lyre,'),
('LEB', 'Psalms', '147', '8', '1', 'who covers <span class=\"supplied\">the</span> heavens with clouds,  who provides rain for the earth,  who causes grass to grow on the mountains.'),
('LEB', 'Psalms', '147', '9', '1', '<span class=\"supplied\">He</span> gives to <span class=\"supplied\">the</span> animal its food,  <span class=\"supplied\">and</span> to <span class=\"supplied\">the</span> young ravens that cry.'),
('LEB', 'Psalms', '147', '10', '1', 'He does not delight in the strength of the horse;  he takes no pleasure in the legs of the man.'),
('LEB', 'Psalms', '147', '11', '1', 'Adonai takes pleasure in those who fear him,  the ones who hope<span class=\"note\">That is, “wait expectantly”</span>for his loyal love.'),
('LEB', 'Psalms', '147', '12', '1', 'Laud Adonai, O Jerusalem;  praise your God, O Zion,'),
('LEB', 'Psalms', '147', '13', '1', 'for he strengthens the bars of your gates.  He blesses your children within you;'),
('LEB', 'Psalms', '147', '14', '1', '<span class=\"supplied\">he</span> makes your border peaceful;  he satisfies you with <span class=\"supplied\">the</span> <span class=\"idiom\">finest of wheat</span>.<span class=\"note\">Literally “<span class=\"supplied\">the</span> fat of the wheat”</span>'),
('LEB', 'Psalms', '147', '15', '1', '<span class=\"supplied\">He</span> sends out his command<span class=\"note\">Or “utterance”</span>to <span class=\"supplied\">the</span> earth;  his word runs swiftly.'),
('LEB', 'Psalms', '147', '16', '1', '<span class=\"supplied\">He</span> gives snow like wool;  he scatters frost like ashes;'),
('LEB', 'Psalms', '147', '17', '1', '<span class=\"supplied\">he</span> throws his hail like crumbs.  Who can stand before his cold?<span class=\"note\">With slight alteration of the Hebrew, the line might read “before his cold water stands”</span>'),
('LEB', 'Psalms', '147', '18', '1', 'He sends out his word and melts them;  he blows his breath, the water flows.'),
('LEB', 'Psalms', '147', '19', '1', '<span class=\"supplied\">He</span> declares his word to Jacob,  his statutes and his ordinances to Israel.'),
('LEB', 'Psalms', '147', '20', '1', 'He has not done so for any nation,  and they do not know <span class=\"supplied\">his</span> ordinances.  Praise Yah.<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '148', '1', '1', '<span class=\"verse-title\">Let All Creation Praise Adonai</span>Praise Yah.<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span> Praise Adonai from the heavens;  praise him in the heights.'),
('LEB', 'Psalms', '148', '2', '1', 'Praise him, all his angels;  praise him, all his hosts.<span class=\"note\">According to the reading tradition (<span class=\"italic\">Qere</span>)</span>'),
('LEB', 'Psalms', '148', '3', '1', 'Praise him, sun and moon;  praise him, all stars of light.'),
('LEB', 'Psalms', '148', '4', '1', 'Praise him, <span class=\"idiom\">highest heavens</span>,<span class=\"note\">Literally “heaven<span class=\"supplied\">s</span> of the heavens”</span> and waters above the heavens.'),
('LEB', 'Psalms', '148', '5', '1', 'Let them praise the name of Adonai,  because he commanded and they were created.'),
('LEB', 'Psalms', '148', '6', '1', 'And he put them in place forever and ever,  <span class=\"supplied\">by</span> a decree he gave that will not pass away.'),
('LEB', 'Psalms', '148', '7', '1', 'Praise Adonai from the earth—  great sea creatures and all deeps,'),
('LEB', 'Psalms', '148', '8', '1', 'fire and hail, snow and cloud,<span class=\"note\">The meaning of “cloud” is uncertain</span> stormy wind doing his bidding,<span class=\"note\">Hebrew “his word”</span>'),
('LEB', 'Psalms', '148', '9', '1', 'the mountains and all hills,  fruit trees and all cedars,'),
('LEB', 'Psalms', '148', '10', '1', 'beasts and all cattle,<span class=\"note\">That is, wild and domesticated</span> creeping things and <span class=\"idiom\">flying birds</span>,<span class=\"note\">Literally “bird of wing”</span>'),
('LEB', 'Psalms', '148', '11', '1', 'kings of <span class=\"supplied\">the</span> earth and all peoples,  princes and all judges of <span class=\"supplied\">the</span> earth,'),
('LEB', 'Psalms', '148', '12', '1', 'young men and young women as well,  <span class=\"supplied\">the</span> old <span class=\"supplied\">together</span> with <span class=\"supplied\">the</span> young—'),
('LEB', 'Psalms', '148', '13', '1', 'let them praise the name of Adonai,  because his name alone <span class=\"supplied\">is</span> is exalted.  His splendor <span class=\"supplied\">is</span> above earth and heavens.'),
('LEB', 'Psalms', '148', '14', '1', 'And he has raised high a horn for his people,  praise for all his faithful—  for the children of Israel,  a people close to him.  Praise Yah.<span class=\"note\">Hebew <span class=\"italic\">hallelujah</span></span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '149', '1', '1', '<span class=\"verse-title\">Praise to God for His Future Judgment</span>Praise Yah.<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span> Sing to Adonai a new song,  his praise in <span class=\"supplied\">the</span> assembly of <span class=\"supplied\">the</span> faithful.'),
('LEB', 'Psalms', '149', '2', '1', 'Let Israel be glad in its maker;  let the children of Zion rejoice in their king.'),
('LEB', 'Psalms', '149', '3', '1', 'Let them praise his name with dancing;  let them sing praises to him with tambourine and lyre.'),
('LEB', 'Psalms', '149', '4', '1', 'For Adonai takes pleasure in his people;  he glorifies <span class=\"supplied\">the</span> afflicted with salvation.'),
('LEB', 'Psalms', '149', '5', '1', 'Let <span class=\"supplied\">the</span> faithful exult in glory;  let them sing for joy on their beds.'),
('LEB', 'Psalms', '149', '6', '1', 'Let the extolling of God <span class=\"supplied\">be</span> in their throat,  and a double-edged sword in their hand,'),
('LEB', 'Psalms', '149', '7', '1', 'to execute vengeance on the nations  <span class=\"supplied\">and</span> punishment on <span class=\"supplied\">the</span> peoples,<span class=\"note\">Reading against the MT and with the LXX</span>'),
('LEB', 'Psalms', '149', '8', '1', 'to bind their kings with chains  and their <span class=\"idiom\">nobles</span><span class=\"note\">Literally “honorable ones”</span>with fetters of iron,'),
('LEB', 'Psalms', '149', '9', '1', 'to execute on them <span class=\"supplied\">the</span> judgment that is decreed.<span class=\"note\">Hebrew “written”</span> This <span class=\"supplied\">will be</span> honor for all his faithful ones;  Praise Yah.<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span>')
INSERT INTO `bible_verses` (`version`, `book`, `chapter`, `verse`, `paragraph`, `content`) VALUES 
('LEB', 'Psalms', '150', '1', '1', '<span class=\"verse-title\">Let Everything Praise Adonai</span>Praise Yah.<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span> Praise God in his sanctuary;  praise him in his mighty firmament.'),
('LEB', 'Psalms', '150', '2', '1', 'Praise him for his mighty deeds;  praise him according to the abundance of his greatness.'),
('LEB', 'Psalms', '150', '3', '1', 'Praise him with blast of horn;  praise him with harp and lyre.'),
('LEB', 'Psalms', '150', '4', '1', 'Praise him with tambourine and dancing;  praise him with strings and flute.'),
('LEB', 'Psalms', '150', '5', '1', 'Praise him with sounding cymbals;  praise him with clashing cymbals.'),
('LEB', 'Psalms', '150', '6', '1', 'Every breathing thing,  let it praise Yah.<span class=\"note\">A shortened form of “Adonai”</span> Praise Yah.<span class=\"note\">Hebrew <span class=\"italic\">hallelujah</span></span>')
